'use client';

import { useState, useEffect, useRef } from 'react';

// Extend CSSProperties to include WebkitAppRegion
declare module 'react' {
  interface CSSProperties {
    WebkitAppRegion?: 'drag' | 'no-drag';
  }
}
import { Input } from './ui/input';

import {
  Search,
  Wand2,
  FileText,
  Code,
  MessageSquare,
  Zap,
  Star
} from 'lucide-react';
import { promptsService, type Prompt } from '../services/promptsService';

interface ActionItem {
  id: string;
  title: string;
  description: string;
  category: 'prompt' | 'action' | 'recent';
  icon: React.ReactNode;
  action: () => void;
  keywords?: string[];
}

export function ActionMenuOverlay() {
  const [searchQuery, setSearchQuery] = useState('');
  const [prompts, setPrompts] = useState<Prompt[]>([]);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const searchInputRef = useRef<HTMLInputElement>(null);

  // Load prompts when component mounts
  useEffect(() => {
    const loadPrompts = async () => {
      try {
        const allPrompts = await promptsService.getAllPrompts();
        setPrompts(allPrompts || []);
      } catch (error) {
        console.error('Failed to load prompts:', error);
        setPrompts([]);
      }
    };

    loadPrompts();

    // Focus search input
    setTimeout(() => {
      searchInputRef.current?.focus();
    }, 100);
  }, []);

  // Create action items from prompts and built-in actions
  const createActionItems = (): ActionItem[] => {
    const items: ActionItem[] = [];

    // Add ALL prompts from prompts service
    prompts.forEach(prompt => {
      items.push({
        id: `prompt-${prompt.id}`,
        title: prompt.name,
        description: prompt.description || prompt.prompt.substring(0, 100) + (prompt.prompt.length > 100 ? '...' : ''),
        category: 'prompt',
        icon: <Wand2 className="h-4 w-4" />,
        action: () => {
          handlePromptSelect(prompt.prompt);
        },
        keywords: [prompt.name, prompt.description, prompt.prompt, prompt.category]
      });
    });

    // Add built-in actions
    const builtInActions: ActionItem[] = [
      {
        id: 'action-summarize',
        title: 'Summarize Text',
        description: 'Summarize the provided text or document',
        category: 'action',
        icon: <FileText className="h-4 w-4" />,
        action: () => {
          handlePromptSelect('Please provide a concise summary of the following text:\n\n{content}\n\nYour summary should capture the main points, key ideas, and overall message of the text in a clear and concise manner. Aim to keep the summary around 100-200 words, depending on the length and complexity of the original text.');
        },
        keywords: ['summarize', 'summary', 'text', 'document']
      },
      {
        id: 'action-explain',
        title: 'Explain Code',
        description: 'Explain how the provided code works',
        category: 'action',
        icon: <Code className="h-4 w-4" />,
        action: () => {
          handlePromptSelect('Please analyze and explain the functionality of the following code snippet. Break down each part of the code to describe what it does, and explain how the code works as a whole.:\n\n{content}');
        },
        keywords: ['explain', 'code', 'programming', 'understand']
      },
      {
        id: 'action-translate',
        title: 'Translate Text',
        description: 'Translate text to another language',
        category: 'action',
        icon: <MessageSquare className="h-4 w-4" />,
        action: () => {
          handlePromptSelect('Please translate the following text to English:\n\n{content}\n\nIf the original text is not in English, provide the translation along with the original language if possible. Thank you!');
        },
        keywords: ['translate', 'language', 'convert']
      },
      {
        id: 'action-improve',
        title: 'Improve Writing',
        description: 'Improve grammar and style of text',
        category: 'action',
        icon: <Zap className="h-4 w-4" />,
        action: () => {
          handlePromptSelect('Please review and improve the grammar and style of the following text. Ensure that the improved version maintains the original meaning while enhancing clarity, readability, and overall quality.:\n\n{content}');
        },
        keywords: ['improve', 'grammar', 'writing', 'style']
      },
      {
        id: 'action-brainstorm',
        title: 'Brainstorm Ideas',
        description: 'Generate creative ideas for a topic',
        category: 'action',
        icon: <Star className="h-4 w-4" />,
        action: () => {
          handlePromptSelect('Please brainstorm creative ideas for:\n\n{content}');
        },
        keywords: ['brainstorm', 'ideas', 'creative', 'generate']
      }
    ];

    return [...items, ...builtInActions];
  };

  const handlePromptSelect = (promptText: string) => {
    // Send the prompt back to the main window and close overlay
    if (typeof window !== 'undefined' && window.electronAPI) {
      console.log('Selected prompt:', promptText);
      // Send the prompt to the main window
      window.electronAPI.sendPromptToMain(promptText);
    }
  };

  const handleClose = () => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.closeActionMenu();
    }
  };

  // Filter items based on search query
  const filteredItems = createActionItems().filter(item => {
    if (!searchQuery.trim()) return true;

    const query = searchQuery.toLowerCase();
    const searchableText = [
      item.title,
      item.description,
      ...(item.keywords || [])
    ].join(' ').toLowerCase();

    return searchableText.includes(query);
  });

  // Handle keyboard navigation
  const handleKeyDown = (e: React.KeyboardEvent) => {
    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setSelectedIndex(prev => Math.min(prev + 1, filteredItems.length - 1));
        break;
      case 'ArrowUp':
        e.preventDefault();
        setSelectedIndex(prev => Math.max(prev - 1, 0));
        break;
      case 'Enter':
        e.preventDefault();
        if (filteredItems[selectedIndex]) {
          filteredItems[selectedIndex].action();
        }
        break;
      case 'Escape':
        e.preventDefault();
        handleClose();
        break;
    }
  };

  // Reset selected index when search changes
  useEffect(() => {
    setSelectedIndex(0);
  }, [searchQuery]);

  return (
    <div className="action-menu-overlay h-full w-full flex flex-col overflow-hidden" style={{ backgroundColor: 'hsl(var(--background))', color: 'hsl(var(--foreground))' }}>
      {/* Draggable Title Bar */}
      <div
        className="flex items-center justify-between px-3 py-2 border-b border-border cursor-grab active:cursor-grabbing bg-muted hover:bg-muted/80 transition-colors"
        style={{
          backgroundColor: 'hsl(var(--muted))',
          borderBottom: '1px solid hsl(var(--border))',
          WebkitAppRegion: 'drag',
          minHeight: '40px'
        }}
        onMouseDown={(e) => {
          // Enable dragging for the entire title bar area
          e.currentTarget.style.cursor = 'grabbing';
        }}
        onMouseUp={(e) => {
          e.currentTarget.style.cursor = 'grab';
        }}
      >
        <div className="flex items-center gap-2">
          <div className="flex flex-col gap-0.5">
            <div className="w-1 h-1 bg-muted-foreground rounded-full"></div>
            <div className="w-1 h-1 bg-muted-foreground rounded-full"></div>
            <div className="w-1 h-1 bg-muted-foreground rounded-full"></div>
          </div>
          <h2 className="text-sm font-medium text-foreground select-none">
            Actions & Prompts
          </h2>
        </div>
        <button
          onClick={handleClose}
          className="h-6 w-6 p-0 hover:bg-muted rounded flex items-center justify-center text-muted-foreground hover:text-foreground transition-colors"
          style={{ WebkitAppRegion: 'no-drag' }}
        >
          <svg className="h-3 w-3" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>
      </div>





      {/* Search */}
      <div className="p-4 border-b border-border">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            ref={searchInputRef}
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Search actions and prompts..."
            className="pl-10 text-base"
          />
        </div>
      </div>

      {/* Results */}
      <div className="flex-1 overflow-y-auto hide-scrollbar overlay-scroll">
        <div className="p-2">
          {filteredItems.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              <Search className="h-8 w-8 mx-auto mb-2 opacity-50" />
              <p>No actions found</p>
              <p className="text-sm">Try a different search term</p>
            </div>
          ) : (
            <div className="space-y-1">
              {filteredItems.map((item) => (
                <div
                  key={item.id}
                  onClick={item.action}
                  style={{
                    width: '100%',
                    textAlign: 'left',
                    padding: '12px',
                    cursor: 'pointer',
                    backgroundColor: 'transparent',
                    border: 'none',
                    outline: 'none',
                    boxShadow: 'none',
                    borderRadius: '0',
                    margin: '0'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = 'hsl(var(--muted) / 0.5)';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = 'transparent';
                  }}
                >
                  <div style={{ display: 'flex', alignItems: 'flex-start', gap: '12px', width: '100%' }}>
                    <div style={{ flexShrink: 0, marginTop: '2px' }}>
                      {item.icon}
                    </div>
                    <div style={{ flex: 1, minWidth: 0 }}>
                      <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
                        <span style={{ fontWeight: 500, fontSize: '14px', color: 'hsl(var(--foreground))' }}>{item.title}</span>
                        <span style={{ fontSize: '12px', padding: '2px 8px', borderRadius: '9999px', color: 'hsl(var(--muted-foreground))', textTransform: 'capitalize' }}>
                          {item.category}
                        </span>
                      </div>
                      <p style={{ fontSize: '12px', color: 'hsl(var(--muted-foreground))', lineHeight: '1.4' }}>
                        {item.description}
                      </p>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Button } from './ui/button';
import type { ProviderApiKeyData } from '../services/secureApiKeyService';
import { settingsService } from '../services/settingsService';

// Function to get secure API key service when needed
function getSecureApiKeyService() {
  if (typeof window === 'undefined') {
    throw new Error('Secure API key service is only available in browser environment');
  }

  try {
    const service = require('../services/secureApiKeyService').secureApiKeyService;
    if (!service) {
      throw new Error('Secure API key service not found');
    }
    return service;
  } catch (error) {
    throw new Error(`Failed to load secure API key service: ${error instanceof Error ? error.message : String(error)}`);
  }
}

interface ApiKeySettingsProps {
  onApiKeyChange?: (providerId: string, hasKey: boolean) => void;
  onRegisterSaveFunction?: (saveFunction: () => Promise<void>) => void;
}

const PROVIDERS = [
  { id: 'openai', name: 'OpenAI', placeholder: 'sk-...' },
  { id: 'anthropic', name: 'Anthropic', placeholder: 'sk-ant-...' },
  { id: 'gemini', name: 'Google Gemini', placeholder: 'API Key...' },
  { id: 'mistral', name: 'Mistral AI', placeholder: 'API Key...' },
  { id: 'deepseek', name: 'DeepSeek', placeholder: 'sk-...' },
  { id: 'deepinfra', name: 'Deepinfra', placeholder: 'API Key...' },
  { id: 'groq', name: 'Groq', placeholder: 'API Key...' },
  { id: 'openrouter', name: 'OpenRouter', placeholder: 'sk-or-...' },
  { id: 'requesty', name: 'Requesty', placeholder: 'API Key...' },
  { id: 'replicate', name: 'Replicate', placeholder: 'r8_...' },
  { id: 'lmstudio', name: 'LM Studio', placeholder: 'Not required', hasBaseUrl: true },
  { id: 'jan', name: 'Jan AI', placeholder: 'API Key...', hasBaseUrl: true },
  { id: 'ollama', name: 'Ollama', placeholder: 'Not required', hasBaseUrl: true },
  { id: 'n8n', name: 'N8N', placeholder: 'API Key...', hasBaseUrl: true },
];

export function ApiKeySettings({ onApiKeyChange, onRegisterSaveFunction }: ApiKeySettingsProps) {
  const [apiKeys, setApiKeys] = useState<Record<string, ProviderApiKeyData>>({});
  const [isLoading, setIsLoading] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);
  const [validationErrors, setValidationErrors] = useState<Record<string, string>>({});


  // Load API keys on component mount
  useEffect(() => {
    // Reduce console spam - only log once per session
    if (!(window as any).__apiKeySettingsLogged) {
      console.log('üîê ApiKeySettings: Component mounted');
      (window as any).__apiKeySettingsLogged = true;
    }

    const initializeApiKeys = async () => {
      try {
        const service = getSecureApiKeyService();
        // Only log during first initialization attempt to reduce console spam
        if (!service.isInitialized()) {
          console.log('üîê ApiKeySettings: Waiting for secure API key service...');

          // Wait for initialization without retrying (service handles its own initialization)
          await service.waitForInitialization();

          if (!service.isInitialized()) {
            console.warn('üîê ApiKeySettings: Service initialization timed out');
            return;
          }
        }

        if (service.isInitialized()) {
          // Service is ready - only log once to reduce console spam
          loadApiKeys(true); // Force reset on initial load

          // Listen for API key changes
          const handleApiKeyChange = (providerId: string, hasKey: boolean) => {
            onApiKeyChange?.(providerId, hasKey);
          };

          service.addListener(handleApiKeyChange);

          return () => {
            service.removeListener(handleApiKeyChange);
          };
        } else {
          console.error('üîê ApiKeySettings: Service failed to initialize');
        }
      } catch (error) {
        console.error('üîê ApiKeySettings: Failed to initialize:', error);
      }
    };

    initializeApiKeys();
  }, []); // Remove onApiKeyChange dependency to prevent re-initialization

  // Listen for settings saved events to refresh API keys
  useEffect(() => {
    const handleSettingsSaved = (event: CustomEvent) => {
      console.log('üîÑ ApiKeySettings: Settings saved event received, reloading API keys');
      // Reload API keys from storage after settings are saved
      setTimeout(() => {
        loadApiKeys(true); // Force reload from storage
      }, 200); // Small delay to ensure save is complete
    };

    window.addEventListener('settingsSaved', handleSettingsSaved as EventListener);
    return () => {
      window.removeEventListener('settingsSaved', handleSettingsSaved as EventListener);
    };
  }, []);

  // Register save function with parent component
  useEffect(() => {
    if (onRegisterSaveFunction) {
      const saveFunction = async () => {
        if (hasChanges) {
          console.log('üîê ApiKeySettings: Executing save via parent request');
          await handleSave();
        } else {
          console.log('üîê ApiKeySettings: No changes to save');
        }
      };

      console.log('üîê ApiKeySettings: Registering save function with parent');
      onRegisterSaveFunction(saveFunction);
    }
  }, [hasChanges, onRegisterSaveFunction]); // Re-register when hasChanges updates

  const loadApiKeys = (forceReset = false) => {
    console.log('üîç loadApiKeys called with forceReset:', forceReset);

    const loadedKeys: Record<string, ProviderApiKeyData> = {};

    try {
      const service = getSecureApiKeyService();
      console.log('üîç Service initialized:', service.isInitialized());

      // Also get settings to merge baseUrl from regular settings
      const currentSettings = settingsService.getSettings();
      console.log('üîç Current settings providers:', currentSettings?.chat?.providers);

      PROVIDERS.forEach(provider => {
        try {
          const data = service.getApiKeyData(provider.id);
          const settingsBaseUrl = currentSettings?.chat?.providers?.[provider.id]?.baseUrl || '';

          if (data) {
            // Merge secure storage data with settings baseUrl if secure storage doesn't have it
            loadedKeys[provider.id] = {
              ...data,
              baseUrl: data.baseUrl || settingsBaseUrl
            };
            console.log(`üîç Loaded API key for ${provider.id}:`, {
              hasApiKey: !!data.apiKey,
              keyLength: data.apiKey?.length || 0,
              hasBaseUrl: !!(data.baseUrl || settingsBaseUrl),
              baseUrlSource: data.baseUrl ? 'secure' : settingsBaseUrl ? 'settings' : 'none'
            });
          } else {
            loadedKeys[provider.id] = {
              apiKey: '',
              baseUrl: settingsBaseUrl,
              lastSelectedModel: ''
            };
            console.log(`üîç No API key data for ${provider.id}, using baseUrl from settings:`, settingsBaseUrl);
          }
        } catch (error) {
          console.error(`üîê ApiKeySettings: Error loading API key for ${provider.id}:`, error);
          // Don't mask the error - let user know service is not ready
          if (error instanceof Error && error.message.includes('not initialized')) {
            console.warn('üîê ApiKeySettings: Service not initialized, will retry when ready');
          }
          const settingsBaseUrl = currentSettings?.chat?.providers?.[provider.id]?.baseUrl || '';
          loadedKeys[provider.id] = { apiKey: '', baseUrl: settingsBaseUrl, lastSelectedModel: '' };
        }
      });
    } catch (error) {
      console.error('üîê ApiKeySettings: Service not available:', error);
      PROVIDERS.forEach(provider => {
        loadedKeys[provider.id] = { apiKey: '', baseUrl: '', lastSelectedModel: '' };
      });
    }

    console.log('üîç Setting API keys in component state:', Object.keys(loadedKeys));
    setApiKeys(loadedKeys);

    // Only reset hasChanges if forced (initial load or cancel) or if no changes exist
    if (forceReset || !hasChanges) {
      setHasChanges(false);
    }

    setValidationErrors({});

  };

  const updateApiKey = (providerId: string, field: keyof ProviderApiKeyData, value: string) => {
    setApiKeys(prev => ({
      ...prev,
      [providerId]: {
        ...prev[providerId],
        [field]: value
      }
    }));

    setHasChanges(true);

    // Notify parent component that API keys have changed
    if (onApiKeyChange) {
      onApiKeyChange(providerId, value.length > 0);
    }

    // Clear validation error for this provider
    if (validationErrors[providerId]) {
      setValidationErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[providerId];
        return newErrors;
      });
    }


  };

  const validateApiKey = (providerId: string, apiKey: string): string | null => {
    if (!apiKey.trim()) {
      // Only validate if the provider requires an API key
      const provider = PROVIDERS.find(p => p.id === providerId);
      if (provider && !['lmstudio', 'ollama'].includes(providerId)) {
        return null; // Allow empty keys, they're optional
      }
      return null;
    }
    
    try {
      const service = getSecureApiKeyService();
      const validation = service.validateApiKey(providerId, apiKey);
      return validation.isValid ? null : validation.error || 'Invalid API key format';
    } catch (error) {
      console.warn('üîê ApiKeySettings: Validation service not available:', error);
      return null; // No validation if service not available
    }
  };

  const handleSave = async () => {
    setIsLoading(true);
    const errors: Record<string, string> = {};

    try {
      // Get service and check if it's available and initialized
      const service = getSecureApiKeyService();

      // ALWAYS SAVE - No conditions that prevent saving
      console.log('üîê ApiKeySettings: Executing save (ALWAYS SAVE mode)');

      // ALWAYS SAVE - No validation that prevents saving
      // Still validate for user feedback, but don't prevent saving
      for (const [providerId, data] of Object.entries(apiKeys)) {
        if (data.apiKey.trim()) {
          const error = validateApiKey(providerId, data.apiKey);
          if (error) {
            errors[providerId] = error;
            console.warn(`‚ö†Ô∏è Validation warning for ${providerId}: ${error} (but saving anyway)`);
          }
        }
      }

      // Set validation errors for display but continue saving
      setValidationErrors(errors);

      // ALWAYS SAVE ALL API KEYS - No conditions that prevent saving
      for (const [providerId, data] of Object.entries(apiKeys)) {
        try {
          // Always save, regardless of whether fields are empty or not
          await service.setApiKeyData(providerId, {
            apiKey: data.apiKey.trim(),
            baseUrl: data.baseUrl?.trim(),
            lastSelectedModel: data.lastSelectedModel
          });
          console.log(`‚úÖ Saved API key for ${providerId}`);

          // Also save baseUrl to regular settings for providers that use it
          if (data.baseUrl?.trim() && (providerId === 'ollama' || providerId === 'lmstudio' || providerId === 'jan' || providerId === 'n8n')) {
            try {
              const currentSettings = settingsService.getSettings();
              if (currentSettings?.chat?.providers) {
                const updatedSettings = {
                  ...currentSettings,
                  chat: {
                    ...currentSettings.chat,
                    providers: {
                      ...currentSettings.chat.providers,
                      [providerId]: {
                        ...currentSettings.chat.providers[providerId],
                        baseUrl: data.baseUrl.trim()
                      }
                    }
                  }
                };
                await settingsService.updateSettings(updatedSettings);
                console.log(`‚úÖ Saved baseUrl to settings for ${providerId}: ${data.baseUrl.trim()}`);
              }
            } catch (settingsError) {
              console.error(`‚ùå Failed to save baseUrl to settings for ${providerId}:`, settingsError);
            }
          }
        } catch (error) {
          console.error(`‚ùå Failed to save API key for ${providerId}:`, error);
          // Don't throw error - continue saving other keys (ALWAYS SAVE mode)
          console.log(`üîÑ Continuing to save other API keys despite error for ${providerId}`);
        }
      }

      setHasChanges(false);
      console.log('‚úÖ All API keys saved successfully');

      // Debug: Check API key state after save
      try {
        const service = getSecureApiKeyService();
        service.debugApiKeyState();
      } catch (error) {
        console.error('üîç Failed to debug API key state:', error);
      }

      // Force reload API keys from storage to ensure UI reflects saved values
      console.log('üîÑ Reloading API keys from storage after save...');
      setTimeout(() => {
        loadApiKeys(true); // Force reload from storage
      }, 100); // Small delay to ensure save is complete
    } catch (error) {
      console.error('‚ùå Failed to save API keys:', error);
      throw error; // Re-throw so parent can handle the error
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    loadApiKeys(true); // Force reset on cancel
  };

  return (
    <div className="space-y-3">
      <div>
        <h3 className="text-sm font-medium mb-2">API Configuration</h3>
        <p className="text-xs text-muted-foreground mb-3">
          API keys are encrypted and stored securely. They are never transmitted in plain text.
        </p>

        <div className="space-y-2">
          {PROVIDERS.map(provider => (
            <div key={provider.id} className="space-y-1">
              <Label htmlFor={`${provider.id}-key`} className="text-xs">{provider.name} API Key</Label>
              <Input
                id={`${provider.id}-key`}
                type="password"
                value={apiKeys[provider.id]?.apiKey || ''}
                placeholder={provider.placeholder}
                className={`h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors ${
                  validationErrors[provider.id] ? 'border-destructive' : ''
                }`}
                onChange={(e) => updateApiKey(provider.id, 'apiKey', e.target.value)}
              />
              {validationErrors[provider.id] && (
                <p className="text-xs text-destructive">{validationErrors[provider.id]}</p>
              )}
              
              {provider.hasBaseUrl && (
                <div className="mt-1">
                  <Label htmlFor={`${provider.id}-url`} className="text-xs">{provider.name} Base URL</Label>
                  <Input
                    id={`${provider.id}-url`}
                    type="url"
                    value={apiKeys[provider.id]?.baseUrl || ''}
                    placeholder={
                      provider.id === 'lmstudio' ? 'http://localhost:1234/v1' :
                      provider.id === 'jan' ? 'http://127.0.0.1:1337/v1' :
                      provider.id === 'ollama' ? 'http://localhost:11434' :
                      provider.id === 'n8n' ? 'https://your-n8n-instance.com/webhook/your-webhook-id' :
                      'Base URL...'
                    }
                    className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                    onChange={(e) => updateApiKey(provider.id, 'baseUrl', e.target.value)}
                  />
                </div>
              )}
            </div>
          ))}
        </div>
        




        {hasChanges && (
          <div className="flex gap-1 mt-3 pt-2 border-t">
            <Button
              variant="outline"
              onClick={handleCancel}
              disabled={isLoading}
              className="h-7 text-xs flex-1"
            >
              Cancel Changes
            </Button>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState, useRef, useEffect, useCallback } from 'react';
import { Button } from './ui/button';
import { Card, CardContent } from './ui/card';
import { X, Minus, MessageSquare, ChevronDown } from 'lucide-react';
import { MessageWithThinking } from './MessageWithThinking';
import { UserMessage } from './UserMessage';
import { ThinkingIndicator } from './ThinkingIndicator';
import { KnowledgeBaseIndicator } from './KnowledgeBaseIndicator';
import { sessionService } from '../services/sessionService';
import type { Message } from '../services/chatService';
import type { SessionStats } from '../services/sessionService';

import './ChatOverlay.css';

interface ChatOverlayProps {
  onClose?: () => void;
}

export function ChatOverlay({ onClose }: ChatOverlayProps) {
  const [messages, setMessages] = useState<Message[]>([]);
  const [sessionStats, setSessionStats] = useState<SessionStats>(sessionService.getSessionStats());
  const [showScrollToBottom, setShowScrollToBottom] = useState(false);
  const [isKnowledgeBaseSearching, setIsKnowledgeBaseSearching] = useState(false);
  const [knowledgeBaseSearchQuery, setKnowledgeBaseSearchQuery] = useState<string>('');
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const scrollContainerRef = useRef<HTMLDivElement>(null);

  // Initialize window dragging using preload script
  useEffect(() => {
    if (typeof window !== 'undefined' && window.electronAPI && window.electronAPI.startDrag) {
      const cleanup = window.electronAPI.startDrag();
      return cleanup;
    }
  }, []);

  const handleClose = () => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.closeChatWindow();
    }
    onClose?.();
  };

  const handleMinimize = () => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.minimizeWindow();
    }
  };

  // Listen for messages from the main window and load initial state
  useEffect(() => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      // Load initial messages from localStorage or request from main window
      const loadInitialMessages = async () => {
        try {
          // First try to get current messages from main window
          const storedMessages = localStorage.getItem('chatWindowMessages');
          if (storedMessages) {
            const parsedMessages = JSON.parse(storedMessages);
            setMessages(parsedMessages);
          }

          // Request current messages from main window
          window.electronAPI.requestCurrentMessages?.();
        } catch (error) {
          console.error('Failed to load initial messages:', error);
        }
      };

      // Listen for message updates from main window
      const handleMessagesUpdate = (newMessages: unknown[]) => {
        const messages = newMessages as Message[];
        console.log('üì® ChatOverlay received messages:', messages.map(m => ({
          id: m.id,
          role: m.role,
          hasContent: !!m.content,
          hasSources: !!m.sources,
          sourcesCount: m.sources?.length || 0,
          sources: m.sources
        })));
        setMessages(messages);
        // Store messages in localStorage for persistence
        localStorage.setItem('chatWindowMessages', JSON.stringify(newMessages));
      };

      // Set up IPC listener for messages
      window.electronAPI.onMessagesUpdate?.(handleMessagesUpdate);

      // Set up IPC listener for knowledge base search updates
      const handleKnowledgeBaseSearchUpdate = (data: {isSearching: boolean, query?: string}) => {
        setIsKnowledgeBaseSearching(data.isSearching);
        if (data.query) {
          setKnowledgeBaseSearchQuery(data.query);
        }
      };

      window.electronAPI.onKnowledgeBaseSearchUpdate?.(handleKnowledgeBaseSearchUpdate);

      // Load initial state
      loadInitialMessages();

      return () => {
        // Clean up listeners
        window.electronAPI.removeAllListeners?.('messages-update');
        window.electronAPI.removeAllListeners?.('knowledge-base-search-update');
      };
    }
  }, []);

  // Update session stats when messages change
  useEffect(() => {
    setSessionStats(sessionService.getSessionStats());
  }, [messages]);

  // Debounced scroll to bottom function using requestAnimationFrame for smooth performance
  const scrollToBottom = useCallback(() => {
    if (!messagesEndRef.current) return;

    requestAnimationFrame(() => {
      messagesEndRef.current?.scrollIntoView({
        behavior: 'smooth',
        block: 'end'
      });
    });
  }, []);

  // Check if user is at bottom of scroll container
  const isAtBottom = useCallback(() => {
    if (!scrollContainerRef.current) return false;

    const { scrollTop, scrollHeight, clientHeight } = scrollContainerRef.current;
    return scrollHeight - scrollTop - clientHeight < 50; // 50px threshold
  }, []);

  // Unified scroll management - handles all scroll scenarios
  useEffect(() => {
    if (!scrollContainerRef.current || messages.length === 0) return;

    const wasAtBottom = isAtBottom();

    // Auto-scroll if user was at bottom or if it's the first message
    if (wasAtBottom || messages.length === 1) {
      // Use a small delay to ensure DOM is updated, but avoid conflicts
      const timeoutId = setTimeout(() => {
        if (isAtBottom() || messages.length === 1) {
          scrollToBottom();
        }
      }, 50);

      return () => clearTimeout(timeoutId);
    }
  }, [messages, scrollToBottom, isAtBottom]);

  // Add scroll listener
  useEffect(() => {
    const scrollContainer = scrollContainerRef.current;
    if (!scrollContainer) return;

    const handleScroll = () => {
      if (!scrollContainerRef.current) return;

      const { scrollTop, scrollHeight, clientHeight } = scrollContainerRef.current;
      const isAtBottom = scrollHeight - scrollTop - clientHeight < 50; // 50px threshold
      setShowScrollToBottom(!isAtBottom && messages.length > 0);
    };

    scrollContainer.addEventListener('scroll', handleScroll);

    // Initial check
    handleScroll();

    return () => {
      scrollContainer.removeEventListener('scroll', handleScroll);
    };
  }, [messages.length]);

  return (
    <div
      className="h-screen w-full bg-background overflow-hidden flex flex-col"
      style={{
        position: 'relative',
        height: '100vh',
        maxHeight: '100vh'
      }}
    >
      {/* Title Bar - Draggable area using preload script */}
      <div
        className="flex-none h-10 bg-muted/50 border-b border-border flex items-center justify-between px-3 select-none chat-title-bar-drag-zone"
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          zIndex: 10000,
          backdropFilter: 'blur(8px)',
          backgroundColor: 'hsl(var(--muted) / 0.98)',
          pointerEvents: 'auto',
          cursor: 'grab'
        }}
      >
        <span className="text-sm font-medium">CHAT WINDOW</span>

        <div className="flex items-center gap-1">
          <Button
            variant="ghost"
            size="sm"
            onClick={handleMinimize}
            className="h-6 w-6 p-0 hover:bg-muted"
            data-interactive="true"
          >
            <Minus className="h-3 w-3" />
          </Button>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleClose}
            className="h-6 w-6 p-0 hover:bg-destructive hover:text-destructive-foreground"
            data-interactive="true"
          >
            <X className="h-3 w-3" />
          </Button>
        </div>
      </div>

      {/* Content Area */}
      <div
        className="chat-overlay-content"
        style={{
          position: 'absolute',
          top: '40px',                    // Start below the fixed title bar
          left: '0',
          right: '0',
          bottom: '0',
          display: 'flex',
          flexDirection: 'column',
          overflow: 'hidden',
          padding: '16px'
        }}
      >
        {/* Knowledge Base Search Indicator */}
        {isKnowledgeBaseSearching && (
          <div className="mb-4">
            <KnowledgeBaseIndicator
              isSearching={isKnowledgeBaseSearching}
              searchQuery={knowledgeBaseSearchQuery}
            />
          </div>
        )}

        {messages.length === 0 ? (
          <div className="flex-1 flex items-center justify-center text-center text-muted-foreground">
            <div>
              <MessageSquare className="h-8 w-8 mx-auto mb-2 opacity-50" />
              <p>No messages yet</p>
              <p className="text-sm">Send a message from the main window to start chatting</p>
            </div>
          </div>
        ) : (
          <div
            ref={scrollContainerRef}
            className="flex-1 overflow-y-auto space-y-4 pr-2 custom-scrollbar"
            style={{
              height: '100%',                  // Fill the content area
              overflowY: 'auto',
              overflowX: 'hidden'
            }}
          >
            {messages.map((message) => (
              <div
                key={message.id}
                className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
              >
                <Card
                  className={`max-w-[85%] shadow-lg ${
                    message.role === 'user'
                      ? 'bg-primary text-primary-foreground user-message'
                      : 'bg-secondary text-foreground assistant-message'
                  }`}
                >
                  <CardContent className="p-3">
                    {message.role === 'assistant' ? (
                      message.isThinking ? (
                        <ThinkingIndicator />
                      ) : (
                        <MessageWithThinking
                          content={
                            typeof message.content === 'string'
                              ? message.content
                              : Array.isArray(message.content)
                                ? message.content.map((item, idx) =>
                                    item.type === 'text' ? item.text : `[Image ${idx + 1}]`
                                  ).join(' ')
                                : String(message.content)
                          }
                          usage={message.usage}
                          cost={message.cost}
                          timing={message.timing}
                          toolCalls={message.toolCalls}
                          sources={message.sources}
                        />
                      )
                    ) : (
                      <UserMessage
                        content={message.content}
                      />
                    )}
                  </CardContent>
                </Card>
              </div>
            ))}
            {/* Scroll anchor */}
            <div ref={messagesEndRef} />
          </div>
        )}

        {/* Scroll to bottom button */}
        {showScrollToBottom && (
          <Button
            onClick={scrollToBottom}
            className="absolute bottom-20 right-4 h-10 w-10 rounded-full bg-primary/90 hover:bg-primary shadow-lg transition-all duration-200 z-50 flex items-center justify-center p-0"
            style={{
              backdropFilter: 'blur(8px)',
            }}
          >
            <ChevronDown className="h-5 w-5" />
          </Button>
        )}
      </div>

      {/* Session Stats Display */}
      {messages.length > 0 && (
        <div className="flex-none px-4 pb-2">
          <div className="text-xs text-muted-foreground bg-muted/30 px-2 py-1 rounded text-center">
            <div className="truncate">
              Session: {sessionStats.totalTokens} tokens ‚Ä¢ {sessionStats.messagesCount} messages ‚Ä¢ {sessionService.formatSessionCost()} ‚Ä¢ {sessionService.formatSessionDuration()}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import React, { useState } from 'react';
import { Copy, Check } from 'lucide-react';
import { Button } from './ui/button';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import './CodeBlock.css';


interface CodeBlockProps {
  code: string;
  language?: string;
  className?: string;
}

// Map common language aliases to their proper syntax highlighter names
const languageMap: Record<string, string> = {
  'js': 'javascript',
  'jsx': 'javascript',
  'ts': 'typescript',
  'tsx': 'typescript',
  'py': 'python',
  'rb': 'ruby',
  'sh': 'bash',
  'shell': 'bash',
  'yml': 'yaml',
  'md': 'markdown',
  'htm': 'html',
  'xml': 'markup',
  'svg': 'markup',
  'vue': 'markup',
  'php': 'php',
  'c': 'c',
  'cpp': 'cpp',
  'c++': 'cpp',
  'cs': 'csharp',
  'java': 'java',
  'kt': 'kotlin',
  'swift': 'swift',
  'go': 'go',
  'rs': 'rust',
  'sql': 'sql',
  'json': 'json',
  'jsonc': 'json',
  'toml': 'toml',
  'ini': 'ini',
  'cfg': 'ini',
  'conf': 'ini',
  'dockerfile': 'docker',
  'makefile': 'makefile',
  'r': 'r',
  'scala': 'scala',
  'clj': 'clojure',
  'cljs': 'clojure',
  'elm': 'elm',
  'haskell': 'haskell',
  'hs': 'haskell',
  'lua': 'lua',
  'perl': 'perl',
  'pl': 'perl',
  'powershell': 'powershell',
  'ps1': 'powershell',
  'diff': 'diff',
  'patch': 'diff',
};

function normalizeLanguage(lang?: string): string {
  if (!lang) return 'text';
  const normalized = lang.toLowerCase().trim();
  return languageMap[normalized] || normalized;
}

export function CodeBlock({ code, language, className = '' }: CodeBlockProps) {
  const [copied, setCopied] = useState(false);
  const normalizedLanguage = normalizeLanguage(language);

  const handleCopy = async () => {
    try {
      await navigator.clipboard.writeText(code);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy code:', error);
      // Fallback for older browsers
      try {
        const textArea = document.createElement('textarea');
        textArea.value = code;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      } catch (fallbackError) {
        console.error('Fallback copy also failed:', fallbackError);
      }
    }
  };

  return (
    <div className={`relative group ${className}`}>
      {/* Header with language and copy button */}
      <div className="flex items-center justify-between bg-slate-700 px-3 py-2 border-b border-slate-600 rounded-t-lg">
        <span className="text-xs text-slate-300 font-medium">
          {language || 'code'}
        </span>
        <Button
          variant="ghost"
          size="sm"
          onClick={handleCopy}
          className="h-6 w-6 p-0 opacity-0 group-hover:opacity-100 transition-opacity hover:bg-slate-600 text-slate-300"
          style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties & { WebkitAppRegion?: string }}
          title="Copy code"
        >
          {copied ? (
            <Check className="h-3 w-3 text-green-400" />
          ) : (
            <Copy className="h-3 w-3" />
          )}
        </Button>
      </div>

      {/* Code content */}
      <div
        className="bg-slate-800 rounded-b-lg overflow-x-auto"
        style={{
          WebkitAppRegion: 'no-drag',
          userSelect: 'text',
          WebkitUserSelect: 'text',
        } as React.CSSProperties & { WebkitAppRegion?: string }}
      >
        <div
          className="syntax-highlighter-isolated"
          style={{
            WebkitAppRegion: 'no-drag',
            userSelect: 'text',
            WebkitUserSelect: 'text',
          } as React.CSSProperties & { WebkitAppRegion?: string }}
        >
          <SyntaxHighlighter
            language={normalizedLanguage}
            style={{}} // Empty style object - we use CSS instead
            customStyle={{
              margin: 0,
              padding: 0,
              background: 'transparent',
              border: 'none',
              outline: 'none',
            }}
            codeTagProps={{
              style: {
                margin: 0,
                padding: 0,
                background: 'transparent',
              }
            }}
            // Render pre at block level to avoid <pre> inside <p> warnings
            PreTag={({ children, ...props }) => (
              <pre
                {...props}
                style={{
                  margin: 0,
                  padding: 0,
                  background: 'transparent',
                }}
              >
                {children}
              </pre>
            )}
          >
            {code}
          </SyntaxHighlighter>
        </div>
      </div>
    </div>
  );
}

interface InlineCodeProps {
  code: string;
  className?: string;
}

export function InlineCode({ code, className = '' }: InlineCodeProps) {
  return (
    <code
      className={`bg-slate-700 text-slate-200 px-1.5 py-0.5 rounded text-sm font-mono ${className}`}
      style={{
        WebkitAppRegion: 'no-drag',
        userSelect: 'text',
        WebkitUserSelect: 'text',
      } as React.CSSProperties & { WebkitAppRegion?: string }}
    >
      {code}
    </code>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { conversationHistoryService, type Conversation } from '../services/conversationHistoryService';

// Removed redundant HistoryOverlay component - only keeping the hook

// Hook to use the history overlay
export function useHistoryOverlay(onLoadConversation: (conversation: Conversation) => Promise<void>) {
  const [loading, setLoading] = useState(false);

  const openHistory = async () => {
    setLoading(true);
    try {
      const allConversations = await conversationHistoryService.getAllConversations();
      
      if (typeof window !== 'undefined' && window.electronAPI) {
        await window.electronAPI.openHistory(allConversations);
      }
    } catch (error) {
      console.error('Failed to open history:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteConversation = async (conversationId: string) => {
    try {
      await conversationHistoryService.deleteConversation(conversationId);
      
      // Refresh and reopen the window
      const allConversations = await conversationHistoryService.getAllConversations();
      if (typeof window !== 'undefined' && window.electronAPI) {
        await window.electronAPI.openHistory(allConversations);
      }
    } catch (error) {
      console.error('Failed to delete conversation:', error);
    }
  };

  const handleClearAllHistory = async () => {
    try {
      await conversationHistoryService.clearAllHistory();
      
      // Close the history window
      if (typeof window !== 'undefined' && window.electronAPI) {
        await window.electronAPI.closeHistory();
      }
    } catch (error) {
      console.error('Failed to clear history:', error);
    }
  };

  const handleLoadConversation = async (conversationId: string) => {
    try {
      const conversation = await conversationHistoryService.getConversation(conversationId);
      if (conversation) {
        await onLoadConversation(conversation);
      }
    } catch (error) {
      console.error('Failed to load conversation:', error);
    }
  };

  useEffect(() => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      // Listen for history events
      window.electronAPI.onHistoryItemSelected(handleLoadConversation);
      window.electronAPI.onHistoryItemDeleted(handleDeleteConversation);
      window.electronAPI.onClearAllHistory(handleClearAllHistory);

      // Cleanup function to remove listeners on unmount
      return () => {
        // Remove listeners to prevent memory leaks
        if (window.electronAPI && window.electronAPI.removeAllListeners) {
          window.electronAPI.removeAllListeners('history-item-selected');
          window.electronAPI.removeAllListeners('history-item-deleted');
          window.electronAPI.removeAllListeners('clear-all-history');
        }
      };
    }
  }, []); // Remove dependencies to prevent re-adding listeners

  return {
    openHistory,
    loading
  };
}

'use client';

import React, { useState, useEffect } from 'react';
import { X, Download, ExternalLink, ZoomIn, ZoomOut, RotateCw } from 'lucide-react';
import { Button } from './ui/button';

interface ImageViewerProps {
  src: string;
  alt?: string;
  isOpen: boolean;
  onClose: () => void;
}

/**
 * Modal image viewer with zoom, rotate, and download functionality
 */
export function ImageViewer({ src, alt = 'Image', isOpen, onClose }: ImageViewerProps) {
  const [zoom, setZoom] = useState(1);
  const [rotation, setRotation] = useState(0);
  const [isDragging, setIsDragging] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });

  // Reset state when modal opens/closes
  useEffect(() => {
    if (isOpen) {
      setZoom(1);
      setRotation(0);
      setPosition({ x: 0, y: 0 });
    }
  }, [isOpen]);

  // Handle keyboard shortcuts
  useEffect(() => {
    if (!isOpen) return;

    const handleKeyDown = (e: KeyboardEvent) => {
      switch (e.key) {
        case 'Escape':
          onClose();
          break;
        case '+':
        case '=':
          e.preventDefault();
          setZoom(prev => Math.min(prev * 1.2, 5));
          break;
        case '-':
          e.preventDefault();
          setZoom(prev => Math.max(prev / 1.2, 0.1));
          break;
        case '0':
          e.preventDefault();
          setZoom(1);
          setPosition({ x: 0, y: 0 });
          setRotation(0);
          break;
        case 'r':
        case 'R':
          e.preventDefault();
          setRotation(prev => (prev + 90) % 360);
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isOpen, onClose]);

  // Handle mouse drag
  const handleMouseDown = (e: React.MouseEvent) => {
    if (zoom > 1) {
      setIsDragging(true);
      setDragStart({
        x: e.clientX - position.x,
        y: e.clientY - position.y
      });
    }
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (isDragging && zoom > 1) {
      setPosition({
        x: e.clientX - dragStart.x,
        y: e.clientY - dragStart.y
      });
    }
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  // Handle download
  const handleDownload = async () => {
    try {
      if (src.startsWith('data:')) {
        // Handle base64 data URLs
        const link = document.createElement('a');
        link.href = src;
        link.download = alt || 'image';
        link.click();
      } else {
        // Handle regular URLs
        const response = await fetch(src);
        const blob = await response.blob();
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = alt || 'image';
        link.click();
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Failed to download image:', error);
      // Fallback: open in new tab
      window.open(src, '_blank');
    }
  };

  // Handle external link
  const handleExternalLink = () => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.openExternal(src);
    } else {
      window.open(src, '_blank');
    }
  };

  if (!isOpen) return null;

  return (
    <div
      className="fixed inset-0 z-50 bg-black/90 flex items-center justify-center"
      onClick={onClose}
      style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties & { WebkitAppRegion?: string }}
    >
      {/* Controls */}
      <div className="absolute top-4 right-4 flex gap-2 z-10">
        <Button
          variant="secondary"
          size="sm"
          onClick={(e) => {
            e.stopPropagation();
            setZoom(prev => Math.min(prev * 1.2, 5));
          }}
          title="Zoom In (+)"
        >
          <ZoomIn className="h-4 w-4" />
        </Button>
        <Button
          variant="secondary"
          size="sm"
          onClick={(e) => {
            e.stopPropagation();
            setZoom(prev => Math.max(prev / 1.2, 0.1));
          }}
          title="Zoom Out (-)"
        >
          <ZoomOut className="h-4 w-4" />
        </Button>
        <Button
          variant="secondary"
          size="sm"
          onClick={(e) => {
            e.stopPropagation();
            setRotation(prev => (prev + 90) % 360);
          }}
          title="Rotate (R)"
        >
          <RotateCw className="h-4 w-4" />
        </Button>
        <Button
          variant="secondary"
          size="sm"
          onClick={(e) => {
            e.stopPropagation();
            handleDownload();
          }}
          title="Download"
        >
          <Download className="h-4 w-4" />
        </Button>
        {!src.startsWith('data:') && (
          <Button
            variant="secondary"
            size="sm"
            onClick={(e) => {
              e.stopPropagation();
              handleExternalLink();
            }}
            title="Open in Browser"
          >
            <ExternalLink className="h-4 w-4" />
          </Button>
        )}
        <Button
          variant="secondary"
          size="sm"
          onClick={onClose}
          title="Close (Esc)"
        >
          <X className="h-4 w-4" />
        </Button>
      </div>

      {/* Zoom indicator */}
      {zoom !== 1 && (
        <div className="absolute top-4 left-4 bg-black/50 text-white px-2 py-1 rounded text-sm">
          {Math.round(zoom * 100)}%
        </div>
      )}

      {/* Help text */}
      <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2 bg-black/50 text-white px-3 py-1 rounded text-sm">
        Use +/- to zoom, R to rotate, drag to pan, 0 to reset, Esc to close
      </div>

      {/* Image */}
      <div
        className="max-w-full max-h-full overflow-hidden cursor-move"
        onClick={(e) => e.stopPropagation()}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        onMouseLeave={handleMouseUp}
        style={{
          cursor: zoom > 1 ? (isDragging ? 'grabbing' : 'grab') : 'default'
        }}
      >
        <img
          src={src}
          alt={alt}
          className="max-w-none transition-transform duration-200"
          style={{
            transform: `scale(${zoom}) rotate(${rotation}deg) translate(${position.x / zoom}px, ${position.y / zoom}px)`,
            transformOrigin: 'center center',
            userSelect: 'none',
            WebkitUserSelect: 'none'
          }}
          draggable={false}
        />
      </div>
    </div>
  );
}

import React from 'react';
import { Search, Database, BookOpen } from 'lucide-react';

interface KnowledgeBaseIndicatorProps {
  isSearching: boolean;
  searchQuery?: string;
  className?: string;
}

export const KnowledgeBaseIndicator: React.FC<KnowledgeBaseIndicatorProps> = ({
  isSearching,
  searchQuery,
  className = ''
}) => {
  if (!isSearching) return null;

  return (
    <div className={`flex items-center gap-2 text-sm text-muted-foreground bg-muted/30 px-3 py-2 rounded-lg border border-border/50 ${className}`}>
      <div className="flex items-center gap-2">
        <div className="relative">
          <Database className="h-4 w-4" />
          <div className="absolute -top-1 -right-1">
            <Search className="h-3 w-3 animate-pulse text-blue-500" />
          </div>
        </div>
        <div className="flex items-center gap-1">
          <BookOpen className="h-3 w-3" />
          <span className="font-medium">Searching knowledge base...</span>
        </div>
      </div>

      {searchQuery && (
        <div className="text-xs text-muted-foreground/70 truncate max-w-[200px]">
          "{searchQuery.length > 50 ? searchQuery.substring(0, 50) + '...' : searchQuery}"
        </div>
