      {/* Animated dots */}
      <div className="flex gap-1">
        <div className="w-1 h-1 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
        <div className="w-1 h-1 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
        <div className="w-1 h-1 bg-blue-500 rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
      </div>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { ProgressLogPanel } from './ProgressLogPanel';
import { Button } from './ui/button';

interface UploadProgress {
  fileName: string;
  status: 'pending' | 'processing' | 'success' | 'error';
  error?: string;
}

interface DocumentWithMetadata {
  source: string;
  metadata: Record<string, unknown>;
  chunkCount: number;
  addedAt?: string;
}

interface KnowledgeBaseStats {
  totalRecords: number;
  totalDocuments: number;
  databaseSize: number;
}

const KnowledgeBaseSettings = () => {
  const [message, setMessage] = useState('');
  const [documents, setDocuments] = useState<DocumentWithMetadata[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState<UploadProgress[]>([]);
  const [googleDocsUrl, setGoogleDocsUrl] = useState<string>('');
  const [isUrlImporting, setIsUrlImporting] = useState(false);
  const [isExporting, setIsExporting] = useState(false);
  const [isImporting, setIsImporting] = useState(false);
  const [kbStats, setKbStats] = useState<KnowledgeBaseStats | null>(null);
  const [exportProgress, setExportProgress] = useState<{step: string, current: number, total: number, message: string} | null>(null);
  const [importProgress, setImportProgress] = useState<{step: string, current: number, total: number, message: string} | null>(null);
  const [progressEntries, setProgressEntries] = useState<Array<{
    id: string;
    timestamp: Date;
    level: 'info' | 'warning' | 'error' | 'success';
    operation: string;
    message: string;
    progress?: { current: number; total: number; percentage: number };
    metadata?: Record<string, unknown>;
  }>>([]);
  const [isProgressLogCollapsed, setIsProgressLogCollapsed] = useState(false);

  const loadDocuments = async () => {
    try {
      setIsLoading(true);
      const result = await window.electronAPI.getDocumentsWithMetadata();
      if (result.success) {
        setDocuments(result.documents);
      } else {
        console.error('Failed to load documents:', result.error);
        setDocuments([]);
      }
    } catch (error) {
      console.error('Error loading documents:', error);
      setDocuments([]);
    } finally {
      setIsLoading(false);
    }
  };

  const loadKnowledgeBaseStats = async () => {
    try {
      const result = await window.electronAPI.getKnowledgeBaseStats();
      if (result.success && result.stats) {
        setKbStats(result.stats);
      }
    } catch (error) {
      console.error('Error loading knowledge base stats:', error);
    }
  };

  useEffect(() => {
    loadDocuments();
    loadKnowledgeBaseStats();

    // Set up progress listeners
    const exportUnsubscribe = window.electronAPI.onExportProgress((progress) => {
      setExportProgress(progress);
    });

    const importUnsubscribe = window.electronAPI.onImportProgress((progress) => {
      setImportProgress(progress);
    });

    const batchUnsubscribe = window.electronAPI.onBatchProgress((progress) => {
      // Add real-time progress entries for batch operations
      const operationId = `batch-upload-${Date.now()}`;

      if (progress.status === 'processing') {
        addProgressEntry(
          'info',
          operationId,
          progress.message,
          { current: progress.fileIndex, total: progress.totalFiles },
          {
            fileName: progress.fileName,
            step: progress.step,
            chunkCount: progress.chunkCount
          }
        );
      } else if (progress.status === 'success') {
        addProgressEntry(
          'success',
          operationId,
          progress.message,
          { current: progress.fileIndex, total: progress.totalFiles },
          {
            fileName: progress.fileName,
            chunkCount: progress.chunkCount
          }
        );
      } else if (progress.status === 'error') {
        addProgressEntry(
          'error',
          operationId,
          progress.message,
          { current: progress.fileIndex, total: progress.totalFiles },
          {
            fileName: progress.fileName,
            error: progress.error
          }
        );
      }

      // Update the upload progress state for the UI
      setUploadProgress(prev => {
        const newProgress = [...prev];
        const fileIndex = progress.fileIndex - 1; // Convert to 0-based index

        if (fileIndex >= 0 && fileIndex < newProgress.length) {
          newProgress[fileIndex] = {
            fileName: progress.fileName,
            status: progress.status === 'success' ? 'success' :
                   progress.status === 'error' ? 'error' : 'processing',
            error: progress.error
          };
        }

        return newProgress;
      });
    });

    return () => {
      exportUnsubscribe();
      importUnsubscribe();
      batchUnsubscribe();
    };
  }, []);

  const addProgressEntry = (
    level: 'info' | 'warning' | 'error' | 'success',
    operation: string,
    message: string,
    progress?: { current: number; total: number },
    metadata?: Record<string, unknown>
  ) => {
    const entry = {
      id: `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
      timestamp: new Date(),
      level,
      operation,
      message,
      progress: progress ? {
        current: progress.current,
        total: progress.total,
        percentage: Math.round((progress.current / progress.total) * 100)
      } : undefined,
      metadata
    };

    setProgressEntries(prev => {
      const newEntries = [...prev, entry];
      // Limit to last 500 entries to prevent memory issues
      return newEntries.slice(-500);
    });
  };

  const clearProgressLog = () => {
    setProgressEntries([]);
    addProgressEntry('info', 'system', 'Progress log cleared');
  };

  const handleAddDocument = async () => {
    try {
      setIsLoading(true);
      const filePath = await window.electronAPI.openFileDialog();
      if (filePath) {
        const operationId = `single-upload-${Date.now()}`;
        const fileName = filePath.split(/[\\/]/).pop() || filePath;

        addProgressEntry('info', operationId, `Starting upload: ${fileName}`);
        setMessage(`Adding document: ${filePath}...`);

        const result = await window.electronAPI.addDocument(filePath);
        if (result.success) {
          setMessage(`Successfully added document: ${filePath}`);
          addProgressEntry('success', operationId, `Successfully uploaded: ${fileName}`);
          await loadDocuments(); // Refresh document list
          await loadKnowledgeBaseStats(); // Refresh stats
        } else {
          setMessage(`Failed to add document: ${result.error}`);
          addProgressEntry('error', operationId, `Failed to upload ${fileName}: ${result.error}`);
        }
      } else {
        setMessage('No file selected.');
        addProgressEntry('info', 'single-upload', 'Upload cancelled: No file selected');
      }
    } catch (error) {
      console.error('Error adding document:', error);
      const errorMessage = `Error adding document: ${error instanceof Error ? error.message : 'Unknown error'}`;
      setMessage(errorMessage);
      addProgressEntry('error', 'single-upload', errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddDocumentsBatch = async () => {
    try {
      setIsLoading(true);
      setUploadProgress([]);
      const filePaths = await window.electronAPI.openKnowledgebaseFileDialog();

      if (filePaths && filePaths.length > 0) {
        const operationId = `batch-upload-${Date.now()}`;
        addProgressEntry('info', operationId, `Starting batch upload of ${filePaths.length} documents`);

        setMessage(`Adding ${filePaths.length} documents...`);

        // Initialize progress tracking
        const initialProgress = filePaths.map(path => ({
          fileName: path.split(/[\\/]/).pop() || path,
          status: 'pending' as const
        }));
        setUploadProgress(initialProgress);

        if (filePaths.length > 5) {
          setMessage(`Processing ${filePaths.length} documents. This may take several minutes for large documents...`);
          addProgressEntry('warning', operationId, `Large batch detected: ${filePaths.length} files may take several minutes to process`);
        }

        // The real-time progress updates will be handled by the batch progress listener
        const result = await window.electronAPI.addDocumentsBatch(filePaths);

        if (result.success) {
          setMessage(result.summary || `Successfully added ${filePaths.length} documents`);
          addProgressEntry('success', operationId, result.summary || `Batch upload completed: ${filePaths.length} documents processed`);

          // Log summary statistics
          if (result.results) {
            const successCount = result.results.filter(r => r.success).length;
            const errorCount = result.results.length - successCount;
            const totalChunks = result.results.reduce((sum, r) => sum + ((r as any).chunkCount || 0), 0);

            addProgressEntry('info', operationId, `Final statistics: ${successCount} successful, ${errorCount} failed, ${totalChunks} total chunks generated`, undefined, {
              successCount,
              errorCount,
              totalFiles: result.results.length,
              totalChunks
            });

            if (errorCount > 0) {
              addProgressEntry('warning', operationId, `${errorCount} files failed to process`, undefined, {
                successCount,
                errorCount,
                totalFiles: result.results.length
              });
            }
          }

          await loadDocuments(); // Refresh document list
          await loadKnowledgeBaseStats(); // Refresh stats
        } else {
          setMessage(`Failed to add documents: ${result.error}`);
          addProgressEntry('error', operationId, `Batch upload failed: ${result.error}`);
        }
      } else {
        setMessage('No files selected.');
        addProgressEntry('info', 'batch-upload', 'Upload cancelled: No files selected');
      }
    } catch (error) {
      console.error('Error adding documents:', error);
      setMessage(`Error adding documents: ${error instanceof Error ? error.message : 'Unknown error'}`);

      // Update progress to show error
      const errorProgress = uploadProgress.map(p => ({
        ...p,
        status: 'error' as const,
        error: error instanceof Error ? error.message : 'Unknown error'
      }));
      setUploadProgress(errorProgress);
    } finally {
      setIsLoading(false);
    }
  };

  const handleAddFromGoogleDocs = async () => {
    if (!googleDocsUrl.trim()) {
      setMessage('Please enter a Google Docs URL.');
      addProgressEntry('warning', 'google-docs-import', 'Import cancelled: No URL provided');
      return;
    }

    try {
      setIsUrlImporting(true);
      const operationId = `google-docs-import-${Date.now()}`;

      addProgressEntry('info', operationId, `Starting Google Docs import: ${googleDocsUrl}`);
      setMessage(`Importing document from Google Docs...`);

      const result = await window.electronAPI.addDocumentFromUrl(googleDocsUrl);

      if (result.success) {
        setMessage(`Successfully imported document from Google Docs`);
        addProgressEntry('success', operationId, 'Google Docs import completed successfully');
        setGoogleDocsUrl(''); // Clear the URL input
        await loadDocuments(); // Refresh document list
        await loadKnowledgeBaseStats(); // Refresh stats
      } else {
        setMessage(`Failed to import from Google Docs: ${result.error}`);
        addProgressEntry('error', operationId, `Google Docs import failed: ${result.error}`);
      }
    } catch (error) {
      console.error('Error importing from Google Docs:', error);
      const errorMessage = `Error importing from Google Docs: ${error instanceof Error ? error.message : 'Unknown error'}`;
      setMessage(errorMessage);
      addProgressEntry('error', 'google-docs-import', errorMessage);
    } finally {
      setIsUrlImporting(false);
    }
  };

  const handleExportKnowledgeBase = async () => {
    try {
      setIsExporting(true);
      setExportProgress(null);
      const operationId = `export-${Date.now()}`;

      addProgressEntry('info', operationId, 'Starting knowledge base export...');
      setMessage('Starting knowledge base export...');

      const result = await window.electronAPI.exportKnowledgeBase();

      if (result.success && result.stats) {
        const sizeInMB = (result.stats.exportSize / (1024 * 1024)).toFixed(2);
        const timeInSeconds = (result.stats.exportTime / 1000).toFixed(1);
        const successMessage = `Export completed: ${result.stats.totalDocuments} documents, ${result.stats.totalRecords} chunks exported. File size: ${sizeInMB}MB, Time: ${timeInSeconds}s`;

        setMessage(`‚úÖ ${successMessage}`);
        addProgressEntry('success', operationId, successMessage, undefined, {
          totalDocuments: result.stats.totalDocuments,
          totalRecords: result.stats.totalRecords,
          exportSize: result.stats.exportSize,
          exportTime: result.stats.exportTime,
          filePath: result.filePath
        });

        await loadKnowledgeBaseStats(); // Refresh stats
      } else {
        const errorMessage = `Export failed: ${result.error}`;
        setMessage(`‚ùå ${errorMessage}`);
        addProgressEntry('error', operationId, errorMessage);
      }
    } catch (error) {
      console.error('Error exporting knowledge base:', error);
      const errorMessage = `Export error: ${error instanceof Error ? error.message : 'Unknown error'}`;
      setMessage(`‚ùå ${errorMessage}`);
      addProgressEntry('error', 'export', errorMessage);
    } finally {
      setIsExporting(false);
      setExportProgress(null);
    }
  };

  const handleImportKnowledgeBase = async (mode: 'replace' | 'merge' = 'replace') => {
    try {
      setIsImporting(true);
      setImportProgress(null);
      const operationId = `import-${mode}-${Date.now()}`;

      addProgressEntry('info', operationId, `Starting knowledge base import (${mode} mode)...`);
      setMessage(`Starting knowledge base import (${mode} mode)...`);

      const result = await window.electronAPI.importKnowledgeBase({ mode });

      if (result.success && result.stats) {
        const timeInSeconds = (result.stats.importTime / 1000).toFixed(1);
        const successMessage = `Import completed: ${result.stats.importedDocuments} documents, ${result.stats.importedRecords} chunks imported. ${result.stats.skippedRecords > 0 ? `${result.stats.skippedRecords} records skipped. ` : ''}Time: ${timeInSeconds}s`;

        setMessage(`‚úÖ ${successMessage}`);
        addProgressEntry('success', operationId, successMessage, undefined, {
          mode,
          importedDocuments: result.stats.importedDocuments,
          importedRecords: result.stats.importedRecords,
          skippedRecords: result.stats.skippedRecords,
          importTime: result.stats.importTime,
          filePath: result.filePath
        });

        if (result.stats.skippedRecords > 0) {
          addProgressEntry('warning', operationId, `${result.stats.skippedRecords} records were skipped during import`);
        }

        await loadDocuments(); // Refresh document list
        await loadKnowledgeBaseStats(); // Refresh stats
      } else {
        const errorMessage = `Import failed: ${result.error}`;
        setMessage(`‚ùå ${errorMessage}`);
        addProgressEntry('error', operationId, errorMessage);
      }
    } catch (error) {
      console.error('Error importing knowledge base:', error);
      const errorMessage = `Import error: ${error instanceof Error ? error.message : 'Unknown error'}`;
      setMessage(`‚ùå ${errorMessage}`);
      addProgressEntry('error', 'import', errorMessage);
    } finally {
      setIsImporting(false);
      setImportProgress(null);
    }
  };

  const handleRemoveDocument = async (documentSource: string) => {
    try {
      setIsLoading(true);
      setMessage(`Removing document: ${documentSource}...`);
      const result = await window.electronAPI.removeDocument(documentSource);
      if (result.success) {
        setMessage(`Successfully removed document: ${documentSource}`);
        await loadDocuments(); // Refresh document list
      } else {
        setMessage(`Failed to remove document: ${result.error}`);
      }
    } catch (error) {
      console.error('Error removing document:', error);
      setMessage(`Error removing document: ${error instanceof Error ? error.message : 'Unknown error'}`);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="space-y-3">
      <h2 className="text-sm font-medium mb-2">Knowledge Base Management</h2>

      {/* Add Document Section */}
      <div className="space-y-2">
        <h3 className="text-xs font-medium mb-1">Add Documents</h3>

        {/* Single Document Upload */}
        <div className="flex items-center space-x-2 mb-2">
          <Button
            onClick={handleAddDocument}
            disabled={isLoading || isUrlImporting}
            size="sm"
            className="h-7 text-xs"
          >
            {isLoading ? 'Processing...' : 'Add Single PDF'}
          </Button>

          <Button
            onClick={handleAddDocumentsBatch}
            disabled={isLoading || isUrlImporting}
            variant="outline"
            size="sm"
            className="h-7 text-xs"
          >
            {isLoading ? 'Processing...' : 'Add Multiple Documents'}
          </Button>
        </div>

        {/* Supported file types info */}
        <div className="mb-2 p-2 bg-muted rounded text-xs">
          <p className="font-medium mb-1 text-foreground">Supported file types:</p>
          <p className="text-muted-foreground">
            ‚Ä¢ PDF documents (.pdf)<br/>
            ‚Ä¢ Text files (.txt)<br/>
            ‚Ä¢ Markdown files (.md)<br/>
            ‚Ä¢ Word documents (.docx, .doc)
          </p>
        </div>

        {/* Google Docs Import Section */}
        <div className="mb-4 p-4 border border-gray-300 rounded">
          <h4 className="font-medium mb-2">Import from Google Docs</h4>
          <div className="flex items-center space-x-2 mb-2">
            <input
              type="text"
              value={googleDocsUrl}
              onChange={(e) => setGoogleDocsUrl(e.target.value)}
              placeholder="Paste Google Docs URL here..."
              disabled={isLoading || isUrlImporting}
              className="flex-1 px-3 py-2 border border-gray-300 rounded text-black"
            />
            <button
              onClick={handleAddFromGoogleDocs}
              disabled={isLoading || isUrlImporting || !googleDocsUrl.trim()}
              className="bg-purple-500 hover:bg-purple-700 disabled:bg-gray-400 text-white font-bold py-2 px-4 rounded"
            >
              {isUrlImporting ? 'Importing...' : 'Import'}
            </button>
          </div>
          <p className="text-xs text-gray-500">
            Note: The Google Docs document must be publicly accessible (shared with "Anyone with the link can view")
          </p>
        </div>

        {/* Upload Progress */}
        {uploadProgress.length > 0 && (
          <div className="mb-4 p-3 border border-gray-300 rounded">
            <h4 className="font-medium mb-2">Upload Progress</h4>
            <div className="space-y-1 max-h-32 overflow-y-auto">
              {uploadProgress.map((progress, index) => (
                <div key={index} className="flex items-center justify-between text-sm">
                  <span className="truncate flex-1">{progress.fileName}</span>
                  <span className={`ml-2 px-2 py-1 rounded text-xs ${
                    progress.status === 'success' ? 'bg-green-100 text-green-800' :
                    progress.status === 'error' ? 'bg-red-100 text-red-800' :
                    progress.status === 'processing' ? 'bg-blue-100 text-blue-800' :
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {progress.status === 'success' ? '‚úì Success' :
                     progress.status === 'error' ? '‚úó Error' :
                     progress.status === 'processing' ? '‚è≥ Processing' :
                     '‚è∏ Pending'}
                  </span>
                </div>
              ))}
            </div>
          </div>
        )}

        {message && <p className="text-sm text-gray-500">{message}</p>}
      </div>

      {/* Knowledge Base Management Section */}
      <div className="mb-6">
        <h3 className="text-md font-semibold mb-3">Knowledge Base Management</h3>

        {/* Statistics Display */}
        {kbStats && (
          <div className="mb-4 p-3 bg-muted rounded text-sm">
            <p className="font-medium mb-1 text-foreground">Current Knowledge Base:</p>
            <div className="text-muted-foreground space-y-1">
              <div className="flex justify-between">
                <span>Documents:</span>
                <span className="font-medium">{kbStats.totalDocuments.toLocaleString()}</span>
              </div>
              <div className="flex justify-between">
                <span>Chunks:</span>
                <span className="font-medium">{kbStats.totalRecords.toLocaleString()}</span>
              </div>
              <div className="flex justify-between">
                <span>Estimated Size:</span>
                <span className="font-medium">{(kbStats.databaseSize / (1024 * 1024)).toFixed(1)} MB</span>
              </div>
            </div>
          </div>
        )}

        {/* Export/Import Controls */}
        <div className="flex items-center space-x-4 mb-4">
          <button
            onClick={handleExportKnowledgeBase}
            disabled={isLoading || isExporting || isImporting || isUrlImporting}
            className="bg-blue-500 hover:bg-blue-700 disabled:bg-gray-400 text-white font-bold py-2 px-4 rounded"
          >
            {isExporting ? 'Exporting...' : 'Export Knowledge Base'}
          </button>

          <button
            onClick={() => handleImportKnowledgeBase('replace')}
            disabled={isLoading || isExporting || isImporting || isUrlImporting}
            className="bg-orange-500 hover:bg-orange-700 disabled:bg-gray-400 text-white font-bold py-2 px-4 rounded"
          >
            {isImporting ? 'Importing...' : 'Import (Replace)'}
          </button>

          <button
            onClick={() => handleImportKnowledgeBase('merge')}
            disabled={isLoading || isExporting || isImporting || isUrlImporting}
            className="bg-green-500 hover:bg-green-700 disabled:bg-gray-400 text-white font-bold py-2 px-4 rounded"
          >
            {isImporting ? 'Importing...' : 'Import (Merge)'}
          </button>
        </div>

        {/* Export/Import Progress */}
        {(exportProgress || importProgress) && (
          <div className="mb-4 p-3 border border-gray-300 rounded">
            <h4 className="font-medium mb-2">
              {exportProgress ? 'Export Progress' : 'Import Progress'}
            </h4>
            {(exportProgress || importProgress) && (
              <div className="space-y-2">
                <div className="flex items-center justify-between text-sm">
                  <span>{(exportProgress || importProgress)?.message}</span>
                  <span className="font-medium">
                    {(exportProgress || importProgress)?.current}% / {(exportProgress || importProgress)?.total}%
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div
                    className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${(exportProgress || importProgress)?.current || 0}%` }}
                  ></div>
                </div>
                <p className="text-xs text-gray-500">
                  Step: {(exportProgress || importProgress)?.step}
                </p>
              </div>
            )}
          </div>
        )}

        {/* Import Mode Information */}
        <div className="mb-4 p-3 bg-yellow-50 border border-yellow-200 rounded text-sm">
          <p className="font-medium mb-1 text-yellow-800">Import Modes:</p>
          <div className="text-yellow-700 space-y-1">
            <p><strong>Replace:</strong> Clears existing knowledge base and imports new data</p>
            <p><strong>Merge:</strong> Adds imported data to existing knowledge base</p>
          </div>
        </div>
      </div>

      {/* Progress Log Panel */}
      <div className="mb-6">
        <ProgressLogPanel
          entries={progressEntries}
          isCollapsed={isProgressLogCollapsed}
          onToggleCollapse={() => setIsProgressLogCollapsed(!isProgressLogCollapsed)}
          onClear={clearProgressLog}
          maxHeight="250px"
        />
      </div>

      {/* Documents List Section */}
      <div>
        <h3 className="text-md font-semibold mb-2">Documents in Knowledge Base</h3>
        {isLoading && uploadProgress.length === 0 ? (
          <p className="text-sm text-gray-500">Loading documents...</p>
        ) : documents.length === 0 ? (
          <div className="p-4 bg-gray-50 rounded text-center">
            <p className="text-sm text-gray-500 mb-2">No documents added yet.</p>
            <p className="text-xs text-gray-400">
              Add documents using the buttons above to start building your knowledge base.
            </p>
          </div>
        ) : (
          <div className="space-y-2">
            {documents.map((doc, index) => {
              const fileExtension = doc.source.split('.').pop()?.toLowerCase();
              const format = doc.metadata.format as string || 'Unknown';
              const uploadDate = doc.addedAt ? new Date(doc.addedAt).toLocaleDateString() : 'Unknown';
              const characterCount = doc.metadata.characterCount as number;
              const fileSize = doc.metadata.fileSize as number;

              const getFileTypeIcon = (ext?: string, format?: string) => {
                if (format === 'Google Docs') return 'üìÑ';
                switch (ext) {
                  case 'pdf': return 'üìÑ';
                  case 'txt': return 'üìù';
                  case 'md': return 'üìã';
                  case 'docx':
                  case 'doc': return 'üìò';
                  default: return 'üìÑ';
                }
              };

              const formatFileSize = (bytes?: number) => {
                if (!bytes) return '';
                if (bytes < 1024) return `${bytes} B`;
                if (bytes < 1024 * 1024) return `${Math.round(bytes / 1024)} KB`;
                return `${Math.round(bytes / (1024 * 1024))} MB`;
              };

              return (
                <div key={index} className="flex items-center justify-between p-3 bg-muted border border-border rounded">
                  <div className="flex items-center flex-1">
                    <span className="mr-3 text-lg">{getFileTypeIcon(fileExtension, format)}</span>
                    <div className="flex-1">
                      <span className="text-sm font-medium text-foreground block truncate">{doc.source}</span>
                      <div className="text-xs text-gray-500 space-y-1">
                        <div className="flex items-center space-x-4">
                          <span>{format}</span>
                          <span>‚Ä¢</span>
                          <span>{doc.chunkCount} chunks</span>
                          {fileSize && (
                            <>
                              <span>‚Ä¢</span>
                              <span>{formatFileSize(fileSize)}</span>
                            </>
                          )}
                          {characterCount && (
                            <>
                              <span>‚Ä¢</span>
                              <span>{characterCount.toLocaleString()} chars</span>
                            </>
                          )}
                        </div>
                        <div>Added: {uploadDate}</div>
                      </div>
                    </div>
                  </div>
                  <button
                    onClick={() => handleRemoveDocument(doc.source)}
                    disabled={isLoading || isUrlImporting}
                    className="bg-red-500 hover:bg-red-700 disabled:bg-gray-400 text-white text-xs py-1 px-3 rounded ml-2"
                  >
                    Remove
                  </button>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default KnowledgeBaseSettings;

'use client';

import { useState, useEffect, useCallback } from 'react';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Textarea } from './ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Badge } from './ui/badge';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import {
  Brain,
  Search,
  Plus,
  Edit,
  Trash2,
  Download,
  Upload,
  RefreshCw,
  Tag,
  Calendar,
  BarChart3,
  FileText,
  Code,
  Lightbulb,
  User,
  MessageSquare,
  FolderOpen
} from 'lucide-react';
import { memoryService } from '../services/memoryService';
import { memoryExportService } from '../services/memoryExportService';
import { memoryCleanupService } from '../services/memoryCleanupService';
import { automaticMemoryService } from '../services/automaticMemoryService';
import { MemoryEntry, MemoryType, SearchQuery, MemoryStats } from '../types/memory';

interface MemoryManagementProps {
  className?: string;
}

export function MemoryManagement({ className }: MemoryManagementProps) {
  const [memories, setMemories] = useState<MemoryEntry[]>([]);
  const [stats, setStats] = useState<MemoryStats | null>(null);
  const [loading, setLoading] = useState(false);
  const [exportLoading, setExportLoading] = useState(false);
  const [importLoading, setImportLoading] = useState(false);
  const [cleanupLoading, setCleanupLoading] = useState(false);
  const [autoMemoryConfig, setAutoMemoryConfig] = useState(automaticMemoryService.getConfig());
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedType, setSelectedType] = useState<MemoryType | 'all'>('all');
  const [showAddMemory, setShowAddMemory] = useState(false);
  const [editingMemory, setEditingMemory] = useState<MemoryEntry | null>(null);
  const [newMemory, setNewMemory] = useState({
    type: 'general' as MemoryType,
    title: '',
    content: '',
    tags: ''
  });

  // Memory type icons and labels
  const memoryTypeConfig = {
    user_preference: { icon: User, label: 'User Preference', color: 'bg-blue-500' },
    conversation_context: { icon: MessageSquare, label: 'Conversation', color: 'bg-green-500' },
    project_knowledge: { icon: FolderOpen, label: 'Project Knowledge', color: 'bg-purple-500' },
    code_snippet: { icon: Code, label: 'Code Snippet', color: 'bg-orange-500' },
    solution: { icon: Lightbulb, label: 'Solution', color: 'bg-yellow-500' },
    general: { icon: FileText, label: 'General', color: 'bg-gray-500' }
  };

  const loadMemories = useCallback(async () => {
    setLoading(true);
    try {
      const query: SearchQuery = {
        limit: 50,
        offset: 0
      };

      if (searchQuery.trim()) {
        query.text = searchQuery.trim();
      }

      if (selectedType !== 'all') {
        query.type = selectedType;
      }

      const result = await memoryService.searchMemories({ query });
      if (result.success && result.data) {
        setMemories(result.data.results.map(r => r.entry));
      }
    } catch (error) {
      console.error('Failed to load memories:', error);
    } finally {
      setLoading(false);
    }
  }, [searchQuery, selectedType]);

  useEffect(() => {
    loadMemories();
    loadStats();
  }, [loadMemories]);

  const loadStats = async () => {
    try {
      const result = await memoryService.getMemoryStats();
      if (result.success && result.data) {
        setStats(result.data);
      }
    } catch (error) {
      console.error('Failed to load memory stats:', error);
    }
  };

  const handleSearch = () => {
    loadMemories();
  };

  const handleAddMemory = async () => {
    if (!newMemory.title.trim() || !newMemory.content.trim()) return;

    try {
      const tags = newMemory.tags.split(',').map(tag => tag.trim()).filter(tag => tag.length > 0);

      const result = await memoryService.storeMemory({
        type: newMemory.type,
        title: newMemory.title.trim(),
        content: newMemory.content.trim(),
        tags,
        source: 'manual_ui'
      });

      if (result.success) {
        setNewMemory({ type: 'general', title: '', content: '', tags: '' });
        setShowAddMemory(false);
        loadMemories();
        loadStats();
      }
    } catch (error) {
      console.error('Failed to add memory:', error);
    }
  };

  const handleEditMemory = async () => {
    if (!editingMemory) return;

    try {
      const tags = editingMemory.metadata.tags;

      const result = await memoryService.updateMemory({
        id: editingMemory.id,
        title: editingMemory.title,
        content: editingMemory.content,
        tags,
        type: editingMemory.type
      });

      if (result.success) {
        setEditingMemory(null);
        loadMemories();
      }
    } catch (error) {
      console.error('Failed to update memory:', error);
    }
  };

  const handleDeleteMemory = async (memoryId: string) => {
    if (!confirm('Are you sure you want to delete this memory?')) return;

    try {
      const result = await memoryService.deleteMemory({ id: memoryId });
      if (result.success) {
        loadMemories();
        loadStats();
      }
    } catch (error) {
      console.error('Failed to delete memory:', error);
    }
  };

  const formatDate = (date: Date) => {
    return new Date(date).toLocaleDateString() + ' ' + new Date(date).toLocaleTimeString();
  };

  const getTypeIcon = (type: MemoryType) => {
    const config = memoryTypeConfig[type];
    const IconComponent = config.icon;
    return <IconComponent className="h-4 w-4" />;
  };

  const getTypeLabel = (type: MemoryType) => {
    return memoryTypeConfig[type].label;
  };

  const getTypeBadgeColor = (type: MemoryType) => {
    return memoryTypeConfig[type].color;
  };

  const handleExportMemories = async () => {
    setExportLoading(true);
    try {
      const exportResult = await memoryExportService.exportMemories({
        description: 'Manual export from LiteLLM Memory Management'
      });

      if (exportResult.success && exportResult.data) {
        const saveResult = await memoryExportService.saveExportToFile(exportResult.data);
        if (saveResult.success) {
          alert(`Memories exported successfully to ${saveResult.filename}`);
        } else {
          alert(`Export failed: ${saveResult.error}`);
        }
      } else {
        alert(`Export failed: ${exportResult.error}`);
      }
    } catch (error) {
      alert(`Export failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    } finally {
      setExportLoading(false);
    }
  };

  const handleImportMemories = async () => {
    setImportLoading(true);
    try {
      const loadResult = await memoryExportService.loadExportFromFile();

      if (loadResult.success && loadResult.data) {
        const importResult = await memoryExportService.importMemories(loadResult.data, {
          skipDuplicates: true,
          validateData: true
        });

        if (importResult.success) {
          alert(`Import completed: ${importResult.imported} memories imported, ${importResult.skipped} skipped, ${importResult.duplicates} duplicates found`);
          loadMemories();
          loadStats();
        } else {
          alert(`Import failed: ${importResult.errors.join(', ')}`);
        }
      } else {
        alert(`Import failed: ${loadResult.error}`);
      }
    } catch (error) {
      alert(`Import failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    } finally {
      setImportLoading(false);
    }
  };

  const handleCleanupMemories = async () => {
    if (!confirm('This will clean up old and duplicate memories. Continue?')) return;

    setCleanupLoading(true);
    try {
      const cleanupResult = await memoryCleanupService.performCleanup({
        maxAge: 365, // 1 year
        archiveOldMemories: true,
        consolidateDuplicates: true,
        removeUnusedMemories: false
      });

      if (cleanupResult.success) {
        const sizeSaved = cleanupResult.sizeBefore - cleanupResult.sizeAfter;
        alert(`Cleanup completed: ${cleanupResult.deleted} deleted, ${cleanupResult.archived} archived, ${cleanupResult.consolidated} consolidated. Saved ${Math.round(sizeSaved / 1024)}KB`);
        loadMemories();
        loadStats();
      } else {
        alert(`Cleanup failed: ${cleanupResult.errors.join(', ')}`);
      }
    } catch (error) {
      alert(`Cleanup failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    } finally {
      setCleanupLoading(false);
    }
  };

  const handleAutoMemoryToggle = (setting: 'enableAutoSearch' | 'enableAutoSave', value: boolean) => {
    const newConfig = { ...autoMemoryConfig, [setting]: value };
    setAutoMemoryConfig(newConfig);
    automaticMemoryService.updateConfig(newConfig);
  };

  const handleAutoMemoryConfigChange = (setting: string, value: number) => {
    const newConfig = { ...autoMemoryConfig, [setting]: value };
    setAutoMemoryConfig(newConfig);
    automaticMemoryService.updateConfig(newConfig);
  };

  return (
    <div className={className}>
      <Tabs defaultValue="browse" className="w-full">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="browse" className="text-xs">Browse Memories</TabsTrigger>
          <TabsTrigger value="stats" className="text-xs">Statistics</TabsTrigger>
          <TabsTrigger value="settings" className="text-xs">Settings</TabsTrigger>
        </TabsList>

        <TabsContent value="browse" className="space-y-2">
          {/* Search and Filter Controls */}
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="flex items-center gap-2 text-sm">
                <Brain className="h-4 w-4" />
                Memory Browser
              </CardTitle>
              <CardDescription className="text-xs">
                Search and manage your stored memories
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-2">
              <div className="flex gap-1">
                <div className="flex-1">
                  <Input
                    placeholder="Search memories..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
                    className="h-7 text-xs"
                  />
                </div>
                <Select value={selectedType} onValueChange={(value) => setSelectedType(value as MemoryType | 'all')}>
                  <SelectTrigger className="w-32 h-7 text-xs">
                    <SelectValue placeholder="Filter by type" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Types</SelectItem>
                    {Object.entries(memoryTypeConfig).map(([type, config]) => (
                      <SelectItem key={type} value={type}>
                        <div className="flex items-center gap-2">
                          {getTypeIcon(type as MemoryType)}
                          {config.label}
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <Button onClick={handleSearch} disabled={loading} size="sm" className="h-7 w-7 p-0">
                  <Search className="h-3 w-3" />
                </Button>
                <Button onClick={() => setShowAddMemory(true)} size="sm" className="h-7 w-7 p-0">
                  <Plus className="h-3 w-3" />
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Memory List */}
          <div className="space-y-2">
            {loading ? (
              <Card>
                <CardContent className="p-4 text-center">
                  <RefreshCw className="h-6 w-6 animate-spin mx-auto mb-2" />
                  Loading memories...
                </CardContent>
              </Card>
            ) : memories.length === 0 ? (
              <Card>
                <CardContent className="p-4 text-center text-muted-foreground">
                  No memories found. Try adjusting your search or add a new memory.
                </CardContent>
              </Card>
            ) : (
              memories.map((memory) => (
                <Card key={memory.id} className="hover:shadow-md transition-shadow">
                  <CardContent className="p-4">
                    <div className="flex items-start justify-between">
                      <div className="flex-1 space-y-2">
                        <div className="flex items-center gap-2">
                          <Badge className={`${getTypeBadgeColor(memory.type)} text-white`}>
                            {getTypeIcon(memory.type)}
                            <span className="ml-1">{getTypeLabel(memory.type)}</span>
                          </Badge>
                          <h3 className="font-medium">{memory.title}</h3>
                        </div>
                        <p className="text-sm text-muted-foreground line-clamp-2">
                          {memory.content}
                        </p>
                        <div className="flex items-center gap-4 text-xs text-muted-foreground">
                          <span className="flex items-center gap-1">
                            <Calendar className="h-3 w-3" />
                            {formatDate(memory.createdAt)}
                          </span>
                          {memory.metadata.tags.length > 0 && (
                            <span className="flex items-center gap-1">
                              <Tag className="h-3 w-3" />
                              {memory.metadata.tags.join(', ')}
                            </span>
                          )}
                        </div>
                      </div>
                      <div className="flex gap-1 ml-4">
                        <Button
                          size="sm"
                          variant="ghost"
                          onClick={() => setEditingMemory(memory)}
                        >
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button
                          size="sm"
                          variant="ghost"
                          onClick={() => handleDeleteMemory(memory.id)}
                        >
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))
            )}
          </div>
        </TabsContent>

        <TabsContent value="stats" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <BarChart3 className="h-5 w-5" />
                Memory Statistics
              </CardTitle>
            </CardHeader>
            <CardContent>
              {stats ? (
                <div className="grid grid-cols-2 gap-4">
                  <div className="space-y-2">
                    <div className="text-2xl font-bold">{stats.totalEntries}</div>
                    <div className="text-sm text-muted-foreground">Total Memories</div>
                  </div>
                  <div className="space-y-2">
                    <div className="text-2xl font-bold">{Math.round(stats.totalSize / 1024)} KB</div>
                    <div className="text-sm text-muted-foreground">Storage Used</div>
                  </div>
                  <div className="col-span-2 space-y-2">
                    <h4 className="font-medium">Memories by Type</h4>
                    {Object.entries(stats.entriesByType).map(([type, count]) => (
                      <div key={type} className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          {getTypeIcon(type as MemoryType)}
                          <span>{getTypeLabel(type as MemoryType)}</span>
                        </div>
                        <Badge variant="secondary">{count}</Badge>
                      </div>
                    ))}
                  </div>
                </div>
              ) : (
                <div className="text-center text-muted-foreground">
                  Loading statistics...
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="settings" className="space-y-4">
          {/* Automatic Memory Settings */}
          <Card>
            <CardHeader>
              <CardTitle>Automatic Memory</CardTitle>
              <CardDescription>
                Configure how AI automatically uses and creates memories
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <Label htmlFor="auto-search">Auto-Search Memories</Label>
                    <input
                      id="auto-search"
                      type="checkbox"
                      checked={autoMemoryConfig.enableAutoSearch}
                      onChange={(e) => handleAutoMemoryToggle('enableAutoSearch', e.target.checked)}
                      className="rounded"
                    />
                  </div>
                  <p className="text-sm text-muted-foreground">
                    Automatically include relevant memories in AI prompts
                  </p>
                </div>

                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <Label htmlFor="auto-save">Auto-Save Memories</Label>
                    <input
                      id="auto-save"
                      type="checkbox"
                      checked={autoMemoryConfig.enableAutoSave}
                      onChange={(e) => handleAutoMemoryToggle('enableAutoSave', e.target.checked)}
                      className="rounded"
                    />
                  </div>
                  <p className="text-sm text-muted-foreground">
                    Automatically save useful information from conversations
                  </p>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="search-threshold">Search Relevance Threshold</Label>
                  <Input
                    id="search-threshold"
                    type="number"
                    min="0"
                    max="1"
                    step="0.1"
                    value={autoMemoryConfig.searchThreshold}
                    onChange={(e) => handleAutoMemoryConfigChange('searchThreshold', parseFloat(e.target.value))}
                  />
                  <p className="text-xs text-muted-foreground">
                    Minimum relevance score to include memories (0.0 - 1.0)
                  </p>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="save-threshold">Auto-Save Confidence Threshold</Label>
                  <Input
                    id="save-threshold"
                    type="number"
                    min="0"
                    max="1"
                    step="0.1"
                    value={autoMemoryConfig.saveThreshold}
                    onChange={(e) => handleAutoMemoryConfigChange('saveThreshold', parseFloat(e.target.value))}
                  />
                  <p className="text-xs text-muted-foreground">
                    Minimum confidence to auto-save information (0.0 - 1.0)
                  </p>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="max-context">Max Context Memories</Label>
                  <Input
                    id="max-context"
                    type="number"
                    min="1"
                    max="10"
                    value={autoMemoryConfig.maxContextMemories}
                    onChange={(e) => handleAutoMemoryConfigChange('maxContextMemories', parseInt(e.target.value))}
                  />
                  <p className="text-xs text-muted-foreground">
                    Maximum memories to include in AI context
                  </p>
                </div>
              </div>

              <div className="pt-4 border-t">
                <div className="text-sm space-y-2">
                  <div className="font-medium">How Automatic Memory Works:</div>
                  <ul className="text-muted-foreground space-y-1 ml-4">
                    <li>‚Ä¢ <strong>Auto-Search:</strong> AI automatically searches for relevant memories before responding</li>
                    <li>‚Ä¢ <strong>Auto-Save:</strong> AI automatically saves preferences, solutions, and useful information</li>
                    <li>‚Ä¢ <strong>Smart Context:</strong> Only the most relevant memories are included to avoid overwhelming the AI</li>
                    <li>‚Ä¢ <strong>Seamless:</strong> Works transparently without requiring manual memory tool usage</li>
                  </ul>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Manual Memory Management */}
          <Card>
            <CardHeader>
              <CardTitle>Memory Management</CardTitle>
              <CardDescription>
                Manual memory operations and maintenance
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-4">
                <div className="flex gap-2 flex-wrap">
                  <Button onClick={loadStats} disabled={loading}>
                    <RefreshCw className="h-4 w-4 mr-2" />
                    Refresh Statistics
                  </Button>
                  <Button
                    variant="outline"
                    onClick={handleExportMemories}
                    disabled={exportLoading}
                  >
                    <Download className="h-4 w-4 mr-2" />
                    {exportLoading ? 'Exporting...' : 'Export Memories'}
                  </Button>
                  <Button
                    variant="outline"
                    onClick={handleImportMemories}
                    disabled={importLoading}
                  >
                    <Upload className="h-4 w-4 mr-2" />
                    {importLoading ? 'Importing...' : 'Import Memories'}
                  </Button>
                  <Button
                    variant="outline"
                    onClick={handleCleanupMemories}
                    disabled={cleanupLoading}
                  >
                    <RefreshCw className="h-4 w-4 mr-2" />
                    {cleanupLoading ? 'Cleaning...' : 'Cleanup Memories'}
                  </Button>
                </div>

                <div className="text-sm space-y-2">
                  <div className="font-medium">Memory Management Features:</div>
                  <ul className="text-muted-foreground space-y-1 ml-4">
                    <li>‚Ä¢ <strong>Export:</strong> Save all memories to a JSON file for backup</li>
                    <li>‚Ä¢ <strong>Import:</strong> Restore memories from a backup file</li>
                    <li>‚Ä¢ <strong>Cleanup:</strong> Remove old memories and consolidate duplicates</li>
                    <li>‚Ä¢ <strong>Auto-Memory:</strong> AI automatically creates memories during conversations</li>
                  </ul>
                </div>
              </div>
              <div className="text-sm text-muted-foreground">
                Memory system is running and ready to store information from AI conversations.
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Add Memory Dialog */}
      {showAddMemory && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <Card className="w-full max-w-md mx-4">
            <CardHeader>
              <CardTitle>Add New Memory</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="memory-type">Type</Label>
                <Select value={newMemory.type} onValueChange={(value) => setNewMemory(prev => ({ ...prev, type: value as MemoryType }))}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {Object.entries(memoryTypeConfig).map(([type, config]) => (
                      <SelectItem key={type} value={type}>
                        <div className="flex items-center gap-2">
                          {getTypeIcon(type as MemoryType)}
                          {config.label}
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="memory-title">Title</Label>
                <Input
                  id="memory-title"
                  value={newMemory.title}
                  onChange={(e) => setNewMemory(prev => ({ ...prev, title: e.target.value }))}
                  placeholder="Enter memory title..."
                />
              </div>
              <div>
                <Label htmlFor="memory-content">Content</Label>
                <Textarea
                  id="memory-content"
                  value={newMemory.content}
                  onChange={(e) => setNewMemory(prev => ({ ...prev, content: e.target.value }))}
                  placeholder="Enter memory content..."
                  rows={4}
                />
              </div>
              <div>
                <Label htmlFor="memory-tags">Tags (comma-separated)</Label>
                <Input
                  id="memory-tags"
                  value={newMemory.tags}
                  onChange={(e) => setNewMemory(prev => ({ ...prev, tags: e.target.value }))}
                  placeholder="tag1, tag2, tag3..."
                />
              </div>
              <div className="flex gap-2 justify-end">
                <Button variant="outline" onClick={() => setShowAddMemory(false)}>
                  Cancel
                </Button>
                <Button onClick={handleAddMemory}>
                  Add Memory
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Edit Memory Dialog */}
      {editingMemory && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <Card className="w-full max-w-md mx-4">
            <CardHeader>
              <CardTitle>Edit Memory</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <Label htmlFor="edit-memory-type">Type</Label>
                <Select value={editingMemory.type} onValueChange={(value) => setEditingMemory(prev => prev ? ({ ...prev, type: value as MemoryType }) : null)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {Object.entries(memoryTypeConfig).map(([type, config]) => (
                      <SelectItem key={type} value={type}>
                        <div className="flex items-center gap-2">
                          {getTypeIcon(type as MemoryType)}
                          {config.label}
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div>
                <Label htmlFor="edit-memory-title">Title</Label>
                <Input
                  id="edit-memory-title"
                  value={editingMemory.title}
                  onChange={(e) => setEditingMemory(prev => prev ? ({ ...prev, title: e.target.value }) : null)}
                />
              </div>
              <div>
                <Label htmlFor="edit-memory-content">Content</Label>
                <Textarea
                  id="edit-memory-content"
                  value={editingMemory.content}
                  onChange={(e) => setEditingMemory(prev => prev ? ({ ...prev, content: e.target.value }) : null)}
                  rows={4}
                />
              </div>
              <div>
                <Label htmlFor="edit-memory-tags">Tags (comma-separated)</Label>
                <Input
                  id="edit-memory-tags"
                  value={editingMemory.metadata.tags.join(', ')}
                  onChange={(e) => {
                    const tags = e.target.value.split(',').map(tag => tag.trim()).filter(tag => tag.length > 0);
                    setEditingMemory(prev => prev ? ({ 
                      ...prev, 
                      metadata: { ...prev.metadata, tags }
                    }) : null);
                  }}
                />
              </div>
              <div className="flex gap-2 justify-end">
                <Button variant="outline" onClick={() => setEditingMemory(null)}>
                  Cancel
                </Button>
                <Button onClick={handleEditMemory}>
                  Save Changes
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

'use client';

import React, { useState, useMemo, useEffect, useCallback, useRef } from 'react';
import { ChevronDown, ChevronRight, Brain, Copy, Check, Wrench, Volume2, VolumeX } from 'lucide-react';
import { Button } from './ui/button';
import { parseTextWithContent } from '../lib/contentParser';
import { SourceAttribution } from './SourceAttribution';
import type { Source } from '../services/chatService';
import { debugLogger } from '../services/debugLogger';
import { getTTSService } from '../services/textToSpeechService';
import { settingsService } from '../services/settingsService';

interface MessageWithThinkingProps {
  content: string;
  className?: string;
  usage?: {
    promptTokens: number;
    completionTokens: number;
    totalTokens: number;
  };
  cost?: {
    inputCost: number;
    outputCost: number;
    totalCost: number;
    currency: string;
    provider: string;
    model: string;
  };
  timing?: {
    startTime: number;
    endTime: number;
    duration: number;
    tokensPerSecond?: number;
  };
  toolCalls?: Array<{
    id: string;
    name: string;
    arguments: Record<string, unknown>;
    result?: string;
    error?: boolean;
  }>;
  sources?: Source[];
}

interface ParsedMessage {
  thinking: string[];
  toolExecution: string[];
  response: string;
}

export function MessageWithThinking({ content, className = '', usage, cost, timing, toolCalls, sources }: MessageWithThinkingProps) {
  const [showThinking, setShowThinking] = useState(false);
  const [showToolExecution, setShowToolExecution] = useState(false);
  const [showTools, setShowTools] = useState(false);
  const [copied, setCopied] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const autoPlayTriggeredRef = useRef(false);

  // Debug tool calls - now handled in useEffect to prevent spam
  useEffect(() => {
    if (toolCalls && toolCalls.length > 0) {
      console.log(`üîß MessageWithThinking received ${toolCalls.length} tool calls:`, toolCalls);
      toolCalls.forEach((tc, index) => {
        console.log(`üîß Tool ${index}: ${tc.name}, has result: ${!!tc.result}, result length: ${tc.result?.length || 0}, error: ${tc.error}`);
        if (tc.result) {
          console.log(`üîß Tool ${index} result preview:`, tc.result.substring(0, 100) + '...');
        }
      });
    }
  }, [toolCalls]);

  // Helper function to remove model-specific template tags
  const removeTemplateTags = (text: string): string => {
    let cleanedText = text;

    // Remove new model format tags: <|start|>, <|message|>, <|channel|>, <|end|>, <|constrain|>
    cleanedText = cleanedText.replace(/<\|start\|>/gi, '');
    cleanedText = cleanedText.replace(/<\|message\|>/gi, '');
    cleanedText = cleanedText.replace(/<\|channel\|>/gi, '');
    cleanedText = cleanedText.replace(/<\|end\|>/gi, '');
    cleanedText = cleanedText.replace(/<\|constrain\|>/gi, '');

    // Remove Qwen3 format tags: <|im_start|>, <|im_end|>
    cleanedText = cleanedText.replace(/<\|im_start\|>/gi, '');
    cleanedText = cleanedText.replace(/<\|im_end\|>/gi, '');

    // Remove concatenated role and channel indicators (e.g., "assistantfinal", "systemcommentary")
    cleanedText = cleanedText.replace(/(system|user|assistant)(final|analysis|commentary)/gi, '');

    // Remove standalone role indicators
    cleanedText = cleanedText.replace(/\b(system|user|assistant)\b/gi, '');

    // Remove standalone channel indicators
    cleanedText = cleanedText.replace(/\b(final|analysis|commentary)\b/gi, '');

    // Remove tool call commands (e.g., "to=web_search json{...}", "to=list_directoryjson{...}") - handles nested JSON, hyphens, function prefixes, optional space, and multiple calls
    cleanedText = cleanedText.replace(/(?:commentary\s+)?to=(?:functions\.)?[a-zA-Z_][a-zA-Z0-9_-]*\s*json\{(?:[^{}]|{[^{}]*})*\}/gi, '');

    // Clean up any remaining template-like patterns
    cleanedText = cleanedText.replace(/<\|[^|]*\|>/gi, '');

    return cleanedText;
  };

  // Parse the message content to extract thinking sections, tool execution, and response
  const parseMessage = (text: string): ParsedMessage => {
    const thinking: string[] = [];
    const toolExecution: string[] = [];
    let response = text;

    // Find all <think>...</think> blocks (structured thinking)
    const thinkRegex = /<think>([\s\S]*?)<\/think>/gi;
    let thinkMatch;
    while ((thinkMatch = thinkRegex.exec(text)) !== null) {
      thinking.push(thinkMatch[1].trim());
    }

    // Find all <tool_execution>...</tool_execution> blocks
    const toolRegex = /<tool_execution>([\s\S]*?)<\/tool_execution>/gi;
    let toolMatch;
    while ((toolMatch = toolRegex.exec(text)) !== null) {
      toolExecution.push(toolMatch[1].trim());
    }

    // Enhanced parsing for thinking models - ONLY for explicit thinking patterns
    // Look for content that appears before tool calls AND has strong thinking indicators
    // Support both ```json and new model format (to=tool_name json{...} or to=tool_namejson{...})
    const beforeToolCallMatch = text.match(/^([\s\S]*?)(?=```json|(?:commentary\s+)?to=[a-zA-Z_][a-zA-Z0-9_-]*\s*json)/);
    if (beforeToolCallMatch) {
      const potentialThinking = beforeToolCallMatch[1].trim();

      // Very specific thinking indicators - must be explicit reasoning language
      const strongThinkingIndicators = [
        'okay let me think through this', 'let me think through', 'i need to think about',
        'let me analyze this', 'let me break this down', 'thinking through this',
        'let me consider the', 'i should think about', 'let me reason through',
        'okay let me think', 'let me think carefully', 'i need to consider',
        'commentary', 'commentarythe', 'according to developer instruction'
      ];

      // Check for multiple strong indicators or very explicit thinking language
      const strongIndicatorCount = strongThinkingIndicators.filter(indicator =>
        potentialThinking.toLowerCase().includes(indicator)
      ).length;

      // Very strict criteria: must have strong thinking language AND be substantial content
      // AND appear before a tool call (not standalone responses)
      const hasToolCall = text.includes('```json') || /(?:commentary\s+)?to=[a-zA-Z_][a-zA-Z0-9_-]*\s*json/.test(text);
      const isExplicitThinking = strongIndicatorCount > 0 && potentialThinking.length > 100;
      const startsWithThinking = /^(okay let me think|let me think|i need to think|thinking through|commentary)/i.test(potentialThinking);

      if (hasToolCall && (isExplicitThinking || startsWithThinking)) {
        thinking.push(potentialThinking);
      }
    }

    // Clean the response by removing unwanted content
    response = text;

    // Remove structured thinking and tool execution blocks
    response = response.replace(thinkRegex, '').replace(toolRegex, '');

    // Remove JSON tool call blocks (these should be in Tools Used section)
    response = response.replace(/```json[\s\S]*?```/gi, '');

    // Remove tool result markers
    response = response.replace(/\[TOOL_RESULT\][\s\S]*?\[END_TOOL_RESULT\]/gi, '');

    // Remove extracted thinking content from response
    for (const thinkingContent of thinking) {
      response = response.replace(thinkingContent, '');
    }

    // Remove model-specific template tags
    response = removeTemplateTags(response);

    // Clean up extra whitespace and empty lines
    response = response.replace(/\n\s*\n\s*\n/g, '\n\n').trim();

    return { thinking, toolExecution, response };
  };

  // Helper function to extract complete JSON object from text starting at a given index
  const extractCompleteJSON = (text: string, startIndex: number): string | null => {
    let braceCount = 0;
    let inString = false;
    let escaped = false;
    let jsonStart = -1;

    for (let i = startIndex; i < text.length; i++) {
      const char = text[i];

      if (escaped) {
        escaped = false;
        continue;
      }

      if (char === '\\' && inString) {
        escaped = true;
        continue;
      }

      if (char === '"') {
        inString = !inString;
        continue;
      }

      if (!inString) {
        if (char === '{') {
          if (jsonStart === -1) jsonStart = i;
          braceCount++;
        } else if (char === '}') {
          braceCount--;
          if (braceCount === 0 && jsonStart !== -1) {
            return text.substring(jsonStart, i + 1);
          }
        }
      }
    }

    return null;
  };

  // Extract tool calls from content if not provided via props
  const extractToolCallsFromContent = (text: string): Array<{id: string, name: string, arguments: Record<string, unknown>}> => {
    const toolCalls: Array<{id: string, name: string, arguments: Record<string, unknown>}> = [];

    // Pattern 1: JSON-wrapped tool calls (```json wrapper)
    const jsonMatches = text.match(/```json\s*([\s\S]*?)\s*```/gi);
    if (jsonMatches) {
      for (const match of jsonMatches) {
        try {
          const jsonContent = match.replace(/```json\s*|\s*```/gi, '').trim();
          const parsed = JSON.parse(jsonContent);

          if (parsed.tool_call && parsed.tool_call.name) {
            // Create deterministic ID based on content to prevent re-render loops
            const contentHash = btoa(JSON.stringify(parsed.tool_call)).replace(/[^a-zA-Z0-9]/g, '').substring(0, 16);
            toolCalls.push({
              id: `extracted_json_${contentHash}`,
              name: parsed.tool_call.name,
              arguments: parsed.tool_call.arguments || {}
            });
          }
        } catch (error) {
          console.warn('Failed to parse JSON-wrapped tool call:', error);
        }
      }
    }

    // Pattern 2: Direct tool_call format (without ```json wrapper)
    // Use a more robust approach to find complete JSON objects
    const toolCallPattern = /\{\s*"tool_call"\s*:\s*\{/gi;
    let match;
    while ((match = toolCallPattern.exec(text)) !== null) {
      try {
        // Find the complete JSON object starting from the match
        const startIndex = match.index;
        const jsonStr = extractCompleteJSON(text, startIndex);

        if (jsonStr) {
          const parsed = JSON.parse(jsonStr);
          if (parsed.tool_call && parsed.tool_call.name) {
            // Create deterministic ID based on content to prevent re-render loops
            const contentHash = btoa(JSON.stringify(parsed.tool_call)).replace(/[^a-zA-Z0-9]/g, '').substring(0, 16);
            toolCalls.push({
              id: `extracted_direct_${contentHash}`,
              name: parsed.tool_call.name,
              arguments: parsed.tool_call.arguments || {}
            });
          }
        }
      } catch (error) {
        console.warn('Failed to parse direct tool call:', error);
      }
    }

    // Pattern 3: Native OpenAI format (tool_calls array)
    const nativeToolCallRegex = /"tool_calls"\s*:\s*\[([\s\S]*?)\]/gi;
    const nativeMatch = nativeToolCallRegex.exec(text);
    if (nativeMatch) {
      try {
        const toolCallsArray = JSON.parse(`[${nativeMatch[1]}]`);
        for (const tc of toolCallsArray) {
          if (tc.function && tc.function.name) {
            let args = {};
            if (tc.function.arguments) {
              try {
                args = typeof tc.function.arguments === 'string'
                  ? JSON.parse(tc.function.arguments)
                  : tc.function.arguments;
              } catch (error) {
                console.warn('Failed to parse native tool call arguments:', error);
              }
            }

            // Create deterministic ID based on content to prevent re-render loops
            const contentHash = btoa(JSON.stringify({name: tc.function.name, args})).replace(/[^a-zA-Z0-9]/g, '').substring(0, 16);
            toolCalls.push({
              id: tc.id || `extracted_native_${contentHash}`,
              name: tc.function.name,
              arguments: args
            });
          }
        }
      } catch (error) {
        console.warn('Failed to parse native tool calls:', error);
      }
    }

    return toolCalls;
  };

  const parsed = useMemo(() => parseMessage(content), [content]);
  const hasThinking = parsed.thinking.length > 0;
  const hasToolExecution = parsed.toolExecution.length > 0;

  // TTS function for speaking the message
  const handleSpeak = useCallback(async () => {
    try {
      const settings = settingsService.getSettings();
      const ttsSettings = settings.ui?.textToSpeech;

      if (!ttsSettings?.enabled) {
        console.log('üîä TTS is disabled in settings');
        return;
      }

      if (isSpeaking) {
        // Stop current speech
        const ttsService = getTTSService(ttsSettings);
        ttsService.stop();
        setIsSpeaking(false);
        console.log('üîä TTS stopped');
      } else {
        // Start speaking
        const ttsService = getTTSService(ttsSettings);

        // Use the parsed response text for cleaner speech
        const textToSpeak = parsed.response || content;

        if (!textToSpeak || textToSpeak.trim().length === 0) {
          console.log('üîä No text to speak');
          return;
        }

        console.log('üîä Starting TTS for text:', textToSpeak.substring(0, 100) + '...');
        setIsSpeaking(true);
        ttsService.speak(textToSpeak);

        // Monitor speech status
        const checkSpeechStatus = () => {
          if (!ttsService.isSpeaking()) {
            setIsSpeaking(false);
            console.log('üîä TTS finished');
          } else {
            setTimeout(checkSpeechStatus, 100);
          }
        };
        setTimeout(checkSpeechStatus, 100);
      }
    } catch (error) {
      console.error('üîä Failed to speak text:', error);
      setIsSpeaking(false);
    }
  }, [content, parsed.response, isSpeaking]);

  // Auto-play TTS for new AI messages if enabled
  useEffect(() => {
    // Reset auto-play trigger when content changes (new message)
    autoPlayTriggeredRef.current = false;
  }, [content]);

  useEffect(() => {
    const settings = settingsService.getSettings();
    const ttsSettings = settings.ui?.textToSpeech;

    // Only auto-play if:
    // 1. TTS is enabled
    // 2. Auto-play is enabled
    // 3. There's content to speak
    // 4. We haven't already triggered auto-play for this message
    // 5. This appears to be an AI response (has parsed response content)
    if (ttsSettings?.enabled &&
        ttsSettings?.autoPlay &&
        content &&
        !autoPlayTriggeredRef.current &&
        parsed.response &&
        parsed.response.trim().length > 0) {

      console.log('üîä Auto-play TTS triggered for new AI message');
      autoPlayTriggeredRef.current = true;

      // Small delay to ensure the message is fully rendered
      const timer = setTimeout(() => {
        handleSpeak();
      }, 1000); // Increased delay to ensure message is complete

      return () => clearTimeout(timer);
    }
  }, [content, parsed.response, handleSpeak]);

  // Extract tool calls from content if not provided via props - MEMOIZED to prevent infinite loops
  const extractedToolCalls = useMemo(() => {
    // Only extract if no tool calls provided via props
    if (toolCalls && toolCalls.length > 0) {
      return [];
    }
    return extractToolCallsFromContent(content);
  }, [content, toolCalls]);

  const allToolCalls = toolCalls && toolCalls.length > 0 ? toolCalls : extractedToolCalls;
  const hasTools = allToolCalls.length > 0;

  // Ensure we always show tool execution section if we have tool calls
  // This fixes inconsistencies across providers
  const shouldShowToolExecution = hasToolExecution || hasTools;

  // Log tool calls only once per unique set to prevent spam
  const toolCallsKey = useMemo(() => {
    if (allToolCalls.length === 0) return '';
    return allToolCalls.map(tc => `${tc.name}:${JSON.stringify(tc.arguments)}`).join('|');
  }, [allToolCalls]);

  const [loggedToolCallsKey, setLoggedToolCallsKey] = useState<string>('');

  useEffect(() => {
    if (toolCallsKey && toolCallsKey !== loggedToolCallsKey && allToolCalls.length > 0) {
      debugLogger.info('MESSAGE', 'MessageWithThinking received toolCalls:', allToolCalls);
      setLoggedToolCallsKey(toolCallsKey);
    }
  }, [toolCallsKey, loggedToolCallsKey, allToolCalls]);

  // Copy function for the entire message content
  const handleCopy = async () => {
    try {
      // Copy the full original content (including thinking sections)
      await navigator.clipboard.writeText(content);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy text:', error);
      // Fallback for older browsers
      try {
        const textArea = document.createElement('textarea');
        textArea.value = content;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      } catch (fallbackError) {
        console.error('Fallback copy also failed:', fallbackError);
      }
    }
  };

  return (
    <div className={`relative group ${className}`}>
      {/* Thinking Section - Only show if there are thinking blocks */}
      {hasThinking && (
        <div className="mb-3">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowThinking(!showThinking)}
            className="flex items-center gap-1 text-xs text-muted-foreground hover:text-foreground p-0.5 h-auto"
          >
            {showThinking ? (
              <ChevronDown style={{ width: '16px', height: '16px' }} />
            ) : (
              <ChevronRight style={{ width: '16px', height: '16px' }} />
            )}
            <Brain style={{ width: '16px', height: '16px' }} />
            <span>Model Thinking ({parsed.thinking.length} section{parsed.thinking.length !== 1 ? 's' : ''})</span>
          </Button>
          
          {showThinking && (
            <div className="mt-1 space-y-1">
              {parsed.thinking.map((thinkingText, index) => (
                <div
                  key={index}
                  className="bg-muted rounded-2xl p-2 text-xs"
                  style={{ border: 'none' }}
                >
                  <div className="flex items-center gap-1 mb-1 text-xs text-muted-foreground">
                    <Brain style={{ width: '16px', height: '16px' }} />
                    <span>Thinking {parsed.thinking.length > 1 ? `${index + 1}` : ''}</span>
                  </div>
                  {parseTextWithContent(
                    thinkingText,
                    "whitespace-pre-wrap text-foreground select-text break-words text-sm",
                    {
                      WebkitAppRegion: 'no-drag',
                      userSelect: 'text',
                      WebkitUserSelect: 'text',
                      textShadow: '0 1px 2px rgba(0, 0, 0, 0.3)',
                      wordWrap: 'break-word',
                      overflowWrap: 'break-word',
                      maxWidth: '100%'
                    } as React.CSSProperties & { WebkitAppRegion?: string }
                  )}
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Tool Execution Section - Show if there are tool execution blocks OR tool calls */}
      {shouldShowToolExecution && (
        <div className="mb-3">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowToolExecution(!showToolExecution)}
            className="flex items-center gap-2 text-xs text-muted-foreground hover:text-foreground p-1 h-auto"
          >
            {showToolExecution ? (
              <ChevronDown className="h-3 w-3" />
            ) : (
              <ChevronRight className="h-3 w-3" />
            )}
            <Wrench className="h-3 w-3" />
            <span>Tool Execution ({Math.max(parsed.toolExecution.length, hasTools ? 1 : 0)} section{(parsed.toolExecution.length !== 1 || hasTools) ? 's' : ''})</span>
          </Button>

          {showToolExecution && (
            <div className="mt-2 space-y-2">
              {parsed.toolExecution.map((toolText, index) => {
                // Parse the tool execution text to extract structured information
                const parseToolExecution = (text: string) => {
                  const lines = text.split('\n');
                  const tools: Array<{name: string, result: string, status: 'success' | 'failed', executionTime?: string}> = [];
                  let currentTool: {name: string, result: string, status: 'success' | 'failed', executionTime?: string} | null = null;
                  let inResult = false;
                  let resultLines: string[] = [];

                  for (const line of lines) {
                    // Check for tool execution summary
                    const summaryMatch = line.match(/üèÅ.*?(\d+)\s*successful,\s*(\d+)\s*failed/);
                    if (summaryMatch) {
                      return {
                        summary: line,
                        tools,
                        successCount: parseInt(summaryMatch[1]),
                        failureCount: parseInt(summaryMatch[2])
                      };
                    }

                    // Check for tool result headers
                    const toolMatch = line.match(/\*\*(.+?)\s+Result:\*\*/);
                    if (toolMatch) {
                      // Save previous tool if exists
                      if (currentTool) {
                        currentTool.result = resultLines.join('\n').trim();
                        tools.push(currentTool);
                      }
                      // Start new tool
                      currentTool = {
                        name: toolMatch[1],
                        result: '',
                        status: 'success'
                      };
                      resultLines = [];
                      inResult = true;
                      continue;
                    }

                    // Check for failed tools section
                    if (line.includes('**Failed Tools:**')) {
                      inResult = false;
                      continue;
                    }

                    // Collect result lines
                    if (inResult && currentTool && line.trim()) {
                      resultLines.push(line);
                    }
                  }

                  // Save last tool
                  if (currentTool) {
                    currentTool.result = resultLines.join('\n').trim();
                    tools.push(currentTool);
                  }

                  return { summary: '', tools, successCount: 0, failureCount: 0 };
                };

                const executionData = parseToolExecution(toolText);

                return (
                  <div
                    key={index}
                    className="bg-muted border border-border rounded-2xl p-3 text-sm"
                  >
                    <div className="flex items-center gap-2 mb-3 text-xs text-muted-foreground">
                      <Wrench className="h-3 w-3" />
                      <span>Tool Execution {parsed.toolExecution.length > 1 ? `${index + 1}` : ''}</span>
                      {executionData.summary && (
                        <span className="ml-auto text-xs">
                          ‚úÖ {executionData.successCount} success, ‚ùå {executionData.failureCount} failed
                        </span>
                      )}
                    </div>

                    {/* Show individual tool results if parsed */}
                    {executionData.tools.length > 0 ? (
                      <div className="space-y-3">
                        {executionData.tools.map((tool, toolIndex) => (
                          <div key={toolIndex} className={`border rounded-lg p-2 ${
                            tool.status === 'success'
                              ? 'border-border/50'
                              : 'border-red-500/30 bg-red-500/5'
                          }`}>
                            <div className="flex items-center gap-2 mb-2">
                              <span className="text-xs font-medium text-foreground">{tool.name}</span>
                              <span className={`text-xs px-1.5 py-0.5 rounded ${
                                tool.status === 'success'
                                  ? 'bg-green-500/20 text-green-400'
                                  : 'bg-red-500/20 text-red-400'
                              }`}>
                                {tool.status === 'success' ? '‚úì Success' : '‚úó Failed'}
                              </span>
                              {tool.status === 'failed' && (
                                <span className="text-xs text-red-400 ml-auto">
                                  Error occurred during execution
                                </span>
                              )}
                            </div>
                            {parseTextWithContent(
                              tool.result || '(no result)',
                              `text-xs whitespace-pre-wrap select-text rounded p-2 break-words ${
                                tool.status === 'success'
                                  ? 'text-muted-foreground bg-background/50'
                                  : 'text-red-300 bg-red-500/10'
                              }`,
                              {
                                WebkitAppRegion: 'no-drag',
                                userSelect: 'text',
                                WebkitUserSelect: 'text',
                                border: 'none',
                                wordWrap: 'break-word',
                                overflowWrap: 'break-word',
                                maxWidth: '100%'
                              } as React.CSSProperties & { WebkitAppRegion?: string }
                            )}
                            {tool.status === 'failed' && (
                              <div className="mt-2 text-xs text-red-400/80">
                                üí° This error has been reported. You can try again or contact support if the issue persists.
                              </div>
                            )}
                          </div>
                        ))}
                      </div>
                    ) : (
                      // Fallback to showing raw text if parsing failed
                      <div
                        className="whitespace-pre-wrap text-foreground select-text break-words text-sm"
                        style={{
                          WebkitAppRegion: 'no-drag',
                          userSelect: 'text',
                          WebkitUserSelect: 'text',
                          textShadow: '0 1px 2px rgba(0, 0, 0, 0.3)',
                          wordWrap: 'break-word',
                          overflowWrap: 'break-word',
                          maxWidth: '100%'
                        } as React.CSSProperties & { WebkitAppRegion?: string }}
                      >
                        {toolText}
                      </div>
                    )}
                  </div>
                );
              })}

              {/* Fallback: Show tool execution info when we have tool calls but no parsed tool execution blocks */}
              {parsed.toolExecution.length === 0 && hasTools && (
                <div className="bg-muted border border-border rounded-2xl p-3 text-sm">
                  <div className="flex items-center gap-2 mb-3 text-xs text-muted-foreground">
                    <Wrench className="h-3 w-3" />
                    <span>Tool Execution</span>
                    <span className="ml-auto text-xs">
                      ‚úÖ {allToolCalls.length} tool{allToolCalls.length !== 1 ? 's' : ''} executed
                    </span>
                  </div>

                  <div className="space-y-3">
                    {allToolCalls.map((tool, toolIndex) => (
                      <div key={toolIndex} className={`border rounded-lg p-3 ${
                        tool.error ? 'border-red-500/30 bg-red-500/5' : 'border-border/50'
                      }`}>
                        <div className="flex items-center gap-2 mb-2">
                          <span className="text-xs font-medium text-foreground">{tool.name}</span>
                          <span className={`text-xs px-1.5 py-0.5 rounded ${
                            tool.error
                              ? 'bg-red-500/20 text-red-400'
                              : 'bg-green-500/20 text-green-400'
                          }`}>
                            {tool.error ? '‚úó Failed' : '‚úì Success'}
                          </span>
                        </div>

                        {/* Show actual tool results */}
                        {tool.result !== undefined ? (
                          <div className="mt-2">
                            <div className="text-xs font-medium text-muted-foreground mb-1">Result:</div>
                            <div className="bg-muted rounded p-2 text-xs whitespace-pre-wrap text-foreground select-text break-words max-h-40 overflow-y-auto"
                                 style={{
                                   WebkitAppRegion: 'no-drag',
                                   userSelect: 'text',
                                   WebkitUserSelect: 'text',
                                   wordWrap: 'break-word',
                                   overflowWrap: 'break-word'
                                 } as React.CSSProperties & { WebkitAppRegion?: string }}>
                              {tool.result || '(empty result)'}
                            </div>
                          </div>
                        ) : (
                          <div className="text-xs text-muted-foreground">
                            Tool executed successfully. No detailed results available.
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      )}

      {/* Tool Usage Section - Only show if there are tool calls */}
      {hasTools && (
        <div className="mb-3">
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setShowTools(!showTools)}
            className="flex items-center gap-2 text-xs text-muted-foreground hover:text-foreground p-1 h-auto"
          >
            {showTools ? (
              <ChevronDown className="h-3 w-3" />
            ) : (
              <ChevronRight className="h-3 w-3" />
            )}
            <Wrench className="h-3 w-3" />
            <span>Tools Used ({allToolCalls.length} tool{allToolCalls.length !== 1 ? 's' : ''})</span>
          </Button>

          {showTools && (
            <div className="mt-2 space-y-2">
              {allToolCalls.map((toolCall, index) => (
                <div
                  key={toolCall.id || index}
                  className="bg-card rounded-2xl p-3 text-sm"
                  style={{
                    backgroundColor: 'rgba(79, 193, 255, 0.1)',
                    border: 'none'
                  }}
                >
                  <div className="flex items-center gap-2 mb-2 text-xs" style={{ color: 'var(--info)' }}>
                    <Wrench className="h-3 w-3" />
                    <span className="font-medium">{toolCall.name}</span>
                    <span className="text-muted-foreground">#{toolCall.id}</span>
                  </div>

                  {/* Tool Status and Summary */}
                  <div className="text-xs text-muted-foreground">
                    <div className="flex items-center gap-1 mb-1">
                      <span className="font-medium">Status:</span>
                      <span className={toolCall.error ? 'text-red-400' : 'text-green-400'}>
                        {toolCall.error ? '‚úó Failed' : '‚úì Executed'}
                      </span>
                    </div>

                    {/* Show arguments summary */}
                    {Object.keys(toolCall.arguments).length > 0 && (
                      <div className="mt-1">
                        <span className="font-medium">Arguments: </span>
                        <span className="text-xs">
                          {Object.entries(toolCall.arguments).map(([key, value]) =>
                            `${key}: ${String(value).substring(0, 50)}${String(value).length > 50 ? '...' : ''}`
                          ).join(', ')}
                        </span>
                      </div>
                    )}

                    <div className="mt-1 text-xs">
                      Full results are shown in the Tool Execution section above.
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Action Buttons - positioned in top right */}
      <div className="absolute top-1 right-1 opacity-0 group-hover:opacity-100 transition-opacity flex gap-1">
        {/* TTS Button */}
        <Button
          variant="ghost"
          size="sm"
          onClick={handleSpeak}
          className="h-6 w-6 p-0"
          style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties & { WebkitAppRegion?: string }}
          title={isSpeaking ? "Stop speaking" : "Speak text"}
        >
          {isSpeaking ? (
            <VolumeX className="h-3 w-3 text-blue-500" />
          ) : (
            <Volume2 className="h-3 w-3" />
          )}
        </Button>

        {/* Copy Button */}
        <Button
          variant="ghost"
          size="sm"
          onClick={handleCopy}
          className="h-6 w-6 p-0"
          style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties & { WebkitAppRegion?: string }}
          title="Copy message"
        >
          {copied ? (
            <Check className="h-3 w-3 text-green-500" />
          ) : (
            <Copy className="h-3 w-3" />
          )}
        </Button>
      </div>

      {/* Main Response */}
      {parsed.response && parseTextWithContent(
        parsed.response,
        "select-text break-words text-sm leading-relaxed",
        {
          WebkitAppRegion: 'no-drag',
          userSelect: 'text',
          WebkitUserSelect: 'text',
          wordWrap: 'break-word',
          overflowWrap: 'break-word',
          maxWidth: '100%'
        } as React.CSSProperties & { WebkitAppRegion?: string }
      )}

      {/* Token Usage, Cost, and Performance Info */}
      {(usage || cost || timing) && (
        <div className="mt-3 pt-2">
          <div className="flex flex-wrap gap-4 text-xs text-muted-foreground">
            {timing?.tokensPerSecond && (
              <div className="flex items-center gap-1">
                <span className="font-medium">‚ö°</span>
                <span>{timing.tokensPerSecond.toFixed(1)} tokens/sec</span>
              </div>
            )}
            {usage && (
              <div className="flex items-center gap-1">
                <span className="font-medium">üìä</span>
                <span>{usage.totalTokens} tokens ({usage.promptTokens} in, {usage.completionTokens} out)</span>
              </div>
            )}
            {cost && (
              <div className="flex items-center gap-1">
                <span className="font-medium">üí∞</span>
                <span>
                  {cost.totalCost < 0.000001 ? '<$0.000001' :
                   cost.totalCost < 0.01 ? `$${cost.totalCost.toFixed(6)}` :
                   `$${cost.totalCost.toFixed(4)}`}
                  {cost.provider && ` (${cost.provider})`}
                </span>
              </div>
            )}
            {timing && (
              <div className="flex items-center gap-1">
                <span className="font-medium">‚è±Ô∏è</span>
                <span>{(timing.duration / 1000).toFixed(2)}s</span>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Source Attribution */}
      {sources && sources.length > 0 && (
        <SourceAttribution sources={sources} />
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { Label } from './ui/label';
import { Textarea } from './ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from './ui/select';
import { Plus, Edit, Trash2, Download, Upload } from 'lucide-react';
import { promptsService, type Prompt } from '../services/promptsService';


interface PromptsContentProps {
  onPromptSelect: (processedPrompt: string) => void;
  clipboardContent?: string;
}

export function PromptsContent({ onPromptSelect, clipboardContent = '' }: PromptsContentProps) {
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [showAddPrompt, setShowAddPrompt] = useState(false);
  const [editingPrompt, setEditingPrompt] = useState<Prompt | null>(null);
  const [isReadingClipboard, setIsReadingClipboard] = useState(false);
  const [newPrompt, setNewPrompt] = useState({
    name: '',
    description: '',
    prompt: '',
    category: 'text',
    icon: '‚úèÔ∏è'
  });

  const allPrompts = promptsService.getAllPrompts();
  const categories = promptsService.getCategories().filter(category => category && typeof category === 'string');
  const filteredPrompts = selectedCategory === 'all' 
    ? allPrompts 
    : promptsService.getPromptsByCategory(selectedCategory);

  const handlePromptSelect = async (prompt: Prompt) => {
    setIsReadingClipboard(true);
    let processedPrompt = prompt.prompt;
    let currentClipboardContent = clipboardContent;

    // If no clipboard content is available, try to read it now
    if (!currentClipboardContent) {
      try {
        if (typeof window !== 'undefined' && window.electronAPI) {
          currentClipboardContent = (await window.electronAPI.readClipboard()).trim();
        } else if (navigator.clipboard) {
          currentClipboardContent = (await navigator.clipboard.readText()).trim();
        }
      } catch (error) {
        console.warn('Failed to read clipboard:', error);
        currentClipboardContent = '';
      }
    }

    // Replace {content} placeholder with clipboard content
    if (processedPrompt.includes('{content}')) {
      if (currentClipboardContent) {
        processedPrompt = processedPrompt.replace(/{content}/g, currentClipboardContent);
      } else {
        processedPrompt = processedPrompt.replace(/{content}/g, '[No clipboard content available]');
      }
    }

    setIsReadingClipboard(false);
    onPromptSelect(processedPrompt);
  };

  const handleAddPrompt = () => {
    if (newPrompt.name && newPrompt.prompt) {
      promptsService.addCustomPrompt({
        name: newPrompt.name,
        description: newPrompt.description,
        prompt: newPrompt.prompt,
        category: newPrompt.category,
        icon: newPrompt.icon
      });
      setNewPrompt({ name: '', description: '', prompt: '', category: 'text', icon: '‚úèÔ∏è' });
      setShowAddPrompt(false);
    }
  };

  const handleUpdatePrompt = () => {
    if (editingPrompt && newPrompt.name && newPrompt.prompt) {
      if (promptsService.isCustomPrompt(editingPrompt.id)) {
        // Update existing custom prompt
        promptsService.updateCustomPrompt(editingPrompt.id, {
          name: newPrompt.name,
          description: newPrompt.description,
          prompt: newPrompt.prompt,
          category: newPrompt.category,
          icon: newPrompt.icon
        });
      } else {
        // Convert built-in prompt to custom prompt by creating a new one
        promptsService.addCustomPrompt({
          name: newPrompt.name,
          description: newPrompt.description,
          prompt: newPrompt.prompt,
          category: newPrompt.category,
          icon: newPrompt.icon
        });
      }
      setEditingPrompt(null);
      setNewPrompt({ name: '', description: '', prompt: '', category: 'text', icon: '‚úèÔ∏è' });
      setShowAddPrompt(false);
    }
  };

  const handleEditPrompt = (prompt: Prompt) => {
    setEditingPrompt(prompt);
    setNewPrompt({
      name: prompt.name,
      description: prompt.description,
      prompt: prompt.prompt,
      category: prompt.category,
      icon: prompt.icon
    });
    setShowAddPrompt(true);
  };

  const handleDeletePrompt = (promptId: string) => {
    promptsService.deleteCustomPrompt(promptId);
  };

  const handleExportPrompts = () => {
    const customPrompts = promptsService.getCustomPrompts();
    const dataStr = JSON.stringify(customPrompts, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'custom-prompts.json';
    link.click();
    URL.revokeObjectURL(url);
  };

  const handleImportPrompts = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    input.onchange = (e) => {
      const file = (e.target as HTMLInputElement).files?.[0];
      if (file) {
        const reader = new FileReader();
        reader.onload = (e) => {
          try {
            const prompts = JSON.parse(e.target?.result as string);
            if (Array.isArray(prompts)) {
              prompts.forEach(prompt => {
                if (prompt.name && prompt.prompt) {
                  promptsService.addCustomPrompt(prompt);
                }
              });
            }
          } catch (error) {
            console.error('Failed to import prompts:', error);
          }
        };
        reader.readAsText(file);
      }
    };
    input.click();
  };

  return (
    <div className="space-y-2">
      {/* Category Filter */}
      <div className="flex items-center gap-2">
        <Label className="text-xs">Category:</Label>
        <Select value={selectedCategory} onValueChange={setSelectedCategory}>
          <SelectTrigger className="w-32 h-7 text-xs">
            <SelectValue />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Categories</SelectItem>
            {categories.map(category => (
              <SelectItem key={category} value={category}>
                {category.charAt(0).toUpperCase() + category.slice(1)}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>

        <div className="flex gap-1 ml-auto">
          <Button
            variant="outline"
            size="sm"
            onClick={() => setShowAddPrompt(true)}
            className="h-7 text-xs bg-primary/20 border-primary/50 text-primary hover:bg-primary/30 hover:border-primary/70"
          >
            <Plus className="h-3 w-3 mr-1" />
            Add Custom Prompt
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={handleExportPrompts}
            className="h-7 text-xs"
          >
            <Download className="h-3 w-3 mr-1" />
            Export
          </Button>
          <Button
            variant="outline"
            size="sm"
            onClick={handleImportPrompts}
            className="h-7 text-xs"
          >
            <Upload className="h-3 w-3 mr-1" />
            Import
          </Button>
        </div>
      </div>

      {/* Add/Edit Prompt Form */}
      {showAddPrompt && (
        <div className="border rounded-lg p-2 space-y-2 bg-muted/50">
          <h3 className="text-xs font-semibold">
            {editingPrompt
              ? (promptsService.isCustomPrompt(editingPrompt.id)
                  ? 'Edit Custom Prompt'
                  : 'Edit Built-in Prompt (will create custom copy)')
              : 'Add New Custom Prompt'
            }
          </h3>
          <div className="grid grid-cols-2 gap-2">
            <div>
              <Label htmlFor="name" className="text-xs">Name</Label>
              <Input
                id="name"
                value={newPrompt.name}
                onChange={(e) => setNewPrompt(prev => ({ ...prev, name: e.target.value }))}
                placeholder="Prompt name"
                className="h-7 text-xs bg-muted border-2 border-border focus:bg-card hover:bg-muted/80 focus:border-ring focus:ring-2 focus:ring-ring/20 transition-all"
              />
            </div>
            <div>
              <Label htmlFor="icon" className="text-xs">Icon</Label>
              <Input
                id="icon"
                value={newPrompt.icon}
                onChange={(e) => setNewPrompt(prev => ({ ...prev, icon: e.target.value }))}
                placeholder="üìù"
                className="h-7 text-xs bg-muted border-2 border-border focus:bg-card hover:bg-muted/80 focus:border-ring focus:ring-2 focus:ring-ring/20 transition-all"
              />
            </div>
          </div>
          <div>
            <Label htmlFor="description" className="text-xs">Description</Label>
            <Input
              id="description"
              value={newPrompt.description}
              onChange={(e) => setNewPrompt(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Brief description"
              className="h-7 text-xs bg-slate-900 border-2 border-slate-600 focus:bg-slate-800 hover:bg-slate-850 focus:border-blue-400 focus:ring-2 focus:ring-blue-400/20 transition-all"
            />
          </div>
          <div>
            <Label htmlFor="category" className="text-xs">Category</Label>
            <Select
              value={newPrompt.category}
              onValueChange={(value) => setNewPrompt(prev => ({ ...prev, category: value }))}
            >
              <SelectTrigger className="h-7 text-xs bg-slate-900 border-2 border-slate-600 focus:bg-slate-800 hover:bg-slate-850 focus:border-blue-400 focus:ring-2 focus:ring-blue-400/20 transition-all">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                {categories.map(category => (
                  <SelectItem key={category} value={category}>
                    {category.charAt(0).toUpperCase() + category.slice(1)}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
          <div>
            <Label htmlFor="prompt" className="text-xs">Prompt Template</Label>
            <Textarea
              id="prompt"
              value={newPrompt.prompt}
              onChange={(e) => setNewPrompt(prev => ({ ...prev, prompt: e.target.value }))}
              placeholder="Your prompt template. Use {content} where the clipboard content should be inserted."
              rows={3}
              className="text-xs bg-muted border-2 border-border focus:bg-card hover:bg-muted/80 focus:border-ring focus:ring-2 focus:ring-ring/20 transition-all"
            />
          </div>
          <div className="flex justify-end gap-1">
            <Button
              variant="outline"
              size="sm"
              onClick={() => {
                setShowAddPrompt(false);
                setEditingPrompt(null);
              }}
              className="h-7 text-xs"
            >
              Cancel
            </Button>
            <Button
              onClick={editingPrompt ? handleUpdatePrompt : handleAddPrompt}
              size="sm"
              className="h-7 text-xs"
            >
              {editingPrompt
                ? (promptsService.isCustomPrompt(editingPrompt.id)
                    ? 'Update Prompt'
                    : 'Save as Custom Prompt')
                : 'Add Prompt'
              }
            </Button>
          </div>
        </div>
      )}

      {/* Prompts List */}
      <div className="space-y-1 max-h-96 overflow-y-auto">
        {filteredPrompts.map((prompt) => (
          <div
            key={prompt.id}
            className="group flex items-center justify-between p-2 rounded-lg hover:bg-muted/50 cursor-pointer"
            style={{ border: '1px solid hsl(var(--border))' }}
            onClick={() => handlePromptSelect(prompt)}
          >
            <div className="flex items-center gap-2 flex-1">
              <span className="text-sm">{prompt.icon}</span>
              <div className="flex-1">
                <div className="text-xs font-medium text-foreground">{prompt.name}</div>
                <div className="text-xs text-muted-foreground">{prompt.description}</div>
              </div>
            </div>
            {/* Always show edit button for ALL prompts, delete only for custom prompts */}
            <div className="flex gap-1 items-center">
              <Button
                variant="ghost"
                size="sm"
                onClick={(e) => {
                  e.stopPropagation();
                  handleEditPrompt(prompt);
                }}
                className="h-6 w-6 p-0 text-primary hover:text-primary/80 hover:bg-primary/10 border border-primary/30 opacity-80 hover:opacity-100"
                title="Edit prompt"
              >
                <Edit className="h-3 w-3" />
              </Button>

              {/* Only show delete button for custom prompts */}
              {promptsService.isCustomPrompt(prompt.id) && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDeletePrompt(prompt.id);
                  }}
                  className="h-6 w-6 p-0 text-destructive hover:text-destructive/80 hover:bg-destructive/10 border border-destructive/30 opacity-80 hover:opacity-100"
                  title="Delete prompt"
                >
                  <Trash2 className="h-3 w-3" />
                </Button>
              )}

              {/* Show indicator for custom prompts */}
              {promptsService.isCustomPrompt(prompt.id) && (
                <div className="text-xs text-primary bg-primary/10 px-1 py-0.5 rounded border border-primary/30 ml-1">
                  CUSTOM
                </div>
              )}
            </div>
          </div>
        ))}
      </div>

      {isReadingClipboard && (
        <div className="text-center text-xs text-muted-foreground">
          Reading clipboard...
        </div>
      )}
    </div>
  );
}

'use client';

