'use client';

import React, { useState } from 'react';
import { Copy, Check } from 'lucide-react';
import { Button } from './ui/button';
import { MessageContent } from './MessageContent';
import { ContentItem } from '../services/chatService';

interface UserMessageProps {
  content: string | ContentItem[];
  className?: string;
}

export function UserMessage({ content, className = '' }: UserMessageProps) {
  const [copied, setCopied] = useState(false);

  // Copy function for the message content
  const handleCopy = async () => {
    try {
      // Convert content to string for copying
      const textContent = typeof content === 'string'
        ? content
        : Array.isArray(content)
          ? content.map(item => item.type === 'text' ? item.text : `[${item.type}]`).join(' ')
          : String(content);

      await navigator.clipboard.writeText(textContent);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (error) {
      console.error('Failed to copy text:', error);
      // Fallback for older browsers
      try {
        const textContent = typeof content === 'string'
          ? content
          : Array.isArray(content)
            ? content.map(item => item.type === 'text' ? item.text : `[${item.type}]`).join(' ')
            : String(content);

        const textArea = document.createElement('textarea');
        textArea.value = textContent;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        setCopied(true);
        setTimeout(() => setCopied(false), 2000);
      } catch (fallbackError) {
        console.error('Fallback copy also failed:', fallbackError);
      }
    }
  };

  return (
    <div className={`relative group ${className}`}>
      {/* Copy Button - positioned in top right */}
      <Button
        variant="ghost"
        size="sm"
        onClick={handleCopy}
        className="absolute top-0.5 right-0.5 opacity-0 group-hover:opacity-100 transition-opacity h-5 w-5 p-0"
        style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties & { WebkitAppRegion?: string }}
      >
        {copied ? (
          <Check style={{ width: '16px', height: '16px' }} className="text-green-500" />
        ) : (
          <Copy style={{ width: '16px', height: '16px' }} />
        )}
      </Button>

      {/* Message Content */}
      <MessageContent
        content={content}
        className="select-text break-words text-sm leading-relaxed"
        style={{
          WebkitAppRegion: 'no-drag',
          userSelect: 'text',
          WebkitUserSelect: 'text',
          wordWrap: 'break-word',
          overflowWrap: 'break-word',
          maxWidth: '100%'
        } as React.CSSProperties & { WebkitAppRegion?: string }}
      />
    </div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import { motion } from "framer-motion";
import React, { forwardRef, useRef } from "react";

export interface AnimatedBeamProps {
  className?: string;
  containerRef: React.RefObject<HTMLElement>;
  fromRef: React.RefObject<HTMLElement>;
  toRef: React.RefObject<HTMLElement>;
  curvature?: number;
  reverse?: boolean;
  pathColor?: string;
  pathWidth?: number;
  pathOpacity?: number;
  gradientStartColor?: string;
  gradientStopColor?: string;
  delay?: number;
  duration?: number;
  startXOffset?: number;
  startYOffset?: number;
  endXOffset?: number;
  endYOffset?: number;
}

export const AnimatedBeam = forwardRef<SVGSVGElement, AnimatedBeamProps>(
  (
    {
      className,
      containerRef,
      fromRef,
      toRef,
      curvature = 0,
      reverse = false,
      duration = Math.random() * 3 + 4,
      delay = 0,
      pathColor = "gray",
      pathWidth = 2,
      pathOpacity = 0.2,
      gradientStartColor = "#ffaa40",
      gradientStopColor = "#9c40ff",
      startXOffset = 0,
      startYOffset = 0,
      endXOffset = 0,
      endYOffset = 0,
    },
    ref,
  ) => {
    const id = React.useId();
    const svgRef = useRef<SVGSVGElement>(null);
    const pathRef = useRef<SVGPathElement>(null);

    React.useEffect(() => {
      const updatePath = () => {
        if (
          containerRef.current &&
          fromRef.current &&
          toRef.current &&
          pathRef.current
        ) {
          const containerRect = containerRef.current.getBoundingClientRect();
          const rectA = fromRef.current.getBoundingClientRect();
          const rectB = toRef.current.getBoundingClientRect();

          const svgWidth = containerRect.width;
          const svgHeight = containerRect.height;
          const svgX = containerRect.left;
          const svgY = containerRect.top;

          const startX =
            rectA.left - svgX + rectA.width / 2 + startXOffset;
          const startY =
            rectA.top - svgY + rectA.height / 2 + startYOffset;
          const endX = rectB.left - svgX + rectB.width / 2 + endXOffset;
          const endY = rectB.top - svgY + rectB.height / 2 + endYOffset;

          const controlPointX = startX + (endX - startX) / 2;
          const controlPointY = startY - curvature;

          const d = `M ${startX},${startY} Q ${controlPointX},${controlPointY} ${endX},${endY}`;
          pathRef.current.setAttribute("d", d);
        }
      };

      // Set up ResizeObserver
      const resizeObserver = new ResizeObserver((entries) => {
        // On the next frame (to ensure elements are rendered)
        requestAnimationFrame(updatePath);
      });

      // Observe the container element
      if (containerRef.current) {
        resizeObserver.observe(containerRef.current);
      }

      // Call the updatePath initially
      updatePath();

      // Clean up the observer on component unmount
      return () => {
        resizeObserver.disconnect();
      };
    }, [
      containerRef,
      fromRef,
      toRef,
      curvature,
      startXOffset,
      startYOffset,
      endXOffset,
      endYOffset,
    ]);

    return (
      <svg
        ref={svgRef}
        fill="none"
        width="100%"
        height="100%"
        xmlns="http://www.w3.org/2000/svg"
        className={cn(
          "pointer-events-none absolute left-0 top-0 transform-gpu stroke-2",
          className,
        )}
        viewBox={`0 0 ${containerRef.current?.getBoundingClientRect().width || 0} ${
          containerRef.current?.getBoundingClientRect().height || 0
        }`}
      >
        <defs>
          <linearGradient
            className={`transform-gpu ${reverse ? "rotate-180" : ""}`}
            id={`${id}`}
            x1="0%"
            x2="100%"
            y1="0%"
            y2="0%"
          >
            <stop offset="0%" stopColor={gradientStartColor} stopOpacity="0" />
            <stop offset="32.5%" stopColor={gradientStartColor} />
            <stop offset="67.5%" stopColor={gradientStopColor} />
            <stop offset="100%" stopColor={gradientStopColor} stopOpacity="0" />
          </linearGradient>
        </defs>
        <motion.path
          ref={pathRef}
          stroke={pathColor}
          strokeWidth={pathWidth}
          strokeOpacity={pathOpacity}
          fill="none"
        />
        <motion.path
          stroke={`url(#${id})`}
          strokeWidth={pathWidth}
          strokeOpacity="1"
          fill="none"
          pathLength="1"
          initial={{
            strokeDasharray: "0 1",
          }}
          animate={{
            strokeDasharray: ["0 1", "1 1", "0 1"],
          }}
          transition={{
            duration,
            repeat: Infinity,
            delay,
            ease: "linear",
          }}
        />
      </svg>
    );
  },
);

AnimatedBeam.displayName = "AnimatedBeam";

"use client";

import { motion } from "framer-motion";
import React from "react";

interface BlurFadeProps {
  children: React.ReactNode;
  className?: string;
  variant?: {
    hidden: { y: number; opacity: number; filter: string };
    visible: { y: number; opacity: number; filter: string };
  };
  duration?: number;
  delay?: number;
  yOffset?: number;
  inView?: boolean;
  inViewMargin?: string;
  blur?: string;
}

const defaultVariant = {
  hidden: { y: 6, opacity: 0, filter: "blur(6px)" },
  visible: { y: -6, opacity: 1, filter: "blur(0px)" },
};

export function BlurFade({
  children,
  className,
  variant = defaultVariant,
  duration = 0.4,
  delay = 0,
  yOffset = 6,
  inView = false,
  inViewMargin = "-50px",
  blur = "6px",
}: BlurFadeProps) {
  const customVariant = {
    hidden: {
      y: yOffset,
      opacity: 0,
      filter: `blur(${blur})`,
    },
    visible: {
      y: -yOffset,
      opacity: 1,
      filter: "blur(0px)",
    },
  };

  return (
    <motion.div
      initial="hidden"
      animate="visible"
      exit="hidden"
      variants={variant || customVariant}
      transition={{
        delay: 0.04 + delay,
        duration,
        ease: "easeOut",
      }}
      className={className}
    >
      {children}
    </motion.div>
  );
}

export function BlurFadeText({
  text,
  className,
  variant,
  characterDelay = 0.03,
  delay = 0,
  yOffset = 8,
  animateByCharacter = false,
}: {
  text: string;
  className?: string;
  variant?: {
    hidden: { y: number; opacity: number; filter: string };
    visible: { y: number; opacity: number; filter: string };
  };
  characterDelay?: number;
  delay?: number;
  yOffset?: number;
  animateByCharacter?: boolean;
}) {
  const defaultVariants = {
    hidden: { y: yOffset, opacity: 0, filter: "blur(8px)" },
    visible: { y: -yOffset, opacity: 1, filter: "blur(0px)" },
  };
  const selectedVariant = variant || defaultVariants;
  
  if (animateByCharacter) {
    return (
      <div className={className}>
        {text.split("").map((char, i) => (
          <motion.span
            key={i}
            initial="hidden"
            animate="visible"
            exit="hidden"
            variants={selectedVariant}
            transition={{
              delay: delay + i * characterDelay,
              duration: 0.2,
              ease: "easeOut",
            }}
            style={{ display: "inline-block" }}
          >
            {char === " " ? "\u00A0" : char}
          </motion.span>
        ))}
      </div>
    );
  }

  return (
    <motion.div
      initial="hidden"
      animate="visible"
      exit="hidden"
      variants={selectedVariant}
      transition={{
        delay,
        duration: 0.4,
        ease: "easeOut",
      }}
      className={className}
    >
      {text}
    </motion.div>
  );
}

"use client";

import { cn } from "@/lib/utils";
import { motion, useMotionValue, useSpring, useTransform } from "framer-motion";
import React, { useRef } from "react";

export interface DockProps extends React.HTMLAttributes<HTMLDivElement> {
  className?: string;
  children: React.ReactNode;
  direction?: "top" | "middle" | "bottom";
}

export interface DockIconProps {
  size?: number;
  magnification?: number;
  distance?: number;
  children?: React.ReactNode;
  className?: string;
}

const DEFAULT_MAGNIFICATION = 60;
const DEFAULT_DISTANCE = 140;

export function Dock({
  className,
  children,
  direction = "bottom",
  ...props
}: DockProps) {
  const mouseX = useMotionValue(Infinity);

  const renderChildren = () => {
    return React.Children.map(children, (child: any) => {
      return React.cloneElement(child, {
        mouseX: mouseX,
      });
    });
  };

  const {
    onDrag,
    onDragStart,
    onDragEnd,
    onAnimationStart,
    onAnimationEnd,
    onAnimationIteration,
    ...motionProps
  } = props;

  return (
    <motion.div
      onMouseMove={(e) => mouseX.set(e.pageX)}
      onMouseLeave={() => mouseX.set(Infinity)}
      {...motionProps}
      className={cn(
        "mx-auto flex h-16 items-end gap-4 rounded-2xl border bg-gray-50 px-4 pb-3 dark:bg-gray-900",
        {
          "items-start pt-3": direction === "top",
          "items-center py-3": direction === "middle",
        },
        className,
      )}
    >
      {renderChildren()}
    </motion.div>
  );
}

export function DockIcon({
  size = 40,
  magnification = DEFAULT_MAGNIFICATION,
  distance = DEFAULT_DISTANCE,
  mouseX,
  className,
  children,
  ...props
}: DockIconProps & { mouseX?: any }) {
  const ref = useRef<HTMLDivElement>(null);

  const distanceCalc = useTransform(mouseX, (val: number) => {
    const bounds = ref.current?.getBoundingClientRect() ?? { x: 0, width: 0 };

    return val - bounds.x - bounds.width / 2;
  });

  const widthSync = useTransform(
    distanceCalc,
    [-distance, 0, distance],
    [size, magnification, size],
  );

  const width = useSpring(widthSync, {
    mass: 0.1,
    stiffness: 150,
    damping: 12,
  });

  return (
    <motion.div
      ref={ref}
      style={{ width }}
      className={cn(
        "flex aspect-square cursor-pointer items-center justify-center rounded-full",
        className,
      )}
      {...props}
    >
      {children}
    </motion.div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { 
  Plus, 
  Edit3, 
  Trash2, 
  Copy, 
  Download, 
  Upload, 
  Search,
  Filter,
  Bot,
  Play
} from 'lucide-react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../ui/card';
import { Badge } from '../ui/badge';
import { cn } from '@/lib/utils';
import { AgentConfiguration, AgentTemplate } from '../../types/agent';
import { agentService } from '../../services/agentService';
import { CreateAgentDialog } from './CreateAgentDialog';
import { EditAgentDialog } from './EditAgentDialog';

interface AgentManagementProps {
  className?: string;
  onAgentSelect?: (agent: AgentConfiguration) => void;
  onClose?: () => void;
}

export function AgentManagement({
  className,
  onAgentSelect,
  onClose
}: AgentManagementProps) {
  const [agents, setAgents] = useState<AgentConfiguration[]>([]);
  const [templates, setTemplates] = useState<AgentTemplate[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [selectedAgent, setSelectedAgent] = useState<AgentConfiguration | null>(null);
  const [loading, setLoading] = useState(true);

  // Load agents and templates
  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      const [agentsData, templatesData] = await Promise.all([
        agentService.getAgents(),
        agentService.getTemplates()
      ]);
      setAgents(agentsData);
      setTemplates(templatesData);
    } catch (error) {
      console.error('Failed to load agent data:', error);
    } finally {
      setLoading(false);
    }
  };

  // Filter agents based on search and category
  const filteredAgents = agents.filter(agent => {
    const matchesSearch = agent.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         agent.description.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         (agent.tags || []).some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()));

    const matchesCategory = selectedCategory === 'all' || 
                           (agent.tags || []).includes(selectedCategory);

    return matchesSearch && matchesCategory;
  });

  // Get unique categories from agents
  const categories = ['all', ...new Set(agents.flatMap(agent => agent.tags || []))];

  const handleCreateAgent = async () => {
    await loadData();
    setCreateDialogOpen(false);
  };

  const handleEditAgent = (agent: AgentConfiguration) => {
    setSelectedAgent(agent);
    setEditDialogOpen(true);
  };

  const handleUpdateAgent = async () => {
    await loadData();
    setEditDialogOpen(false);
    setSelectedAgent(null);
  };

  const handleDeleteAgent = async (agent: AgentConfiguration) => {
    if (confirm(`Are you sure you want to delete "${agent.name}"?`)) {
      try {
        await agentService.deleteAgent(agent.id);
        await loadData();
      } catch (error) {
        console.error('Failed to delete agent:', error);
      }
    }
  };

  const handleDuplicateAgent = async (agent: AgentConfiguration) => {
    try {
      await agentService.duplicateAgent(agent.id);
      await loadData();
    } catch (error) {
      console.error('Failed to duplicate agent:', error);
    }
  };

  const handleExportAgent = async (agent: AgentConfiguration) => {
    try {
      const exportData = await agentService.exportAgent(agent.id);
      if (exportData) {
        const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `${agent.name.replace(/[^a-z0-9]/gi, '_').toLowerCase()}_agent.json`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }
    } catch (error) {
      console.error('Failed to export agent:', error);
    }
  };

  const handleImportAgent = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    try {
      const text = await file.text();
      const exportData = JSON.parse(text);
      const result = await agentService.importAgent(exportData);

      if (result.success) {
        await loadData();
        alert(`Agent imported successfully! ${result.warnings?.length ? `\nWarnings: ${result.warnings.join(', ')}` : ''}`);
      } else {
        alert(`Failed to import agent: ${result.errors?.join(', ')}`);
      }
    } catch (error) {
      console.error('Failed to import agent:', error);
      alert('Failed to import agent: Invalid file format');
    }

    // Reset file input
    event.target.value = '';
  };

  const handleUseAgent = (agent: AgentConfiguration) => {
    onAgentSelect?.(agent);
    onClose?.();
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-gray-400">Loading agents...</div>
      </div>
    );
  }

  return (
    <div className={cn("flex flex-col h-full bg-gray-950 text-white", className)}>
      {/* Header */}
      <div className="flex items-center justify-between p-6 border-b border-gray-800">
        <div className="flex items-center gap-3">
          <Bot className="w-6 h-6 text-blue-400" />
          <h1 className="text-xl font-semibold">Custom Agents</h1>
        </div>
        <div className="flex items-center gap-2">
          <input
            type="file"
            accept=".json"
            onChange={handleImportAgent}
            className="hidden"
            id="import-agent"
          />
          <Button
            variant="outline"
            size="sm"
            onClick={() => document.getElementById('import-agent')?.click()}
            className="text-gray-300 border-gray-600 hover:bg-gray-800"
          >
            <Upload className="w-4 h-4 mr-2" />
            Import
          </Button>
          <Button
            size="sm"
            onClick={() => setCreateDialogOpen(true)}
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Plus className="w-4 h-4 mr-2" />
            Create Agent
          </Button>
        </div>
      </div>

      {/* Search and Filter */}
      <div className="flex items-center gap-4 p-6 border-b border-gray-800">
        <div className="flex-1 relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-gray-400" />
          <Input
            placeholder="Search agents..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10 bg-gray-900 border-gray-700 text-white"
          />
        </div>
        <div className="flex items-center gap-2">
          <Filter className="w-4 h-4 text-gray-400" />
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="bg-gray-900 border border-gray-700 rounded px-3 py-2 text-white text-sm"
          >
            {categories.map(category => (
              <option key={category} value={category}>
                {category === 'all' ? 'All Categories' : category}
              </option>
            ))}
          </select>
        </div>
      </div>

      {/* Agent Grid */}
      <div className="flex-1 overflow-auto p-6">
        {filteredAgents.length === 0 ? (
          <div className="flex flex-col items-center justify-center h-64 text-gray-400">
            <Bot className="w-12 h-12 mb-4 opacity-50" />
            <p className="text-lg mb-2">No agents found</p>
            <p className="text-sm">Create your first custom agent to get started</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {filteredAgents.map((agent) => (
              <AgentCard
                key={agent.id}
                agent={agent}
                onEdit={() => handleEditAgent(agent)}
                onDelete={() => handleDeleteAgent(agent)}
                onDuplicate={() => handleDuplicateAgent(agent)}
                onExport={() => handleExportAgent(agent)}
                onUse={() => handleUseAgent(agent)}
              />
            ))}
          </div>
        )}
      </div>

      {/* Create Agent Dialog */}
      <CreateAgentDialog
        open={createDialogOpen}
        onOpenChange={setCreateDialogOpen}
        onSuccess={handleCreateAgent}
        templates={templates}
      />

      {/* Edit Agent Dialog */}
      {selectedAgent && (
        <EditAgentDialog
          open={editDialogOpen}
          onOpenChange={setEditDialogOpen}
          agent={selectedAgent}
          onSuccess={handleUpdateAgent}
        />
      )}
    </div>
  );
}

interface AgentCardProps {
  agent: AgentConfiguration;
  onEdit: () => void;
  onDelete: () => void;
  onDuplicate: () => void;
  onExport: () => void;
  onUse: () => void;
}

function AgentCard({ agent, onEdit, onDelete, onDuplicate, onExport, onUse }: AgentCardProps) {
  return (
    <Card className="bg-gray-900 border-gray-700 hover:border-gray-600 transition-colors">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-center gap-3">
            <div className="text-2xl">{agent.icon || 'ü§ñ'}</div>
            <div>
              <CardTitle className="text-white text-lg">{agent.name}</CardTitle>
              <CardDescription className="text-gray-400 text-sm">
                {agent.description}
              </CardDescription>
            </div>
          </div>
        </div>
      </CardHeader>
      <CardContent className="pt-0">
        <div className="space-y-3">
          {/* Tags */}
          {agent.tags && agent.tags.length > 0 && (
            <div className="flex flex-wrap gap-1">
              {agent.tags.map((tag) => (
                <Badge key={tag} variant="secondary" className="text-xs bg-gray-800 text-gray-300">
                  {tag}
                </Badge>
              ))}
            </div>
          )}

          {/* Stats */}
          <div className="flex items-center gap-4 text-xs text-gray-400">
            <span>{agent.selectedTools.length} tools</span>
            <span>{agent.enabledMCPServers.length} MCP servers</span>
            <span>{agent.defaultProvider}</span>
          </div>

          {/* Actions */}
          <div className="flex items-center gap-2">
            <Button
              size="sm"
              onClick={onUse}
              className="flex-1 bg-blue-600 hover:bg-blue-700"
            >
              <Play className="w-3 h-3 mr-1" />
              Use
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={onEdit}
              className="border-gray-600 hover:bg-gray-800"
            >
              <Edit3 className="w-3 h-3" />
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={onDuplicate}
              className="border-gray-600 hover:bg-gray-800"
            >
              <Copy className="w-3 h-3" />
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={onExport}
              className="border-gray-600 hover:bg-gray-800"
            >
              <Download className="w-3 h-3" />
            </Button>
            <Button
              variant="outline"
              size="sm"
              onClick={onDelete}
              className="border-red-600 text-red-400 hover:bg-red-900/20"
            >
              <Trash2 className="w-3 h-3" />
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import {
  Paperclip,
  Camera,
  Database,
  Wrench,
  ChevronDown,
  Plus,
  Send,
  Square
} from 'lucide-react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { cn } from '@/lib/utils';

interface BottomInputAreaProps {
  className?: string;
  value?: string;
  onChange?: (value: string) => void;
  onSend?: (message: string) => void;
  onStop?: () => void;
  onFileUpload?: (files: FileList) => void;
  onScreenshot?: () => void;
  selectedModel?: string;
  onModelChange?: (model: string) => void;
  availableModels?: string[];
  selectedProvider?: string;
  isLoading?: boolean;
  toolsEnabled?: boolean;
  onToggleTools?: (enabled: boolean) => void;
  mcpEnabled?: boolean;
  onToggleMCP?: (enabled: boolean) => void;
  knowledgeBaseEnabled?: boolean;
  onToggleKnowledgeBase?: (enabled: boolean) => void;
  onStartNewChat?: () => void;
  selectedAgent?: any;
  onAgentChange?: (agent: any) => void;
  availableAgents?: any[];
}

export function BottomInputArea({
  className,
  value = '',
  onChange,
  onSend,
  onStop,
  onFileUpload,
  onScreenshot,
  selectedModel = 'gemma3:gpu',
  onModelChange,
  availableModels = [],
  selectedProvider = 'ollama',
  isLoading = false,
  toolsEnabled = false,
  onToggleTools,
  mcpEnabled = false,
  onToggleMCP,
  knowledgeBaseEnabled = false,
  onToggleKnowledgeBase,
  onStartNewChat,
  selectedAgent,
  onAgentChange,
  availableAgents = []
}: BottomInputAreaProps) {
  
  const [inputValue, setInputValue] = useState(value);
  const [isFocused, setIsFocused] = useState(false);
  const [showModelDropdown, setShowModelDropdown] = useState(false);
  const [showAgentDropdown, setShowAgentDropdown] = useState(false);
  const [modelSearchQuery, setModelSearchQuery] = useState('');
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const modelSearchRef = useRef<HTMLInputElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  // Sync local input value with prop value (for prompt selection)
  useEffect(() => {
    if (value !== undefined && value !== inputValue) {
      setInputValue(value);
      // Reset textarea height when value changes externally
      if (textareaRef.current) {
        textareaRef.current.style.height = '30px';
      }
    }
  }, [value, inputValue]);

  // Reset textarea height when input is cleared
  useEffect(() => {
    if (!inputValue && textareaRef.current) {
      textareaRef.current.style.height = '30px';
    }
  }, [inputValue]);

  // Simple fuzzy search function
  const fuzzySearch = (query: string, text: string): number => {
    if (!query) return 1; // No query means perfect match

    const queryLower = query.toLowerCase();
    const textLower = text.toLowerCase();

    // Exact match gets highest score
    if (textLower.includes(queryLower)) {
      return 1;
    }

    // Character-by-character fuzzy matching
    let queryIndex = 0;
    let score = 0;

    for (let i = 0; i < textLower.length && queryIndex < queryLower.length; i++) {
      if (textLower[i] === queryLower[queryIndex]) {
        score++;
        queryIndex++;
      }
    }

    // Return score as percentage of query characters found
    return queryIndex === queryLower.length ? score / queryLower.length : 0;
  };

  // Filter and sort models based on search query
  const filteredModels = availableModels
    .map(model => ({
      model,
      score: fuzzySearch(modelSearchQuery, model)
    }))
    .filter(item => item.score > 0)
    .sort((a, b) => b.score - a.score)
    .map(item => item.model);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (files && files.length > 0) {
      onFileUpload?.(files);
    }
  };

  // Add global "/" key listener to focus textarea
  useEffect(() => {
    const handleGlobalKeyDown = (event: KeyboardEvent) => {
      // Only focus if no input/textarea is currently focused and the key is "/"
      if (event.key === '/' &&
          document.activeElement?.tagName !== 'INPUT' &&
          document.activeElement?.tagName !== 'TEXTAREA' &&
          !event.ctrlKey && !event.metaKey && !event.altKey) {
        event.preventDefault();
        textareaRef.current?.focus();
      }
    };

    document.addEventListener('keydown', handleGlobalKeyDown);
    return () => document.removeEventListener('keydown', handleGlobalKeyDown);
  }, []);

  // Focus search input when dropdown opens - simplified
  useEffect(() => {
    if (showModelDropdown && modelSearchRef.current) {
      modelSearchRef.current.focus();
    }
  }, [showModelDropdown]);

  // Handle input change
  const handleInputChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const newValue = e.target.value;
    setInputValue(newValue);
    onChange?.(newValue);

    // Auto-resize textarea
    const textarea = e.target;
    textarea.style.height = 'auto';
    const scrollHeight = textarea.scrollHeight;
    const minHeight = 30; // 1.25 rows
    const maxHeight = 120; // 5 rows

    if (scrollHeight <= maxHeight) {
      textarea.style.height = Math.max(minHeight, scrollHeight) + 'px';
    } else {
      textarea.style.height = maxHeight + 'px';
    }
  };

  // Handle send message
  const handleSend = () => {
    if (inputValue.trim() && !isLoading) {
      onSend?.(inputValue.trim());
      setInputValue('');
      onChange?.('');
    }
  };

  // Handle stop generation
  const handleStop = () => {
    if (isLoading) {
      onStop?.();
    }
  };

  // Handle key press
  const handleKeyPress = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
    
    // Focus on "/" key press
    if (e.key === '/' && !isFocused) {
      e.preventDefault();
      textareaRef.current?.focus();
    }
  };

  // Handle file upload
  const handleFileUpload = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.multiple = true;
    input.accept = 'image/*,.pdf,.txt,.doc,.docx,.xlsx,.xls,.ods,.pptx,.ppt,.csv,.json,.html,.htm,.xml,.ics,.rtf,.jpg,.png,.md,.log';
    input.onchange = (e) => {
      const files = (e.target as HTMLInputElement).files;
      if (files) {
        onFileUpload?.(files);
      }
    };
    input.click();
  };

  // Auto-resize textarea - moved to input handler to avoid useEffect issues

  // Focus management removed - was causing input issues

  return (
    <div
      className={cn(
        "flex flex-col bg-gray-900/50 border-t border-gray-800/50",
        className
      )}
    >
      {/* Top Row - Model Dropdown Only */}
      <div className="flex items-center px-2 py-0.5 border-b border-gray-800/30">
        <div className="relative">
          {showModelDropdown && (
            <div className="absolute bottom-full left-0 mb-1 w-80 bg-gray-800 border border-gray-700 rounded-md shadow-lg z-50 max-h-64 overflow-hidden">
              <div className="p-1 border-b border-gray-700">
                <Input
                  placeholder="Search models..."
                  value={modelSearchQuery}
                  onChange={(e) => setModelSearchQuery(e.target.value)}
                  className="h-6 text-xs bg-gray-700 border-gray-600"
                  autoFocus
                />
              </div>
              <div className="max-h-48 overflow-y-auto">
                {filteredModels.map((model) => (
                  <div
                    key={model}
                    className={cn(
                      "px-2 py-1 text-xs cursor-pointer transition-colors",
                      model === selectedModel
                        ? "bg-blue-600 text-white"
                        : "text-gray-300 hover:bg-gray-700"
                    )}
                    onClick={() => {
                      onModelChange?.(model);
                      setShowModelDropdown(false);
                      setModelSearchQuery('');
                    }}
                  >
                    {model}
                  </div>
                ))}
              </div>
            </div>
          )}

          <Button
            variant="ghost"
            size="sm"
            className="h-5 px-1.5 text-xs text-gray-400 hover:text-white hover:bg-gray-800/50 transition-colors"
            onClick={() => {
              setShowModelDropdown(!showModelDropdown);
              if (!showModelDropdown) {
                setModelSearchQuery(''); // Clear search when opening
              }
            }}
            title={`Current Model: ${selectedModel}`}
          >
            <span className="text-xs mr-1">{selectedModel}</span>
            <ChevronDown className="w-2.5 h-2.5" />
          </Button>
        </div>
      </div>

      {/* Main Row - Text Input with Agent and Action Buttons */}
      <div className="flex items-center gap-1 p-2">
        {/* Text Input */}
        <div className="flex-1 relative">
          <textarea
            ref={textareaRef}
            value={inputValue}
            onChange={handleInputChange}
            onKeyDown={handleKeyPress}
            onFocus={() => setIsFocused(true)}
            onBlur={() => setIsFocused(false)}
            placeholder="Press / to focus here and start typing..."
            className={cn(
              "w-full resize-none rounded-md border border-gray-700 bg-gray-800/50 px-2 py-1 text-sm text-white placeholder-gray-400",
              "focus:outline-none focus:ring-1 focus:ring-blue-500/50 focus:border-blue-500/50",
              "transition-all duration-200"
            )}
            style={{
              minHeight: '30px', // 1.25 rows (24px line height + 6px padding)
              maxHeight: '120px', // 5 rows (24px * 5 + padding)
              lineHeight: '24px'
            }}
            rows={1}
            disabled={isLoading}
          />
        </div>

        {/* Agent Button - Right of text input */}
        <div className="relative">
          {showAgentDropdown && (
            <div className="absolute bottom-full right-0 mb-1 w-64 bg-gray-800 border border-gray-700 rounded-md shadow-lg z-50 max-h-48 overflow-y-auto">
              <div className="p-1">
                <div
                  className="px-2 py-1 text-xs text-gray-300 hover:bg-gray-700 rounded cursor-pointer"
                  onClick={() => {
                    onAgentChange?.(null);
                    setShowAgentDropdown(false);
                  }}
                >
                  No Agent
                </div>
                {availableAgents.map((agent) => (
                  <div
                    key={agent.id}
                    className="px-2 py-1 text-xs text-gray-300 hover:bg-gray-700 rounded cursor-pointer flex items-center gap-1"
                    onClick={() => {
                      onAgentChange?.(agent);
                      setShowAgentDropdown(false);
                    }}
                  >
                    <span>{agent.icon || 'ü§ñ'}</span>
                    <span>{agent.name}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          <Button
            variant="ghost"
            size="sm"
            className="h-7 px-1.5 text-xs text-gray-400 hover:text-white hover:bg-gray-800/50 transition-colors"
            onClick={() => setShowAgentDropdown(!showAgentDropdown)}
            title={selectedAgent ? `Current Agent: ${selectedAgent.name}` : 'No Agent Selected'}
          >
            <span className="text-xs mr-1">
              {selectedAgent ? `${selectedAgent.icon || 'ü§ñ'} ${selectedAgent.name}` : 'No Agent'}
            </span>
            <ChevronDown className="w-2.5 h-2.5" />
          </Button>
        </div>

        {/* Right side - All action buttons */}
        <div className="flex items-center gap-0.5">
          {/* Tool Calling Toggle */}
          <Button
            variant={(toolsEnabled && mcpEnabled) ? "default" : "ghost"}
            size="sm"
            onClick={() => {
              const newState = !(toolsEnabled && mcpEnabled);
              onToggleTools?.(newState);
              onToggleMCP?.(newState);
            }}
            className={cn(
              "h-7 w-7 p-0 transition-colors",
              (toolsEnabled && mcpEnabled)
                ? "bg-blue-600 text-white hover:bg-blue-700"
                : "text-gray-400 hover:text-white hover:bg-gray-800/50"
            )}
            title={(toolsEnabled && mcpEnabled) ? "Disable Tool Calling" : "Enable Tool Calling"}
          >
            <Wrench style={{ width: '16px', height: '16px', color: 'inherit', minWidth: '16px', minHeight: '16px' }} />
          </Button>

          {/* Knowledge Base Toggle */}
          <Button
            variant={knowledgeBaseEnabled ? "default" : "ghost"}
            size="sm"
            onClick={() => onToggleKnowledgeBase?.(!knowledgeBaseEnabled)}
            className={cn(
              "h-7 w-7 p-0 transition-colors",
              knowledgeBaseEnabled
                ? "bg-purple-600 text-white hover:bg-purple-700"
                : "text-gray-400 hover:text-white hover:bg-gray-800/50"
            )}
            title={knowledgeBaseEnabled ? "Disable Knowledge Base" : "Enable Knowledge Base"}
          >
            <Database style={{ width: '16px', height: '16px', color: 'inherit', minWidth: '16px', minHeight: '16px' }} />
          </Button>

          {/* Screenshot Button */}
          <Button
            variant="ghost"
            size="sm"
            onClick={onScreenshot}
            className="h-7 w-7 p-0 text-gray-400 hover:text-white hover:bg-gray-800/50 transition-colors"
            title="Take Screenshot"
          >
            <Camera style={{ width: '16px', height: '16px', color: 'inherit', minWidth: '16px', minHeight: '16px' }} />
          </Button>

          {/* File Upload Button */}
          <Button
            variant="ghost"
            size="sm"
            onClick={handleFileUpload}
            className="h-7 w-7 p-0 text-gray-400 hover:text-white hover:bg-gray-800/50 transition-colors"
            title="Add Attachment"
          >
            <Paperclip style={{ width: '16px', height: '16px', color: 'inherit', minWidth: '16px', minHeight: '16px' }} />
          </Button>

          {/* New Chat Button */}
          <Button
            onClick={() => onStartNewChat?.()}
            className={cn(
              "h-7 w-7 rounded-md flex-shrink-0 transition-all duration-200",
              "bg-green-600 hover:bg-green-700 text-white"
            )}
            title="Start New Chat"
          >
            <Plus style={{ width: '16px', height: '16px', color: 'white', minWidth: '16px', minHeight: '16px' }} />
          </Button>

          {/* Send/Stop Button */}
          <Button
            onClick={isLoading ? handleStop : handleSend}
            disabled={!isLoading && !inputValue.trim()}
            className={cn(
              "h-7 w-7 rounded-md flex-shrink-0 transition-all duration-200",
              isLoading
                ? "bg-red-600 hover:bg-red-700 text-white shadow-lg shadow-red-500/25"
                : inputValue.trim()
                ? "bg-blue-600 hover:bg-blue-700 text-white"
                : "bg-gray-700 text-gray-400 cursor-not-allowed"
            )}
            title={isLoading ? "Stop Generation" : "Send Message"}
          >
            {isLoading ? (
              <Square style={{ width: '16px', height: '16px', color: 'white', minWidth: '16px', minHeight: '16px' }} />
            ) : (
              <Send style={{ width: '16px', height: '16px', color: 'white', minWidth: '16px', minHeight: '16px' }} />
            )}
          </Button>
        </div>
      </div>

      {/* File input for uploads */}
      <input
        ref={fileInputRef}
        type="file"
        multiple
        accept=".txt,.md,.pdf,.doc,.docx,.csv,.json,.xml,.html,.js,.ts,.py,.java,.cpp,.c,.h,.css,.scss,.less,.yaml,.yml,.toml,.ini,.cfg,.conf,.log,.sql,.sh,.bat,.ps1,.rb,.php,.go,.rs,.swift,.kt,.scala,.clj,.hs,.elm,.ex,.exs,.erl,.pl,.r,.m,.mm,.f,.f90,.f95,.pas,.ada,.cob,.cobol,.asm,.s,.vb,.vbs,.ps,.psm1,.psd1,.lua,.tcl,.awk,.sed,.vim,.emacs,.org,.tex,.bib,.rtf,.odt,.ods,.odp,.pages,.numbers,.key,.epub,.mobi,.azw,.azw3,.fb2,.lit,.pdb,.prc,.oxps,.xps,.cbr,.cbz,.cb7,.cbt,.cba"
        onChange={handleFileChange}
        className="hidden"
      />
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { X, MessageSquare, Clock, Search, Trash2 } from 'lucide-react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { conversationHistoryService, type Conversation } from '../../services/conversationHistoryService';
import { cn } from '@/lib/utils';

// Use the existing Conversation type from the service

interface ChatHistoryPanelProps {
  isOpen: boolean;
  onClose: () => void;
  onSelectChat?: (chatId: string) => void;
  className?: string;
}

export function ChatHistoryPanel({ 
  isOpen, 
  onClose, 
  onSelectChat,
  className 
}: ChatHistoryPanelProps) {
  const [searchQuery, setSearchQuery] = useState('');
  const [chatHistory, setChatHistory] = useState<Conversation[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  // Load chat history when panel opens
  useEffect(() => {
    if (isOpen) {
      loadChatHistory();
    }
  }, [isOpen]);

  const loadChatHistory = async () => {
    setIsLoading(true);
    try {
      // Load real conversation history from the service
      const conversations = await conversationHistoryService.getAllConversations();
      console.log('Loaded conversations:', conversations.length);
      setChatHistory(conversations);
    } catch (error) {
      console.error('Failed to load chat history:', error);
      setChatHistory([]);
    } finally {
      setIsLoading(false);
    }
  };

  const filteredHistory = chatHistory.filter(chat => {
    const lastMessage = chat.messages.length > 0 ? chat.messages[chat.messages.length - 1].content : '';
    const messageText = typeof lastMessage === 'string' ? lastMessage :
      Array.isArray(lastMessage) ? lastMessage.map(item => item.type === 'text' ? item.text : '').join(' ') : '';
    return chat.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
           messageText.toLowerCase().includes(searchQuery.toLowerCase());
  });

  const formatTimestamp = (timestamp: Date) => {
    const now = new Date();
    const diffMs = now.getTime() - timestamp.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

    if (diffMins < 60) {
      return `${diffMins}m ago`;
    } else if (diffHours < 24) {
      return `${diffHours}h ago`;
    } else if (diffDays < 7) {
      return `${diffDays}d ago`;
    } else {
      return timestamp.toLocaleDateString();
    }
  };

  const handleChatSelect = (chatId: string) => {
    onSelectChat?.(chatId);
    onClose();
  };

  const handleDeleteChat = async (chatId: string, event: React.MouseEvent) => {
    // Prevent the chat selection when clicking delete
    event.stopPropagation();

    try {
      await conversationHistoryService.deleteConversation(chatId);
      // Reload the chat history
      await loadChatHistory();
      console.log('Chat deleted successfully:', chatId);
    } catch (error) {
      console.error('Failed to delete chat:', error);
    }
  };

  const handleClearAllHistory = async () => {
    if (confirm('Are you sure you want to clear all chat history? This cannot be undone.')) {
      try {
        await conversationHistoryService.clearAllHistory();
        // Reload the chat history (should be empty now)
        await loadChatHistory();
        console.log('All chat history cleared');
      } catch (error) {
        console.error('Failed to clear chat history:', error);
      }
    }
  };

  return (
    <div
      className={cn(
        "fixed top-0 right-0 h-full w-96 bg-gray-900/95 border-l border-gray-700/50 shadow-2xl transform transition-transform duration-300 ease-in-out z-40 flex flex-col",
        isOpen ? "translate-x-0" : "translate-x-full",
        className
      )}
    >
      {/* Header */}
      <div className="flex items-center justify-between p-2 border-b border-gray-700/50 flex-shrink-0">
        <div className="flex items-center gap-2">
          <MessageSquare style={{ width: '16px', height: '16px' }} className="text-blue-400" />
          <h2 className="text-sm font-semibold text-white">Chat History</h2>
        </div>
        <Button
          variant="ghost"
          size="sm"
          onClick={onClose}
          className="h-6 w-6 p-0 text-gray-400 hover:text-white hover:bg-gray-800/50"
          title="Close History"
        >
          <X style={{ width: '16px', height: '16px' }} />
        </Button>
      </div>

      {/* Search */}
      <div className="p-2 border-b border-gray-700/50">
        <div className="relative">
          <Search className="absolute left-2 top-1/2 transform -translate-y-1/2" style={{ width: '16px', height: '16px' }} />
          <Input
            placeholder="Search conversations..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-8 h-7 text-xs bg-gray-800/50 border-gray-700/50 text-white placeholder-gray-400"
          />
        </div>
      </div>

      {/* Chat List */}
      <div className="flex-1 overflow-y-auto">
        {isLoading ? (
          <div className="flex items-center justify-center p-4">
            <div className="text-gray-400 text-xs">Loading chat history...</div>
          </div>
        ) : filteredHistory.length === 0 ? (
          <div className="flex flex-col items-center justify-center p-4 text-center">
            <MessageSquare style={{ width: '16px', height: '16px' }} className="text-gray-600 mb-1" />
            <div className="text-gray-400 mb-1 text-xs">
              {searchQuery ? 'No matching conversations' : 'No chat history yet'}
            </div>
            <div className="text-xs text-gray-500">
              {searchQuery ? 'Try a different search term' : 'Start a conversation to see it here'}
            </div>
          </div>
        ) : (
          <div className="p-1">
            {filteredHistory.map((chat) => (
              <div
                key={chat.id}
                onClick={() => handleChatSelect(chat.id)}
                className="p-2 rounded-lg cursor-pointer transition-colors hover:bg-gray-800/50 mb-1"
              >
                <div className="flex items-start justify-between mb-1">
                  <h3 className="text-white font-medium text-xs truncate flex-1 mr-1">
                    {chat.title}
                  </h3>
                  <div className="flex items-center gap-1 flex-shrink-0">
                    <div className="flex items-center gap-0.5 text-xs text-gray-400">
                      <Clock style={{ width: '12px', height: '12px' }} />
                      {formatTimestamp(chat.updatedAt)}
                    </div>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={(e) => handleDeleteChat(chat.id, e)}
                      className="h-5 w-5 p-0 text-gray-400 hover:text-red-400 hover:bg-red-500/10"
                      title="Delete conversation"
                    >
                      <Trash2 style={{ width: '12px', height: '12px' }} />
                    </Button>
                  </div>
                </div>

                <p className="text-gray-400 text-xs mb-1 line-clamp-2">
                  {chat.messages.length > 0 ? (() => {
                    const content = chat.messages[chat.messages.length - 1].content;
                    const text = typeof content === 'string' ? content :
                      Array.isArray(content) ? content.map(item => item.type === 'text' ? item.text : '').join(' ') : '';
                    return text.substring(0, 100) + '...';
                  })() : 'No messages'}
                </p>

                <div className="flex items-center justify-between">
                  <span className="text-xs text-gray-500">
                    {chat.messages.length} messages
                  </span>
                  <div className="w-2 h-2 rounded-full bg-blue-500 opacity-60" />
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* Footer */}
      <div className="p-2 border-t border-gray-700/50 flex-shrink-0">
        <Button
          variant="outline"
          size="sm"
          className="w-full text-gray-400 border-gray-700/50 hover:text-white hover:bg-gray-800/50"
          onClick={handleClearAllHistory}
        >
          Clear History
        </Button>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import {
  Bot,
  CheckCircle,
  Loader2,
  Sparkles
} from 'lucide-react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { Textarea } from '../ui/textarea';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '../ui/dialog';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '../ui/card';
import { Badge } from '../ui/badge';
import { cn } from '@/lib/utils';
import { AgentTemplate, AgentTool, CreateAgentRequest } from '../../types/agent';
import { agentService } from '../../services/agentService';
import { llmService } from '../../services/llmService';
import { chatService } from '../../services/chatService';
import { secureApiKeyService } from '../../services/secureApiKeyService';

interface CreateAgentDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onSuccess: () => void;
  templates: AgentTemplate[];
}

export function CreateAgentDialog({
  open,
  onOpenChange,
  onSuccess,
  templates
}: CreateAgentDialogProps) {
  const [step, setStep] = useState<'template' | 'configure' | 'tools' | 'prompt' | 'review'>('template');
  const [selectedTemplate, setSelectedTemplate] = useState<AgentTemplate | null>(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    icon: 'ü§ñ',
    userDescription: '',
    defaultProvider: 'anthropic',
    defaultModel: 'claude-3-sonnet-20240229',
    temperature: 0.7,
    maxTokens: 4000,
    tags: [] as string[]
  });
  const [availableTools, setAvailableTools] = useState<AgentTool[]>([]);
  const [selectedTools, setSelectedTools] = useState<string[]>([]);
  const [generatedPrompt, setGeneratedPrompt] = useState('');
  const [isGeneratingPrompt, setIsGeneratingPrompt] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const [providers, setProviders] = useState<any[]>([]);
  const [availableModels, setAvailableModels] = useState<string[]>([]);
  const [loadingModels, setLoadingModels] = useState(false);
  const [expandedTools, setExpandedTools] = useState<Set<string>>(new Set());

  // Load data when dialog opens
  useEffect(() => {
    if (open) {
      loadData();
      resetForm();
    }
  }, [open]);

  const loadData = async () => {
    try {
      const [tools, providersData] = await Promise.all([
        agentService.getAvailableTools(),
        llmService.getProviders()
      ]);
      setAvailableTools(tools);
      setProviders(providersData);

      // Load models for the default provider
      if (formData.defaultProvider) {
        await loadModelsForProvider(formData.defaultProvider);
      }
    } catch (error) {
      console.error('Failed to load data:', error);
    }
  };

  // Load models for a specific provider
  const loadModelsForProvider = async (providerId: string) => {
    try {
      setLoadingModels(true);
      setAvailableModels([]);

      // Get API key and base URL from secure storage
      let apiKey = '';
      let baseUrl = '';

      try {
        const apiKeyData = secureApiKeyService?.getApiKeyData(providerId);
        apiKey = apiKeyData?.apiKey || '';
        baseUrl = apiKeyData?.baseUrl || '';
      } catch (error) {
        console.warn(`Failed to get API key data for ${providerId}:`, error);
      }

      // Skip model loading for remote providers without API keys
      if (!apiKey && providerId !== 'ollama' && providerId !== 'lmstudio' && providerId !== 'n8n') {
        console.warn(`No API key found for ${providerId}, skipping model loading`);
        setAvailableModels([]);
        return;
      }

      // Fetch models using the chat service
      const models = await chatService.fetchModels(providerId, apiKey, baseUrl);
      console.log(`Loaded ${models.length} models for ${providerId}:`, models);

      setAvailableModels(models);

      // Auto-select first model if none selected
      if (models.length > 0 && !formData.defaultModel) {
        setFormData(prev => ({ ...prev, defaultModel: models[0] }));
      }
    } catch (error) {
      console.error('Failed to load models for provider:', providerId, error);
      setAvailableModels([]);
    } finally {
      setLoadingModels(false);
    }
  };

  const resetForm = () => {
    setStep('template');
    setSelectedTemplate(null);
    setFormData({
      name: '',
      description: '',
      icon: 'ü§ñ',
      userDescription: '',
      defaultProvider: 'anthropic',
      defaultModel: 'claude-3-sonnet-20240229',
      temperature: 0.7,
      maxTokens: 4000,
      tags: []
    });
    setSelectedTools([]);
    setGeneratedPrompt('');
  };

  const handleTemplateSelect = (template: AgentTemplate | null) => {
    setSelectedTemplate(template);
    if (template) {
      setFormData(prev => ({
        ...prev,
        name: template.name,
        description: template.description,
        icon: template.icon,
        defaultProvider: template.defaultProvider || 'anthropic',
        defaultModel: template.defaultModel || 'claude-3-sonnet-20240229',
        temperature: template.temperature || 0.7,
        maxTokens: template.maxTokens || 4000,
        tags: [template.category]
      }));
      setSelectedTools(template.suggestedTools);
    }
    setStep('configure');
  };

  const handleGeneratePrompt = async () => {
    if (!formData.userDescription.trim()) {
      alert('Please provide a description of what you want the agent to do.');
      return;
    }

    if (!formData.defaultModel.trim()) {
      alert('Please select or enter a model for prompt generation.');
      return;
    }

    setIsGeneratingPrompt(true);
    try {
      const selectedToolObjects = availableTools.filter(tool =>
        selectedTools.includes(tool.name)
      );

      const response = await agentService.generatePrompt({
        userDescription: formData.userDescription,
        selectedTools: selectedToolObjects,
        agentName: formData.name,
        agentDescription: formData.description,
        provider: formData.defaultProvider,
        model: formData.defaultModel
      });

      if (response.success && response.generatedPrompt) {
        setGeneratedPrompt(response.generatedPrompt);
        setStep('review');
      } else {
        const errorMessage = response.error || 'Unknown error occurred';
        console.error('Prompt generation failed:', errorMessage);
        alert(`Failed to generate prompt: ${errorMessage}`);
      }
    } catch (error) {
      console.error('Failed to generate prompt:', error);
      alert('Failed to generate prompt. Please try again.');
    } finally {
      setIsGeneratingPrompt(false);
    }
  };

  // Handle provider change
  const handleProviderChange = async (providerId: string) => {
    setFormData(prev => ({ ...prev, defaultProvider: providerId, defaultModel: '' }));
    await loadModelsForProvider(providerId);
  };

  // Toggle tool description expansion
  const toggleToolExpansion = (toolName: string) => {
    setExpandedTools(prev => {
      const newSet = new Set(prev);
      if (newSet.has(toolName)) {
        newSet.delete(toolName);
      } else {
        newSet.add(toolName);
      }
      return newSet;
    });
  };



  const handleCreateAgent = async () => {
    setIsCreating(true);
    try {
      const request: CreateAgentRequest = {
        name: formData.name,
        description: formData.description,
        icon: formData.icon,
        userDescription: formData.userDescription,
        selectedTools,
        enabledMCPServers: [], // Empty for now - tools are unified
        defaultProvider: formData.defaultProvider,
        defaultModel: formData.defaultModel,
        temperature: formData.temperature,
        maxTokens: formData.maxTokens,
        tags: formData.tags
      };

      const agentId = await agentService.createAgent(request);

      // Update with generated prompt if available
      if (generatedPrompt) {
        await agentService.updateAgent({
          id: agentId,
          systemPrompt: generatedPrompt
        });
      }

      onSuccess();
      onOpenChange(false);
    } catch (error) {
      console.error('Failed to create agent:', error);
      alert('Failed to create agent. Please try again.');
    } finally {
      setIsCreating(false);
    }
  };

  const renderTemplateStep = () => (
    <div className="space-y-4">
      <div className="text-center mb-6">
        <h3 className="text-lg font-semibold mb-2">Choose a Template</h3>
        <p className="text-gray-400 text-sm">Start with a template or create from scratch</p>
      </div>

      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3">
        {/* Custom Agent Option */}
        <Card
          className={cn(
            "cursor-pointer border-2 transition-colors bg-gray-900",
            !selectedTemplate ? "border-blue-500" : "border-gray-700 hover:border-gray-600"
          )}
          onClick={() => handleTemplateSelect(null)}
        >
          <CardHeader className="text-center p-4">
            <div className="text-2xl mb-1">‚ö°</div>
            <CardTitle className="text-white text-sm">Custom Agent</CardTitle>
            <CardDescription className="text-xs">Create from scratch</CardDescription>
          </CardHeader>
        </Card>

        {/* Template Options */}
        {templates.map((template) => (
          <Card
            key={template.id}
            className={cn(
              "cursor-pointer border-2 transition-colors bg-gray-900",
              selectedTemplate?.id === template.id ? "border-blue-500" : "border-gray-700 hover:border-gray-600"
            )}
            onClick={() => handleTemplateSelect(template)}
          >
            <CardHeader className="text-center p-4">
              <div className="text-2xl mb-1">{template.icon}</div>
              <CardTitle className="text-white text-sm">{template.name}</CardTitle>
              <CardDescription className="text-xs line-clamp-2">{template.description}</CardDescription>
              <Badge variant="secondary" className="w-fit mx-auto mt-1 text-xs">
                {template.category}
              </Badge>
            </CardHeader>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderConfigureStep = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="space-y-2">
          <Label htmlFor="name">Agent Name</Label>
          <Input
            id="name"
            value={formData.name}
            onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
            placeholder="My Custom Agent"
            className="bg-gray-900 border-gray-700"
          />
        </div>
        <div className="space-y-2">
          <Label htmlFor="icon">Icon (Emoji)</Label>
          <Input
            id="icon"
            value={formData.icon}
            onChange={(e) => setFormData(prev => ({ ...prev, icon: e.target.value }))}
            placeholder="ü§ñ"
            className="bg-gray-900 border-gray-700"
          />
        </div>
      </div>

      <div className="space-y-2">
        <Label htmlFor="description">Description</Label>
        <Input
          id="description"
          value={formData.description}
          onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
          placeholder="Brief description of what this agent does"
          className="bg-gray-900 border-gray-700"
        />
      </div>

      <div className="space-y-2">
        <Label htmlFor="userDescription">Agent Purpose</Label>
        <Textarea
          id="userDescription"
          value={formData.userDescription}
          onChange={(e) => setFormData(prev => ({ ...prev, userDescription: e.target.value }))}
          placeholder="Describe in detail what you want this agent to do and how it should behave..."
          className="bg-gray-900 border-gray-700 min-h-[100px]"
        />
        <p className="text-xs text-gray-400">
          This description will be used to generate a specialized system prompt for your agent.
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="space-y-2">
          <Label htmlFor="provider">Default Provider</Label>
          <select
            id="provider"
            value={formData.defaultProvider}
            onChange={(e) => handleProviderChange(e.target.value)}
            className="w-full bg-gray-900 border border-gray-700 rounded px-3 py-2 text-white"
          >
            {providers.map(provider => (
              <option key={provider.id} value={provider.id}>
                {provider.name}
              </option>
            ))}
          </select>
        </div>
        <div className="space-y-2">
          <Label htmlFor="model">Default Model</Label>
          {loadingModels ? (
            <div className="flex items-center gap-2 px-3 py-2 bg-gray-900 border border-gray-700 rounded">
              <Loader2 className="w-4 h-4 animate-spin" />
              <span className="text-gray-400">Loading models...</span>
            </div>
          ) : availableModels.length > 0 ? (
            <select
              id="model"
              value={formData.defaultModel}
              onChange={(e) => setFormData(prev => ({ ...prev, defaultModel: e.target.value }))}
              className="w-full bg-gray-900 border border-gray-700 rounded px-3 py-2 text-white"
            >
              <option value="">Select a model</option>
              {availableModels.map(model => (
                <option key={model} value={model}>
                  {model}
                </option>
              ))}
            </select>
          ) : (
            <Input
              id="model"
              value={formData.defaultModel}
              onChange={(e) => setFormData(prev => ({ ...prev, defaultModel: e.target.value }))}
              placeholder="Enter model name manually"
              className="bg-gray-900 border-gray-700"
            />
          )}
        </div>
      </div>
    </div>
  );

  const renderToolsStep = () => (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-semibold mb-4">Select Tools</h3>
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-3">
          {availableTools.map((tool) => (
            <Card
              key={tool.name}
              className={cn(
                "border transition-colors bg-gray-900",
                selectedTools.includes(tool.name) ? "border-blue-500 bg-blue-950/20" : "border-gray-700 hover:border-gray-600"
              )}
            >
              <CardContent className="p-3">
                <div className="space-y-2">
                  <div className="flex items-center justify-between">
                    <h4 className="font-medium text-white text-sm truncate flex-1">{tool.name}</h4>
                    {selectedTools.includes(tool.name) && (
                      <CheckCircle className="w-4 h-4 text-blue-400 flex-shrink-0 ml-1" />
                    )}
                  </div>

                  <div className="flex gap-1">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => {
                        setSelectedTools(prev =>
                          prev.includes(tool.name)
                            ? prev.filter(t => t !== tool.name)
                            : [...prev, tool.name]
                        );
                      }}
                      className={cn(
                        "text-xs h-6 flex-1",
                        selectedTools.includes(tool.name)
                          ? "bg-blue-600 border-blue-600 text-white hover:bg-blue-700"
                          : "border-gray-600 text-gray-300 hover:bg-gray-800"
                      )}
                    >
                      {selectedTools.includes(tool.name) ? 'Selected' : 'Select'}
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => toggleToolExpansion(tool.name)}
                      className="text-xs h-6 px-2 text-gray-400 hover:text-white"
                    >
                      {expandedTools.has(tool.name) ? '‚àí' : '+'}
                    </Button>
                  </div>

                  {expandedTools.has(tool.name) && (
                    <div className="space-y-1">
                      <p className="text-xs text-gray-400">{tool.description}</p>
                      <Badge variant="outline" className="text-xs">
                        {tool.category}
                      </Badge>
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>


    </div>
  );

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-auto bg-gray-950 border-gray-800">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-white">
            <Bot className="w-5 h-5" />
            Create Custom Agent
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Progress Steps */}
          <div className="flex items-center justify-center space-x-4">
            {['template', 'configure', 'tools', 'prompt', 'review'].map((stepName, index) => (
              <div key={stepName} className="flex items-center">
                <div className={cn(
                  "w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium",
                  step === stepName ? "bg-blue-600 text-white" : "bg-gray-700 text-gray-400"
                )}>
                  {index + 1}
                </div>
                {index < 4 && <div className="w-8 h-px bg-gray-700 mx-2" />}
              </div>
            ))}
          </div>

          {/* Step Content */}
          {step === 'template' && renderTemplateStep()}
          {step === 'configure' && renderConfigureStep()}
          {step === 'tools' && renderToolsStep()}

          {step === 'prompt' && (
            <div className="space-y-4">
              <div className="text-center">
                <h3 className="text-lg font-semibold mb-2">Generate System Prompt</h3>
                <p className="text-gray-400 text-sm">AI will create a specialized prompt based on your configuration</p>
              </div>



              <div className="flex justify-center">
                <Button
                  onClick={handleGeneratePrompt}
                  disabled={isGeneratingPrompt || !formData.userDescription.trim() || !formData.defaultModel.trim()}
                  className="bg-purple-600 hover:bg-purple-700"
                >
                  {isGeneratingPrompt ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Generating...
                    </>
                  ) : (
                    <>
                      <Sparkles className="w-4 h-4 mr-2" />
                      Generate Prompt
                    </>
                  )}
                </Button>
              </div>
            </div>
          )}

          {step === 'review' && (
            <div className="space-y-4">
              <h3 className="text-lg font-semibold">Review & Create</h3>

              <div className="bg-gray-900 rounded-lg p-4 space-y-3">
                <div><strong>Name:</strong> {formData.name}</div>
                <div><strong>Description:</strong> {formData.description}</div>
                <div><strong>Tools:</strong> {selectedTools.length} selected</div>
                <div><strong>Provider:</strong> {formData.defaultProvider}</div>
              </div>

              {generatedPrompt && (
                <div className="space-y-2">
                  <Label>Generated System Prompt</Label>
                  <Textarea
                    value={generatedPrompt}
                    onChange={(e) => setGeneratedPrompt(e.target.value)}
                    className="bg-gray-900 border-gray-700 min-h-[150px]"
                  />
                </div>
              )}
            </div>
          )}

          {/* Navigation */}
          <div className="flex justify-between pt-4 border-t border-gray-800">
            <Button
              variant="outline"
              onClick={() => {
                if (step === 'template') {
                  onOpenChange(false);
                } else if (step === 'configure') {
                  setStep('template');
                } else if (step === 'tools') {
                  setStep('configure');
                } else if (step === 'prompt') {
                  setStep('tools');
                } else if (step === 'review') {
                  setStep('prompt');
                }
              }}
              className="border-gray-600 text-gray-300"
            >
              {step === 'template' ? 'Cancel' : 'Back'}
            </Button>

            <Button
              onClick={() => {
                if (step === 'template') {
                  // Template step requires selection
                  return;
                } else if (step === 'configure') {
                  setStep('tools');
                } else if (step === 'tools') {
                  setStep('prompt');
                } else if (step === 'prompt') {
                  // Generate prompt step
                  return;
                } else if (step === 'review') {
                  handleCreateAgent();
                }
              }}
              disabled={
                (step === 'template') ||
                (step === 'configure' && (!formData.name.trim() || !formData.userDescription.trim())) ||
                (step === 'prompt') ||
                (step === 'review' && isCreating)
              }
              className="bg-blue-600 hover:bg-blue-700"
            >
              {step === 'review' ? (
                isCreating ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Creating...
                  </>
                ) : (
                  'Create Agent'
                )
              ) : (
                'Next'
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import { useState, useEffect } from 'react';
import {
  Bot,
  Save,
  CheckCircle,
  Loader2,
  Sparkles,
  AlertTriangle
} from 'lucide-react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { Textarea } from '../ui/textarea';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '../ui/dialog';
import { Card, CardContent } from '../ui/card';
import { Badge } from '../ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../ui/tabs';
import { cn } from '@/lib/utils';
import { AgentConfiguration, AgentTool, UpdateAgentRequest } from '../../types/agent';
import { agentService } from '../../services/agentService';
import { mcpService } from '../../services/mcpService';
import { llmService } from '../../services/llmService';
import { chatService } from '../../services/chatService';
import { secureApiKeyService } from '../../services/secureApiKeyService';

interface EditAgentDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  agent: AgentConfiguration;
  onSuccess: () => void;
}

export function EditAgentDialog({
  open,
  onOpenChange,
  agent,
  onSuccess
}: EditAgentDialogProps) {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    icon: 'ü§ñ',
    userDescription: '',
    systemPrompt: '',
    defaultProvider: 'anthropic',
    defaultModel: 'claude-3-sonnet-20240229',
    temperature: 0.7,
    maxTokens: 4000,
    tags: [] as string[]
  });
  const [availableTools, setAvailableTools] = useState<AgentTool[]>([]);
  const [selectedTools, setSelectedTools] = useState<string[]>([]);
  const [availableMCPServers, setAvailableMCPServers] = useState<any[]>([]);
  const [selectedMCPServers, setSelectedMCPServers] = useState<string[]>([]);
  const [isRegeneratingPrompt, setIsRegeneratingPrompt] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [providers, setProviders] = useState<any[]>([]);
  const [availableModels, setAvailableModels] = useState<string[]>([]);
  const [loadingModels, setLoadingModels] = useState(false);
  const [validationResult, setValidationResult] = useState<any>(null);

  // Load data when dialog opens
  useEffect(() => {
    if (open && agent) {
      loadData();
      populateForm();
    }
  }, [open, agent]);

  const loadData = async () => {
    try {
      const [tools, mcpServers, providersData] = await Promise.all([
        agentService.getAvailableTools(),
        mcpService.getServers(),
        llmService.getProviders()
      ]);
      setAvailableTools(tools);
      setAvailableMCPServers(mcpServers);
      setProviders(providersData);

      // Validate agent
      const validation = await agentService.validateAgent(agent.id);
      setValidationResult(validation);

      // Load models for the agent's provider
      if (agent.defaultProvider) {
        await loadModelsForProvider(agent.defaultProvider);
      }
    } catch (error) {
      console.error('Failed to load data:', error);
    }
  };

  // Load models for a specific provider
  const loadModelsForProvider = async (providerId: string) => {
    try {
      setLoadingModels(true);
      setAvailableModels([]);

      // Get API key and base URL from secure storage
      let apiKey = '';
      let baseUrl = '';

      try {
        const apiKeyData = secureApiKeyService?.getApiKeyData(providerId);
        apiKey = apiKeyData?.apiKey || '';
        baseUrl = apiKeyData?.baseUrl || '';
      } catch (error) {
        console.warn(`Failed to get API key data for ${providerId}:`, error);
      }

      // Skip model loading for remote providers without API keys
      if (!apiKey && providerId !== 'ollama' && providerId !== 'lmstudio' && providerId !== 'n8n') {
        console.warn(`No API key found for ${providerId}, skipping model loading`);
        setAvailableModels([]);
        return;
      }

      // Fetch models using the chat service
      const models = await chatService.fetchModels(providerId, apiKey, baseUrl);
      console.log(`Loaded ${models.length} models for ${providerId}:`, models);

      setAvailableModels(models);
    } catch (error) {
      console.error('Failed to load models for provider:', providerId, error);
      setAvailableModels([]);
    } finally {
      setLoadingModels(false);
    }
  };

  const populateForm = () => {
    setFormData({
      name: agent.name,
      description: agent.description,
      icon: agent.icon || 'ü§ñ',
      userDescription: agent.userDescription || '',
      systemPrompt: agent.systemPrompt,
      defaultProvider: agent.defaultProvider,
      defaultModel: agent.defaultModel,
      temperature: agent.temperature || 0.7,
      maxTokens: agent.maxTokens || 4000,
      tags: agent.tags || []
    });
    setSelectedTools(agent.selectedTools.map(tool => tool.name));
    setSelectedMCPServers([...agent.enabledMCPServers]);
  };

  const handleRegeneratePrompt = async () => {
    if (!formData.userDescription.trim()) {
      alert('Please provide a description to regenerate the prompt.');
      return;
    }

    if (!formData.defaultModel.trim()) {
      alert('Please select or enter a model for prompt generation.');
      return;
    }

    setIsRegeneratingPrompt(true);
    try {
      const selectedToolObjects = availableTools.filter(tool =>
        selectedTools.includes(tool.name)
      );

      const response = await agentService.generatePrompt({
        userDescription: formData.userDescription,
        selectedTools: selectedToolObjects,
        agentName: formData.name,
        agentDescription: formData.description,
        provider: formData.defaultProvider,
        model: formData.defaultModel
      });

      if (response.success && response.generatedPrompt) {
        setFormData(prev => ({ ...prev, systemPrompt: response.generatedPrompt! }));
      } else {
        alert(`Failed to regenerate prompt: ${response.error}`);
      }
    } catch (error) {
      console.error('Failed to regenerate prompt:', error);
      alert('Failed to regenerate prompt. Please try again.');
    } finally {
      setIsRegeneratingPrompt(false);
    }
  };

  // Handle provider change
  const handleProviderChange = async (providerId: string) => {
    setFormData(prev => ({ ...prev, defaultProvider: providerId, defaultModel: '' }));
    await loadModelsForProvider(providerId);
  };

  const handleSave = async () => {
    setIsSaving(true);
    try {
      const request: UpdateAgentRequest = {
        id: agent.id,
        name: formData.name,
        description: formData.description,
        icon: formData.icon,
        userDescription: formData.userDescription,
        systemPrompt: formData.systemPrompt,
        selectedTools,
        enabledMCPServers: selectedMCPServers,
        defaultProvider: formData.defaultProvider,
        defaultModel: formData.defaultModel,
        temperature: formData.temperature,
        maxTokens: formData.maxTokens,
        tags: formData.tags
      };

      const success = await agentService.updateAgent(request);
      if (success) {
        onSuccess();
        onOpenChange(false);
      } else {
        alert('Failed to update agent. Please try again.');
      }
    } catch (error) {
      console.error('Failed to update agent:', error);
      alert('Failed to update agent. Please try again.');
    } finally {
      setIsSaving(false);
    }
  };

  const addTag = (tag: string) => {
    if (tag.trim() && !formData.tags.includes(tag.trim())) {
      setFormData(prev => ({ ...prev, tags: [...prev.tags, tag.trim()] }));
    }
  };

  const removeTag = (tagToRemove: string) => {
    setFormData(prev => ({ 
      ...prev, 
      tags: prev.tags.filter(tag => tag !== tagToRemove) 
    }));
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-auto bg-gray-950 border-gray-800">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-white">
            <Bot className="w-5 h-5" />
            Edit Agent: {agent.name}
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Validation Warnings */}
          {validationResult && !validationResult.isValid && (
            <div className="bg-red-900/20 border border-red-700 rounded-lg p-4">
              <div className="flex items-center gap-2 text-red-400 mb-2">
                <AlertTriangle className="w-4 h-4" />
                <span className="font-medium">Validation Issues</span>
              </div>
              <ul className="text-sm text-red-300 space-y-1">
                {validationResult.errors.map((error: string, index: number) => (
                  <li key={index}>‚Ä¢ {error}</li>
                ))}
                {validationResult.warnings.map((warning: string, index: number) => (
                  <li key={index} className="text-yellow-300">‚Ä¢ {warning}</li>
                ))}
              </ul>
            </div>
          )}

          <Tabs defaultValue="basic" className="w-full">
            <TabsList className="grid w-full grid-cols-4 bg-gray-900">
              <TabsTrigger value="basic" className="text-gray-300">Basic Info</TabsTrigger>
              <TabsTrigger value="tools" className="text-gray-300">Tools & MCP</TabsTrigger>
              <TabsTrigger value="prompt" className="text-gray-300">System Prompt</TabsTrigger>
              <TabsTrigger value="settings" className="text-gray-300">Settings</TabsTrigger>
            </TabsList>

            <TabsContent value="basic" className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="name">Agent Name</Label>
                  <Input
                    id="name"
                    value={formData.name}
                    onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                    className="bg-gray-900 border-gray-700"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="icon">Icon (Emoji)</Label>
                  <Input
                    id="icon"
                    value={formData.icon}
                    onChange={(e) => setFormData(prev => ({ ...prev, icon: e.target.value }))}
                    className="bg-gray-900 border-gray-700"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="description">Description</Label>
                <Input
                  id="description"
                  value={formData.description}
                  onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                  className="bg-gray-900 border-gray-700"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="userDescription">Agent Purpose</Label>
                <Textarea
                  id="userDescription"
                  value={formData.userDescription}
                  onChange={(e) => setFormData(prev => ({ ...prev, userDescription: e.target.value }))}
                  placeholder="Describe what this agent should do..."
                  className="bg-gray-900 border-gray-700 min-h-[100px]"
                />
              </div>

              <div className="space-y-2">
                <Label>Tags</Label>
                <div className="flex flex-wrap gap-2 mb-2">
                  {formData.tags.map((tag) => (
                    <Badge 
                      key={tag} 
                      variant="secondary" 
                      className="bg-gray-800 text-gray-300 cursor-pointer"
                      onClick={() => removeTag(tag)}
                    >
                      {tag} √ó
                    </Badge>
                  ))}
                </div>
                <Input
                  placeholder="Add tag and press Enter"
                  onKeyPress={(e) => {
                    if (e.key === 'Enter') {
                      addTag(e.currentTarget.value);
                      e.currentTarget.value = '';
                    }
                  }}
                  className="bg-gray-900 border-gray-700"
                />
              </div>
            </TabsContent>

            <TabsContent value="tools" className="space-y-6">
              <div>
                <h3 className="text-lg font-semibold mb-4">Available Tools</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 max-h-64 overflow-auto">
                  {availableTools.map((tool) => (
                    <Card 
                      key={tool.name}
                      className={cn(
                        "cursor-pointer border transition-colors bg-gray-900",
                        selectedTools.includes(tool.name) ? "border-blue-500 bg-blue-950/20" : "border-gray-700 hover:border-gray-600"
                      )}
                      onClick={() => {
                        setSelectedTools(prev => 
                          prev.includes(tool.name) 
                            ? prev.filter(t => t !== tool.name)
                            : [...prev, tool.name]
                        );
                      }}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between">
                          <div>
                            <h4 className="font-medium text-white">{tool.name}</h4>
                            <p className="text-sm text-gray-400 mt-1">{tool.description}</p>
                            <Badge variant="outline" className="mt-2 text-xs">
                              {tool.category}
                            </Badge>
                          </div>
                          {selectedTools.includes(tool.name) && (
                            <CheckCircle className="w-5 h-5 text-blue-400" />
                          )}
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </div>

              <div>
                <h3 className="text-lg font-semibold mb-4">MCP Servers</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 max-h-64 overflow-auto">
                  {availableMCPServers.map((server) => (
                    <Card 
                      key={server.id}
                      className={cn(
                        "cursor-pointer border transition-colors bg-gray-900",
                        selectedMCPServers.includes(server.id) ? "border-blue-500 bg-blue-950/20" : "border-gray-700 hover:border-gray-600"
                      )}
                      onClick={() => {
                        setSelectedMCPServers(prev => 
                          prev.includes(server.id) 
                            ? prev.filter(s => s !== server.id)
                            : [...prev, server.id]
                        );
                      }}
                    >
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between">
                          <div>
                            <h4 className="font-medium text-white">{server.name}</h4>
                            <p className="text-sm text-gray-400 mt-1">{server.description || 'MCP Server'}</p>
                          </div>
                          {selectedMCPServers.includes(server.id) && (
                            <CheckCircle className="w-5 h-5 text-blue-400" />
                          )}
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </div>
            </TabsContent>

            <TabsContent value="prompt" className="space-y-4">
              <div className="flex items-center justify-between">
                <Label htmlFor="systemPrompt">System Prompt</Label>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={handleRegeneratePrompt}
                  disabled={isRegeneratingPrompt || !formData.userDescription.trim() || !formData.defaultModel.trim()}
                  className="border-purple-600 text-purple-400 hover:bg-purple-900/20"
                >
                  {isRegeneratingPrompt ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Regenerating...
                    </>
                  ) : (
                    <>
                      <Sparkles className="w-4 h-4 mr-2" />
                      Regenerate
                    </>
                  )}
                </Button>
              </div>


              <Textarea
                id="systemPrompt"
                value={formData.systemPrompt}
                onChange={(e) => setFormData(prev => ({ ...prev, systemPrompt: e.target.value }))}
                placeholder="Enter the system prompt for this agent..."
                className="bg-gray-900 border-gray-700 min-h-[300px]"
              />
              <p className="text-xs text-gray-400">
                This prompt defines how the agent behaves and responds to user requests.
              </p>
            </TabsContent>

            <TabsContent value="settings" className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="provider">Default Provider</Label>
                  <select
                    id="provider"
                    value={formData.defaultProvider}
                    onChange={(e) => handleProviderChange(e.target.value)}
                    className="w-full bg-gray-900 border border-gray-700 rounded px-3 py-2 text-white"
                  >
                    {providers.map(provider => (
                      <option key={provider.id} value={provider.id}>
                        {provider.name}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="model">Default Model</Label>
                  {loadingModels ? (
                    <div className="flex items-center gap-2 px-3 py-2 bg-gray-900 border border-gray-700 rounded">
                      <Loader2 className="w-4 h-4 animate-spin" />
                      <span className="text-gray-400">Loading models...</span>
                    </div>
                  ) : availableModels.length > 0 ? (
                    <select
                      id="model"
                      value={formData.defaultModel}
                      onChange={(e) => setFormData(prev => ({ ...prev, defaultModel: e.target.value }))}
                      className="w-full bg-gray-900 border border-gray-700 rounded px-3 py-2 text-white"
                    >
                      <option value="">Select a model</option>
                      {availableModels.map(model => (
                        <option key={model} value={model}>
                          {model}
                        </option>
                      ))}
                    </select>
                  ) : (
                    <Input
                      id="model"
                      value={formData.defaultModel}
                      onChange={(e) => setFormData(prev => ({ ...prev, defaultModel: e.target.value }))}
                      placeholder="Enter model name manually"
                      className="bg-gray-900 border-gray-700"
                    />
                  )}
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="temperature">Temperature</Label>
                  <Input
                    id="temperature"
                    type="number"
                    min="0"
                    max="2"
                    step="0.1"
                    value={formData.temperature}
                    onChange={(e) => setFormData(prev => ({ ...prev, temperature: parseFloat(e.target.value) }))}
                    className="bg-gray-900 border-gray-700"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="maxTokens">Max Tokens</Label>
                  <Input
                    id="maxTokens"
                    type="number"
                    min="100"
                    max="32000"
                    value={formData.maxTokens}
                    onChange={(e) => setFormData(prev => ({ ...prev, maxTokens: parseInt(e.target.value) }))}
                    className="bg-gray-900 border-gray-700"
                  />
                </div>
              </div>

              <div className="bg-gray-900 rounded-lg p-4 space-y-2">
                <h4 className="font-medium text-white">Agent Statistics</h4>
                <div className="grid grid-cols-2 gap-4 text-sm text-gray-400">
                  <div>Created: {agent.createdAt.toLocaleDateString()}</div>
                  <div>Updated: {agent.updatedAt.toLocaleDateString()}</div>
                  <div>Tools: {selectedTools.length}</div>
                  <div>MCP Servers: {selectedMCPServers.length}</div>
                </div>
              </div>
            </TabsContent>
          </Tabs>

          {/* Actions */}
          <div className="flex justify-between pt-4 border-t border-gray-800">
            <Button
              variant="outline"
              onClick={() => onOpenChange(false)}
              className="border-gray-600 text-gray-300"
            >
              Cancel
            </Button>

            <Button
              onClick={handleSave}
              disabled={isSaving || !formData.name.trim()}
              className="bg-blue-600 hover:bg-blue-700"
            >
              {isSaving ? (
                <>
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  Saving...
                </>
              ) : (
                <>
                  <Save className="w-4 h-4 mr-2" />
                  Save Changes
                </>
              )}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import type React from 'react';

import { useState, useEffect } from 'react';
import {
  Server,
  FileText,
  Settings,
  History,
  Terminal,
  Bot,
  Volume2,
  VolumeX
} from 'lucide-react';
import { Button } from '../ui/button';
import { cn } from '@/lib/utils';
import { DEFAULT_PROVIDERS } from '../../services/providers/constants';
import { ProviderLogo } from '../ui/provider-logo';
import { settingsService } from '../../services/settingsService';

interface SidebarItem {
  id: string;
  label: string;
  icon: React.ComponentType<{ className?: string }>;
  isActive?: boolean;
  onClick?: () => void;
  providers?: string[]; // For provider sections
}

interface LeftSidebarProps {
  className?: string;
  onItemClick?: (itemId: string) => void;
  selectedProvider?: string;
  onProviderClick?: (element: HTMLElement) => void;
}

export function LeftSidebar({
  className,
  onItemClick,
  selectedProvider,
  onProviderClick
}: LeftSidebarProps) {
  const [activeItem, setActiveItem] = useState<string>('');
  const [ttsEnabled, setTtsEnabled] = useState(false);

  // Load TTS on mount and subscribe to settings changes
  useEffect(() => {
    const loadTtsSettings = () => {
      const settings = settingsService.getSettings();
      const enabled = settings.ui?.textToSpeech?.enabled || false;
      setTtsEnabled(enabled);
    };
    loadTtsSettings();
    const handleSettingsChange = () => loadTtsSettings();
    window.addEventListener('settingsSaved', handleSettingsChange);
    return () => window.removeEventListener('settingsSaved', handleSettingsChange);
  }, []);

  const handleTtsToggle = async () => {
    try {
      const settings = settingsService.getSettings();
      const newTtsEnabled = !ttsEnabled;
      const updatedSettings = {
        ...settings,
        ui: {
          ...settings.ui,
          textToSpeech: {
            ...settings.ui?.textToSpeech,
            enabled: newTtsEnabled,
            voice: settings.ui?.textToSpeech?.voice || '',
            rate: settings.ui?.textToSpeech?.rate || 1.0,
            pitch: settings.ui?.textToSpeech?.pitch || 1.0,
            volume: settings.ui?.textToSpeech?.volume || 0.8,
            autoPlay: settings.ui?.textToSpeech?.autoPlay || false,
          }
        }
      };
      await settingsService.updateSettings(updatedSettings);
      setTtsEnabled(newTtsEnabled);
    } catch (err) {
      console.error('Failed to toggle TTS:', err);
    }
  };

  const selectedProviderData = DEFAULT_PROVIDERS.find(p => p.id === selectedProvider);

  const sidebarItems: SidebarItem[] = [
    {
      id: 'agents',
      label: 'CUSTOM AGENTS',
      icon: Bot,
      onClick: () => handleItemClick('agents')
    },
    {
      id: 'mcp-servers',
      label: 'MCP SERVERS',
      icon: Server,
      onClick: () => handleItemClick('mcp-servers')
    },
    {
      id: 'prompts',
      label: 'PROMPTS',
      icon: FileText,
      onClick: () => handleItemClick('prompts')
    },
    {
      id: 'history',
      label: 'HISTORY',
      icon: History,
      onClick: () => handleItemClick('history')
    },
    {
      id: 'console',
      label: 'CONSOLE',
      icon: Terminal,
      onClick: () => handleItemClick('console')
    },
    {
      id: 'settings',
      label: 'SETTINGS',
      icon: Settings,
      onClick: () => handleItemClick('settings')
    }
  ];

  const handleItemClick = (itemId: string) => {
    setActiveItem(itemId);
    onItemClick?.(itemId);
  };

  return (
    <div
      className={cn(
        "flex flex-col w-12 h-full bg-gray-900/50 border-r border-gray-800/50",
        className
      )}
    >
      {/* Top controls: Provider + TTS */}
      <div className="p-1 space-y-1" style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties}>
        {/* Provider selector */}
        <Button
          variant="ghost"
          className="w-10 h-10 p-0 text-gray-400 hover:text-white hover:bg-gray-800/50"
          onClick={(e) => onProviderClick?.(e.currentTarget)}
          title={selectedProviderData ? selectedProviderData.name : 'Select Provider'}
        >
          {selectedProviderData ? (
            <div className="w-4 h-4">
              <ProviderLogo provider={selectedProviderData} size={16} />
            </div>
          ) : (
            <Server className="w-4 h-4" />
          )}
        </Button>

        {/* TTS toggle */}
        <Button
          variant="ghost"
          className={cn(
            "w-10 h-10 p-0 transition-colors",
            ttsEnabled ? "text-blue-400 hover:text-blue-300 hover:bg-blue-600/20" : "text-gray-400 hover:text-white hover:bg-gray-800/50"
          )}
          onClick={handleTtsToggle}
          title={ttsEnabled ? 'Disable Text to Speech' : 'Enable Text to Speech'}
        >
          {ttsEnabled ? <Volume2 className="w-4 h-4" /> : <VolumeX className="w-4 h-4" />}
        </Button>
      </div>

      {/* Spacer to push navigation to bottom */}
      <div className="flex-1"></div>

      {/* Navigation Items - At bottom */}
      <div className="p-1 space-y-1">
        {sidebarItems.map((item) => {
          const Icon = item.icon;
          const isActive = activeItem === item.id;

          return (
            <Button
              key={item.id}
              variant="ghost"
              className={cn(
                "w-10 h-10 p-0",
                "hover:bg-gray-800/50 transition-colors duration-200",
                isActive && "bg-gray-800/70 text-white",
                !isActive && "text-gray-400 hover:text-white"
              )}
              onClick={item.onClick}
              title={item.label}
            >
              <Icon className="w-4 h-4" />
            </Button>
          );
        })}
      </div>
    </div>
  );
}

'use client';

import { useEffect, useRef, useState, useCallback } from 'react';
import { Edit3, Zap, ChevronDown } from 'lucide-react';
import { Button } from '../ui/button';
import { MessageWithThinking } from '../MessageWithThinking';
import { UserMessage } from '../UserMessage';
import { cn } from '@/lib/utils';
import type { Message } from '../../services/chatService';

interface MainChatAreaProps {
  className?: string;
  selectedModel?: string;
  messages?: Message[];
  isLoading?: boolean;
  onEditModelInstructions?: () => void;
  onQuickPrompts?: () => void;
}

export function MainChatArea({
  className,
  selectedModel = 'gemma3:gpu',
  messages = [],
  isLoading = false,
  onEditModelInstructions,
  onQuickPrompts
}: MainChatAreaProps) {

  const messagesEndRef = useRef<HTMLDivElement>(null);
  const scrollContainerRef = useRef<HTMLDivElement>(null);
  const [showScrollToBottom, setShowScrollToBottom] = useState(false);

  const handleEditModelInstructions = () => {
    console.log('Edit Model Instructions clicked');
    onEditModelInstructions?.();
  };

  // Scroll to bottom function
  const scrollToBottom = useCallback(() => {
    if (!messagesEndRef.current) return;

    requestAnimationFrame(() => {
      messagesEndRef.current?.scrollIntoView({
        behavior: 'smooth',
        block: 'end'
      });
    });
  }, []);

  // Check if user is at bottom of scroll container
  const isAtBottom = useCallback(() => {
    if (!scrollContainerRef.current) return false;

    const { scrollTop, scrollHeight, clientHeight } = scrollContainerRef.current;
    return scrollHeight - scrollTop - clientHeight < 50; // 50px threshold
  }, []);

  // Auto-scroll when new messages arrive
  useEffect(() => {
    if (!scrollContainerRef.current || messages.length === 0) return;

    const wasAtBottom = isAtBottom();

    // Auto-scroll if user was at bottom or if it's the first message
    if (wasAtBottom || messages.length === 1) {
      const timeoutId = setTimeout(() => {
        if (isAtBottom() || messages.length === 1) {
          scrollToBottom();
        }
      }, 50);

      return () => clearTimeout(timeoutId);
    }
  }, [messages, scrollToBottom, isAtBottom]);

  // Add scroll listener for scroll-to-bottom button
  useEffect(() => {
    const scrollContainer = scrollContainerRef.current;
    if (!scrollContainer) return;

    const handleScroll = () => {
      if (!scrollContainerRef.current) return;

      const { scrollTop, scrollHeight, clientHeight } = scrollContainerRef.current;
      const isAtBottom = scrollHeight - scrollTop - clientHeight < 50; // 50px threshold
      setShowScrollToBottom(!isAtBottom && messages.length > 0);
    };

    scrollContainer.addEventListener('scroll', handleScroll);

    // Initial check
    handleScroll();

    return () => {
      scrollContainer.removeEventListener('scroll', handleScroll);
    };
  }, [messages.length]);

  // Show welcome state when no messages
  const showWelcomeState = messages.length === 0;

  return (
    <div
      className={cn(
        "flex-1 flex flex-col bg-gray-950/50 relative overflow-hidden",
        className
      )}
    >
      {showWelcomeState ? (
        // Welcome State - Centered model selection
        <div className="flex-1 flex flex-col items-center justify-center p-8">
          {/* Model Avatar */}
          <div className="mb-6">
            <div className="w-20 h-20 rounded-full bg-gradient-to-br from-gray-700 to-gray-800 flex items-center justify-center border border-gray-600/50 shadow-lg">
              {/* Cute animal face icon - simplified version */}
              <div className="text-3xl">üê∑</div>
            </div>
          </div>

          {/* Model Name */}
          <div className="text-center mb-4">
            <h1 className="text-2xl font-medium text-white mb-2">
              {selectedModel}
            </h1>
            
            {/* Action Buttons */}
            <div className="flex gap-2">
              <Button
                variant="ghost"
                size="sm"
                onClick={handleEditModelInstructions}
                className="text-gray-400 hover:text-white transition-colors h-auto p-2"
              >
                <Edit3 className="w-4 h-4 mr-2" />
                <span className="text-sm">Custom Prompt</span>
              </Button>

              <Button
                variant="ghost"
                size="sm"
                onClick={() => onQuickPrompts?.()}
                className="text-gray-400 hover:text-white transition-colors h-auto p-2"
              >
                <Zap className="w-4 h-4 mr-2" />
                <span className="text-sm">Quick Prompts</span>
              </Button>
            </div>
          </div>

          {/* Optional: Model description or status */}
          <div className="text-center text-gray-500 text-sm max-w-md">
            Ready to chat. Type your message below to get started.
          </div>
        </div>
      ) : (
        // Chat Messages Area
        <div className="flex-1 flex flex-col overflow-hidden">
          {/* Messages Container */}
          <div
            ref={scrollContainerRef}
            className="flex-1 overflow-y-auto p-2 pt-8 space-y-2 chat-messages"
            style={
              {
                WebkitAppRegion: 'no-drag',
                // Ensure nothing inside can extend under the sticky title bar
                overflowX: 'hidden',
                overscrollBehaviorY: 'contain',
                // Extra guard to keep content below header area
                scrollPaddingTop: '32px'
              } as React.CSSProperties
            }
          >
            {messages.map((message, index) => (
              <div
                key={message.id || index}
                className={cn(
                  "flex",
                  message.role === 'user' ? 'justify-end' : 'justify-start'
                )}
                // Prevent extremely wide content from expanding layout
                style={{ maxWidth: '85vw' } as React.CSSProperties}
              >
                <div
                  className={cn(
                    // Bubble container: enforce cropping and safe wrapping
                    "max-w-[85%] rounded-lg shadow-sm overflow-hidden break-words",
                    message.role === 'user'
                      ? 'bg-blue-600 text-white p-3'
                      : 'bg-gray-800 text-gray-100 border border-gray-700 p-3'
                  )}
                  style={{
                    // Ensure long unbroken content (URLs, code, long words) wraps and is clipped
                    overflowWrap: 'anywhere',
                    wordBreak: 'break-word'
                  } as React.CSSProperties}
                >
                  {/* Toolbar area should never extend outside bubble */}
                  <div
                    className="relative group"
                    // Make sure inner toolbar/content stays within cropped bubble
                    style={{ overflow: 'hidden' }}
                  >
                    {message.role === 'assistant' ? (
                      <MessageWithThinking
                        content={
                          typeof message.content === 'string'
                            ? message.content
                            : Array.isArray(message.content)
                              ? message.content
                                  .map((item, idx) =>
                                    item.type === 'text' ? item.text : `[Image ${idx + 1}]`
                                  )
                                  .join(' ')
                              : String(message.content)
                        }
                        usage={message.usage}
                        timing={message.timing}
                        toolCalls={message.toolCalls}
                        sources={message.sources}
                      />
                    ) : (
                      <UserMessage content={message.content} />
                    )}
                  </div>
                </div>
              </div>
            ))}

            {/* Loading indicator */}
            {isLoading && (
              <div className="flex justify-start">
                <div className="bg-gray-800 border border-gray-700 rounded-lg p-3 shadow-sm">
                  <div className="flex items-center space-x-2">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                      <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    </div>
                    <span className="text-sm text-gray-400">Thinking...</span>
                  </div>
                </div>
              </div>
            )}

            {/* Scroll anchor */}
            <div ref={messagesEndRef} />
          </div>

          {/* Scroll to bottom button */}
          {showScrollToBottom && (
            <Button
              onClick={scrollToBottom}
              className="absolute bottom-20 right-4 h-10 w-10 rounded-full bg-blue-600/90 hover:bg-blue-600 shadow-lg transition-all duration-200 z-50 flex items-center justify-center p-0"
              style={{
                backdropFilter: 'blur(8px)',
              }}
            >
              <ChevronDown className="h-5 w-5 text-white" />
            </Button>
          )}
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect, useCallback, useRef } from 'react';
import { LeftSidebar } from './LeftSidebar';
import { MainChatArea } from './MainChatArea';
import { BottomInputArea } from './BottomInputArea';
import { RightPanel } from './RightPanel';
import { SettingsModal } from './SettingsModal';
import { ChatHistoryPanel } from './ChatHistoryPanel';
import { FloatingProviderSelector } from './FloatingProviderSelector';
import { AttachmentPreview } from '../AttachmentPreview';
import { AgentManagement } from './AgentManagement';
import { agentService } from '../../services/agentService';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '../ui/dialog';
import { Textarea } from '../ui/textarea';
import { Label } from '../ui/label';
import { Button } from '../ui/button';
import { cn } from '@/lib/utils';

// Import existing services
import { chatService, type ChatSettings, type Message, type ContentItem } from '../../services/chatService';
import type { AgentConfiguration } from '../../types/agent';
import { settingsService } from '../../services/settingsService';
import { conversationHistoryService } from '../../services/conversationHistoryService';
import { secureApiKeyService } from '../../services/secureApiKeyService';

interface ModernChatInterfaceProps {
  className?: string;
}

export function ModernChatInterface({ className }: ModernChatInterfaceProps) {
  // State management
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isStreaming, setIsStreaming] = useState(false);
  const [selectedModel, setSelectedModel] = useState('gemma3:gpu');
  const [selectedProvider, setSelectedProvider] = useState('ollama');
  const [availableModels, setAvailableModels] = useState<string[]>([]);
  const [attachedFiles, setAttachedFiles] = useState<File[]>([]);
  const [abortController, setAbortController] = useState<AbortController | null>(null);

  const [rightPanelOpen, setRightPanelOpen] = useState(false);
  const [activePanel, setActivePanel] = useState('');
  const [settingsModalOpen, setSettingsModalOpen] = useState(false);
  const [historyPanelOpen, setHistoryPanelOpen] = useState(false);
  const [providerSelectorOpen, setProviderSelectorOpen] = useState(false);
  const [providerAnchorElement, setProviderAnchorElement] = useState<HTMLElement | null>(null);
  const [agentManagementOpen, setAgentManagementOpen] = useState(false);
  const [selectedAgent, setSelectedAgent] = useState<AgentConfiguration | null>(null);
  const [availableAgents, setAvailableAgents] = useState<AgentConfiguration[]>([]);

  // Track if user has manually changed provider to avoid init overwrite
  const userChangedProviderRef = useRef(false);
  // Track current provider synchronously for async guards
  const selectedProviderRef = useRef(selectedProvider);
  useEffect(() => {
    selectedProviderRef.current = selectedProvider;
  }, [selectedProvider]);

  // Model instructions and quick prompts state
  const [modelInstructionsOpen, setModelInstructionsOpen] = useState(false);
  const [quickPromptsOpen, setQuickPromptsOpen] = useState(false);
  const [customSystemPrompt, setCustomSystemPrompt] = useState('');
  const [premadePrompts, setPremadePrompts] = useState<Array<{title: string, content: string}>>([]);
  
  // Settings state
  const [settings, setSettings] = useState<ChatSettings>({
    provider: 'ollama',
    model: 'gemma3:gpu',
    temperature: 0.7,
    maxTokens: 2000,
    systemPrompt: '',
    toolCallingEnabled: false,
    ragEnabled: false,
    providers: {
      openai: { lastSelectedModel: '' },
      anthropic: { lastSelectedModel: '' },
      gemini: { lastSelectedModel: '' },
      mistral: { lastSelectedModel: '' },
      deepseek: { lastSelectedModel: '' },
      deepinfra: { lastSelectedModel: '' },
      groq: { lastSelectedModel: '' },
      lmstudio: { baseUrl: '', lastSelectedModel: '' },
      jan: { baseUrl: '', lastSelectedModel: '' },
      ollama: { baseUrl: '', lastSelectedModel: '' },
      openrouter: { lastSelectedModel: '' },
      requesty: { lastSelectedModel: '' },
      replicate: { lastSelectedModel: '' },
      n8n: { baseUrl: '', lastSelectedModel: '' },
    },
  });

  // Toggle states
  const [toolsEnabled, setToolsEnabled] = useState(false);
  const [mcpEnabled, setMcpEnabled] = useState(false);
  const [knowledgeBaseEnabled, setKnowledgeBaseEnabled] = useState(false);

  // Handle settings updates
  const updateSettings = useCallback((newSettings: Partial<ChatSettings>) => {
    setSettings(prevSettings => {
      const updatedSettings = { ...prevSettings, ...newSettings };
      settingsService.updateChatSettingsInMemory(updatedSettings);
      settingsService.saveSettingsToDisk();
      return updatedSettings;
    });
  }, []); // Remove settings dependency to prevent infinite loop

  // Load models for a specific provider
  const loadModelsForProvider = useCallback(async (providerId: string) => {
    try {
      console.log('Loading models for provider:', providerId);
      setAvailableModels([]); // Clear current models while loading

      // Get API key and base URL from secure storage
      let apiKey = '';
      let baseUrl = '';

      try {
        const apiKeyData = secureApiKeyService?.getApiKeyData(providerId);
        apiKey = apiKeyData?.apiKey || '';
        baseUrl = apiKeyData?.baseUrl || '';
      } catch (error) {
        console.warn(`Failed to get API key data for ${providerId}:`, error);
      }

      // Skip model loading for remote providers without API keys
      if (!apiKey && providerId !== 'ollama' && providerId !== 'lmstudio' && providerId !== 'n8n') {
        console.warn(`No API key found for ${providerId}, skipping model loading`);
        setAvailableModels([]);
        return;
      }

      // Fetch models using the existing chat service
      const models = await chatService.fetchModels(providerId, apiKey, baseUrl);
      console.log(`Loaded ${models.length} models for ${providerId}:`, models);

      // If the provider has changed since this request started, ignore results
      if (providerId !== selectedProviderRef.current) {
        console.log('‚è≠Ô∏è Ignoring models for stale provider load:', providerId, 'current is', selectedProviderRef.current);
        return;
      }

      setAvailableModels(models);

      // Try to restore the last selected model for this provider
      let modelToSelect = '';

      // First, check if there's a saved last selected model for this provider
      try {
        const apiKeyData = secureApiKeyService?.getApiKeyData(providerId);
        const lastSelectedModel = apiKeyData?.lastSelectedModel;

        if (lastSelectedModel && models.includes(lastSelectedModel)) {
          modelToSelect = lastSelectedModel;
          console.log(`‚úÖ Restored last selected model for ${providerId}:`, lastSelectedModel);
        }
      } catch (error) {
        console.warn(`Failed to get last selected model for ${providerId}:`, error);
      }

      // If no valid last selected model, use first available
      if (!modelToSelect && models.length > 0) {
        modelToSelect = models[0];
        console.log(`üîÑ Using first available model for ${providerId}:`, modelToSelect);
      }

      // Update the selected model if we found one
      if (modelToSelect) {
        // Guard again in case provider changed while computing model
        if (providerId !== selectedProviderRef.current) {
          console.log('‚è≠Ô∏è Skipping model apply for stale provider:', providerId);
          return;
        }
        setSelectedModel(modelToSelect);
        updateSettings({ model: modelToSelect });
      }
    } catch (error) {
      console.error('Failed to load models for provider:', providerId, error);
      setAvailableModels([]);

      // Add error message to chat if there are existing messages
      if (messages.length > 0) {
        let errorContent = 'ü§ñ **Model Loading Error**: Failed to load models for the selected provider.';

        if (error instanceof Error) {
          const errorMessage = error.message.toLowerCase();

          if (errorMessage.includes('api key') || errorMessage.includes('unauthorized')) {
            errorContent = `üîë **Provider Setup Required**: Please configure your API key for ${providerId} in Settings to load available models.`;
          } else if (errorMessage.includes('network') || errorMessage.includes('connection')) {
            errorContent = `üåê **Connection Error**: Unable to connect to ${providerId}. Please check your internet connection and provider settings.`;
          } else if (errorMessage.includes('not found') || errorMessage.includes('404')) {
            errorContent = `‚ùì **Provider Not Found**: The ${providerId} service is not available. Please check your provider settings.`;
          } else {
            errorContent = `‚ö†Ô∏è **Model Loading Failed**: ${error.message}. Please check your ${providerId} configuration in Settings.`;
          }
        }

        const errorMessage: Message = {
          id: Date.now().toString(),
          content: errorContent,
          role: 'assistant',
          timestamp: new Date()
        };

        setMessages(prev => [...prev, errorMessage]);
      }
    }
  }, [messages.length, updateSettings]);

  // Load settings on mount
  useEffect(() => {
    const loadSettings = async () => {
      try {
        const savedSettings = settingsService.getChatSettings();
        setSettings(savedSettings);

        const provider = savedSettings.provider || 'ollama';
        // Only apply initial provider if user hasn't changed it yet
        if (!userChangedProviderRef.current) {
          setSelectedProvider(provider);
        } else {
          console.log('üõë Skipping init provider apply; user already changed provider');
        }
        setToolsEnabled(savedSettings.toolCallingEnabled ?? false); // Use nullish coalescing to respect explicit false
        setKnowledgeBaseEnabled(savedSettings.ragEnabled ?? false);

        // Try to get the last selected model for this provider
        let modelToUse = savedSettings.model || 'gemma3:gpu';
        try {
          const apiKeyData = secureApiKeyService?.getApiKeyData(provider);
          const lastSelectedModel = apiKeyData?.lastSelectedModel;
          if (lastSelectedModel) {
            modelToUse = lastSelectedModel;
            console.log(`‚úÖ Restored last selected model for ${provider} on startup:`, lastSelectedModel);
          }
        } catch (error) {
          console.warn(`Failed to get last selected model for ${provider} on startup:`, error);
        }

        // Only apply initial model/load if user hasn't changed provider
        if (!userChangedProviderRef.current) {
          setSelectedModel(modelToUse);
          // Load models for the selected provider (this will validate and potentially update the model)
          await loadModelsForProvider(provider);
        } else {
          console.log('üõë Skipping init model load; user already changed provider');
        }
      } catch (error) {
        console.error('Failed to load settings:', error);
      }
    };

    loadSettings();
    loadPremadePrompts();
  }, [loadModelsForProvider]); // Add loadModelsForProvider to dependency array

  // Load available agents on mount
  useEffect(() => {
    const loadAgents = async () => {
      try {
        const agents = await agentService.getAgents();
        setAvailableAgents(agents);
        console.log(`‚úÖ Loaded ${agents.length} available agents`);
      } catch (error) {
        console.error('Failed to load agents:', error);
        setAvailableAgents([]);
      }
    };

    loadAgents();
  }, []);

  // Handle sidebar item clicks
  const handleSidebarItemClick = (itemId: string) => {
    console.log('Sidebar item clicked:', itemId);

    // Handle different sidebar actions
    switch (itemId) {
      case 'agents':
        setAgentManagementOpen(true);
        break;
      case 'settings':
        setSettingsModalOpen(true);
        break;
      case 'prompts':
        // Open the action menu (prompts selector) - same as the old interface
        if (typeof window !== 'undefined' && window.electronAPI) {
          window.electronAPI.openActionMenu();
        }
        break;
      case 'mcp-servers':
        setActivePanel('mcp-servers');
        setRightPanelOpen(true);
        break;
      case 'history':
        // Open the chat history panel (sliding panel from the right)
        setHistoryPanelOpen(true);
        break;
      case 'console':
        // Toggle console window
        if (typeof window !== 'undefined' && window.electronAPI) {
          window.electronAPI.toggleConsoleWindow();
        }
        break;
      case 'add-split-chat':
        console.log('Adding split chat...');
        break;
    }
  };



  // Handle message sending - cloned from VoilaInterface
  const handleSendMessage = async (message: string, providedFiles?: File[]) => {
    if (!message.trim()) return;

    // Use provided files or current attached files
    const filesToSend = providedFiles || attachedFiles;

    // Ensure we have a valid model selected
    if (!selectedModel) {
      console.error('No model selected');
      return;
    }

    // Ensure we have valid settings with the current model and provider
    const currentSettings = {
      ...settings,
      model: selectedModel,
      provider: selectedProvider
    };

    const messageContent = message.trim();

    // Create content array that includes both text and files
    const contentArray: Array<ContentItem> = [];

    // Add text content if present
    if (messageContent) {
      contentArray.push({
        type: 'text',
        text: messageContent
      });
    }

    // Add file content
    for (const file of filesToSend) {
      if (file.type.startsWith('image/')) {
        // Convert image to base64 for display in chat
        const base64 = await chatService.fileToBase64(file);
        contentArray.push({
          type: 'image_url',
          image_url: {
            url: base64
          }
        });
      } else {
        // For non-image files, add a file reference
        contentArray.push({
          type: 'text',
          text: `\n\n[File attached: ${file.name}]`
        });
      }
    }

    // Add user message to messages array
    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: contentArray.length === 1 && contentArray[0].type === 'text'
        ? contentArray[0].text || messageContent
        : contentArray,
      timestamp: new Date()
    };

    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);

    // Clear input and attached files after sending
    setInputValue('');
    setAttachedFiles([]);

    // Sync user message to chat window (but don't auto-open it since we're using the modern interface)
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.syncMessagesToChat(updatedMessages);
      // Note: Not opening chat window since the modern interface is self-contained
    }

    setIsLoading(true); // Start thinking indicator
    console.log('üß† Started thinking indicator - user message sent');

    // Create abort controller for this request
    const controller = new AbortController();
    setAbortController(controller);

    // Track when we started processing for timeout detection
    const processingStartTime = Date.now();

    // Save conversation immediately after user message
    try {
      const currentConversationId = conversationHistoryService.getCurrentConversationId();
      if (currentConversationId) {
        await conversationHistoryService.updateConversation(currentConversationId, updatedMessages);
      } else {
        const newConversationId = await conversationHistoryService.createNewConversation(updatedMessages);
        conversationHistoryService.setCurrentConversationId(newConversationId);
      }
    } catch (error) {
      console.error('Failed to save conversation after user message:', error);
    }

    try {
      // Create assistant message for streaming
      let assistantContent = '';
      const assistantMessage: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: '',
        timestamp: new Date()
      };

      // Add the assistant message immediately for streaming
      setMessages(prev => [...prev, assistantMessage]);

      // Get conversation history (exclude the current user message we just added)
      const conversationHistory = updatedMessages.slice(0, -1); // Exclude the current user message

      const response = await chatService.sendMessage(
        messageContent,
        filesToSend,
        currentSettings,
        conversationHistory,
        (chunk: string) => {
          // Ensure chunk is a string and handle edge cases
          if (typeof chunk !== 'string') {
            console.warn('‚ö†Ô∏è Received non-string chunk in onStream:', typeof chunk, chunk);
            return;
          }

          // Log when streaming starts (first chunk received) and update states properly
          if (assistantContent === '' && chunk.trim().length > 0) {
            const processingDuration = Date.now() - processingStartTime;
            console.log(`ü§ñ Model started streaming after ${processingDuration}ms, switching to streaming mode`);
            setIsLoading(false); // Stop loading indicator (thinking animation)
            setIsStreaming(true); // Start streaming mode (keep stop button active)
          }

          // Handle streaming response
          assistantContent += chunk;
          setMessages(prev =>
            prev.map(msg =>
              msg.id === assistantMessage.id
                ? { ...msg, content: assistantContent }
                : msg
            )
          );
        },
        controller.signal, // signal
        conversationHistoryService.getCurrentConversationId() || undefined,
        (isSearching: boolean, query?: string) => {
          // Handle knowledge base search indicator
          console.log('üîç Knowledge base search state changed:', { isSearching, query });
          // TODO: Add knowledge base search indicator to UI
        }
      );

      // Update final response with usage, toolCalls, and sources
      if (!assistantContent) {
        // No streaming occurred, update with full response
        setMessages(prev =>
          prev.map(msg =>
            msg.id === assistantMessage.id
              ? { ...msg, content: response.content, usage: response.usage, toolCalls: response.toolCalls, sources: response.sources }
              : msg
          )
        );
      } else {
        // Streaming occurred, just update usage, toolCalls, and sources without changing content
        setMessages(prev =>
          prev.map(msg =>
            msg.id === assistantMessage.id
              ? { ...msg, usage: response.usage, toolCalls: response.toolCalls, sources: response.sources }
              : msg
          )
        );
      }

      // Stop all indicators when final response is complete
      const totalProcessingTime = Date.now() - processingStartTime;
      console.log(`‚úÖ Final response complete after ${totalProcessingTime}ms, stopping all indicators`);
      setIsLoading(false);
      setIsStreaming(false);

      // Clean up abort controller
      setAbortController(null);

      // Save conversation to history
      const currentConversationId = conversationHistoryService.getCurrentConversationId();
      const finalMessages = [...updatedMessages, { ...assistantMessage, content: response.content, usage: response.usage, toolCalls: response.toolCalls, sources: response.sources }];

      if (currentConversationId) {
        await conversationHistoryService.updateConversation(currentConversationId, finalMessages);
      } else {
        const newConversationId = await conversationHistoryService.createNewConversation(finalMessages);
        conversationHistoryService.setCurrentConversationId(newConversationId);
      }

    } catch (error) {
      console.error('Failed to send message:', error);

      // Check if this was an abort (user stopped generation)
      if (error instanceof Error && (error.name === 'AbortError' || error.message.includes('aborted'))) {
        console.log('üõë Request was aborted by user');
        setIsLoading(false);
        setAbortController(null);
        return; // Don't show error message for user-initiated aborts
      }

      // Create user-friendly error message based on error type
      let errorContent = 'Sorry, I encountered an error while processing your message. Please try again.';

      if (error instanceof Error) {
        const errorMessage = error.message.toLowerCase();

        // Provider-specific errors
        if (errorMessage.includes('api key') || errorMessage.includes('unauthorized') || errorMessage.includes('authentication')) {
          errorContent = 'üîë **Authentication Error**: Please check your API key in Settings. The provider may require a valid API key to process requests.';
        } else if (errorMessage.includes('tool calling') || errorMessage.includes('function calling')) {
          errorContent = 'üîß **Tool Calling Error**: This provider doesn\'t support tool calling. Please disable tools or switch to a compatible provider like OpenAI, Anthropic, or Ollama.';
        } else if (errorMessage.includes('rate limit') || errorMessage.includes('quota')) {
          errorContent = '‚è±Ô∏è **Rate Limit**: You\'ve exceeded the API rate limit. Please wait a moment before trying again.';
        } else if (errorMessage.includes('network') || errorMessage.includes('connection') || errorMessage.includes('timeout')) {
          errorContent = 'üåê **Connection Error**: Unable to connect to the provider. Please check your internet connection and try again.';
        } else if (errorMessage.includes('model') || errorMessage.includes('not found')) {
          errorContent = 'ü§ñ **Model Error**: The selected model is not available. Please choose a different model in the dropdown below.';
        } else if (errorMessage.includes('file') || errorMessage.includes('upload')) {
          errorContent = 'üìÅ **File Upload Error**: Failed to process the uploaded file. Please check the file format and try again.';
        } else if (errorMessage.includes('context') || errorMessage.includes('token limit')) {
          errorContent = 'üìù **Context Length Error**: Your message is too long for this model. Please try a shorter message or use a model with a larger context window.';
        } else {
          // Generic error with more details
          errorContent = `‚ùå **Error**: ${error.message}\n\nPlease try again or check your settings if the problem persists.`;
        }
      }

      // Add error message to chat
      const errorMessage: Message = {
        id: (Date.now() + 1).toString(),
        content: errorContent,
        role: 'assistant',
        timestamp: new Date()
      };

      setMessages(prev => [...prev, errorMessage]);
      setIsLoading(false);
      setIsStreaming(false);

      // Clean up abort controller
      setAbortController(null);
    }
  };

  // Handle stopping message generation
  const handleStopGeneration = () => {
    if (abortController && (isLoading || isStreaming)) {
      console.log('üõë User requested to stop generation');
      abortController.abort();
      setIsLoading(false);
      setIsStreaming(false);
      setAbortController(null);

      // Add a message indicating the generation was stopped
      setMessages(prev => {
        const lastMessage = prev[prev.length - 1];
        if (lastMessage && lastMessage.role === 'assistant') {
          return prev.map((msg, index) =>
            index === prev.length - 1
              ? { ...msg, content: msg.content + '\n\n*[Generation stopped by user]*' }
              : msg
          );
        }
        return prev;
      });
    }
  };

  // Handle file upload - simplified to match original behavior
  const handleFileUpload = async (files: FileList) => {
    console.log('Files uploaded:', Array.from(files).map(f => f.name));

    // Add files to attached files list - parsing will be handled by chatService
    const newFiles = Array.from(files);
    setAttachedFiles(prev => [...prev, ...newFiles]);
  };

  // Handle removing attached files
  const handleRemoveFile = (index: number) => {
    setAttachedFiles(prev => prev.filter((_, i) => i !== index));
  };

  // Handle model change with persistence
  const handleModelChange = async (newModel: string) => {
    setSelectedModel(newModel);
    console.log('Model changed to:', newModel);

    // Save to general settings
    try {
      const updatedSettings = {
        ...settings,
        model: newModel
      };

      await settingsService.updateSettings({ chat: updatedSettings });
      setSettings(updatedSettings);
      console.log('Model saved to general settings:', newModel);
    } catch (error) {
      console.error('Failed to save model to general settings:', error);
    }

    // Also save as last selected model for current provider
    try {
      if (selectedProvider) {
        const currentApiKeyData = secureApiKeyService?.getApiKeyData(selectedProvider);
        if (currentApiKeyData) {
          await secureApiKeyService.setApiKeyData(selectedProvider, {
            ...currentApiKeyData,
            lastSelectedModel: newModel
          });
        } else {
          await secureApiKeyService.setApiKeyData(selectedProvider, {
            apiKey: '',
            baseUrl: '',
            lastSelectedModel: newModel
          });
        }
        console.log(`‚úÖ Saved last selected model for ${selectedProvider}:`, newModel);
      }
    } catch (error) {
      console.error('Failed to save last selected model for provider:', error);
    }
  };


  // Test function for screenshot (can be called from console)
  if (typeof window !== 'undefined') {
    (window as unknown as { testScreenshot: () => Promise<{ success: boolean; dataURL?: string; error?: string }> }).testScreenshot = async () => {
    console.log('üß™ Testing screenshot functionality...');
    try {
      if (typeof window !== 'undefined' && window.electronAPI) {
        const result = await window.electronAPI.takeScreenshot();
        console.log('üß™ Test result:', result);
        return result;
      } else {
        console.log('üß™ electronAPI not available');
        return { success: false, error: 'electronAPI not available' };
      }
    } catch (error) {
      console.error('üß™ Test failed:', error);
      return { success: false, error: error instanceof Error ? error.message : 'Unknown error' };
    }
  };
  }

  // Handle screenshot - enhanced with better feedback
  const handleScreenshot = async () => {
    try {
      console.log('üì∏ Screenshot button clicked');

      if (typeof window !== 'undefined' && window.electronAPI) {
        console.log('üì∏ Calling electronAPI.takeScreenshot...');
        const result = await window.electronAPI.takeScreenshot();
        console.log('üì∏ Screenshot result:', { success: result.success, hasDataURL: !!result.dataURL, error: result.error });

        if (typeof result === 'object' && result.success && result.dataURL) {
          console.log('üì∏ Converting screenshot to file...');
          const response = await fetch(result.dataURL);
          const blob = await response.blob();
          const file = new File([blob], `screenshot-${Date.now()}.png`, { type: 'image/png' });

          console.log(`üì∏ Screenshot file created: ${file.name} (${Math.round(file.size / 1024)}KB)`);

          // Auto-attach screenshot to chat
          setAttachedFiles(prev => [...prev, file]);
          console.log('‚úÖ Screenshot captured and attached to chat');

          // Show a brief success indicator
          const successMsg = document.createElement('div');
          successMsg.textContent = 'üì∏ Screenshot captured!';
          successMsg.style.cssText = `
            position: fixed;
            top: 20px;
            right: 20px;
            background: #10b981;
            color: white;
            padding: 8px 16px;
            border-radius: 6px;
            z-index: 10000;
            font-size: 14px;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
          `;
          document.body.appendChild(successMsg);
          setTimeout(() => document.body.removeChild(successMsg), 2000);

        } else {
          throw new Error(result.error || 'Screenshot capture failed');
        }
      } else {
        throw new Error('Screenshot functionality is not available in this environment');
      }
    } catch (error) {
      console.error('‚ùå Failed to take screenshot:', error);

      // Show error notification
      const errorMsg = document.createElement('div');
      errorMsg.textContent = `‚ùå Screenshot failed: ${error instanceof Error ? error.message : 'Unknown error'}`;
      errorMsg.style.cssText = `
        position: fixed;
        top: 20px;
        right: 20px;
        background: #ef4444;
        color: white;
        padding: 8px 16px;
        border-radius: 6px;
        z-index: 10000;
        font-size: 14px;
        box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        max-width: 300px;
      `;
      document.body.appendChild(errorMsg);
      setTimeout(() => document.body.removeChild(errorMsg), 4000);
    }
  };



  // Handle toggle states
  const handleToggleTools = (enabled: boolean) => {
    setToolsEnabled(enabled);
    updateSettings({ toolCallingEnabled: enabled });
  };

  const handleToggleMCP = (enabled: boolean) => {
    setMcpEnabled(enabled);
    // MCP settings are managed separately, not through chat settings
    console.log('MCP toggled:', enabled);
  };

  const handleToggleKnowledgeBase = (enabled: boolean) => {
    setKnowledgeBaseEnabled(enabled);
    updateSettings({ ragEnabled: enabled });
  };

  // Handle provider selection
  const handleProviderSelect = async (providerId: string) => {
    console.log('Provider selected:', providerId);
    userChangedProviderRef.current = true;
    setSelectedProvider(providerId);
    updateSettings({ provider: providerId });

    // Load models for the selected provider (this will restore the last selected model)
    await loadModelsForProvider(providerId);
  };

  // Handle provider selector opening
  const handleProviderClick = (element: HTMLElement) => {
    setProviderAnchorElement(element);
    setProviderSelectorOpen(true);
  };

  // Handle agent selection from management interface
  const handleAgentSelect = async (agent: AgentConfiguration) => {
    try {
      console.log('ü§ñ Agent selected from management:', agent.name);

      // Set the selected agent
      setSelectedAgent(agent);

      // Update settings with agent configuration
      setSettings(prev => ({
        ...prev,
        provider: agent.defaultProvider,
        model: agent.defaultModel,
        systemPrompt: agent.systemPrompt,
        temperature: agent.temperature || 0.7,
        maxTokens: agent.maxTokens || 4000,
        toolCallingEnabled: agent.toolCallingEnabled
      }));

      // Set selected provider and model
      userChangedProviderRef.current = true;
      setSelectedProvider(agent.defaultProvider);
      setSelectedModel(agent.defaultModel);

      // Load models for the provider
      await loadModelsForProvider(agent.defaultProvider);

      // Start a new chat with the agent configuration
      handleStartNewChat();

      console.log('‚úÖ Agent configuration applied successfully');
    } catch (error) {
      console.error('‚ùå Failed to apply agent configuration:', error);
    }
  };

  // Handle agent change from dropdown
  const handleAgentChange = async (agent: AgentConfiguration | null) => {
    try {
      console.log('ü§ñ Agent changed from dropdown:', agent?.name || 'No Agent');

      setSelectedAgent(agent);

      if (agent) {
        // Apply agent configuration
        setSettings(prev => ({
          ...prev,
          provider: agent.defaultProvider,
          model: agent.defaultModel,
          systemPrompt: agent.systemPrompt,
          temperature: agent.temperature || 0.7,
          maxTokens: agent.maxTokens || 4000,
          toolCallingEnabled: agent.toolCallingEnabled
        }));

        userChangedProviderRef.current = true;
        setSelectedProvider(agent.defaultProvider);
        setSelectedModel(agent.defaultModel);
        await loadModelsForProvider(agent.defaultProvider);
      } else {
        // Clear agent configuration - preserve user's tool calling preference
        const savedSettings = settingsService.getChatSettings();
        setSettings(prev => ({
          ...prev,
          systemPrompt: '',
          temperature: 0.7,
          maxTokens: 4000,
          toolCallingEnabled: savedSettings.toolCallingEnabled // Preserve user's preference
        }));
      }

      console.log('‚úÖ Agent change applied successfully');
    } catch (error) {
      console.error('‚ùå Failed to apply agent change:', error);
    }
  };

  // Load premade prompts from file
  const loadPremadePrompts = async () => {
    try {
      console.log('üéØ Starting to load premade prompts...');

      if (typeof window !== 'undefined' && window.electronAPI) {
        console.log('üéØ Electron API available, attempting to read file...');

        // Try different file path formats
        const possiblePaths = [
          'z:\\Python\\AI Assistant\\littlellm\\Premadeprompts.md',
          'Z:\\Python\\AI Assistant\\littlellm\\Premadeprompts.md',
          './Premadeprompts.md',
          'Premadeprompts.md'
        ];

        let promptsContent = '';
        let successfulPath = '';

        for (const path of possiblePaths) {
          try {
            console.log(`üéØ Trying to read file from: ${path}`);
            const fileData = await window.electronAPI.readFile(path);

            // Handle the new API response format
            if (fileData && fileData.success && fileData.content) {
              promptsContent = fileData.content;
              successfulPath = path;
              console.log(`üéØ Successfully read file from: ${path}, content length: ${promptsContent.length}`);
              break;
            } else if (fileData && !fileData.success) {
              console.log(`üéØ Failed to read from ${path}: ${fileData.error}`);
              continue;
            }
          } catch (pathError) {
            console.log(`üéØ Failed to read from ${path}:`, pathError);
            continue;
          }
        }

        if (!promptsContent) {
          console.error('üéØ Failed to read prompts file from any path');
          setPremadePrompts([]);
          return;
        }

        // Parse prompts from the structured content (title: "..." prompt: "...")
        console.log('üéØ Parsing prompts from structured content...');
        const lines = promptsContent.split('\n');
        const prompts: Array<{title: string, content: string}> = [];

        let currentTitle = '';
        let currentPrompt = '';

        for (let i = 0; i < lines.length; i++) {
          const line = lines[i]?.trim();

          // Skip empty lines
          if (!line) continue;

          // Check for title line (with or without quotes)
          if (line.startsWith('title')) {
            // Extract title - handle both "title: Title" and "title: "Title""
            let titleMatch = line.match(/title:?\s*"(.+)"/); // With quotes
            if (!titleMatch) {
              titleMatch = line.match(/title:?\s*(.+)/); // Without quotes
            }
            if (titleMatch) {
              currentTitle = titleMatch[1].trim();
            }
          }

          // Check for prompt line (with or without quotes)
          else if (line.startsWith('prompt:')) {
            // Extract prompt - handle both formats
            let promptMatch = line.match(/prompt:\s*"(.+)"/); // With quotes
            if (!promptMatch) {
              promptMatch = line.match(/prompt:\s*(.+)/); // Without quotes
            }
            if (promptMatch) {
              currentPrompt = promptMatch[1].trim();

              // If we have both title and prompt, add to prompts array
              if (currentTitle && currentPrompt) {
                prompts.push({
                  title: currentTitle,
                  content: currentPrompt
                });
                console.log(`üéØ Found prompt: ${currentTitle}`);

                // Reset for next prompt
                currentTitle = '';
                currentPrompt = '';
              }
            }
          }
        }

        setPremadePrompts(prompts);
        console.log('üéØ Successfully loaded', prompts.length, 'premade prompts from', successfulPath);
      } else {
        console.error('üéØ Electron API not available');
      }
    } catch (error) {
      console.error('üéØ Failed to load premade prompts:', error);
      setPremadePrompts([]);
    }
  };

  // Handle model instructions editing
  const handleEditModelInstructions = () => {
    setCustomSystemPrompt('');
    setModelInstructionsOpen(true);
  };

  // Apply custom prompt as system prompt (enhances existing system prompt)
  const handleApplyModelInstructions = async () => {
    if (customSystemPrompt.trim()) {
      try {
        // Combine with existing system prompt if any
        const existingSystemPrompt = settings.systemPrompt || '';
        const combinedSystemPrompt = existingSystemPrompt
          ? `${existingSystemPrompt}\n\n## Additional Instructions\n${customSystemPrompt.trim()}`
          : customSystemPrompt.trim();

        const updatedSettings = {
          ...settings,
          systemPrompt: combinedSystemPrompt
        };

        await settingsService.updateSettings({ chat: updatedSettings });
        setSettings(updatedSettings);
        setModelInstructionsOpen(false);
        setCustomSystemPrompt('');

        console.log('Custom prompt applied as system prompt:', combinedSystemPrompt.substring(0, 100) + '...');
      } catch (error) {
        console.error('Failed to apply custom prompt:', error);
      }
    }
  };

  // Clear system prompt
  const handleClearSystemPrompt = async () => {
    try {
      const updatedSettings = {
        ...settings,
        systemPrompt: ''
      };

      await settingsService.updateSettings({ chat: updatedSettings });
      setSettings(updatedSettings);

      console.log('System prompt cleared');
    } catch (error) {
      console.error('Failed to clear system prompt:', error);
    }
  };

  // Handle quick prompt selection (apply as system prompt)
  const handleQuickPromptSelect = async (prompt: {title: string, content: string}) => {
    try {
      // Combine with existing system prompt if any
      const existingSystemPrompt = settings.systemPrompt || '';
      const combinedSystemPrompt = existingSystemPrompt
        ? `${existingSystemPrompt}\n\n## ${prompt.title} Mode\n${prompt.content}`
        : prompt.content;

      const updatedSettings = {
        ...settings,
        systemPrompt: combinedSystemPrompt
      };

      await settingsService.updateSettings({ chat: updatedSettings });
      setSettings(updatedSettings);
      setQuickPromptsOpen(false);

      console.log(`Quick prompt "${prompt.title}" applied as system prompt:`, combinedSystemPrompt.substring(0, 100) + '...');
    } catch (error) {
      console.error('Failed to apply quick prompt:', error);
    }
  };



  // Handle chat history selection
  const handleChatSelect = async (chatId: string) => {
    console.log('Selected chat:', chatId);
    try {
      // Load the selected conversation from the history service
      const conversation = await conversationHistoryService.getConversation(chatId);
      if (conversation) {
        // Set the messages state to the conversation's messages
        setMessages(conversation.messages);
        // Set the current conversation ID
        conversationHistoryService.setCurrentConversationId(chatId);
        console.log(`Loaded conversation "${conversation.title}" with ${conversation.messages.length} messages`);
      } else {
        console.error('Conversation not found:', chatId);
      }
    } catch (error) {
      console.error('Failed to load conversation:', error);
    }
  };

  // Handle starting a new chat
  const handleStartNewChat = async () => {
    console.log('Starting new chat...');

    // Clear messages
    setMessages([]);

    // Clear input and attached files
    setInputValue('');
    setAttachedFiles([]);

    // Reset system prompt to empty for new chat
    try {
      const updatedSettings = {
        ...settings,
        systemPrompt: ''
      };

      await settingsService.updateSettings({ chat: updatedSettings });
      setSettings(updatedSettings);
      console.log('System prompt reset for new chat');
    } catch (error) {
      console.error('Failed to reset system prompt:', error);
    }

    // Clear the current conversation ID
    conversationHistoryService.setCurrentConversationId(null);

    // Clear localStorage for chat window
    localStorage.removeItem('chatWindowMessages');

    // Sync empty state to chat window
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.syncMessagesToChat([]);
    }

    console.log('New chat started - all state cleared');
  };

  // Listen for prompt selections from action menu overlay
  useEffect(() => {
    console.log('üéØ Setting up prompt selection listener in ModernChatInterface');

    if (typeof window !== 'undefined' && window.electronAPI) {
      const handlePromptSelected = async (promptText: string) => {
        console.log('üéØ ModernChatInterface received prompt from action menu:', promptText);

        let processedPrompt = promptText;

        // If the prompt contains {content} placeholder, replace it with clipboard content
        if (promptText.includes('{content}')) {
          try {
            console.log('üéØ Prompt contains {content}, reading clipboard...');
            const clipboardContent = await window.electronAPI.readClipboard();

            if (clipboardContent && clipboardContent.trim()) {
              processedPrompt = promptText.replace('{content}', clipboardContent.trim());
              console.log('üéØ Replaced {content} with clipboard content');
            } else {
              processedPrompt = promptText.replace('{content}', '[No clipboard content available]');
              console.log('üéØ No clipboard content available, using placeholder');
            }
          } catch (error) {
            console.error('üéØ Failed to read clipboard:', error);
            processedPrompt = promptText.replace('{content}', '[Clipboard access failed]');
          }
        }

        // Set the processed prompt in the input field
        setInputValue(processedPrompt);
        console.log('üéØ ModernChatInterface set input with processed prompt:', processedPrompt);
      };

      console.log('üéØ Registering onPromptSelected listener');
      window.electronAPI.onPromptSelected?.(handlePromptSelected);

      return () => {
        console.log('üéØ Cleaning up prompt selection listener');
        window.electronAPI.removeAllListeners('prompt-selected');
      };
    } else {
      console.warn('üéØ electronAPI not available for prompt selection');
    }
  }, []); // Empty dependency array - only setup once



  return (
    <div
      className={cn(
        "flex h-screen bg-gray-950 text-white overflow-hidden modern-chat-interface",
        className
      )}
      style={{
        borderRadius: '32px',
        overflow: 'hidden'
      }}
    >
      {/* Left Sidebar */}
      <LeftSidebar
        onItemClick={handleSidebarItemClick}
        selectedProvider={selectedProvider}
        onProviderClick={handleProviderClick}
        className="flex-shrink-0"
      />

      {/* Main Content Area */}
      <div
        className="flex-1 flex flex-col overflow-hidden"
      >
        {/* Native Windows title bar provides drag; no custom header */}

        {/* Chat Area */}
        <MainChatArea
          selectedModel={selectedModel}
          messages={messages}
          isLoading={isLoading}
          onEditModelInstructions={handleEditModelInstructions}
          onQuickPrompts={() => setQuickPromptsOpen(true)}
          className="flex-1"
        />

        {/* Attachment Preview */}
        {attachedFiles.length > 0 && (
          <div className="px-4 pb-2">
            <AttachmentPreview
              files={attachedFiles}
              onRemoveFile={handleRemoveFile}
            />
          </div>
        )}

        {/* Bottom Input */}
        <BottomInputArea
          value={inputValue}
          onChange={setInputValue}
          onSend={handleSendMessage}
          onStop={handleStopGeneration}
          onFileUpload={handleFileUpload}
          onScreenshot={handleScreenshot}
          selectedModel={selectedModel}
          onModelChange={handleModelChange}
          availableModels={availableModels}
          selectedProvider={selectedProvider}
          isLoading={isLoading || isStreaming}
          toolsEnabled={toolsEnabled}
          onToggleTools={handleToggleTools}
          mcpEnabled={mcpEnabled}
          onToggleMCP={handleToggleMCP}
          knowledgeBaseEnabled={knowledgeBaseEnabled}
          onToggleKnowledgeBase={handleToggleKnowledgeBase}
          onStartNewChat={handleStartNewChat}
          selectedAgent={selectedAgent}
          onAgentChange={handleAgentChange}
          availableAgents={availableAgents}
          className="flex-shrink-0"
        />
      </div>

      {/* Right Panel */}
      <RightPanel
        isOpen={rightPanelOpen}
        activePanel={activePanel}
        onClose={() => setRightPanelOpen(false)}
        className="flex-shrink-0"
      />

      {/* Settings Modal Overlay */}
      {settingsModalOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center">
          {/* Backdrop with blur */}
          <div
            className="absolute inset-0 bg-black/50 backdrop-blur-sm"
            onClick={() => setSettingsModalOpen(false)}
          />

          {/* Settings Modal */}
          <div className="relative w-[90vw] h-[90vh] max-w-6xl max-h-[800px] bg-background border border-border rounded-2xl shadow-2xl overflow-hidden">
            <SettingsModal
              isOpen={settingsModalOpen}
              onClose={() => setSettingsModalOpen(false)}
            />
          </div>
        </div>
      )}

      {/* Chat History Panel */}
      <ChatHistoryPanel
        isOpen={historyPanelOpen}
        onClose={() => setHistoryPanelOpen(false)}
        onSelectChat={handleChatSelect}
      />

      {/* Agent Management Modal */}
      {agentManagementOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center">
          {/* Backdrop with blur */}
          <div
            className="absolute inset-0 bg-black/50 backdrop-blur-sm"
            onClick={() => setAgentManagementOpen(false)}
          />

          {/* Agent Management Modal */}
          <div className="relative w-[95vw] h-[95vh] max-w-7xl max-h-[900px] bg-background border border-border rounded-2xl shadow-2xl overflow-hidden">
            <AgentManagement
              onAgentSelect={handleAgentSelect}
              onClose={() => setAgentManagementOpen(false)}
            />
          </div>
        </div>
      )}

      {/* Floating Provider Selector */}
      <FloatingProviderSelector
        isOpen={providerSelectorOpen}
        onClose={() => setProviderSelectorOpen(false)}
        onProviderSelect={handleProviderSelect}
        selectedProvider={selectedProvider}
        anchorElement={providerAnchorElement}
      />

      {/* Model Instructions Dialog */}
      <Dialog open={modelInstructionsOpen} onOpenChange={setModelInstructionsOpen}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Create Custom Prompt</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="system-prompt">Custom Prompt</Label>
              <Textarea
                id="system-prompt"
                value={customSystemPrompt}
                onChange={(e) => setCustomSystemPrompt(e.target.value)}
                placeholder="Enter your custom prompt here..."
                rows={8}
                className="bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
              />
              <p className="text-sm text-muted-foreground">
                This prompt will be applied as a system prompt to enhance the AI&apos;s behavior. It will be combined with existing system prompts and tool instructions.
              </p>
            </div>
            <div className="flex justify-between">
              <Button
                variant="outline"
                onClick={handleClearSystemPrompt}
                className="text-red-400 hover:text-red-300"
              >
                Clear System Prompt
              </Button>
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  onClick={() => {
                    setModelInstructionsOpen(false);
                    setCustomSystemPrompt('');
                  }}
                >
                  Cancel
                </Button>
                <Button onClick={handleApplyModelInstructions}>
                  Apply Prompt
                </Button>
              </div>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Quick Prompts Dialog */}
      <Dialog open={quickPromptsOpen} onOpenChange={setQuickPromptsOpen}>
        <DialogContent className="max-w-4xl max-h-[80vh]">
          <DialogHeader>
            <DialogTitle>Quick Prompts</DialogTitle>
          </DialogHeader>
          <div className="space-y-4">
            <p className="text-sm text-muted-foreground">
              Select a pre-made prompt to enhance the AI&apos;s behavior as a system prompt. These will be combined with existing system prompts and tool instructions.
            </p>
            <div className="max-h-96 overflow-y-auto space-y-2">
              {premadePrompts.map((prompt, index) => (
                <div
                  key={index}
                  className="p-3 border border-border rounded-lg hover:bg-muted/50 cursor-pointer transition-colors"
                  onClick={() => handleQuickPromptSelect(prompt)}
                >
                  <div className="font-medium text-sm mb-1">{prompt.title}</div>
                  <div className="text-xs text-muted-foreground line-clamp-2">
                    {prompt.content}
                  </div>
                </div>
              ))}
              {premadePrompts.length === 0 && (
                <div className="text-center py-8 text-muted-foreground">
                  Loading prompts...
                </div>
              )}
            </div>
            <div className="flex justify-between">
              <Button
                variant="outline"
                onClick={handleClearSystemPrompt}
                className="text-red-400 hover:text-red-300"
              >
                Clear System Prompt
              </Button>
              <Button
                variant="outline"
                onClick={() => setQuickPromptsOpen(false)}
              >
                Close
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { X } from 'lucide-react';
import { Button } from '../ui/button';
import { mcpService, type MCPServer } from '../../services/mcpService';
import { cn } from '@/lib/utils';

interface RightPanelProps {
  className?: string;
  isOpen?: boolean;
  onClose?: () => void;
  activePanel?: string;
}

export function RightPanel({
  className,
  isOpen = false,
  onClose,
  activePanel
}: RightPanelProps) {
  const [mcpServers, setMcpServers] = useState<MCPServer[]>([]);
  const [connectedServers, setConnectedServers] = useState<Set<string>>(new Set());
  const [isLoading, setIsLoading] = useState(false);

  // Load MCP servers when panel opens
  useEffect(() => {
    if (isOpen && activePanel === 'mcp-servers') {
      loadMCPServers();
    }
  }, [isOpen, activePanel]);

  const loadMCPServers = async () => {
    setIsLoading(true);
    try {
      const servers = await mcpService.getServers();
      setMcpServers(servers);

      // Get connection status
      const connectedIds = await mcpService.getConnectedServerIds();
      setConnectedServers(new Set(connectedIds));
    } catch (error) {
      console.error('Failed to load MCP servers:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleToggleServer = async (serverId: string, currentEnabled: boolean) => {
    try {
      // Update server enabled status
      const success = await mcpService.updateServer(serverId, { enabled: !currentEnabled });
      if (success) {
        // Reload servers to get updated state
        await loadMCPServers();
      }
    } catch (error) {
      console.error('Failed to toggle MCP server:', error);
    }
  };


  const renderPanelContent = () => {
    switch (activePanel) {
      case 'chat-history':
        return (
          <div className="p-6">
            <h2 className="text-xl font-semibold text-white mb-4">Chat History</h2>
            <div className="text-gray-400">
              <p>Your conversation history will appear here.</p>
              <p className="text-sm mt-2">Select a conversation to load it in the main chat area.</p>
            </div>
          </div>
        );

      case 'mcp-servers':
        return (
          <div className="p-6">
            <h2 className="text-xl font-semibold text-white mb-4">MCP Servers</h2>
            {isLoading ? (
              <div className="flex items-center justify-center p-8">
                <div className="text-gray-400">Loading MCP servers...</div>
              </div>
            ) : mcpServers.length === 0 ? (
              <div className="text-gray-400 text-center p-8">
                <p>No MCP servers configured.</p>
                <p className="text-sm mt-2">Configure servers in Settings ‚Üí MCP.</p>
              </div>
            ) : (
              <div className="space-y-3">
                {mcpServers.map((server) => {
                  const isConnected = connectedServers.has(server.id);
                  return (
                    <div
                      key={server.id}
                      className="flex items-center justify-between p-3 rounded-lg bg-gray-800/50 border border-gray-700/50"
                    >
                      <div className="flex items-center gap-3">
                        <div className={cn(
                          "w-3 h-3 rounded-full",
                          server.enabled && isConnected ? "bg-green-500" :
                          server.enabled ? "bg-yellow-500" : "bg-gray-500"
                        )} />
                        <div>
                          <span className="font-medium text-white">{server.name}</span>
                          {server.description && (
                            <p className="text-xs text-gray-400 mt-1">{server.description}</p>
                          )}
                        </div>
                      </div>
                      <button
                        onClick={() => handleToggleServer(server.id, server.enabled)}
                        className={cn(
                          "px-3 py-1 text-xs rounded-md transition-colors",
                          server.enabled
                            ? "bg-green-600/20 text-green-400 hover:bg-green-600/30"
                            : "bg-gray-600/20 text-gray-400 hover:bg-gray-600/30"
                        )}
                      >
                        {server.enabled ? 'Enabled' : 'Disabled'}
                      </button>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        );

      case 'prompts':
        return (
          <div className="p-6">
            <h2 className="text-xl font-semibold text-white mb-4">Prompts</h2>
            <div className="text-gray-400">
              <p>Manage your custom prompts and templates here.</p>
            </div>
          </div>
        );

      case 'history':
        return (
          <div className="p-6">
            <h2 className="text-xl font-semibold text-white mb-4">Chat History</h2>
            <div className="text-gray-400">
              <p>View and manage your conversation history here.</p>
            </div>
          </div>
        );

      case 'console':
        return (
          <div className="p-6">
            <h2 className="text-xl font-semibold text-white mb-4">Console</h2>
            <div className="text-gray-400">
              <p>Debug console and logs will appear here.</p>
            </div>
          </div>
        );

      case 'settings':
        return (
          <div className="p-6">
            <h2 className="text-xl font-semibold text-white mb-4">Settings</h2>
            <div className="space-y-6">
              {/* General Settings */}
              <div>
                <h3 className="text-lg font-medium text-white mb-3">General</h3>
                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <span className="text-gray-300">Language</span>
                    <select className="bg-gray-800 border border-gray-700 rounded px-3 py-1 text-white">
                      <option>English</option>
                    </select>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-gray-300">Theme</span>
                    <select className="bg-gray-800 border border-gray-700 rounded px-3 py-1 text-white">
                      <option>Dark</option>
                      <option>Light</option>
                    </select>
                  </div>
                </div>
              </div>

              {/* Model Settings */}
              <div>
                <h3 className="text-lg font-medium text-white mb-3">Model Settings</h3>
                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <span className="text-gray-300">Local Models</span>
                    <label className="relative inline-flex items-center cursor-pointer">
                      <input type="checkbox" className="sr-only peer" defaultChecked />
                      <div className="w-11 h-6 bg-gray-700 peer-focus:outline-none rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
                    </label>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-gray-300">Remote Models</span>
                    <label className="relative inline-flex items-center cursor-pointer">
                      <input type="checkbox" className="sr-only peer" defaultChecked />
                      <div className="w-11 h-6 bg-gray-700 peer-focus:outline-none rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-blue-600"></div>
                    </label>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );

      default:
        return (
          <div className="p-6">
            <div className="text-gray-400 text-center">
              <p>Select an item from the sidebar to view its settings.</p>
            </div>
          </div>
        );
    }
  };

  if (!isOpen) return null;

  return (
    <div 
      className={cn(
        "w-96 bg-gray-900/50 border-l border-gray-800/50 flex flex-col",
        className
      )}
    >
      {/* Panel Header */}
      <div className="flex items-center justify-between p-4 border-b border-gray-800/50">
        <h2 className="text-lg font-semibold text-white">
          {activePanel?.replace('-', ' ').toUpperCase() || 'Panel'}
        </h2>
        <Button
          variant="ghost"
          size="sm"
          onClick={onClose}
          className="h-8 w-8 p-0 text-gray-400 hover:text-white hover:bg-gray-800/50"
          title="Close Panel"
        >
          <X className="w-4 h-4" />
        </Button>
      </div>

      {/* Panel Content */}
      <div className="flex-1 overflow-y-auto">
        {renderPanelContent()}
      </div>

      {/* Panel Footer - Action buttons */}
      <div className="p-4 border-t border-gray-800/50">
        <div className="flex items-center gap-2 justify-end">
          <Button
            variant="ghost"
            size="sm"
            className="text-gray-400 hover:text-white"
          >
            Cancel
          </Button>
          <Button
            variant="ghost"
            size="sm"
            className="text-gray-400 hover:text-white"
          >
            Reload
          </Button>
          <Button
            size="sm"
            className="bg-blue-600 hover:bg-blue-700 text-white"
          >
            Save
          </Button>
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { Button } from '../ui/button';
import { Input } from '../ui/input';
import { Label } from '../ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '../ui/select';
import { ToggleSwitch } from '../ui/toggle-switch';
import { Textarea } from '../ui/textarea';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '../ui/dialog';
import { settingsService, type AppSettings } from '../../services/settingsService';
import { MemoryManagement } from '../MemoryManagement';
import KnowledgeBaseSettings from '../KnowledgeBaseSettings';
import { mcpService, type MCPServer } from '../../services/mcpService';
import { PromptsContent } from '../PromptsContent';
import { ApiKeySettings } from '../ApiKeySettings';
import { getTTSService, type TTSVoice } from '../../services/textToSpeechService';
import type { TextToSpeechSettings } from '../../types/settings';
import { Plus, Trash2, Server, FileText, Palette, RotateCcw, X, Key, Keyboard, MessageSquare, Terminal, Brain, Database, Settings, Edit, Volume2 } from 'lucide-react';
import { ColorPicker } from '../ui/color-picker';
import { ThemeSelector } from '../ui/theme-selector';
import { useTheme } from '../../contexts/ThemeContext';
import { cn } from '@/lib/utils';

interface SettingsModalProps {
  isOpen: boolean;
  onClose: () => void;
  className?: string;
}

interface SidebarItem {
  id: string;
  label: string;
  icon: React.ReactNode;
}

const sidebarItems: SidebarItem[] = [
  { id: 'api-keys', label: 'API Keys', icon: <Key style={{ width: '16px', height: '16px' }} /> },
  { id: 'shortcuts', label: 'Shortcuts', icon: <Keyboard style={{ width: '16px', height: '16px' }} /> },
  { id: 'prompts', label: 'Prompts', icon: <FileText style={{ width: '16px', height: '16px' }} /> },
  { id: 'chat', label: 'Chat', icon: <MessageSquare style={{ width: '16px', height: '16px' }} /> },
  { id: 'text-to-speech', label: 'Text to Speech', icon: <Volume2 style={{ width: '16px', height: '16px' }} /> },
  { id: 'mcp', label: 'MCP', icon: <Server style={{ width: '16px', height: '16px' }} /> },
  { id: 'internal-commands', label: 'Commands', icon: <Terminal style={{ width: '16px', height: '16px' }} /> },
  { id: 'memory', label: 'Memory', icon: <Brain style={{ width: '16px', height: '16px' }} /> },
  { id: 'knowledge-base', label: 'Knowledge Base', icon: <Database style={{ width: '16px', height: '16px' }} /> },
  { id: 'appearance', label: 'Appearance', icon: <Palette className="w-4 h-4" /> },
  { id: 'general', label: 'General', icon: <Settings className="w-4 h-4" /> },
];

export function SettingsModal({ isOpen, onClose, className }: SettingsModalProps) {
  // Copy all state from SettingsOverlay
  const [activeTab, setActiveTab] = useState('api-keys');
  const [formData, setFormData] = useState<AppSettings | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);
  const [saveError, setSaveError] = useState<string | null>(null);
  const [saveSuccess, setSaveSuccess] = useState(false);
  const apiKeySaveRef = useRef<(() => Promise<void>) | null>(null);
  const {
    customColors,
    setCustomColors,
    resetToDefaults,
    selectedThemePreset,
    setSelectedThemePreset,
    colorMode,
    setColorMode,
    themePresets
  } = useTheme();
  const [mcpServers, setMcpServers] = useState<MCPServer[]>([]);
  const [showAddMcpServer, setShowAddMcpServer] = useState(false);
  const [editingMcpServer, setEditingMcpServer] = useState<MCPServer | null>(null);
  const [showMcpJsonEditor, setShowMcpJsonEditor] = useState(false);
  const [mcpJsonContent, setMcpJsonContent] = useState('');
  const [newMcpServer, setNewMcpServer] = useState({
    name: '',
    command: '',
    args: [] as string[],
    description: '',
    enabled: true,
    env: {} as Record<string, string>
  });

  // Internal Commands state
  const [internalCommandsEnabled, setInternalCommandsEnabled] = useState(false);
  const [allowedDirectories, setAllowedDirectories] = useState<string[]>([]);
  const [blockedCommands, setBlockedCommands] = useState<string[]>([]);
  const [enabledCommandCategories, setEnabledCommandCategories] = useState({
    terminal: true,
    filesystem: true,
    textEditing: true,
    system: true
  });
  const [enabledTools, setEnabledTools] = useState<Record<string, boolean>>({});
  const [availableTools, setAvailableTools] = useState<Array<{
    name: string;
    description: string;
    category: string;
    inputSchema: Record<string, unknown>;
  }>>([]);



  // TTS state
  const [availableVoices, setAvailableVoices] = useState<TTSVoice[]>([]);
  const [ttsSettings, setTtsSettings] = useState<TextToSpeechSettings>({
    enabled: false,
    voice: '',
    rate: 1.0,
    pitch: 1.0,
    volume: 0.8,
    autoPlay: false,
  });
  const [newDirectory, setNewDirectory] = useState('');
  const [newBlockedCommand, setNewBlockedCommand] = useState('');

  // Copy all utility functions from SettingsOverlay
  const updateFormData = (updates: Partial<AppSettings>) => {
    setFormData(prev => prev ? { ...prev, ...updates } : null);
    setHasChanges(true);
  };

  const loadMcpJson = async () => {
    try {
      if (typeof window !== 'undefined' && window.electronAPI) {
        const jsonData = await window.electronAPI.getMCPServers();
        setMcpJsonContent(JSON.stringify(jsonData, null, 2));
        setShowMcpJsonEditor(true);
      }
    } catch (error) {
      console.error('Failed to load MCP JSON:', error);
    }
  };

  const handleSaveMcpJson = async () => {
    try {
      const parsedData = JSON.parse(mcpJsonContent);
      if (typeof window !== 'undefined' && window.electronAPI) {
        const success = await window.electronAPI.saveMCPServers(parsedData);
        if (success) {
          setShowMcpJsonEditor(false);
          loadMcpServers();
        }
      }
    } catch (error) {
      console.error('Failed to save MCP JSON:', error);
    }
  };

  const loadMcpServers = async () => {
    try {
      const servers = await mcpService.getServers();
      setMcpServers(servers);
    } catch (error) {
      console.error('Failed to load MCP servers:', error);
      setMcpServers([]);
    }
  };

  const handleAddMcpServer = async () => {
    try {
      const success = await mcpService.addServer(newMcpServer);
      if (success) {
        setNewMcpServer({
          name: '',
          command: '',
          args: [],
          description: '',
          enabled: true,
          env: {}
        });
        setShowAddMcpServer(false);
        await loadMcpServers();
      }
    } catch (error) {
      console.error('Failed to add MCP server:', error);
    }
  };

  const handleUpdateMcpServer = async () => {
    if (!editingMcpServer) return;

    try {
      console.log('üîß Updating MCP server:', editingMcpServer.id, newMcpServer);
      const success = await mcpService.updateServer(editingMcpServer.id, {
        name: newMcpServer.name,
        command: newMcpServer.command,
        args: newMcpServer.args,
        description: newMcpServer.description,
        enabled: newMcpServer.enabled,
        env: newMcpServer.env
      });

      if (success) {
        console.log('üîß MCP server updated successfully');
        setEditingMcpServer(null);
        setNewMcpServer({
          name: '',
          command: '',
          args: [],
          description: '',
          enabled: true,
          env: {}
        });
        setShowAddMcpServer(false);
        await loadMcpServers();
      } else {
        console.error('üîß Failed to update MCP server - updateServer returned false');
      }
    } catch (error) {
      console.error('üîß Failed to update MCP server:', error);
    }
  };

  // Helper functions for managing arguments and environment variables
  const addArgument = () => {
    setNewMcpServer(prev => ({
      ...prev,
      args: [...prev.args, '']
    }));
  };

  const removeArgument = (index: number) => {
    setNewMcpServer(prev => ({
      ...prev,
      args: prev.args.filter((_, i) => i !== index)
    }));
  };

  const updateArgument = (index: number, value: string) => {
    setNewMcpServer(prev => ({
      ...prev,
      args: prev.args.map((arg, i) => i === index ? value : arg)
    }));
  };

  const addEnvVariable = () => {
    const key = `ENV_VAR_${Object.keys(newMcpServer.env).length + 1}`;
    setNewMcpServer(prev => ({
      ...prev,
      env: { ...prev.env, [key]: '' }
    }));
  };

  const removeEnvVariable = (key: string) => {
    setNewMcpServer(prev => {
      const newEnv = { ...prev.env };
      delete newEnv[key];
      return { ...prev, env: newEnv };
    });
  };

  const updateEnvVariable = (oldKey: string, newKey: string, value: string) => {
    setNewMcpServer(prev => {
      const newEnv = { ...prev.env };
      if (oldKey !== newKey) {
        delete newEnv[oldKey];
      }
      newEnv[newKey] = value;
      return { ...prev, env: newEnv };
    });
  };

  const handleDeleteMcpServer = async (serverId: string) => {
    try {
      const success = await mcpService.removeServer(serverId);
      if (success) {
        await loadMcpServers();
      }
    } catch (error) {
      console.error('Failed to delete MCP server:', error);
    }
  };

  const handleToggleMcpServer = async (serverId: string, enabled: boolean) => {
    try {
      console.log('üîß Toggling MCP server:', serverId, 'to enabled:', enabled);
      const success = await mcpService.updateServer(serverId, { enabled });
      console.log('üîß MCP server toggle result:', success);
      if (success) {
        console.log('üîß Reloading MCP servers after toggle...');
        await loadMcpServers();
      } else {
        console.error('üîß Failed to toggle MCP server - updateServer returned false');
      }
    } catch (error) {
      console.error('üîß Failed to toggle MCP server:', error);
    }
  };

  // Internal Commands functions
  const loadInternalCommandsSettings = async () => {
    try {
      const currentSettings = settingsService.getSettings();
      console.log('üîß Loading internal commands settings:', currentSettings?.internalCommands);
      if (currentSettings?.internalCommands) {
        const enabled = currentSettings.internalCommands.enabled || false;
        const directories = currentSettings.internalCommands.allowedDirectories || [];
        const blocked = currentSettings.internalCommands.blockedCommands || [];
        const commands = currentSettings.internalCommands.enabledCommands || {
          terminal: true,
          filesystem: true,
          textEditing: true,
          system: true
        };
        const tools = currentSettings.internalCommands.enabledTools || {};

        setInternalCommandsEnabled(enabled);
        setAllowedDirectories(directories);
        setBlockedCommands(blocked);
        setEnabledCommandCategories(commands);
        setEnabledTools(tools);

        // Also update formData to ensure it's in sync
        setFormData(prev => prev ? {
          ...prev,
          internalCommands: {
            ...prev.internalCommands,
            enabled: enabled,
            allowedDirectories: directories,
            blockedCommands: blocked,
            enabledCommands: commands,
            enabledTools: tools
          }
        } : null);
      }
    } catch (error) {
      console.error('Failed to load internal commands settings:', error);
    }
  };

  const saveInternalCommandsSettings = async () => {
    try {
      const currentSettings = settingsService.getSettings();
      const updatedSettings = {
        ...currentSettings,
        internalCommands: {
          ...currentSettings.internalCommands,
          enabled: internalCommandsEnabled,
          allowedDirectories,
          blockedCommands,
          enabledCommands: enabledCommandCategories,
          enabledTools
        }
      };

      await settingsService.updateSettings(updatedSettings);
      console.log('Internal commands settings saved successfully');
    } catch (error) {
      console.error('Failed to save internal commands settings:', error);
    }
  };

  const saveInternalCommandsSettingsWithCommands = async (commands: { terminal: boolean; filesystem: boolean; textEditing: boolean; system: boolean }) => {
    try {
      const currentSettings = settingsService.getSettings();
      const updatedSettings = {
        ...currentSettings,
        internalCommands: {
          ...currentSettings.internalCommands,
          enabled: internalCommandsEnabled,
          allowedDirectories,
          blockedCommands,
          enabledCommands: commands,
          enabledTools
        }
      };

      await settingsService.updateSettings(updatedSettings);
      console.log('Internal commands settings with commands saved successfully');
    } catch (error) {
      console.error('Failed to save internal commands settings with commands:', error);
    }
  };

  const handleAddDirectory = () => {
    if (newDirectory.trim() && !allowedDirectories.includes(newDirectory.trim())) {
      const updatedDirectories = [...allowedDirectories, newDirectory.trim()];
      setAllowedDirectories(updatedDirectories);
      setNewDirectory('');
      setHasChanges(true);

      // Update formData with directory changes
      setFormData(prev => prev ? {
        ...prev,
        internalCommands: {
          ...prev.internalCommands,
          enabled: internalCommandsEnabled,
          allowedDirectories: updatedDirectories,
          blockedCommands: blockedCommands,
          enabledCommands: enabledCommandCategories,
          enabledTools: enabledTools
        }
      } : null);

      saveInternalCommandsSettings();
    }
  };

  const handleRemoveDirectory = (directory: string) => {
    const updatedDirectories = allowedDirectories.filter(d => d !== directory);
    setAllowedDirectories(updatedDirectories);
    setHasChanges(true);

    // Update formData with directory changes
    setFormData(prev => prev ? {
      ...prev,
      internalCommands: {
        ...prev.internalCommands,
        enabled: internalCommandsEnabled,
        allowedDirectories: updatedDirectories,
        blockedCommands: blockedCommands,
        enabledCommands: enabledCommandCategories,
        enabledTools: enabledTools
      }
    } : null);

    saveInternalCommandsSettings();
  };

  const handleAddBlockedCommand = () => {
    if (newBlockedCommand.trim() && !blockedCommands.includes(newBlockedCommand.trim())) {
      const updatedCommands = [...blockedCommands, newBlockedCommand.trim()];
      setBlockedCommands(updatedCommands);
      setNewBlockedCommand('');
      setHasChanges(true);

      // Update formData with blocked command changes
      setFormData(prev => prev ? {
        ...prev,
        internalCommands: {
          ...prev.internalCommands,
          enabled: internalCommandsEnabled,
          allowedDirectories: allowedDirectories,
          blockedCommands: updatedCommands,
          enabledCommands: enabledCommandCategories,
          enabledTools: enabledTools
        }
      } : null);

      saveInternalCommandsSettings();
    }
  };

  const handleRemoveBlockedCommand = (command: string) => {
    const updatedCommands = blockedCommands.filter(c => c !== command);
    setBlockedCommands(updatedCommands);
    setHasChanges(true);

    // Update formData with blocked command changes
    setFormData(prev => prev ? {
      ...prev,
      internalCommands: {
        ...prev.internalCommands,
        enabled: internalCommandsEnabled,
        allowedDirectories: allowedDirectories,
        blockedCommands: updatedCommands,
        enabledCommands: enabledCommandCategories,
        enabledTools: enabledTools
      }
    } : null);

    saveInternalCommandsSettings();
  };

  const handleCommandCategoryToggle = (category: string, enabled: boolean) => {
    const updatedCommands = {
      ...enabledCommandCategories,
      [category]: enabled
    };
    setEnabledCommandCategories(updatedCommands);
    setHasChanges(true);

    // Update formData with command category changes
    setFormData(prev => prev ? {
      ...prev,
      internalCommands: {
        ...prev.internalCommands,
        enabled: internalCommandsEnabled,
        allowedDirectories: allowedDirectories,
        blockedCommands: blockedCommands,
        enabledCommands: updatedCommands,
        enabledTools: enabledTools
      }
    } : null);

    // Immediately save the settings with the updated commands
    saveInternalCommandsSettingsWithCommands(updatedCommands);
  };

  const handleOpenMcpJsonEditor = () => {
    loadMcpJson();
  };

  // Main save function (copied from SettingsOverlay)
  const handleSave = async () => {
    if (!formData) {
      console.error('üîß No formData to save');
      return;
    }

    console.log('üîß Starting save process with formData:', formData);
    console.log('üîß Theme data being saved:', formData.ui);

    setIsLoading(true);
    setSaveError(null);
    setSaveSuccess(false);

    try {
      // Save general settings
      console.log('üîß Calling settingsService.updateSettings...');
      const success = await settingsService.updateSettings(formData);
      console.log('üîß Settings save result:', success);

      // Always save API keys if the save function is available
      let apiKeySaveSuccess = true;
      let apiKeyError: string | null = null;

      if (apiKeySaveRef.current) {
        try {
          console.log('üîê SettingsModal: Triggering API key save via ref');
          await apiKeySaveRef.current();
          console.log('üîê SettingsModal: API key save completed');
        } catch (error) {
          console.error('üîê SettingsModal: Failed to save API keys:', error);
          apiKeySaveSuccess = false;
          apiKeyError = error instanceof Error ? error.message : 'Unknown API key save error';
        }
      }

      if (success && apiKeySaveSuccess) {
        console.log('üîÑ Settings and API keys saved successfully');

        setHasChanges(false);

        // Trigger model refresh for all components by dispatching a custom event
        console.log('üîÑ Settings saved successfully - triggering model refresh');
        window.dispatchEvent(new CustomEvent('settingsSaved', {
          detail: {
            apiKeysSaved: apiKeySaveSuccess,
            provider: formData.chat?.provider
          }
        }));

        // If any theme-related settings were changed, notify other windows
        if (formData.ui?.customColors ||
            formData.ui?.useCustomColors !== undefined ||
            formData.ui?.selectedThemePreset ||
            formData.ui?.colorMode) {
          console.log('Settings modal: Preparing to notify theme change');

          if (typeof window !== 'undefined' && window.electronAPI) {
            // Determine the actual colors to apply based on the current mode
            let colorsToApply = customColors;
            const currentMode = formData.ui.colorMode || colorMode;
            const currentPreset = formData.ui.selectedThemePreset || selectedThemePreset;

            if (currentMode === 'preset') {
              // Use preset colors
              const preset = themePresets.find(p => p.id === currentPreset);
              if (preset) {
                colorsToApply = preset.colors;
              }
            }

            console.log('Notifying theme change with colors:', colorsToApply);
            window.electronAPI.notifyThemeChange({
              customColors: colorsToApply,
              useCustomColors: currentMode === 'custom'
            });
          }
        }

        setSaveSuccess(true);
        setTimeout(() => setSaveSuccess(false), 3000);

        // Close the modal after successful save
        onClose();
      } else {
        if (!success) {
          setSaveError('Failed to save settings');
        }
        if (!apiKeySaveSuccess && apiKeyError) {
          setSaveError(apiKeyError);
        }
      }
    } catch (error) {
      console.error('Failed to save settings:', error);
      setSaveError(error instanceof Error ? error.message : 'Unknown error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  // Initialization (copied from SettingsOverlay)
  useEffect(() => {
    const loadSettings = async () => {
      try {
        const currentSettings = settingsService.getSettings();
        setFormData(currentSettings);


      } catch (error) {
        console.error('Failed to load settings:', error);
      }
    };

    if (isOpen) {
      loadSettings();
      loadMcpServers();
      loadInternalCommandsSettings();
    }
  }, [isOpen]);

  // Load available internal command tools only
  useEffect(() => {
    const loadAvailableTools = async () => {
      try {
        if (typeof window !== 'undefined' && window.electronAPI) {
          console.log('üîß SettingsModal: Loading internal command tools...');
          console.log('üîß SettingsModal: electronAPI available:', !!window.electronAPI);
          console.log('üîß SettingsModal: getInternalCommandsTools method exists:', typeof window.electronAPI.getInternalCommandsTools);

          // First, ensure configuration is sent to Electron main process
          const currentSettings = settingsService.getSettings();
          console.log('üîß SettingsModal: Sending config to Electron:', currentSettings.internalCommands);
          await window.electronAPI.setInternalCommandsConfig(currentSettings.internalCommands);

          // Now load the tools
          const internalTools = await window.electronAPI.getInternalCommandsTools();
          console.log(`üîß SettingsModal: Received ${(internalTools as any[])?.length || 0} internal command tools:`, internalTools);
          setAvailableTools((internalTools as Array<{
            name: string;
            description: string;
            category: string;
            inputSchema: Record<string, unknown>;
          }>) || []);
        } else {
          console.log('üîß SettingsModal: No electronAPI available');
        }
      } catch (error) {
        console.error('Failed to load available tools:', error);
      }
    };

    if (isOpen) {
      loadAvailableTools();
    }
  }, [isOpen]);

  // Initialize TTS and load voices
  useEffect(() => {
    if (isOpen && formData?.ui) {
      // Ensure TTS settings exist with defaults
      const defaultTtsSettings = {
        enabled: false,
        voice: '',
        rate: 1.0,
        pitch: 1.0,
        volume: 0.8,
        autoPlay: false,
      };

      const ttsSettings = formData.ui.textToSpeech || defaultTtsSettings;
      setTtsSettings(ttsSettings);

      // If TTS settings don't exist in formData, add them
      if (!formData.ui.textToSpeech) {
        updateFormData({
          ui: {
            ...formData.ui,
            textToSpeech: defaultTtsSettings
          }
        });
      }

      // Initialize TTS service and load voices
      try {
        const ttsService = getTTSService(ttsSettings);
        const allVoices = ttsService.getAvailableVoices();
        const highQualityVoices = ttsService.getHighQualityVoices();
        const sortedVoices = ttsService.getAllVoicesWithQualityInfo();

        // Show all voices sorted by quality (don't filter too strictly)
        const voicesToShow = sortedVoices.length > 0 ? sortedVoices : allVoices;

        console.log('üîä SettingsModal: Total voices:', allVoices.length);
        console.log('üîä SettingsModal: High-quality voices:', highQualityVoices.length);
        console.log('üîä SettingsModal: Showing voices (sorted):', voicesToShow.length);
        console.log('üîä SettingsModal: Voice details:', voicesToShow.map(v => ({
          name: v.name,
          lang: v.lang,
          localService: v.localService
        })));

        setAvailableVoices(voicesToShow);

        // Set up voice change listener
        ttsService.onVoicesChanged(() => {
          const allUpdatedVoices = ttsService.getAvailableVoices();
          const highQualityUpdatedVoices = ttsService.getHighQualityVoices();
          const sortedUpdatedVoices = ttsService.getAllVoicesWithQualityInfo();
          const updatedVoicesToShow = sortedUpdatedVoices.length > 0 ? sortedUpdatedVoices : allUpdatedVoices;

          console.log('üîä SettingsModal: Voices updated - Total:', allUpdatedVoices.length, 'High-quality:', highQualityUpdatedVoices.length);
          console.log('üîä SettingsModal: Updated voice details:', updatedVoicesToShow.slice(0, 5).map(v => v.name));
          setAvailableVoices(updatedVoicesToShow);

          // If no voice is selected and voices are available, select the first high-quality one
          if (!ttsSettings.voice && updatedVoicesToShow.length > 0) {
            const newTtsSettings = {
              ...ttsSettings,
              voice: updatedVoicesToShow[0].name
            };
            setTtsSettings(newTtsSettings);
            updateFormData({
              ui: {
                ...formData.ui,
                textToSpeech: newTtsSettings
              }
            });
          }
        });
      } catch (error) {
        console.error('Failed to initialize TTS service:', error);
      }
    }
  }, [isOpen, formData?.ui]);

  if (!isOpen) return null;

  return (
    <div className={cn("flex h-full bg-background text-foreground", className)}>
      {/* Left Sidebar */}
      <div className="w-64 bg-muted/30 border-r border-border flex flex-col flex-shrink-0">
        {/* Header */}
        <div className="p-4 border-b border-border flex items-center justify-between flex-shrink-0">
          <h2 className="text-sm font-semibold">Settings</h2>
          <Button
            variant="ghost"
            size="sm"
            onClick={onClose}
            className="h-8 w-8 p-0 hover:bg-muted"
          >
            <X style={{ width: '16px', height: '16px' }} />
          </Button>
        </div>

        {/* Navigation */}
        <div className="flex-1 overflow-y-auto p-1">
          <div className="space-y-0.5">
            {sidebarItems.map((item) => (
              <button
                key={item.id}
                onClick={() => setActiveTab(item.id)}
                className={cn(
                  "w-full flex items-center gap-2 px-2 py-1.5 text-xs rounded-md transition-colors text-left",
                  activeTab === item.id
                    ? "bg-primary text-primary-foreground"
                    : "text-muted-foreground hover:text-foreground hover:bg-muted/50"
                )}
              >
                {item.icon}
                {item.label}
              </button>
            ))}
          </div>
        </div>

        {/* Save/Cancel Buttons */}
        <div className="p-2 border-t border-border space-y-1 flex-shrink-0">
          {saveError && (
            <div className="text-xs text-destructive mb-1">{saveError}</div>
          )}
          {saveSuccess && (
            <div className="text-xs text-green-600 mb-1">Settings saved successfully!</div>
          )}
          <Button
            onClick={() => {
              console.log('üîß Save button clicked - hasChanges:', hasChanges, 'formData:', formData);
              handleSave();
            }}
            disabled={isLoading || !hasChanges}
            className="w-full h-7 text-xs"
          >
            {isLoading ? 'Saving...' : 'Save Settings'}
          </Button>
          <Button
            variant="outline"
            onClick={onClose}
            className="w-full h-7 text-xs"
          >
            Cancel
          </Button>
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Content */}
        <div className="flex-1 overflow-y-auto p-2">
          {/* API Keys Tab */}
          {activeTab === 'api-keys' && (
            <ApiKeySettings
              onApiKeyChange={() => setHasChanges(true)}
              onRegisterSaveFunction={(saveFunction: () => Promise<void>) => {
                apiKeySaveRef.current = saveFunction;
              }}
            />
          )}

          {/* Shortcuts Tab */}
          {activeTab === 'shortcuts' && formData?.shortcuts && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">Keyboard Shortcuts</h3>
                <div className="space-y-2">
                  <div className="space-y-1">
                    <Label htmlFor="toggle-window" className="text-xs">Toggle Window</Label>
                    <Input
                      id="toggle-window"
                      value={formData.shortcuts.toggleWindow}
                      placeholder="CommandOrControl+Shift+L"
                      className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                      onChange={(e) => updateFormData({
                        shortcuts: { ...formData.shortcuts, toggleWindow: e.target.value }
                      })}
                    />
                  </div>
                  <div className="space-y-1">
                    <Label htmlFor="process-clipboard" className="text-xs">Process Clipboard</Label>
                    <Input
                      id="process-clipboard"
                      value={formData.shortcuts.processClipboard}
                      placeholder="CommandOrControl+Shift+V"
                      className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                      onChange={(e) => updateFormData({
                        shortcuts: { ...formData.shortcuts, processClipboard: e.target.value }
                      })}
                    />
                  </div>
                  <div className="space-y-1">
                    <Label htmlFor="action-menu" className="text-xs">Action Menu</Label>
                    <Input
                      id="action-menu"
                      value={formData.shortcuts.actionMenu}
                      placeholder="CommandOrControl+Shift+Space"
                      className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                      onChange={(e) => updateFormData({
                        shortcuts: { ...formData.shortcuts, actionMenu: e.target.value }
                      })}
                    />
                  </div>
                  <div className="space-y-1">
                    <Label htmlFor="open-shortcuts" className="text-xs">Open Shortcuts</Label>
                    <Input
                      id="open-shortcuts"
                      value={formData.shortcuts.openShortcuts}
                      placeholder="CommandOrControl+Shift+K"
                      className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                      onChange={(e) => updateFormData({
                        shortcuts: { ...formData.shortcuts, openShortcuts: e.target.value }
                      })}
                    />
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Prompts Tab */}
          {activeTab === 'prompts' && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">Prompts Management</h3>
                <PromptsContent onPromptSelect={() => {}} />
              </div>
            </div>
          )}

          {/* Chat Tab */}
          {activeTab === 'chat' && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">Chat Configuration</h3>
                <div className="space-y-2">
                  {formData?.chat && (
                    <>
                      <div className="space-y-1">
                        <Label htmlFor="system-prompt" className="text-xs">Default System Prompt</Label>
                        <Textarea
                          id="system-prompt"
                          value={formData.chat.systemPrompt || ''}
                          placeholder="Enter your default system prompt that will be used for all conversations..."
                          rows={3}
                          className="text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                          onChange={(e) => updateFormData({
                            chat: { ...formData.chat, systemPrompt: e.target.value }
                          })}
                        />
                        <p className="text-xs text-muted-foreground">
                          This prompt will be sent with every conversation to set the AI behavior and personality.
                        </p>
                      </div>
                      <div className="grid grid-cols-2 gap-2">
                        <div className="space-y-1">
                          <Label htmlFor="temperature" className="text-xs">Temperature</Label>
                          <Input
                            id="temperature"
                            type="number"
                            min="0"
                            max="2"
                            step="0.1"
                            value={formData.chat.temperature}
                            className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                            onChange={(e) => updateFormData({
                              chat: { ...formData.chat, temperature: parseFloat(e.target.value) || 0 }
                            })}
                          />
                          <p className="text-xs text-muted-foreground">
                            Controls randomness (0.0 = focused, 2.0 = creative)
                          </p>
                        </div>
                        <div className="space-y-1">
                          <Label htmlFor="max-tokens" className="text-xs">Max Tokens</Label>
                          <Input
                            id="max-tokens"
                            type="number"
                            min="1"
                            max="32768"
                            value={formData.chat.maxTokens}
                            className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                            onChange={(e) => updateFormData({
                              chat: { ...formData.chat, maxTokens: parseInt(e.target.value) || 8192 }
                            })}
                          />
                          <p className="text-xs text-muted-foreground">
                            Maximum response length
                          </p>
                        </div>
                      </div>
                      <div className="flex items-center space-x-2">
                        <ToggleSwitch
                          size="sm"
                          enabled={formData.chat.toolCallingEnabled}
                          onToggle={(enabled: boolean) => updateFormData({
                            chat: { ...formData.chat, toolCallingEnabled: enabled }
                          })}
                        />
                        <div>
                          <Label className="text-xs">Enable Tool Calling</Label>
                          <p className="text-xs text-muted-foreground">
                            Allow the AI to use external tools and functions
                          </p>
                        </div>
                      </div>
                    </>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Text to Speech Tab */}
          {activeTab === 'text-to-speech' && formData?.ui?.textToSpeech && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">Text to Speech</h3>
                <div className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <ToggleSwitch
                      size="sm"
                      enabled={ttsSettings.enabled}
                      onToggle={(enabled: boolean) => {
                        const newTtsSettings = { ...ttsSettings, enabled };
                        setTtsSettings(newTtsSettings);
                        updateFormData({
                          ui: {
                            ...formData.ui,
                            textToSpeech: newTtsSettings
                          }
                        });
                      }}
                    />
                    <div>
                      <Label className="text-xs">Enable Text to Speech</Label>
                      <p className="text-xs text-muted-foreground">
                        Enable voice synthesis for AI responses
                      </p>
                    </div>
                  </div>

                  {ttsSettings.enabled && (
                    <>
                      <div className="space-y-2">
                        <Label htmlFor="voice-select">Voice</Label>
                        <Select
                          value={ttsSettings.voice}
                          onValueChange={(value) => {
                            const newTtsSettings = { ...ttsSettings, voice: value };
                            setTtsSettings(newTtsSettings);
                            updateFormData({
                              ui: {
                                ...formData.ui,
                                textToSpeech: newTtsSettings
                              }
                            });
                          }}
                        >
                          <SelectTrigger className="bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors">
                            <SelectValue placeholder="Select a voice" />
                          </SelectTrigger>
                          <SelectContent>
                            {availableVoices.map((voice) => (
                              <SelectItem key={voice.name} value={voice.name}>
                                <div className="flex flex-col">
                                  <span className="font-medium">
                                    {voice.name}
                                    {voice.name.toLowerCase().includes('google') && ' üéØ'}
                                    {voice.name.toLowerCase().includes('neural') && ' ‚ö°'}
                                  </span>
                                  <span className="text-xs text-muted-foreground">
                                    {voice.lang} ‚Ä¢ {voice.localService ? 'Local' : 'Remote/Cloud'}
                                  </span>
                                </div>
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <div className="flex items-center justify-between">
                          <p className="text-xs text-muted-foreground">
                            Choose a voice for speech synthesis. üéØ = Google voices, ‚ö° = Neural voices. Remote/Cloud voices typically offer the best quality.
                          </p>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => {
                              try {
                                console.log('üîä Manual voice refresh triggered');
                                const ttsService = getTTSService(ttsSettings);

                                // Force voice reload
                                if (typeof window !== 'undefined' && window.speechSynthesis) {
                                  // Cancel any ongoing speech
                                  window.speechSynthesis.cancel();

                                  // Trigger voice loading
                                  const voices = window.speechSynthesis.getVoices();
                                  console.log('üîä Manual refresh: Found', voices.length, 'voices');

                                  // Update available voices
                                  const sortedVoices = ttsService.getAllVoicesWithQualityInfo();
                                  setAvailableVoices(sortedVoices);

                                  console.log('üîä Manual refresh: Updated to', sortedVoices.length, 'voices');
                                }
                              } catch (error) {
                                console.error('üîä Failed to refresh voices:', error);
                              }
                            }}
                            className="text-xs"
                          >
                            Refresh Voices
                          </Button>
                        </div>

                        {/* Voice count and help */}
                        <div className="text-xs text-muted-foreground bg-muted/30 p-3 rounded-lg">
                          <div className="flex items-center justify-between mb-2">
                            <span>Available voices: {availableVoices.length}</span>
                            <span>
                              {availableVoices.filter(v => !v.localService).length} remote voices
                            </span>
                          </div>

                          {availableVoices.filter(v => v.name.toLowerCase().includes('google')).length === 0 && (
                            <div className="mb-3 p-2 bg-yellow-500/10 border border-yellow-500/20 rounded">
                              <p className="font-medium text-yellow-400 mb-1">üö® No Google Voices Found</p>
                              <p className="text-yellow-300">
                                Google voices (including Whisper-quality voices) are only available in Chrome browser, not in Electron apps.
                              </p>
                            </div>
                          )}

                          <div className="space-y-2">
                            <p className="font-medium">üí° To get Google voices:</p>
                            <ul className="space-y-1 ml-4">
                              <li>‚Ä¢ <strong>Chrome Browser:</strong> Open this app in Chrome for Google cloud voices</li>
                              <li>‚Ä¢ <strong>Windows Store:</strong> Download "Voice Recorder" app which installs premium voices</li>
                              <li>‚Ä¢ <strong>Windows Settings:</strong> Settings ‚Üí Time & Language ‚Üí Speech ‚Üí Manage voices</li>
                              <li>‚Ä¢ <strong>Speech Platform:</strong> Download Microsoft Speech Platform Runtime + voices</li>
                            </ul>

                            <p className="mt-2 text-xs opacity-75">
                              <strong>Note:</strong> Electron apps are limited to locally installed voices. For the best voice selection,
                              consider running this app in Chrome browser where Google's cloud voices are available.
                            </p>
                          </div>
                        </div>
                      </div>

                      <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                          <Label htmlFor="speech-rate">Rate: {ttsSettings.rate.toFixed(1)}</Label>
                          <input
                            id="speech-rate"
                            type="range"
                            min="0.1"
                            max="3.0"
                            step="0.1"
                            value={ttsSettings.rate}
                            className="w-full"
                            onChange={(e) => {
                              const newTtsSettings = { ...ttsSettings, rate: parseFloat(e.target.value) };
                              setTtsSettings(newTtsSettings);
                              updateFormData({
                                ui: {
                                  ...formData.ui,
                                  textToSpeech: newTtsSettings
                                }
                              });
                            }}
                          />
                          <p className="text-xs text-muted-foreground">
                            Speech speed (0.1 = very slow, 3.0 = very fast)
                          </p>
                        </div>

                        <div className="space-y-2">
                          <Label htmlFor="speech-pitch">Pitch: {ttsSettings.pitch.toFixed(1)}</Label>
                          <input
                            id="speech-pitch"
                            type="range"
                            min="0"
                            max="2"
                            step="0.1"
                            value={ttsSettings.pitch}
                            className="w-full"
                            onChange={(e) => {
                              const newTtsSettings = { ...ttsSettings, pitch: parseFloat(e.target.value) };
                              setTtsSettings(newTtsSettings);
                              updateFormData({
                                ui: {
                                  ...formData.ui,
                                  textToSpeech: newTtsSettings
                                }
                              });
                            }}
                          />
                          <p className="text-xs text-muted-foreground">
                            Voice pitch (0 = low, 2 = high)
                          </p>
                        </div>
                      </div>

                      <div className="space-y-2">
                        <Label htmlFor="speech-volume">Volume: {Math.round(ttsSettings.volume * 100)}%</Label>
                        <input
                          id="speech-volume"
                          type="range"
                          min="0"
                          max="1"
                          step="0.1"
                          value={ttsSettings.volume}
                          className="w-full"
                          onChange={(e) => {
                            const newTtsSettings = { ...ttsSettings, volume: parseFloat(e.target.value) };
                            setTtsSettings(newTtsSettings);
                            updateFormData({
                              ui: {
                                ...formData.ui,
                                textToSpeech: newTtsSettings
                              }
                            });
                          }}
                        />
                        <p className="text-xs text-muted-foreground">
                          Speech volume level
                        </p>
                      </div>

                      <div className="flex items-center space-x-2">
                        <ToggleSwitch
                          size="sm"
                          enabled={ttsSettings.autoPlay}
                          onToggle={(enabled: boolean) => {
                            const newTtsSettings = { ...ttsSettings, autoPlay: enabled };
                            setTtsSettings(newTtsSettings);
                            updateFormData({
                              ui: {
                                ...formData.ui,
                                textToSpeech: newTtsSettings
                              }
                            });
                          }}
                        />
                        <div>
                          <Label className="text-xs">Auto-play AI Responses</Label>
                          <p className="text-xs text-muted-foreground">
                            Automatically speak AI responses when they arrive
                          </p>
                        </div>
                      </div>

                      {/* Test Voice Button */}
                      <div className="pt-4 border-t border-border">
                        <Button
                          variant="outline"
                          onClick={() => {
                            try {
                              const ttsService = getTTSService(ttsSettings);
                              ttsService.speak("Hello! This is a test of the text to speech functionality. How does it sound?");
                            } catch (error) {
                              console.error('Failed to test TTS:', error);
                            }
                          }}
                          className="w-full"
                        >
                          <Volume2 className="w-4 h-4 mr-2" />
                          Test Voice
                        </Button>
                      </div>
                    </>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* MCP Tab */}
          {activeTab === 'mcp' && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">MCP Servers</h3>
                <div className="space-y-2">
                  <p className="text-xs text-muted-foreground">
                    Configure Model Context Protocol (MCP) servers for enhanced functionality.
                  </p>

                  {/* Add Server Button */}
                  <div className="flex justify-between items-center">
                    <div className="text-xs text-muted-foreground">
                      {mcpServers.length} server{mcpServers.length !== 1 ? 's' : ''} configured
                    </div>
                    <div className="flex gap-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={handleOpenMcpJsonEditor}
                        className="flex items-center gap-2"
                      >
                        <FileText className="h-4 w-4" />
                        Edit JSON
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => setShowAddMcpServer(true)}
                        className="flex items-center gap-2"
                      >
                        <Plus className="h-4 w-4" />
                        Add Server
                      </Button>
                    </div>
                  </div>

                  {/* Add New Server Form */}
                  {showAddMcpServer && !editingMcpServer && (
                    <div className="border border-border rounded-lg p-2 space-y-2 bg-background">
                      <h4 className="text-xs font-medium">Add New MCP Server</h4>
                      <div className="grid grid-cols-2 gap-2">
                        <div className="space-y-1">
                          <Label htmlFor="mcp-name" className="text-xs">Server Name</Label>
                          <Input
                            id="mcp-name"
                            value={newMcpServer.name}
                            placeholder="My MCP Server"
                            className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                            onChange={(e) => setNewMcpServer(prev => ({ ...prev, name: e.target.value }))}
                          />
                        </div>
                        <div className="space-y-1">
                          <Label htmlFor="mcp-command" className="text-xs">Command</Label>
                          <Input
                            id="mcp-command"
                            value={newMcpServer.command}
                            placeholder="node server.js"
                            className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                            onChange={(e) => setNewMcpServer(prev => ({ ...prev, command: e.target.value }))}
                          />
                        </div>
                      </div>

                      <div className="space-y-1">
                        <Label htmlFor="mcp-description" className="text-xs">Description (Optional)</Label>
                        <Input
                          id="mcp-description"
                          value={newMcpServer.description}
                          placeholder="Description of what this server does"
                          className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                          onChange={(e) => setNewMcpServer(prev => ({ ...prev, description: e.target.value }))}
                        />
                      </div>

                      {/* Arguments Section */}
                      <div className="space-y-1">
                        <div className="flex items-center justify-between">
                          <Label className="text-xs">Arguments</Label>
                          <Button
                            type="button"
                            variant="outline"
                            size="sm"
                            onClick={addArgument}
                            className="h-6 text-xs flex items-center gap-1"
                          >
                            <Plus className="h-3 w-3" />
                            Add Argument
                          </Button>
                        </div>
                        {newMcpServer.args.map((arg, index) => (
                          <div key={index} className="flex gap-1">
                            <Input
                              value={arg}
                              placeholder={`Argument ${index + 1}`}
                              className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                              onChange={(e) => updateArgument(index, e.target.value)}
                            />
                            <Button
                              type="button"
                              variant="ghost"
                              size="sm"
                              onClick={() => removeArgument(index)}
                              className="h-7 w-7 p-0 text-destructive hover:text-destructive"
                            >
                              <Trash2 className="h-3 w-3" />
                            </Button>
                          </div>
                        ))}
                        {newMcpServer.args.length === 0 && (
                          <p className="text-xs text-muted-foreground">No arguments configured</p>
                        )}
                      </div>

                      {/* Environment Variables Section */}
                      <div className="space-y-1">
                        <div className="flex items-center justify-between">
                          <Label className="text-xs">Environment Variables</Label>
                          <Button
                            type="button"
                            variant="outline"
                            size="sm"
                            onClick={addEnvVariable}
                            className="h-6 text-xs flex items-center gap-1"
                          >
                            <Plus className="h-3 w-3" />
                            Add Variable
                          </Button>
                        </div>
                        {Object.entries(newMcpServer.env).map(([key, value]) => (
                          <div key={key} className="flex gap-1">
                            <Input
                              value={key}
                              placeholder="Variable name"
                              className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                              onChange={(e) => updateEnvVariable(key, e.target.value, value)}
                            />
                            <Input
                              value={value}
                              placeholder="Variable value"
                              className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                              onChange={(e) => updateEnvVariable(key, key, e.target.value)}
                            />
                            <Button
                              type="button"
                              variant="outline"
                              size="sm"
                              onClick={() => removeEnvVariable(key)}
                              className="h-7 w-7 p-0 text-destructive hover:text-destructive"
                            >
                              <Trash2 className="h-3 w-3" />
                            </Button>
                          </div>
                        ))}
                        {Object.keys(newMcpServer.env).length === 0 && (
                          <p className="text-xs text-muted-foreground">No environment variables configured</p>
                        )}
                      </div>

                      <div className="flex justify-end gap-1">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => {
                            setShowAddMcpServer(false);
                            setEditingMcpServer(null);
                            setNewMcpServer({
                              name: '',
                              command: '',
                              args: [],
                              description: '',
                              enabled: true,
                              env: {}
                            });
                          }}
                          className="h-7 text-xs"
                        >
                          Cancel
                        </Button>
                        <Button
                          onClick={handleAddMcpServer}
                          size="sm"
                          className="h-7 text-xs"
                        >
                          Add Server
                        </Button>
                      </div>
                    </div>
                  )}

                  {/* Server List */}
                  <div className="space-y-2">
                    {mcpServers.map((server) => (
                      <div key={server.name}>
                        <div className="flex items-center justify-between p-2 border border-border rounded-lg bg-background">
                          <div className="flex items-center gap-2">
                            <Server className="h-3 w-3 text-muted-foreground" />
                            <div>
                              <div className="text-xs font-medium">{server.name}</div>
                              <div className="text-xs text-muted-foreground">{server.command}</div>
                              {server.description && (
                                <div className="text-xs text-muted-foreground">{server.description}</div>
                              )}
                            </div>
                          </div>
                          <div className="flex items-center gap-1">
                            <ToggleSwitch
                              size="sm"
                              enabled={server.enabled}
                              onToggle={(enabled) => {
                                console.log('üîß MCP Server toggle clicked:', server.name, 'id:', server.id, 'enabled:', enabled);
                                handleToggleMcpServer(server.id, enabled);
                              }}
                            />
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => {
                                console.log('üîß Edit MCP server:', server.name);
                                if (editingMcpServer?.id === server.id) {
                                  // If already editing this server, close the form
                                  setEditingMcpServer(null);
                                  setShowAddMcpServer(false);
                                } else {
                                  // Edit this server
                                  setEditingMcpServer(server);
                                  setNewMcpServer({
                                    name: server.name,
                                    command: server.command,
                                    args: server.args || [],
                                    description: server.description || '',
                                    enabled: server.enabled,
                                    env: server.env || {}
                                  });
                                  setShowAddMcpServer(true);
                                }
                              }}
                              className="h-6 w-6 p-0 text-muted-foreground hover:text-foreground"
                            >
                              <Edit className="h-3 w-3" />
                            </Button>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => handleDeleteMcpServer(server.id)}
                              className="h-6 w-6 p-0 text-destructive hover:text-destructive"
                            >
                              <Trash2 className="h-3 w-3" />
                            </Button>
                          </div>
                        </div>

                        {/* Inline Edit Form - appears right below the server being edited */}
                        {editingMcpServer?.id === server.id && showAddMcpServer && (
                          <div className="mt-1 border border-border rounded-lg p-2 space-y-2 bg-muted/30">
                            <h4 className="text-xs font-medium">Edit MCP Server</h4>
                            <div className="grid grid-cols-2 gap-2">
                              <div className="space-y-1">
                                <Label htmlFor="mcp-name" className="text-xs">Server Name</Label>
                                <Input
                                  id="mcp-name"
                                  value={newMcpServer.name}
                                  placeholder="My MCP Server"
                                  className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                                  onChange={(e) => setNewMcpServer(prev => ({ ...prev, name: e.target.value }))}
                                />
                              </div>
                              <div className="space-y-1">
                                <Label htmlFor="mcp-command" className="text-xs">Command</Label>
                                <Input
                                  id="mcp-command"
                                  value={newMcpServer.command}
                                  placeholder="node server.js"
                                  className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                                  onChange={(e) => setNewMcpServer(prev => ({ ...prev, command: e.target.value }))}
                                />
                              </div>
                            </div>

                            <div className="space-y-1">
                              <Label htmlFor="mcp-description" className="text-xs">Description (Optional)</Label>
                              <Input
                                id="mcp-description"
                                value={newMcpServer.description}
                                placeholder="Description of what this server does"
                                className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                                onChange={(e) => setNewMcpServer(prev => ({ ...prev, description: e.target.value }))}
                              />
                            </div>

                            {/* Arguments Section */}
                            <div className="space-y-1">
                              <div className="flex items-center justify-between">
                                <Label className="text-xs">Arguments</Label>
                                <Button
                                  type="button"
                                  variant="outline"
                                  size="sm"
                                  onClick={addArgument}
                                  className="h-6 text-xs flex items-center gap-1"
                                >
                                  <Plus className="h-3 w-3" />
                                  Add Argument
                                </Button>
                              </div>
                              {newMcpServer.args.map((arg, index) => (
                                <div key={index} className="flex gap-1">
                                  <Input
                                    value={arg}
                                    placeholder={`Argument ${index + 1}`}
                                    className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                                    onChange={(e) => updateArgument(index, e.target.value)}
                                  />
                                  <Button
                                    type="button"
                                    variant="ghost"
                                    size="sm"
                                    onClick={() => removeArgument(index)}
                                    className="h-7 w-7 p-0 text-destructive hover:text-destructive"
                                  >
                                    <Trash2 className="h-3 w-3" />
                                  </Button>
                                </div>
                              ))}
                              {newMcpServer.args.length === 0 && (
                                <p className="text-xs text-muted-foreground">No arguments configured</p>
                              )}
                            </div>

                            {/* Environment Variables Section */}
                            <div className="space-y-1">
                              <div className="flex items-center justify-between">
                                <Label className="text-xs">Environment Variables</Label>
                                <Button
                                  type="button"
                                  variant="outline"
                                  size="sm"
                                  onClick={addEnvVariable}
                                  className="h-6 text-xs flex items-center gap-1"
                                >
                                  <Plus className="h-3 w-3" />
                                  Add Variable
                                </Button>
                              </div>
                              {Object.entries(newMcpServer.env).map(([key, value]) => (
                                <div key={key} className="flex gap-1">
                                  <Input
                                    value={key}
                                    placeholder="Variable name"
                                    className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                                    onChange={(e) => updateEnvVariable(key, e.target.value, value)}
                                  />
                                  <Input
                                    value={value}
                                    placeholder="Variable value"
                                    className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                                    onChange={(e) => updateEnvVariable(key, key, e.target.value)}
                                  />
                                  <Button
                                    type="button"
                                    variant="outline"
                                    size="sm"
                                    onClick={() => removeEnvVariable(key)}
                                    className="h-7 w-7 p-0 text-destructive hover:text-destructive"
                                  >
                                    <Trash2 className="h-3 w-3" />
                                  </Button>
                                </div>
                              ))}
                              {Object.keys(newMcpServer.env).length === 0 && (
                                <p className="text-xs text-muted-foreground">No environment variables configured</p>
                              )}
                            </div>

                            <div className="flex justify-end gap-1">
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => {
                                  setShowAddMcpServer(false);
                                  setEditingMcpServer(null);
                                  setNewMcpServer({
                                    name: '',
                                    command: '',
                                    args: [],
                                    description: '',
                                    enabled: true,
                                    env: {}
                                  });
                                }}
                                className="h-7 text-xs"
                              >
                                Cancel
                              </Button>
                              <Button
                                onClick={handleUpdateMcpServer}
                                size="sm"
                                className="h-7 text-xs"
                              >
                                Update Server
                              </Button>
                            </div>
                          </div>
                        )}
                      </div>
                    ))}
                    {mcpServers.length === 0 && (
                      <div className="text-center py-8 text-muted-foreground">
                        No MCP servers configured. Add one to get started.
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Internal Commands Tab */}
          {activeTab === 'internal-commands' && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">Internal Commands</h3>
                <div className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <ToggleSwitch
                      size="sm"
                      enabled={internalCommandsEnabled}
                      onToggle={(enabled) => {
                        setInternalCommandsEnabled(enabled);
                        setHasChanges(true);

                        // Update formData with internal commands changes
                        setFormData(prev => prev ? {
                          ...prev,
                          internalCommands: {
                            ...prev.internalCommands,
                            enabled: enabled,
                            allowedDirectories: allowedDirectories,
                            blockedCommands: blockedCommands,
                            enabledCommands: enabledCommandCategories,
                            enabledTools: enabledTools
                          }
                        } : null);

                        // Also save immediately
                        saveInternalCommandsSettings();
                      }}
                    />
                    <div>
                      <Label className="text-xs">Enable Internal Commands</Label>
                      <p className="text-xs text-muted-foreground">
                        Allow the AI to execute system commands and file operations
                      </p>
                    </div>
                  </div>

                  {internalCommandsEnabled && (
                    <>
                      <div className="space-y-1">
                        <Label className="text-xs">Command Categories</Label>
                        <div className="space-y-1">
                          {Object.entries(enabledCommandCategories).map(([category, enabled]) => (
                            <div key={category} className="flex items-center space-x-2">
                              <ToggleSwitch
                                size="sm"
                                enabled={enabled}
                                onToggle={(enabled) => handleCommandCategoryToggle(category, enabled)}
                              />
                              <Label className="text-xs capitalize">{category.replace(/([A-Z])/g, ' $1').trim()}</Label>
                            </div>
                          ))}
                        </div>
                      </div>

                      <div className="space-y-1">
                        <Label className="text-xs">Allowed Directories</Label>
                        <div className="flex gap-1">
                          <Input
                            value={newDirectory}
                            onChange={(e) => setNewDirectory(e.target.value)}
                            placeholder="Enter directory path"
                            className="h-7 text-xs flex-1"
                          />
                          <Button
                            onClick={async () => {
                              if (typeof window !== 'undefined' && window.electronAPI) {
                                try {
                                  const selectedPath = await window.electronAPI.selectDirectory();
                                  if (selectedPath) {
                                    setNewDirectory(selectedPath);
                                  }
                                } catch (error) {
                                  console.error('Failed to select directory:', error);
                                }
                              }
                            }}
                            size="sm"
                            variant="outline"
                            className="h-7 text-xs"
                          >
                            Browse
                          </Button>
                          <Button
                            onClick={handleAddDirectory}
                            size="sm"
                            className="h-7 w-7 p-0"
                          >
                            <Plus className="h-3 w-3" />
                          </Button>
                        </div>
                        <div className="space-y-1">
                          {allowedDirectories.map((dir) => (
                            <div key={dir} className="flex items-center justify-between p-1 bg-muted rounded">
                              <span className="text-xs">{dir}</span>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleRemoveDirectory(dir)}
                                className="h-6 w-6 p-0 text-destructive"
                              >
                                <Trash2 className="h-3 w-3" />
                              </Button>
                            </div>
                          ))}
                        </div>
                      </div>

                      {/* Available Internal Command Tools Section - positioned after directories */}
                      <div className="space-y-1">
                        <Label className="text-xs">Available Internal Command Tools</Label>
                        <div className="max-h-64 overflow-y-auto space-y-1 border border-border rounded p-2 bg-muted/20">
                          {availableTools.length > 0 ? (
                            availableTools.map((tool) => (
                              <div key={tool.name} className="flex items-center justify-between p-2 bg-background rounded">
                                <div>
                                  <div className="text-xs font-medium">{tool.name}</div>
                                  <div className="text-xs text-muted-foreground">{tool.description}</div>
                                  <div className="text-xs text-muted-foreground">Category: {tool.category}</div>
                                </div>
                                <ToggleSwitch
                                  size="sm"
                                  enabled={enabledTools[tool.name] !== false}
                                  onToggle={(enabled) => {
                                    setEnabledTools(prev => ({
                                      ...prev,
                                      [tool.name]: enabled
                                    }));
                                    saveInternalCommandsSettings();
                                  }}
                                />
                              </div>
                            ))
                          ) : (
                            <div className="text-center py-3 text-muted-foreground text-xs">
                              No internal command tools detected. Make sure internal commands are enabled and configured.
                            </div>
                          )}
                        </div>
                      </div>

                      <div className="space-y-1">
                        <Label className="text-xs">Blocked Commands</Label>
                        <div className="flex gap-1">
                          <Input
                            value={newBlockedCommand}
                            onChange={(e) => setNewBlockedCommand(e.target.value)}
                            placeholder="Enter command to block (e.g., rm, del)"
                            className="h-7 text-xs flex-1"
                          />
                          <Button
                            onClick={handleAddBlockedCommand}
                            size="sm"
                            className="h-7 w-7 p-0"
                          >
                            <Plus className="h-3 w-3" />
                          </Button>
                        </div>
                        <div className="space-y-1">
                          {blockedCommands.map((cmd) => (
                            <div key={cmd} className="flex items-center justify-between p-1 bg-muted rounded">
                              <span className="text-xs">{cmd}</span>
                              <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => handleRemoveBlockedCommand(cmd)}
                                className="h-6 w-6 p-0 text-destructive"
                              >
                                <Trash2 className="h-3 w-3" />
                              </Button>
                            </div>
                          ))}
                        </div>
                      </div>


                    </>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Memory Tab */}
          {activeTab === 'memory' && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">Memory Management</h3>
                <MemoryManagement />
              </div>
            </div>
          )}

          {/* Knowledge Base Tab */}
          {activeTab === 'knowledge-base' && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">Knowledge Base</h3>
                <KnowledgeBaseSettings />
              </div>
            </div>
          )}

          {/* Appearance Tab */}
          {activeTab === 'appearance' && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">Appearance</h3>
                <div className="space-y-2">
                  <div className="space-y-1">
                    <Label className="text-xs">Theme</Label>
                    <ThemeSelector
                      selectedThemeId={selectedThemePreset}
                      onThemeSelect={(theme) => {
                        setSelectedThemePreset(theme.id);
                        setHasChanges(true);

                        // Update formData with theme changes
                        setFormData(prev => prev ? {
                          ...prev,
                          ui: {
                            ...prev.ui,
                            selectedThemePreset: theme.id,
                            colorMode: 'preset',
                            customColors: theme.colors
                          }
                        } : null);

                        // Apply theme change immediately
                        if (typeof window !== 'undefined' && window.electronAPI) {
                          console.log('Applying theme preset change immediately:', theme.colors);
                          window.electronAPI.notifyThemeChange({
                            customColors: theme.colors,
                            useCustomColors: false
                          });
                        }
                      }}
                    />
                  </div>


                  <div className="space-y-2">
                    <Label>Color Mode</Label>
                    <Select
                      value={colorMode}
                      onValueChange={(value: 'preset' | 'custom') => {
                        setColorMode(value);
                        setHasChanges(true);

                        // Determine colors to apply
                        let colorsToApply = customColors;
                        if (value === 'preset') {
                          const preset = themePresets.find(p => p.id === selectedThemePreset);
                          if (preset) {
                            colorsToApply = preset.colors;
                          }
                        }

                        // Update formData with theme changes
                        setFormData(prev => prev ? {
                          ...prev,
                          ui: {
                            ...prev.ui,
                            colorMode: value,
                            selectedThemePreset: selectedThemePreset,
                            customColors: colorsToApply,
                            useCustomColors: value === 'custom'
                          }
                        } : null);

                        // Apply theme change immediately
                        if (typeof window !== 'undefined' && window.electronAPI) {
                          console.log('Applying theme change immediately:', colorsToApply);
                          window.electronAPI.notifyThemeChange({
                            customColors: colorsToApply,
                            useCustomColors: value === 'custom'
                          });
                        }
                      }}
                    >
                      <SelectTrigger className="bg-muted/80 border-input">
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="preset">Use Theme Preset</SelectItem>
                        <SelectItem value="custom">Custom Colors</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  {colorMode === 'custom' && (
                    <div className="space-y-4">
                      <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                          <Label>Background</Label>
                          <ColorPicker
                            value={customColors.background}
                            onChange={(color) => {
                              const newColors = { ...customColors, background: color };
                              setCustomColors(newColors);
                              setHasChanges(true);

                              // Update formData with custom color changes
                              setFormData(prev => prev ? {
                                ...prev,
                                ui: {
                                  ...prev.ui,
                                  customColors: newColors,
                                  colorMode: 'custom',
                                  useCustomColors: true
                                }
                              } : null);

                              // Apply color change immediately
                              if (typeof window !== 'undefined' && window.electronAPI) {
                                console.log('Applying background color change immediately:', newColors);
                                window.electronAPI.notifyThemeChange({
                                  customColors: newColors,
                                  useCustomColors: true
                                });
                              }
                            }}
                          />
                        </div>
                        <div className="space-y-2">
                          <Label>Foreground</Label>
                          <ColorPicker
                            value={customColors.foreground}
                            onChange={(color) => {
                              const newColors = { ...customColors, foreground: color };
                              setCustomColors(newColors);
                              setHasChanges(true);

                              // Update formData with custom color changes
                              setFormData(prev => prev ? {
                                ...prev,
                                ui: {
                                  ...prev.ui,
                                  customColors: newColors,
                                  colorMode: 'custom',
                                  useCustomColors: true
                                }
                              } : null);

                              // Apply color change immediately
                              if (typeof window !== 'undefined' && window.electronAPI) {
                                console.log('Applying foreground color change immediately:', newColors);
                                window.electronAPI.notifyThemeChange({
                                  customColors: newColors,
                                  useCustomColors: true
                                });
                              }
                            }}
                          />
                        </div>
                        <div className="space-y-2">
                          <Label>Primary</Label>
                          <ColorPicker
                            value={customColors.primary}
                            onChange={(color) => {
                              const newColors = { ...customColors, primary: color };
                              setCustomColors(newColors);
                              setHasChanges(true);

                              // Update formData with custom color changes
                              setFormData(prev => prev ? {
                                ...prev,
                                ui: {
                                  ...prev.ui,
                                  customColors: newColors,
                                  colorMode: 'custom',
                                  useCustomColors: true
                                }
                              } : null);

                              // Apply color change immediately
                              if (typeof window !== 'undefined' && window.electronAPI) {
                                console.log('Applying primary color change immediately:', newColors);
                                window.electronAPI.notifyThemeChange({
                                  customColors: newColors,
                                  useCustomColors: true
                                });
                              }
                            }}
                          />
                        </div>
                        <div className="space-y-2">
                          <Label>Secondary</Label>
                          <ColorPicker
                            value={customColors.secondary}
                            onChange={(color) => {
                              const newColors = { ...customColors, secondary: color };
                              setCustomColors(newColors);
                              setHasChanges(true);

                              // Update formData with custom color changes
                              setFormData(prev => prev ? {
                                ...prev,
                                ui: {
                                  ...prev.ui,
                                  customColors: newColors,
                                  colorMode: 'custom',
                                  useCustomColors: true
                                }
                              } : null);

                              // Apply color change immediately
                              if (typeof window !== 'undefined' && window.electronAPI) {
                                console.log('Applying secondary color change immediately:', newColors);
                                window.electronAPI.notifyThemeChange({
                                  customColors: newColors,
                                  useCustomColors: true
                                });
                              }
                            }}
                          />
                        </div>
                      </div>
                      <Button
                        variant="outline"
                        onClick={resetToDefaults}
                        className="flex items-center gap-2"
                      >
                        <RotateCcw className="h-4 w-4" />
                        Reset to Defaults
                      </Button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* General Tab */}
          {activeTab === 'general' && formData?.general && (
            <div className="space-y-3">
              <div>
                <h3 className="text-sm font-medium mb-2">General Settings</h3>
                <div className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <ToggleSwitch
                      size="sm"
                      enabled={formData.general.autoStartWithSystem}
                      onToggle={(enabled) => updateFormData({
                        general: { ...formData.general, autoStartWithSystem: enabled }
                      })}
                    />
                    <div>
                      <Label className="text-xs">Auto-start with system</Label>
                      <p className="text-xs text-muted-foreground">
                        Launch LittleLLM automatically when your computer starts
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <ToggleSwitch
                      size="sm"
                      enabled={formData.general.showNotifications}
                      onToggle={(enabled) => updateFormData({
                        general: { ...formData.general, showNotifications: enabled }
                      })}
                    />
                    <div>
                      <Label className="text-xs">Show notifications</Label>
                      <p className="text-xs text-muted-foreground">
                        Display system notifications for important events
                      </p>
                    </div>
                  </div>

                  <div className="flex items-center space-x-2">
                    <ToggleSwitch
                      size="sm"
                      enabled={formData.general.saveConversationHistory}
                      onToggle={(enabled) => updateFormData({
                        general: { ...formData.general, saveConversationHistory: enabled }
                      })}
                    />
                    <div>
                      <Label className="text-xs">Save conversation history</Label>
                      <p className="text-xs text-muted-foreground">
                        Keep a record of your conversations for future reference
                      </p>
                    </div>
                  </div>

                  <div className="space-y-1">
                    <Label htmlFor="history-length" className="text-xs">Conversation history length</Label>
                    <Input
                      id="history-length"
                      type="number"
                      min="1"
                      max="100"
                      value={formData.general.conversationHistoryLength}
                      className="h-7 text-xs bg-muted/80 border-input focus:bg-muted hover:bg-muted/90 transition-colors"
                      onChange={(e) => updateFormData({
                        general: { ...formData.general, conversationHistoryLength: parseInt(e.target.value) || 10 }
                      })}
                    />
                    <p className="text-xs text-muted-foreground">
                      Number of recent conversations to keep in history
                    </p>
                  </div>

                  <div className="flex items-center space-x-2">
                    <ToggleSwitch
                      size="sm"
                      enabled={formData.general.debugLogging}
                      onToggle={(enabled) => updateFormData({
                        general: { ...formData.general, debugLogging: enabled }
                      })}
                    />
                    <div>
                      <Label className="text-xs">Debug logging</Label>
                      <p className="text-xs text-muted-foreground">
                        Enable detailed logging for troubleshooting
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* MCP JSON Editor Dialog */}
      {showMcpJsonEditor && (
        <Dialog open={showMcpJsonEditor} onOpenChange={setShowMcpJsonEditor}>
          <DialogContent className="max-w-6xl w-[90vw] max-h-[90vh] h-[80vh] flex flex-col">
            <DialogHeader className="flex-shrink-0">
              <DialogTitle>Edit MCP Configuration</DialogTitle>
            </DialogHeader>
            <div className="flex-1 flex flex-col space-y-4 min-h-0">
              <div className="flex-1 min-h-0">
                <Textarea
                  value={mcpJsonContent}
                  onChange={(e) => setMcpJsonContent(e.target.value)}
                  className="font-mono text-sm h-full resize-none overflow-auto"
                  placeholder="MCP configuration JSON..."
                />
              </div>
              <div className="flex justify-end gap-2 flex-shrink-0">
                <Button
                  variant="outline"
                  onClick={() => setShowMcpJsonEditor(false)}
                >
                  Cancel
                </Button>
                <Button onClick={handleSaveMcpJson}>
                  Save JSON
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

'use client';

import {
  Minus,
  Square,
  X,
  Server,
  Volume2,
  VolumeX
} from 'lucide-react';
import { Button } from '../ui/button';
import { ProviderLogo } from '../ui/provider-logo';
import { DEFAULT_PROVIDERS } from '../../services/providers/constants';
import { cn } from '@/lib/utils';
import { settingsService } from '../../services/settingsService';
import { useState, useEffect } from 'react';

interface TopHeaderProps {
  className?: string;
  onHistoryClick?: () => void;
  onConsoleClick?: () => void;
  onAddSplitChatClick?: () => void;
  onMinimize?: () => void;
  onMaximize?: () => void;
  onClose?: () => void;
  selectedProvider?: string;
  onProviderClick?: (element: HTMLElement) => void;
}

export function TopHeader({
  className,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  onHistoryClick,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  onConsoleClick,
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  onAddSplitChatClick,
  onMinimize,
  onMaximize,
  onClose,
  selectedProvider,
  onProviderClick
}: TopHeaderProps) {
  
  // Reserved handlers (currently unused in this header variant)
  // Keeping typed props for future header actions without triggering ESLint unused warnings
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const handleHistoryClick = () => {};
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const handleConsoleClick = () => {};
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  const handleAddSplitChatClick = () => {};

  const handleMinimize = () => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.minimizeWindow();
    }
    onMinimize?.();
  };

  const handleMaximize = () => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.maximizeWindow();
    }
    onMaximize?.();
  };

  const handleClose = () => {
    if (typeof window !== 'undefined' && window.electronAPI) {
      window.electronAPI.closeWindow();
    }
    onClose?.();
  };

  // TTS state
  const [ttsEnabled, setTtsEnabled] = useState(false);

  // Load TTS settings
  useEffect(() => {
    const loadTtsSettings = () => {
      const settings = settingsService.getSettings();
      const enabled = settings.ui?.textToSpeech?.enabled || false;
      console.log('üîä TopHeader: Loading TTS settings, enabled:', enabled);
      setTtsEnabled(enabled);
    };

    loadTtsSettings();

    // Listen for settings changes
    const handleSettingsChange = () => {
      console.log('üîä TopHeader: Settings changed, reloading TTS settings...');
      loadTtsSettings();
    };

    window.addEventListener('settingsSaved', handleSettingsChange);

    return () => {
      window.removeEventListener('settingsSaved', handleSettingsChange);
    };
  }, []);

  const handleTtsToggle = async () => {
    try {
      const settings = settingsService.getSettings();
      const newTtsEnabled = !ttsEnabled;

      const updatedSettings = {
        ...settings,
        ui: {
          ...settings.ui,
          textToSpeech: {
            ...settings.ui?.textToSpeech,
            enabled: newTtsEnabled,
            voice: settings.ui?.textToSpeech?.voice || '',
            rate: settings.ui?.textToSpeech?.rate || 1.0,
            pitch: settings.ui?.textToSpeech?.pitch || 1.0,
            volume: settings.ui?.textToSpeech?.volume || 0.8,
            autoPlay: settings.ui?.textToSpeech?.autoPlay || false,
          }
        }
      };

      await settingsService.updateSettings(updatedSettings);
      setTtsEnabled(newTtsEnabled);

      console.log('üîä TTS toggled:', newTtsEnabled);
    } catch (error) {
      console.error('Failed to toggle TTS:', error);
    }
  };

  const handleProviderClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    onProviderClick?.(event.currentTarget);
  };

  // Get selected provider data
  const allProviders = DEFAULT_PROVIDERS;
  const selectedProviderData = allProviders.find(p => p.id === selectedProvider);

  return (
    <div
      className={cn(
        "relative h-8 draggable-title-bar select-none", // explicit draggable class for preload hook
        className
      )}
      style={{
        // Note: positioning is controlled by the fixed wrapper in ModernChatInterface
        // Using relative here prevents creating an extra stacking context that could
        // interact poorly with overlapping content and Electron drag hit-testing.
        position: 'relative',
        userSelect: 'none',
        WebkitAppRegion: 'drag',
        cursor: 'default'
      } as React.CSSProperties}
    >
      {/* Full header drag background: sits above visual bg, below controls */}
      <div
        aria-hidden="true"
        style={{
          position: 'absolute',
          inset: 0,
          WebkitAppRegion: 'drag',
          pointerEvents: 'auto',
          zIndex: 1,
          background: 'transparent'
        } as React.CSSProperties}
      />

      {/* Visual layer: blur + background, non-interactive */}
      <div
        aria-hidden="true"
        style={{
          position: 'absolute',
          inset: 0,
          zIndex: 0,
          pointerEvents: 'none',
          backdropFilter: 'blur(8px)',
          backgroundColor: 'rgba(17, 24, 39, 0.95)'
        } as React.CSSProperties}
      />

      {/* Content row: header surface is draggable; interactive elements inside are no-drag */}
      <div
        className="relative h-8 px-4 flex items-center justify-between"
        style={{ zIndex: 4, WebkitAppRegion: 'drag', pointerEvents: 'auto' } as React.CSSProperties}
      >
        {/* Left side - Provider selector */}
        <div className="flex items-center" style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties}>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleProviderClick}
            className="h-5 px-1.5 text-gray-400 hover:text-white hover:bg-gray-800/50 transition-colors"
            title="Select Provider"
          >
            {selectedProviderData ? (
              <div className="flex items-center gap-1">
                <div className="w-3 h-3">
                  <ProviderLogo provider={selectedProviderData} size={12} />
                </div>
                <span className="text-xs">{selectedProviderData.name}</span>
              </div>
            ) : (
              <div className="flex items-center gap-1">
                <Server style={{ width: '12px', height: '12px' }} />
                <span className="text-xs">Select Provider</span>
              </div>
            )}
          </Button>
        </div>

        {/* Center - TTS Toggle */}
        <div className="flex items-center" style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties}>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleTtsToggle}
            className={cn(
              "h-6 w-6 p-0 transition-colors",
              ttsEnabled
                ? "text-blue-400 hover:text-blue-300 hover:bg-blue-600/20"
                : "text-gray-400 hover:text-white hover:bg-gray-800/50"
            )}
            title={ttsEnabled ? "Disable Text to Speech" : "Enable Text to Speech"}
          >
            {ttsEnabled ? (
              <Volume2 className="w-3 h-3" />
            ) : (
              <VolumeX className="w-3 h-3" />
            )}
          </Button>
        </div>

        {/* Right side - Window controls */}
        <div className="flex items-center gap-1" style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties}>
          <Button
            variant="ghost"
            size="sm"
            onClick={handleMinimize}
            className="h-6 w-6 p-0 text-gray-400 hover:text-white hover:bg-gray-800/50 transition-colors"
            title="Minimize"
          >
            <Minus className="w-3 h-3" />
          </Button>

          <Button
            variant="ghost"
            size="sm"
            onClick={handleMaximize}
            className="h-6 w-6 p-0 text-gray-400 hover:text-white hover:bg-gray-800/50 transition-colors"
            title="Maximize"
          >
            <Square className="w-3 h-3" />
          </Button>

          <Button
            variant="ghost"
            size="sm"
            onClick={handleClose}
            className="h-6 w-6 p-0 text-gray-400 hover:text-white hover:bg-red-600/50 transition-colors"
            title="Close"
          >
            <X className="w-3 h-3" />
          </Button>
        </div>
      </div>
    </div>
  );
}

'use client';

import React, { useState, useRef, useEffect } from 'react';



interface ColorPickerProps {
  value: string;
  onChange: (color: string) => void;
  label?: string;
  className?: string;
  supportAlpha?: boolean; // Support transparency/alpha values
}

export function ColorPicker({ value, onChange, label, className, supportAlpha = false }: ColorPickerProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [inputValue, setInputValue] = useState(value);
  const colorInputRef = useRef<HTMLInputElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    setInputValue(value);
  }, [value]);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
        setIsOpen(false);
      }
    };

    if (isOpen) {
      document.addEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isOpen]);

  const handleColorChange = (newColor: string) => {
    setInputValue(newColor);
    onChange(newColor);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newValue = e.target.value;
    setInputValue(newValue);
    
    // Validate hex color format or rgba format
    if (/^#[0-9A-Fa-f]{6}$/.test(newValue) || /^rgba?\(\d+,\s*\d+,\s*\d+(?:,\s*[\d.]+)?\)$/.test(newValue)) {
      onChange(newValue);
    }
  };

  const handleInputBlur = () => {
    // If input is invalid, revert to current value
    if (!/^#[0-9A-Fa-f]{6}$/.test(inputValue) && !/^rgba?\(\d+,\s*\d+,\s*\d+(?:,\s*[\d.]+)?\)$/.test(inputValue)) {
      setInputValue(value);
    }
  };

  const handleNativeColorChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const newColor = e.target.value;
    console.log('üé® Native color picker changed to:', newColor);
    handleColorChange(newColor);
    setIsOpen(false);
  };

  const presetColors = [
    '#569cd6', '#4fc1ff', '#e04539', '#f44747', '#dcdcaa', '#ce9178',
    '#9cdcfe', '#c586c0', '#4ec9b0', '#b5cea8', '#d7ba7d', '#808080',
    '#181829', '#1d1d33', '#211f32', '#3b3b68', '#d4d4d4', '#ffffff'
  ];

  const transparentColors = supportAlpha ? [
    'rgba(86, 156, 214, 0.8)', 'rgba(79, 193, 255, 0.8)', 'rgba(224, 69, 57, 0.8)',
    'rgba(244, 71, 71, 0.8)', 'rgba(220, 220, 170, 0.8)', 'rgba(206, 145, 120, 0.8)',
    'rgba(255, 255, 255, 0.5)', 'rgba(0, 0, 0, 0.5)', 'rgba(255, 0, 0, 0.3)',
    'rgba(0, 255, 0, 0.3)', 'rgba(0, 0, 255, 0.3)', 'rgba(255, 255, 0, 0.3)'
  ] : [];

  return (
    <div
      className={`space-y-2 relative ${className || ''}`}
      ref={containerRef}
    >
      {label && (
        <label
          className="text-sm font-medium leading-none"
          style={{
            color: 'var(--card-foreground)',
            display: 'block',
            marginBottom: '8px'
          }}
        >
          {label}
        </label>
      )}

      <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
        {/* Color Preview Button - Opens system color picker */}
        <button
          type="button"
          onClick={() => {
            // Open system color picker
            if (colorInputRef.current) {
              colorInputRef.current.click();
            }
          }}
          title="Click to open system color picker"
          style={{
            width: '48px',
            height: '40px',
            padding: '0',
            margin: '0',
            backgroundColor: value,
            border: '2px solid var(--border)',
            borderRadius: '6px',
            cursor: 'pointer',
            outline: 'none',
            transition: 'all 0.2s ease',
            boxShadow: 'none'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.transform = 'scale(1.05)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.transform = 'scale(1)';
          }}
        >
          <span style={{ position: 'absolute', left: '-9999px' }}>Open system color picker</span>
        </button>

        {/* Dropdown Toggle Button */}
        <button
          type="button"
          onClick={() => setIsOpen(!isOpen)}
          title="Click to open preset colors"
          style={{
            width: '40px',
            height: '40px',
            padding: '0',
            margin: '0',
            backgroundColor: 'var(--muted)',
            border: '2px solid var(--border)',
            borderRadius: '6px',
            cursor: 'pointer',
            outline: 'none',
            transition: 'all 0.2s ease',
            boxShadow: 'none',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            color: 'var(--foreground)'
          }}
          onMouseEnter={(e) => {
            e.currentTarget.style.backgroundColor = 'var(--accent)';
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.backgroundColor = 'var(--muted)';
          }}
        >
          ‚ñº
        </button>

        {/* Hex Input - Custom styled */}
        <input
          type="text"
          value={inputValue}
          onChange={handleInputChange}
          placeholder={supportAlpha ? "#000000 or rgba(0,0,0,0.5)" : "#000000"}
          maxLength={supportAlpha ? 25 : 7}
          style={{
            flex: '1',
            height: '40px',
            padding: '8px 12px',
            backgroundColor: 'var(--muted)',
            color: 'var(--foreground)',
            border: '2px solid var(--border)',
            borderRadius: '6px',
            fontFamily: 'monospace',
            fontSize: '14px',
            outline: 'none',
            transition: 'all 0.2s ease'
          }}
          onFocus={(e) => {
            e.currentTarget.style.borderColor = 'hsl(var(--ring))';
            e.currentTarget.style.boxShadow = '0 0 0 2px hsl(var(--ring) / 0.2)';
          }}
          onBlur={(e) => {
            e.currentTarget.style.borderColor = 'hsl(var(--border))';
            e.currentTarget.style.boxShadow = 'none';
            handleInputBlur();
          }}
        />

        {/* Native color input for system color picker */}
        <input
          ref={colorInputRef}
          type="color"
          value={value}
          onChange={(e) => handleColorChange(e.target.value)}
          style={{ position: 'absolute', left: '-9999px', opacity: 0, pointerEvents: 'none' }}
        />
      </div>

      {/* Hidden native color input for system color picker */}
      <input
        ref={colorInputRef}
