            )}

            {/* Native Color Picker Button */}
            <div>
              <button
                type="button"
                onClick={() => {
                  console.log('üé® System color picker button clicked');
                  if (colorInputRef.current) {
                    colorInputRef.current.click();
                  }
                }}
                style={{
