                `}
                style={{
                  backgroundColor: theme.colors.card,
                  borderColor: isSelected ? theme.colors.primary : theme.colors.border,
                }}
              >
                {/* Selected Indicator */}
                {isSelected && (
                  <div
                    className="absolute top-2 right-2 w-6 h-6 rounded-full flex items-center justify-center"
                    style={{ backgroundColor: theme.colors.primary }}
                  >
                    <Check size={14} style={{ color: theme.colors.primaryForeground }} />
                  </div>
                )}

                {/* Theme Icon and Name */}
                <div className="flex items-center gap-3 mb-3">
                  <span className="text-2xl">{theme.icon}</span>
                  <div>
                    <h3 
                      className="font-medium text-sm"
                      style={{ color: theme.colors.cardForeground }}
                    >
                      {theme.name}
                    </h3>
                    <p 
                      className="text-xs opacity-75"
                      style={{ color: theme.colors.mutedForeground }}
                    >
                      {theme.description}
                    </p>
                  </div>
                </div>

                {/* Color Palette Preview */}
                <div className="flex gap-1 mb-3">
                  <div
                    className="w-4 h-4 rounded-sm"
                    style={{ backgroundColor: theme.colors.background }}
                    title="Background"
                  />
                  <div
                    className="w-4 h-4 rounded-sm"
                    style={{ backgroundColor: theme.colors.primary }}
                    title="Primary"
                  />
                  <div
                    className="w-4 h-4 rounded-sm"
                    style={{ backgroundColor: theme.colors.secondary }}
                    title="Secondary"
                  />
                  <div
                    className="w-4 h-4 rounded-sm"
                    style={{ backgroundColor: theme.colors.accent }}
                    title="Accent"
                  />
                  <div
                    className="w-4 h-4 rounded-sm"
                    style={{ backgroundColor: theme.colors.muted }}
                    title="Muted"
                  />
                </div>

                {/* Mini UI Preview */}
                <div 
                  className="p-2 rounded border"
                  style={{ 
                    backgroundColor: theme.colors.background,
                    borderColor: theme.colors.border 
                  }}
                >
                  {/* Mini Button */}
                  <div
                    className="inline-block px-2 py-1 rounded text-xs mb-1"
                    style={{
                      backgroundColor: theme.colors.primary,
                      color: theme.colors.primaryForeground,
                    }}
                  >
                    Button
                  </div>
                  
                  {/* Mini Text */}
                  <div 
                    className="text-xs"
                    style={{ color: theme.colors.foreground }}
                  >
                    Sample text
                  </div>
                </div>

                {/* Hover Effect Overlay */}
                {isHovered && !isSelected && (
                  <div 
                    className="absolute inset-0 rounded-lg opacity-10"
                    style={{ backgroundColor: theme.colors.primary }}
                  />
                )}
              </div>
            </div>
          );
        })}
      </div>

      {/* Custom Theme Option */}
      <div className="pt-4 border-t border-border">
        <div className="text-sm text-muted-foreground">
          Want more control? You can still create a custom theme by selecting "Custom Colors" below.
        </div>
      </div>
    </div>
  );
}

/**
 * SINGLE POINT OF COLOR REFERENCE
 * All colors in the entire application are defined here
 * This is the ONLY place where colors should be defined
 */

export interface ColorDefinition {
  hex: string;
  hsl: string;
  description: string;
}

export interface ColorScheme {
  background: ColorDefinition;
  foreground: ColorDefinition;
  card: ColorDefinition;
  cardForeground: ColorDefinition;
  primary: ColorDefinition;
  primaryForeground: ColorDefinition;
  secondary: ColorDefinition;
  secondaryForeground: ColorDefinition;
  accent: ColorDefinition;
  accentForeground: ColorDefinition;
  muted: ColorDefinition;
  mutedForeground: ColorDefinition;
  border: ColorDefinition;
  input: ColorDefinition;
  ring: ColorDefinition;
  destructive: ColorDefinition;
  destructiveForeground: ColorDefinition;
  systemText: ColorDefinition;
}

/**
 * Convert hex to HSL format for CSS variables
 */
function hexToHsl(hex: string): string {
  // Remove # if present
  hex = hex.replace('#', '');
  
  // Parse RGB
  const r = parseInt(hex.substring(0, 2), 16) / 255;
  const g = parseInt(hex.substring(2, 4), 16) / 255;
  const b = parseInt(hex.substring(4, 6), 16) / 255;

  const max = Math.max(r, g, b);
  const min = Math.min(r, g, b);
  let h = 0;
  let s = 0;
  const l = (max + min) / 2;

  if (max !== min) {
    const d = max - min;
    s = l > 0.5 ? d / (2 - max - min) : d / (max + min);
    
    switch (max) {
      case r: h = (g - b) / d + (g < b ? 6 : 0); break;
      case g: h = (b - r) / d + 2; break;
      case b: h = (r - g) / d + 4; break;
    }
    h /= 6;
  }

  return `${Math.round(h * 360)} ${Math.round(s * 100)}% ${Math.round(l * 100)}%`;
}

/**
 * Create color definition from hex
 */
function createColor(hex: string, description: string): ColorDefinition {
  return {
    hex,
    hsl: hexToHsl(hex),
    description
  };
}

/**
 * DEFAULT COLOR SCHEME
 * These are the default values for "Reset to Defaults"
 * Users can change these through Settings Appearance
 * NO HARDCODED VALUES are used anywhere else
 */
export const DEFAULT_COLORS: ColorScheme = {
  background: createColor('#181829', 'Main application background'),
  foreground: createColor('#d4d4d4', 'Main text color'),
  card: createColor('#211f32', 'Panel and card backgrounds'),
  cardForeground: createColor('#ffffff', 'Panel and card text'),
  primary: createColor('#569cd6', 'Primary buttons and links'),
  primaryForeground: createColor('#ffffff', 'Primary button text'),
  secondary: createColor('#4fc1ff', 'Secondary elements'),
  secondaryForeground: createColor('#adadad', 'Secondary text'),
  accent: createColor('#569cd6', 'Accent and hover states'),
  accentForeground: createColor('#ffffff', 'Accent text'),
  muted: createColor('#2d2a41', 'Subtle backgrounds'),
  mutedForeground: createColor('#9ca3af', 'Subtle text'),
  border: createColor('#3b3b68', 'Borders and dividers'),
  input: createColor('#949494', 'Input field backgrounds'),
  ring: createColor('#569cd6', 'Focus outlines'),
  destructive: createColor('#f44747', 'Error and delete actions'),
  destructiveForeground: createColor('#ffffff', 'Error text'),
  systemText: createColor('#e0e0e0', 'System UI text (labels, buttons, etc.)')
};

/**
 * Get CSS variables string for injection
 */
export function getCSSVariables(): string {
  return Object.entries(DEFAULT_COLORS)
    .map(([key, color]) => {
      const cssVar = `--${key.replace(/([A-Z])/g, '-$1').toLowerCase()}`;
      return `${cssVar}: ${color.hsl};`;
    })
    .join('\n    ');
}

/**
 * Get default hex colors for settings interface
 * These are used for "Reset to Defaults" functionality
 */
export function getDefaultHexColors() {
  return {
    background: DEFAULT_COLORS.background.hex,
    foreground: DEFAULT_COLORS.foreground.hex,
    card: DEFAULT_COLORS.card.hex,
    cardForeground: DEFAULT_COLORS.cardForeground.hex,
    primary: DEFAULT_COLORS.primary.hex,
    primaryForeground: DEFAULT_COLORS.primaryForeground.hex,
    secondary: DEFAULT_COLORS.secondary.hex,
    secondaryForeground: DEFAULT_COLORS.secondaryForeground.hex,
    accent: DEFAULT_COLORS.accent.hex,
    accentForeground: DEFAULT_COLORS.accentForeground.hex,
    muted: DEFAULT_COLORS.muted.hex,
    mutedForeground: DEFAULT_COLORS.mutedForeground.hex,
    border: DEFAULT_COLORS.border.hex,
    input: DEFAULT_COLORS.input.hex,
    ring: DEFAULT_COLORS.ring.hex,
    destructive: DEFAULT_COLORS.destructive.hex,
    destructiveForeground: DEFAULT_COLORS.destructiveForeground.hex,
    systemText: DEFAULT_COLORS.systemText.hex,
  };
}

/**
 * Apply colors to DOM and Electron window background
 * REQUIRES custom colors - no fallback to defaults
 */
export function applyColorsToDOM(customColors: Record<string, string>) {
  if (!customColors) {
    console.error('‚ùå CRITICAL: applyColorsToDOM called without colors - cannot apply theme');
    return;
  }

  const root = document.documentElement;
  const defaults = getDefaultHexColors();
  const colorsToApply = customColors;

  // Ensure all required colors exist (fallback for missing systemText in old settings)
  const completeColors = {
    ...defaults,
    ...colorsToApply,
    systemText: colorsToApply.systemText || defaults.systemText
  };

  Object.entries(completeColors).forEach(([key, hex]) => {
    const cssVar = `--${key.replace(/([A-Z])/g, '-$1').toLowerCase()}`;
    const hsl = hexToHsl(hex as string);
    root.style.setProperty(cssVar, hsl, 'important');
  });

  // Also update the Electron window background color
  if (typeof window !== 'undefined' && window.electronAPI && completeColors.background) {
    window.electronAPI.setWindowBackgroundColor(completeColors.background).catch((error: Error) => {
      console.error('Failed to set window background color:', error);
    });
  }
}

'use client';

import React, { createContext, useContext, useEffect, useState } from 'react';
import { settingsService } from '../services/settingsService';
import { themeSyncService } from '../services/themeSyncService';
import { getDefaultHexColors, applyColorsToDOM } from '../config/colors';
import { THEME_PRESETS, getThemePreset, type ThemePreset } from '../config/themes';
import type { ColorSettings } from '../types/settings';

export interface Theme {
  id: string;
  name: string;
  icon: string;
  colors: {
    background: string;
    foreground: string;
    card: string;
    cardForeground: string;
    primary: string;
    primaryForeground: string;
    secondary: string;
    secondaryForeground: string;
    accent: string;
    accentForeground: string;
    muted: string;
    mutedForeground: string;
    border: string;
    input: string;
    ring: string;
    destructive: string;
    destructiveForeground: string;
  };
}

// Single VS Code theme - the only theme we need
export const themes: Theme[] = [
  {
    id: 'vscode',
    name: 'VS Code',
    icon: 'üíª',
    colors: {
      background: '#13131f',
      foreground: '#d4d4d4',
      card: '#1d1d33',
      cardForeground: '#d4d4d4',
      primary: '#569cd6',
      primaryForeground: '#ffffff',
      secondary: '#4fc1ff',
      secondaryForeground: '#13131f',
      accent: '#e04539',
      accentForeground: '#ffffff',
      muted: '#1e1b2e',
      mutedForeground: '#9ca3af',
      border: '#3b3b68',
      input: '#1e1b2e',
      ring: '#569cd6',
      destructive: '#f44747',
      destructiveForeground: '#ffffff',
    },
  },
];

interface ThemeContextType {
  theme: Theme;
  setTheme: (theme: Theme) => void;
  themes: Theme[];
  customColors: ColorSettings;
  setCustomColors: (colors: ColorSettings) => void;
  useCustomColors: boolean;
  setUseCustomColors: (use: boolean) => void;
  resetToDefaults: () => void;
  // Theme preset functionality
  selectedThemePreset: string;
  setSelectedThemePreset: (presetId: string) => void;
  colorMode: 'preset' | 'custom';
  setColorMode: (mode: 'preset' | 'custom') => void;
  themePresets: ThemePreset[];
  getCurrentColors: () => ColorSettings;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);


// Hex to HSL conversion is now handled in colors.ts

// Theme application is now handled by applyColorsToDOM from colors.ts

// NO DEFAULT COLORS - will be loaded from settings only
export function ThemeProvider({ children }: { children: React.ReactNode }) {
  const [themeState, setThemeState] = useState<Theme>(themes[0]);
  const [customColors, setCustomColors] = useState<ColorSettings>({} as ColorSettings);
  const [useCustomColors, setUseCustomColors] = useState(false);
  const [selectedThemePreset, setSelectedThemePreset] = useState('');
  const [colorMode, setColorMode] = useState<'preset' | 'custom'>('preset');

  // SIMPLIFIED: Load theme ONCE from main process at startup
  useEffect(() => {
    const loadTheme = async () => {
      try {
        if (typeof window === 'undefined') return;

        // Loading theme from main process

        // Get theme directly from main process
        if (window.electronAPI?.getCurrentTheme) {
          const currentTheme = await window.electronAPI.getCurrentTheme();
          // Received theme from main process

          if (currentTheme?.customColors) {
            // Apply theme immediately
            setCustomColors(currentTheme.customColors as unknown as ColorSettings);
            setUseCustomColors(currentTheme.useCustomColors);
            applyColorsToDOM(currentTheme.customColors as unknown as ColorSettings);
            // Theme applied successfully
            return;
          }
        }

        // NO FALLBACK - app requires valid theme settings
        console.error('‚ùå CRITICAL: No theme data from main process - app requires valid theme settings');
      } catch (error) {
        console.error('‚ùå CRITICAL: Error loading theme:', error);
        console.error('‚ùå App requires valid theme settings - no fallback to defaults');
      }
    };

    loadTheme();
  }, []);

  // Listen for theme changes from other windows
  useEffect(() => {
    if (typeof window === 'undefined' || !window.electronAPI) return;

    const handleThemeChange = (themeData: { customColors: unknown; useCustomColors: boolean }) => {
      // Received theme change
      const receivedColors = themeData.customColors as ColorSettings;

      // Update local state
      setCustomColors(receivedColors);
      setUseCustomColors(themeData.useCustomColors);

      // Apply the colors directly - the main process has already resolved preset vs custom
      // The customColors field contains the actual colors to apply (either preset or custom)
      if (receivedColors && Object.keys(receivedColors).length > 0) {
        applyColorsToDOM(receivedColors);
        // Applied theme change to DOM
      } else {
        // NO FALLBACK - log error but don't apply defaults
        console.error('‚ùå CRITICAL: No colors received in theme change - cannot apply theme');
      }
    };

    // Listen for theme changes and store the wrapped callback
    const wrappedCallback = window.electronAPI.onThemeChange(handleThemeChange);

    return () => {
      // Cleanup listener if available
      if (window.electronAPI.removeThemeChangeListener && wrappedCallback) {
        window.electronAPI.removeThemeChangeListener(wrappedCallback);
      }
    };
  }, [themeState]);

  const setTheme = (newTheme: Theme) => {
    if (newTheme.id === 'vscode') {
      setThemeState(newTheme);
      const colorsToApply = getCurrentColors();
      applyColorsToDOM(colorsToApply);
    }
  };

  const handleSetSelectedThemePreset = (presetId: string, shouldSave = true) => {
    // Setting theme preset
    setSelectedThemePreset(presetId);

    // Apply preset colors immediately - NO FALLBACK
    const preset = getThemePreset(presetId);
    if (!preset) {
      console.error(`‚ùå CRITICAL: Theme preset '${presetId}' not found - cannot apply theme`);
      return;
    }
    setCustomColors(preset.colors);
    applyColorsToDOM(preset.colors);

    if (shouldSave) {
      // Save to settings immediately
      const currentSettings = settingsService.getSettings();
      settingsService.updateSettings({
        ui: {
          ...currentSettings.ui,
          selectedThemePreset: presetId,
          colorMode: 'preset'
        }
      });

      // Notify main process immediately
      const themeData = {
        customColors: preset.colors,
        useCustomColors: false
      };

      if (window.electronAPI?.notifyThemeChange) {
        window.electronAPI.notifyThemeChange(themeData);
        // Theme change notified to main process
      }
    }
  };

  const handleSetColorMode = (mode: 'preset' | 'custom', shouldSave = true) => {
    setColorMode(mode);

    // Apply appropriate colors immediately
    const colorsToApply = getCurrentColors(mode);
    applyColorsToDOM(colorsToApply);

    if (shouldSave) {
      try {
        const currentSettings = settingsService.getSettings();
        settingsService.updateSettings({
          ui: { ...currentSettings.ui, colorMode: mode }
        });
      } catch (error) {
        console.error('Error saving color mode:', error);
      }

      // Broadcast theme change
      const themeData = {
        customColors: colorsToApply,
        useCustomColors: mode === 'custom'
      };
      themeSyncService.broadcastThemeChange(themeData);

      // Store current theme in main process for new windows
      if (typeof window !== 'undefined' && window.electronAPI) {
        window.electronAPI.notifyThemeChange(themeData);
      }
    }
  };

  const getCurrentColors = (mode?: 'preset' | 'custom'): ColorSettings => {
    const currentMode = mode || colorMode;
    if (currentMode === 'preset') {
      const preset = getThemePreset(selectedThemePreset);
      if (!preset) {
        console.error(`‚ùå CRITICAL: Theme preset '${selectedThemePreset}' not found - cannot get colors`);
        return {} as ColorSettings;
      }
      return preset.colors;
    } else {
      if (!useCustomColors || !customColors) {
        console.error('‚ùå CRITICAL: Custom colors mode but no custom colors available');
        return {} as ColorSettings;
      }
      return customColors;
    }
  };

  const handleSetCustomColors = (colors: ColorSettings, shouldSave = true) => {
    setCustomColors(colors);

    // Apply theme immediately
    applyColorsToDOM(colors);

    // Only save and notify if requested (settings overlay will handle this)
    if (shouldSave) {
      // Save to settings
      try {
        const currentSettings = settingsService.getSettings();
        settingsService.updateSettings({
          ui: { ...currentSettings.ui, customColors: colors }
        });
      } catch (error) {
        console.error('Error saving custom colors:', error);
      }

      // Broadcast theme change to all windows via sync service
      const themeData = {
        customColors: colors,
        useCustomColors: useCustomColors
      };
      themeSyncService.broadcastThemeChange(themeData);

      // Store current theme in main process for new windows
      if (typeof window !== 'undefined' && window.electronAPI) {
        window.electronAPI.notifyThemeChange(themeData);
      }
    }
  };

  const handleSetUseCustomColors = (use: boolean, shouldSave = true) => {
    setUseCustomColors(use);

    // Apply theme immediately
    const colorsToApply = use ? customColors : getDefaultHexColors();
    applyColorsToDOM(colorsToApply);

    // Only save and notify if requested (settings overlay will handle this)
    if (shouldSave) {
      // Save to settings
      try {
        const currentSettings = settingsService.getSettings();
        settingsService.updateSettings({
          ui: { ...currentSettings.ui, useCustomColors: use }
        });
      } catch (error) {
        console.error('Error saving useCustomColors setting:', error);
      }

      // Broadcast theme change to all windows via sync service
      const themeData = {
        customColors: customColors,
        useCustomColors: use
      };
      themeSyncService.broadcastThemeChange(themeData);

      // Store current theme in main process for new windows
      if (typeof window !== 'undefined' && window.electronAPI) {
        window.electronAPI.notifyThemeChange(themeData);
      }
    }
  };

  const resetToDefaults = () => {
    console.error('‚ùå resetToDefaults is disabled - no default themes available');
    console.log('üí° Use theme presets instead of resetting to defaults');
  };

  const value = {
    theme: themeState,
    setTheme,
    themes,
    customColors,
    setCustomColors: handleSetCustomColors,
    useCustomColors,
    setUseCustomColors: handleSetUseCustomColors,
    resetToDefaults,
    selectedThemePreset,
    setSelectedThemePreset: handleSetSelectedThemePreset,
    colorMode,
    setColorMode: handleSetColorMode,
    themePresets: THEME_PRESETS,
    getCurrentColors
  };

  return (
    <ThemeContext.Provider value={value}>
      {children}
    </ThemeContext.Provider>
  );
}

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    // NO DEFAULTS - even during static generation
    throw new Error('useTheme must be used within a ThemeProvider - no fallback themes available');
  }
  return context;
};

'use client';

import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';

interface TransparencyConfig {
  opacity: number;
  vibrancyType: string;
  blurRadius: number;
  saturation: number;
}

interface PlatformCapabilities {
  supportsTransparency: boolean;
  supportsVibrancy: boolean;
  supportsBackdropFilter: boolean;
  platform: string;
}

interface TransparencyContextType {
  isTransparencyEnabled: boolean;
  config: TransparencyConfig | null;
  capabilities: PlatformCapabilities | null;
  enableTransparency: () => Promise<void>;
  disableTransparency: () => Promise<void>;
  updateOpacity: (opacity: number) => Promise<void>;
  updateVibrancyType: (type: string) => Promise<void>;
  isSupported: boolean;
  isInitialized: boolean;
}

const TransparencyContext = createContext<TransparencyContextType | undefined>(undefined);

interface TransparencyProviderProps {
  children: ReactNode;
}

export function TransparencyProvider({ children }: TransparencyProviderProps) {
  const [isTransparencyEnabled, setIsTransparencyEnabled] = useState(true);
  const [config, setConfig] = useState<TransparencyConfig>({
    opacity: 0.85,
    vibrancyType: 'under-window',
    blurRadius: 20,
    saturation: 180,
  });
  const [capabilities, setCapabilities] = useState<PlatformCapabilities | null>(null);
  const [isInitialized, setIsInitialized] = useState(false);

  // Detect platform capabilities
  useEffect(() => {
    const detectCapabilities = () => {
      const isElectron = typeof window !== 'undefined' && (window.electronAPI || window.require);
      const supportsBackdropFilter = CSS.supports('backdrop-filter', 'blur(1px)') ||
                                   CSS.supports('-webkit-backdrop-filter', 'blur(1px)');

      const platformCapabilities: PlatformCapabilities = {
        supportsTransparency: true, // Force enable for testing
        supportsVibrancy: !!isElectron,
        supportsBackdropFilter: supportsBackdropFilter, // Use actual detection
        platform: typeof navigator !== 'undefined' ? navigator.platform : 'unknown',
      };

      setCapabilities(platformCapabilities);
      setIsInitialized(true);
      console.log('Platform capabilities:', platformCapabilities);
    };

    detectCapabilities();
  }, []);

  const enableTransparency = async () => {
    setIsTransparencyEnabled(true);
    // Notify Electron main process if available
    if (window.electronAPI?.setTransparency) {
      await window.electronAPI.setTransparency(true);
    }
  };

  const disableTransparency = async () => {
    setIsTransparencyEnabled(false);
    // Notify Electron main process if available
    if (window.electronAPI?.setTransparency) {
      await window.electronAPI.setTransparency(false);
    }
  };

  const updateOpacity = async (opacity: number) => {
    setConfig(prev => prev ? { ...prev, opacity } : {
      opacity,
      vibrancyType: 'dark',
      blurRadius: 10,
      saturation: 1.0
    });
    // Notify Electron main process if available
    if (window.electronAPI?.setOpacity) {
      await window.electronAPI.setOpacity(opacity);
    }
  };

  const updateVibrancyType = async (type: string) => {
    setConfig(prev => prev ? { ...prev, vibrancyType: type } : {
      opacity: 0.9,
      vibrancyType: type,
      blurRadius: 10,
      saturation: 1.0
    });
    // Notify Electron main process if available
    if (window.electronAPI?.setVibrancyType) {
      await window.electronAPI.setVibrancyType(type);
    }
  };

  const isSupported = capabilities?.supportsTransparency && capabilities?.supportsBackdropFilter;

  return (
    <TransparencyContext.Provider
      value={{
        isTransparencyEnabled,
        config,
        capabilities,
        enableTransparency,
        disableTransparency,
        updateOpacity,
        updateVibrancyType,
        isSupported: isSupported || false,
        isInitialized,
      }}
    >
      {children}
    </TransparencyContext.Provider>
  );
}

export function useTransparency() {
  const context = useContext(TransparencyContext);
  if (context === undefined) {
    // During static generation, return default values
    if (typeof window === 'undefined') {
      return {
        isTransparencyEnabled: true,
        setIsTransparencyEnabled: () => {},
        config: {
          opacity: 0.85,
          vibrancyType: 'under-window' as const,
          blurRadius: 20,
          saturation: 180,
        },
        setConfig: () => {},
        capabilities: null,
        isSupported: false,
        isInitialized: false
      };
    }
    throw new Error('useTransparency must be used within a TransparencyProvider');
  }
  return context;
}

export function useTransparencyAwareStyles() {
  const { isTransparencyEnabled, isSupported } = useTransparency();
  
  const getTransparencyClass = (baseClass: string, transparentClass: string) => {
    return isTransparencyEnabled && isSupported ? transparentClass : baseClass;
  };

  const getFloatingClass = (level: 1 | 2 | 3 | 4 | 'modal' = 1) => {
    if (!isTransparencyEnabled || !isSupported) return '';
    return `floating-layer-${level}`;
  };

  return {
    isTransparent: isTransparencyEnabled && isSupported,
    getTransparencyClass,
    getFloatingClass,
    textClass: isTransparencyEnabled && isSupported ? 'transparent-text' : '',
    mutedTextClass: isTransparencyEnabled && isSupported ? 'transparent-text-muted' : '',
    accentTextClass: isTransparencyEnabled && isSupported ? 'transparent-text-accent' : ''
  };
}

/**
 * Electron Main Process Internal Command Handler
 * Handles internal commands in the main process where Node.js APIs are available
 */

import { spawn, ChildProcess, exec } from 'child_process';
import { promisify } from 'util';
import fs from 'fs/promises';
import path from 'path';
import os from 'os';
import {
  CommandResult,
  InternalCommandConfig,
  FileInfo
} from '../types/internalCommands';

// Electron-specific ProcessSession with proper ChildProcess type
interface ElectronProcessSession {
  pid: number;
  command: string;
  shell: string;
  startTime: Date;
  lastActivity: Date;
  status: 'running' | 'finished' | 'error';
  blocked: boolean;
  process?: ChildProcess; // Proper Node.js ChildProcess type
}

export class ElectronInternalCommandHandler {
  private config: InternalCommandConfig | null = null;
  private commandHistory: Array<{ timestamp: number; command: string; success: boolean }> = [];
  private readonly MAX_COMMANDS_PER_MINUTE = 60;
  private readonly MAX_HISTORY_SIZE = 1000;
  private sessions: Map<number, ElectronProcessSession> = new Map();
  private nextPid = 1000;

  constructor() {
    this.setupIpcHandlers();
  }

  /**
   * Set up IPC handlers for internal commands
   * Note: IPC handlers are now registered in main.ts setupIPC() to avoid duplicates
   */
  private setupIpcHandlers(): void {
    // IPC handlers moved to main.ts setupIPC() function to prevent duplicate registration
    console.log('üîß Internal command handler initialized (IPC handlers in main.ts)');
  }

  /**
   * Public methods for main process integration
   */
  public async setConfig(config: InternalCommandConfig): Promise<boolean> {
    console.log(`üîß ElectronInternalCommandHandler.setConfig() called with enabled: ${config.enabled}`);
    this.config = config;
    console.log(`üîß Config updated - enabled: ${this.config?.enabled}, has enabledCommands: ${!!this.config?.enabledCommands}`);
    return true;
  }

  public async getTools(): Promise<Array<{name: string; description: string; category: string; inputSchema: unknown}>> {
    const tools = this.getAvailableTools();
    console.log(`üîß InternalCommandHandler.getTools() called - returning ${tools.length} tools`);
    console.log(`üîß Config enabled: ${this.config?.enabled}, has config: ${!!this.config}`);
    if (tools.length > 0) {
      console.log(`üîß Sample tools:`, tools.slice(0, 3).map(t => ({ name: t.name, category: t.category })));
    }
    return tools;
  }

  public async execute(toolName: string, args: unknown): Promise<CommandResult> {
    return this.executeCommand(toolName, args);
  }

  public async isEnabled(): Promise<boolean> {
    return this.config?.enabled || false;
  }

  /**
   * Execute an internal command
   */
  private async executeCommand(toolName: string, args: unknown): Promise<CommandResult> {
    let success = false;

    try {
      // Check if service is enabled
      if (!this.config?.enabled) {
        throw new Error('Internal commands are disabled');
      }

      // Check rate limiting
      this.checkRateLimit();

      // Import and execute the appropriate command
      const result = await this.executeSpecificCommand(toolName, args);
      success = result.success;

      return result;

    } catch (error) {
      console.error(`‚ùå Internal command failed: ${toolName}`, error);

      return {
        success: false,
        content: [{
          type: 'text',
          text: `Error executing ${toolName}: ${error instanceof Error ? error.message : String(error)}`
        }],
        error: error instanceof Error ? error.message : String(error)
      };
    } finally {
      // Record command execution for rate limiting and monitoring
      this.recordCommandExecution(toolName, success);
    }
  }

  /**
   * Execute specific command based on tool name
   */
  private async executeSpecificCommand(toolName: string, args: unknown): Promise<CommandResult> {
    console.log(`üîß Executing internal command: ${toolName}`, args);

    // Terminal commands
    if (toolName === 'start_process') {
      return this.startProcess(args as { command: string; timeout_ms: number; shell?: string });
    } else if (toolName === 'read_process_output') {
      return this.readProcessOutput(args as { pid: number });
    } else if (toolName === 'interact_with_process') {
      return this.interactWithProcess(args as { pid: number; input: string });
    } else if (toolName === 'force_terminate') {
      return this.forceTerminate(args as { pid: number });
    } else if (toolName === 'list_sessions') {
      return this.listSessions();
    } else if (toolName === 'kill_process') {
      return this.killProcess(args as { pid: number });
    } else if (toolName === 'list_processes') {
      return this.listProcesses();
    } else if (toolName === 'get_cpu_usage') {
      return this.getCpuUsage();
    } else if (toolName === 'get_memory_usage') {
      return this.getMemoryUsage();
    } else if (toolName === 'get_system_info') {
      return this.getSystemInfo();
    }

    // Filesystem commands
    else if (toolName === 'read_file') {
      return this.readFile(args as { path: string; isUrl?: boolean; offset?: number; length?: number });
    } else if (toolName === 'write_file') {
      return this.writeFile(args as { path: string; content: string; append?: boolean });
    } else if (toolName === 'create_directory') {
      return this.createDirectory(args as { path: string });
    } else if (toolName === 'list_directory') {
      return this.listDirectory(args as { path: string });
    } else if (toolName === 'move_file') {
      return this.moveFile(args as { source: string; destination: string });
    } else if (toolName === 'search_files') {
      return this.searchFiles(args as { path: string; pattern: string; recursive?: boolean });
    } else if (toolName === 'get_file_info') {
      return this.getFileInfo(args as { path: string });
    } else if (toolName === 'delete_file') {
      return this.deleteFile(args as { path: string });
    }

    // Text editing commands
    else if (toolName === 'edit_block') {
      return this.editBlock(args as { file_path: string; old_string: string; new_string: string; expected_replacements?: number });
    }

    else {
      throw new Error(`Unknown internal command: ${toolName}`);
    }
  }

  /**
   * Validate file path against allowed directories
   */
  private validateFilePath(filePath: string): void {
    if (!this.config) {
      console.error('üö® validateFilePath: Configuration not initialized');
      throw new Error('Configuration not initialized');
    }

    const allowedDirs = this.config.allowedDirectories;
    console.log(`üîß validateFilePath: Checking path "${filePath}" against allowed directories:`, allowedDirs);

    // If no allowed directories configured, deny all access
    if (allowedDirs.length === 0) {
      console.error('üö® validateFilePath: No allowed directories configured - blocking all file operations');
      throw new Error('No allowed directories configured. Please configure allowed directories in settings.');
    }

    // Resolve the absolute path
    const absolutePath = path.resolve(filePath);
    console.log(`üîß validateFilePath: Resolved absolute path: "${absolutePath}"`);

    // Check if path is within any allowed directory
    const isAllowed = allowedDirs.some(allowedDir => {
      const absoluteAllowedDir = path.resolve(allowedDir);

      // Normalize paths for Windows compatibility (case-insensitive, consistent separators)
      const normalizedPath = absolutePath.toLowerCase().replace(/\\/g, '/');
      const normalizedAllowedDir = absoluteAllowedDir.toLowerCase().replace(/\\/g, '/');

      // Check if paths are exactly equal (for accessing the directory itself)
      if (normalizedPath === normalizedAllowedDir) {
        console.log(`üîß validateFilePath: Exact match "${absolutePath}" === "${absoluteAllowedDir}" -> true`);
        return true;
      }

      // Check if path is within the allowed directory (subdirectory access)
      const allowedDirWithSlash = normalizedAllowedDir.endsWith('/') ? normalizedAllowedDir : normalizedAllowedDir + '/';
      const isWithinDir = normalizedPath.startsWith(allowedDirWithSlash);

      console.log(`üîß validateFilePath: Checking "${normalizedPath}" against "${allowedDirWithSlash}" -> ${isWithinDir}`);
      return isWithinDir;
    });

    if (!isAllowed) {
      console.error(`üö® validateFilePath: Access denied for path "${filePath}" (resolved: "${absolutePath}")`);
      console.error(`üö® validateFilePath: Allowed directories:`, allowedDirs.map(dir => path.resolve(dir)));

      throw new Error(`Access denied: Path '${filePath}' is not within allowed directories`);
    }

    console.log(`‚úÖ validateFilePath: Path "${filePath}" is allowed`);
  }

  /**
   * Validate command against blocked commands
   */
  private validateCommand(command: string): void {
    if (!this.config) {
      throw new Error('Configuration not initialized');
    }

    const blockedCommands = this.config.blockedCommands;

    // Check if command contains any blocked patterns
    const isBlocked = blockedCommands.some(blockedCmd =>
      command.toLowerCase().includes(blockedCmd.toLowerCase())
    );

    if (isBlocked) {
      throw new Error(`Command blocked for security: '${command}'`);
    }
  }

  // ===== TERMINAL COMMANDS =====

  /**
   * Start a new process
   */
  private async startProcess(args: { command: string; timeout_ms: number; shell?: string }): Promise<CommandResult> {
    try {
      this.validateCommand(args.command);

      const pid = this.nextPid++;
      const startTime = new Date();

      // Determine shell to use
      const useShell = args.shell || this.getDefaultShell();

      // Parse command and arguments
      const { cmd, cmdArgs } = this.parseCommand(args.command, useShell);

      // Spawn the process
      const childProcess = spawn(cmd, cmdArgs, {
        stdio: ['pipe', 'pipe', 'pipe'],
        shell: useShell === 'bash' || useShell === 'sh',
        env: { ...process.env },
        cwd: process.cwd()
      });

      // Create session
      const session: ElectronProcessSession = {
        pid,
        command: args.command,
        shell: useShell,
        startTime,
        lastActivity: startTime,
        status: 'running',
        blocked: false,
        process: childProcess
      };

      this.sessions.set(pid, session);

      // Set up process event handlers
      this.setupProcessHandlers(session, childProcess);

      // Set up timeout if specified
      if (args.timeout_ms && args.timeout_ms > 0) {
        setTimeout(() => {
          if (this.sessions.has(pid) && this.sessions.get(pid)!.status === 'running') {
            this.forceTerminateInternal(pid);
          }
        }, args.timeout_ms);
      }

      console.log(`üöÄ Started process ${pid}: ${args.command}`);

      return this.createSuccessResponse(
        `Process started successfully:\nPID: ${pid}\nCommand: ${args.command}\nStatus: running\nStarted: ${startTime.toISOString()}`
      );

    } catch (error) {
      return this.createErrorResponse(error instanceof Error ? error.message : String(error));
    }
  }

  /**
   * Read output from a process
   */
  private async readProcessOutput(args: { pid: number; timeout_ms?: number }): Promise<CommandResult> {
    try {
      const session = this.sessions.get(args.pid);
      if (!session) {
        throw new Error(`Process ${args.pid} not found`);
      }

      if (!session.process) {
        throw new Error(`Process ${args.pid} has no associated child process`);
      }

      const output = await this.readProcessOutputInternal(session, args.timeout_ms || 5000);
      return this.createSuccessResponse(output || 'No output available');

    } catch (error) {
      return this.createErrorResponse(error instanceof Error ? error.message : String(error));
    }
  }

  /**
   * Send input to a process and get response
   */
  private async interactWithProcess(args: { pid: number; input: string; timeout_ms?: number }): Promise<CommandResult> {
    try {
      const session = this.sessions.get(args.pid);
      if (!session) {
        throw new Error(`Process ${args.pid} not found`);
      }

      if (!session.process || !session.process.stdin) {
        throw new Error(`Process ${args.pid} is not accepting input`);
      }

      // Send input to the process
      session.process.stdin.write(args.input + '\n');
      session.lastActivity = new Date();

      // Wait for output
      const output = await this.readProcessOutputInternal(session, args.timeout_ms || 5000);
      return this.createSuccessResponse(output || 'Command executed, no output returned');

    } catch (error) {
      return this.createErrorResponse(error instanceof Error ? error.message : String(error));
    }
  }

  /**
   * Force terminate a process
   */
  private async forceTerminate(args: { pid: number }): Promise<CommandResult> {
    try {
      const success = this.forceTerminateInternal(args.pid);
      if (success) {
        return this.createSuccessResponse(`Process ${args.pid} terminated successfully`);
      } else {
        return this.createErrorResponse(`Failed to terminate process ${args.pid}`);
      }
    } catch (error) {
      return this.createErrorResponse(error instanceof Error ? error.message : String(error));
    }
  }

  /**
   * List all active sessions
   */
  private async listSessions(): Promise<CommandResult> {
    try {
      const sessions = Array.from(this.sessions.values()).map(session => ({
        pid: session.pid,
        command: session.command,
        status: session.status,
        startTime: session.startTime,
        blocked: session.blocked,
        runtime: Date.now() - session.startTime.getTime()
      }));

      if (sessions.length === 0) {
        return this.createSuccessResponse('No active sessions');
      }

      const sessionList = sessions.map(session =>
        `PID: ${session.pid} | Command: ${session.command} | Status: ${session.status} | ` +
        `Blocked: ${session.blocked} | Runtime: ${Math.round(session.runtime / 1000)}s`
      ).join('\n');

      return this.createSuccessResponse(`Active Sessions (${sessions.length}):\n${sessionList}`);

    } catch (error) {
      return this.createErrorResponse(error instanceof Error ? error.message : String(error));
    }
  }

  /**
   * List all running processes on the system
   */
  private async listProcesses(): Promise<CommandResult> {
    try {
      const execAsync = promisify(exec);

      let command: string;
      if (process.platform === 'win32') {
        // Windows: Get processes with CPU and memory usage
        command = 'powershell "Get-Process | Sort-Object CPU -Descending | Select-Object -First 20 Name,Id,CPU,WorkingSet | Format-Table -AutoSize"';
      } else if (process.platform === 'darwin') {
        // macOS: Get top processes with CPU and memory
        command = 'ps aux | head -21';
      } else {
        // Linux: Get top processes with CPU and memory
        command = 'ps aux --sort=-%cpu | head -21';
      }

      console.log(`üîß Executing system command: ${command}`);
      const { stdout } = await execAsync(command);
      console.log(`üîß Command output length: ${stdout.length} characters`);

      return this.createSuccessResponse(stdout);
    } catch (error) {
      console.error(`üîß listProcesses error:`, error);
      return this.createErrorResponse(error instanceof Error ? error.message : String(error));
    }
  }

