'use client';

import { useEffect } from 'react';
import { debugLogger } from '../services/debugLogger';

/**
 * Simple window dragging hook using Electron's built-in web API
 * This avoids high DPI scaling issues by using CSS -webkit-app-region
 */
export function useEnhancedWindowDrag() {
  // This hook now uses CSS-based dragging only

  useEffect(() => {
    // Only enable in Electron environment
    if (typeof window === 'undefined' || !window.electronAPI) {
      return;
    }

    // Scoped strategy: ensure root elements have no app-region so component-level regions work
    document.documentElement.style.removeProperty('-webkit-app-region');
    document.body.style.removeProperty('-webkit-app-region');

    // Debug computed styles to verify correct regions
    try {
      const logRegion = (label: string, el: Element | null) => {
        if (!el) return;
        const cs = getComputedStyle(el as HTMLElement) as CSSStyleDeclaration;
        // Access vendor property without using 'any'
        const region =
          (cs as unknown as Record<string, string>)['-webkit-app-region'] ||
          (cs.getPropertyValue && cs.getPropertyValue('-webkit-app-region')) ||
          'unset';
        debugLogger.debug(`EnhancedWindowDrag: ${label} app-region=${region}`);
      };
      logRegion('html', document.documentElement);
      logRegion('body', document.body);
      const header = document.querySelector('.draggable-title-bar');
      logRegion('.draggable-title-bar', header);
    } catch {
      // ignore logging errors
    }

    return () => {
      // leave root without app-region
      document.documentElement.style.removeProperty('-webkit-app-region');
      document.body.style.removeProperty('-webkit-app-region');
    };
  }, []);

  return {
    isDragging: false // CSS-based dragging doesn't need state tracking
  };
}

import React from 'react';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { CodeBlock, InlineCode } from '../components/CodeBlock';

/**
 * Regular expressions for different content types
 */
const URL_REGEX = /https?:\/\/(www\.)?[-a-zA-Z0-9@:%._+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_+.~#?&//=]*)/g;
const DOMAIN_REGEX = /(^|[\s([{])((?:www\.)?[-a-zA-Z0-9@:%._+~#=]{1,256}\.(?:[a-zA-Z]{2,6}|localhost)(?::\d+)?(?:[-a-zA-Z0-9()@:%_+.~#?&//=]*)?)/g;
const CODE_BLOCK_REGEX = /```(\w+)?\n?([\s\S]*?)```/g;
const INLINE_CODE_REGEX = /`([^`\n]+)`/g;
const IMAGE_URL_REGEX = /https?:\/\/(www\.)?[-a-zA-Z0-9@:%._+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_+.~#?&//=]*)\.(jpg|jpeg|png|gif|webp|svg|bmp|ico)(\?[^)\s]*)?/gi;

/**
 * Interface for parsed content segments
 */
interface ContentSegment {
  type: 'text' | 'link' | 'code-block' | 'inline-code' | 'image';
  content: string;
  url?: string;
  language?: string;
  index: number;
  length: number;
}

/**
 * Handles clicking on external links
 */
function handleLinkClick(url: string) {
  if (typeof window !== 'undefined' && window.electronAPI?.openExternalLink) {
    window.electronAPI.openExternalLink(url);
  } else {
    console.log('Would open URL:', url);
  }
}

/**
 * Parses text and identifies all content segments (code blocks, inline code, links, text)
 * @param text - The input text to parse
 * @returns Array of content segments
 */
function parseContentSegments(text: string): ContentSegment[] {
  const segments: ContentSegment[] = [];

  // Find all code blocks first (highest priority)
  CODE_BLOCK_REGEX.lastIndex = 0;
  let match: RegExpExecArray | null;
  while ((match = CODE_BLOCK_REGEX.exec(text)) !== null) {
    segments.push({
      type: 'code-block',
      content: match[2].trim(),
      language: match[1] || undefined,
      index: match.index,
      length: match[0].length
    });
  }

  // Find all inline code (second priority)
  INLINE_CODE_REGEX.lastIndex = 0;
  while ((match = INLINE_CODE_REGEX.exec(text)) !== null) {
    // Skip if this inline code is inside a code block
    const isInsideCodeBlock = segments.some(segment =>
      segment.type === 'code-block' &&
      match!.index >= segment.index &&
      match!.index < segment.index + segment.length
    );
    
    if (!isInsideCodeBlock) {
      segments.push({
        type: 'inline-code',
        content: match[1],
        index: match.index,
        length: match[0].length
      });
    }
  }

  // Find all image URLs first (higher priority than regular links)
  IMAGE_URL_REGEX.lastIndex = 0;
  while ((match = IMAGE_URL_REGEX.exec(text)) !== null) {
    // Skip if this URL is inside a code block or inline code
    const isInsideCode = segments.some(segment =>
      (segment.type === 'code-block' || segment.type === 'inline-code') &&
      match!.index >= segment.index &&
      match!.index < segment.index + segment.length
    );

    if (!isInsideCode) {
      segments.push({
        type: 'image',
        content: match[0],
        url: match[0],
        index: match.index,
        length: match[0].length
      });
    }
  }

  // Find all HTTP/HTTPS URLs (excluding images already found)
  URL_REGEX.lastIndex = 0;
  while ((match = URL_REGEX.exec(text)) !== null) {
    // Skip if this URL is inside a code block, inline code, or already covered by an image
    const isInsideCodeOrImage = segments.some(segment =>
      ((segment.type === 'code-block' || segment.type === 'inline-code' || segment.type === 'image') &&
       match!.index >= segment.index &&
       match!.index < segment.index + segment.length)
    );

    if (!isInsideCodeOrImage) {
      segments.push({
        type: 'link',
        content: match[0],
        url: match[0],
        index: match.index,
        length: match[0].length
      });
    }
  }

  // Find all domain-only URLs
  DOMAIN_REGEX.lastIndex = 0;
  while ((match = DOMAIN_REGEX.exec(text)) !== null) {
    const domainMatch = match[2];
    const domainIndex = match.index + match[1].length;
    
    // Remove trailing punctuation
    const cleanDomain = domainMatch.replace(/[)\]}.,:;!?]*$/, '');
    
    // Skip if this domain is inside code or already covered by a full URL
    const isInsideCodeOrCovered = segments.some(segment => 
      ((segment.type === 'code-block' || segment.type === 'inline-code') &&
       domainIndex >= segment.index && domainIndex < segment.index + segment.length) ||
      (segment.type === 'link' &&
       domainIndex >= segment.index && domainIndex < segment.index + segment.length)
    );
    
    if (!isInsideCodeOrCovered && cleanDomain) {
      const fullUrl = cleanDomain.startsWith('http') ? cleanDomain : `https://${cleanDomain}`;
      segments.push({
        type: 'link',
        content: cleanDomain,
        url: fullUrl,
        index: domainIndex,
        length: cleanDomain.length
      });
    }
  }

  // Sort segments by index to process them in order
  segments.sort((a, b) => a.index - b.index);

  return segments;
}

/**
 * Renders parsed content segments as JSX
 * @param text - The original text
 * @param segments - The parsed content segments
 * @returns JSX elements
 */
function renderContentSegments(text: string, segments: ContentSegment[]): JSX.Element[] {
  const elements: JSX.Element[] = [];
  let lastIndex = 0;

  segments.forEach((segment, index) => {
    // Add text before this segment if any
    if (segment.index > lastIndex) {
      const textContent = text.slice(lastIndex, segment.index);
      if (textContent) {
        elements.push(
          <span key={`text-${index}-${lastIndex}`}>
            {textContent}
          </span>
        );
      }
    }

    // Add the segment
    switch (segment.type) {
      case 'code-block':
        elements.push(
          <CodeBlock
            key={`code-block-${index}`}
            code={segment.content}
            language={segment.language}
            className="my-2"
          />
        );
        break;
      
      case 'inline-code':
        elements.push(
          <InlineCode
            key={`inline-code-${index}`}
            code={segment.content}
          />
        );
        break;
      
      case 'image':
        elements.push(
          <div key={`image-${index}`} className="my-2">
            <img
              src={segment.url!}
              alt="Image"
              className="max-w-full h-auto rounded-lg border border-border cursor-pointer hover:opacity-80 transition-opacity"
              style={{
                maxHeight: '400px',
                objectFit: 'contain',
                WebkitAppRegion: 'no-drag'
              } as React.CSSProperties & { WebkitAppRegion?: string }}
              onClick={() => {
                // Open image in new window/tab
                if (typeof window !== 'undefined' && window.electronAPI) {
                  window.electronAPI.openExternal(segment.url!);
                } else {
                  window.open(segment.url!, '_blank');
                }
              }}
              onError={(e) => {
                // Fallback to link if image fails to load
                const target = e.target as HTMLImageElement;
                const parent = target.parentElement;
                if (parent) {
                  parent.innerHTML = `<a href="#" class="text-blue-500 hover:text-blue-700 underline cursor-pointer" onclick="event.preventDefault(); ${typeof window !== 'undefined' && window.electronAPI ? 'window.electronAPI.openExternal' : 'window.open'}('${segment.url}', '_blank')" title="Open ${segment.url} in browser">${segment.content}</a>`;
                }
              }}
              title={`Click to open ${segment.url} in browser`}
            />
          </div>
        );
        break;

      case 'link':
        elements.push(
          <a
            key={`link-${index}`}
            href="#"
            onClick={(e) => {
              e.preventDefault();
              handleLinkClick(segment.url!);
            }}
            className="text-blue-500 hover:text-blue-700 underline cursor-pointer"
            style={{
              WebkitAppRegion: 'no-drag',
              textDecoration: 'underline',
              color: 'rgb(59 130 246)',
            } as React.CSSProperties & { WebkitAppRegion?: string }}
            title={`Open ${segment.url} in browser`}
          >
            {segment.content}
          </a>
        );
        break;
    }

    lastIndex = segment.index + segment.length;
  });

  // Add remaining text after the last segment
  if (lastIndex < text.length) {
    const textContent = text.slice(lastIndex);
    if (textContent) {
      elements.push(
        <span key={`text-final-${lastIndex}`}>
          {textContent}
        </span>
      );
    }
  }

  return elements;
}

/**
 * Props for the ParsedContent component
 */
interface ParsedContentProps {
  children: string;
  className?: string;
  style?: React.CSSProperties;
}

/**
 * Component that renders text with code blocks, inline code, and clickable links
 * @param children - The text content to parse and render
 * @param className - CSS classes to apply to the container
 * @param style - Inline styles to apply to the container
 */
export function ParsedContent({ children, className, style }: ParsedContentProps) {
  const segments = parseContentSegments(children);
  const elements = renderContentSegments(children, segments);

  return (
    <div className={className} style={style}>
      {elements.length > 0 ? elements : children}
    </div>
  );
}

/**
 * Utility function to parse text with all content types
 * @param text - The text to parse
 * @param className - Optional CSS classes for the container
 * @param style - Optional inline styles for the container
 * @returns JSX element with parsed content
 */
/**
 * Enhanced markdown parser using react-markdown
 * @param text - The input text to parse as markdown
 * @returns JSX elements with parsed markdown content
 */
export function parseMarkdownContent(text: string): React.ReactNode {
  if (!text) return null;

  return (
    <ReactMarkdown
      remarkPlugins={[remarkGfm]}
      components={{
        // Custom code block component
        // Types for react-markdown renderers kept broad to match library's dynamic nodes
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        // eslint-disable-next-line @typescript-eslint/no-unused-vars, @typescript-eslint/no-explicit-any
        code({ node, inline, className, children, ...props }: any) {
          const match = /language-(\w+)/.exec(className || '');
          const language = match ? match[1] : undefined;

          if (inline) {
            return <InlineCode code={String(children).replace(/\n$/, '')} />;
          }

          // Block code: render as a block-level container to avoid nesting <pre> within <p>
          return (
            <div className="my-2">
              <CodeBlock
                code={String(children).replace(/\n$/, '')}
                language={language}
              />
            </div>
          );
        },
        // Custom link component
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        // eslint-disable-next-line @typescript-eslint/no-unused-vars, @typescript-eslint/no-explicit-any
        a({ href, children, ...props }: any) {
          return (
            <button
              onClick={() => href && handleLinkClick(href)}
              className="text-blue-400 hover:text-blue-300 underline cursor-pointer bg-transparent border-none p-0 font-inherit"
              style={{ WebkitAppRegion: 'no-drag' } as React.CSSProperties & { WebkitAppRegion?: string }}
            >
              {children}
            </button>
          );
        },
        // Style headers
        h1: ({ children }: any) => <h1 className="text-2xl font-bold mb-4 mt-6">{children}</h1>,
        h2: ({ children }: any) => <h2 className="text-xl font-bold mb-3 mt-5">{children}</h2>,
        h3: ({ children }: any) => <h3 className="text-lg font-bold mb-2 mt-4">{children}</h3>,
        h4: ({ children }: any) => <h4 className="text-base font-bold mb-2 mt-3">{children}</h4>,
        h5: ({ children }: any) => <h5 className="text-sm font-bold mb-1 mt-2">{children}</h5>,
        h6: ({ children }: any) => <h6 className="text-xs font-bold mb-1 mt-2">{children}</h6>,
        // Style paragraphs with minimal spacing
        // Avoid placing block elements like <pre> inside <p>
        p: ({ children }: any) => <p className="mb-1 last:mb-0">{children}</p>,
        // Style lists with minimal spacing
        ul: ({ children }: any) => <ul className="list-disc list-inside mb-2 space-y-0">{children}</ul>,
        ol: ({ children }: any) => <ol className="list-decimal list-inside mb-2 space-y-0">{children}</ol>,
        li: ({ children }: any) => <li className="ml-4">{children}</li>,
        // Style blockquotes with minimal spacing
        blockquote: ({ children }: any) => (
          <blockquote className="border-l-4 border-gray-500 pl-4 italic my-2 text-gray-300">
            {children}
          </blockquote>
        ),
        // Style tables
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        table: ({ children }: any) => (
          <div className="overflow-x-auto my-4">
            <table className="min-w-full border-collapse border border-gray-600">
              {children}
            </table>
          </div>
        ),
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        th: ({ children }: any) => (
          <th className="border border-gray-600 px-3 py-2 bg-gray-700 font-semibold text-left">
            {children}
          </th>
        ),
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        td: ({ children }: any) => (
          <td className="border border-gray-600 px-3 py-2">
            {children}
          </td>
        ),
        // Style horizontal rules with minimal spacing
        hr: () => <hr className="my-3 border-gray-600" />,
        // Style strong and emphasis
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        strong: ({ children }: any) => <strong className="font-bold">{children}</strong>,
        // eslint-disable-next-line @typescript-eslint/no-explicit-any
        em: ({ children }: any) => <em className="italic">{children}</em>,
      }}
    >
      {text}
    </ReactMarkdown>
  );
}

export function parseTextWithContent(
  text: string,
  className?: string,
  style?: React.CSSProperties
): JSX.Element {
  // Clean up excessive line breaks and normalize spacing
  const cleanText = text
    .replace(/\n{3,}/g, '\n\n') // Replace 3+ consecutive newlines with 2
    .replace(/[ \t]+/g, ' '); // Replace multiple spaces/tabs with single space

  // Check if the text contains markdown elements
  const hasMarkdown = /^#{1,6}\s|^\*\s|^\d+\.\s|^>\s|^\|.*\||```|`[^`]+`|\*\*.*\*\*|\*.*\*|_.*_|\[.*\]\(.*\)/m.test(cleanText);

  if (hasMarkdown) {
    return (
      <div className={className} style={style}>
        {parseMarkdownContent(cleanText)}
      </div>
    );
  }

  // Fallback to the original parser for simple text
  return (
    <ParsedContent className={className} style={style}>
      {cleanText}
    </ParsedContent>
  );
}

import React from 'react';

/**
 * Regular expression to detect HTTP and HTTPS URLs in text
 * Matches URLs with or without www, with various TLDs, paths, query parameters, and fragments
 */
const URL_REGEX = /https?:\/\/(www\.)?[-a-zA-Z0-9@:%._+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_+.~#?&//=]*)/g;

/**
 * Regular expression to detect domain names without protocol (e.g., example.com, www.google.com)
 * More conservative pattern to avoid false positives
 * Handles domains in parentheses and other punctuation contexts
 */
const DOMAIN_REGEX = /(^|[\s([{])((?:www\.)?[-a-zA-Z0-9@:%._+~#=]{1,256}\.(?:[a-zA-Z]{2,6}|localhost)(?::\d+)?(?:[-a-zA-Z0-9()@:%_+.~#?&//=]*)?)/g;

/**
 * Interface for parsed text segments
 */
interface TextSegment {
  type: 'text' | 'link';
  content: string;
  url?: string;
}

/**
 * Parses text and splits it into segments of regular text and URLs
 * @param text - The input text to parse
 * @returns Array of text segments
 */
function parseTextSegments(text: string): TextSegment[] {
  const segments: TextSegment[] = [];
  const allMatches: Array<{ index: number; length: number; content: string; url: string }> = [];

  // Find all HTTP/HTTPS URLs
  URL_REGEX.lastIndex = 0;
  let match;
  while ((match = URL_REGEX.exec(text)) !== null) {
    allMatches.push({
      index: match.index,
      length: match[0].length,
      content: match[0],
      url: match[0]
    });
  }

  // Find all domain-only URLs (like example.com)
  DOMAIN_REGEX.lastIndex = 0;
  while ((match = DOMAIN_REGEX.exec(text)) !== null) {
    let domainMatch = match[2]; // The domain part (without leading whitespace)
    const domainIndex = match.index + match[1].length; // Adjust for leading whitespace

    // Remove trailing punctuation that shouldn't be part of the URL
    const trailingPunctuationMatch = domainMatch.match(/^(.+?)[)\]}.:,;!?]*$/);
    if (trailingPunctuationMatch) {
      domainMatch = trailingPunctuationMatch[1];
    }

    // Skip if this domain is already covered by a full URL
    const isAlreadyCovered = allMatches.some(existing =>
      domainIndex >= existing.index &&
      domainIndex < existing.index + existing.length
    );

    if (!isAlreadyCovered && domainMatch) {
      // Add https:// prefix for the URL
      const fullUrl = domainMatch.startsWith('http') ? domainMatch : `https://${domainMatch}`;
      allMatches.push({
        index: domainIndex,
        length: domainMatch.length,
        content: domainMatch,
        url: fullUrl
      });
    }
  }

  // Sort matches by index to process them in order
  allMatches.sort((a, b) => a.index - b.index);

  let lastIndex = 0;
  for (const urlMatch of allMatches) {
    // Add text before the URL if any
    if (urlMatch.index > lastIndex) {
      const textContent = text.slice(lastIndex, urlMatch.index);
      if (textContent) {
        segments.push({
          type: 'text',
          content: textContent
        });
      }
    }

    // Add the URL segment
    segments.push({
      type: 'link',
      content: urlMatch.content,
      url: urlMatch.url
    });

    lastIndex = urlMatch.index + urlMatch.length;
  }

  // Add remaining text after the last URL if any
  if (lastIndex < text.length) {
    const textContent = text.slice(lastIndex);
    if (textContent) {
      segments.push({
        type: 'text',
        content: textContent
      });
    }
  }

  // If no URLs were found, return the entire text as a single segment
  if (segments.length === 0) {
    segments.push({
      type: 'text',
      content: text
    });
  }

  return segments;
}

/**
 * Handles clicking on external links
 * Opens the URL in the user's default browser via Electron's shell.openExternal
 * @param url - The URL to open
 */
function handleLinkClick(url: string) {
  // Prevent default link behavior
  if (typeof window !== 'undefined' && window.electronAPI?.openExternalLink) {
    // Use Electron API to open in external browser
    window.electronAPI.openExternalLink(url);
  } else {
    // Fallback for non-Electron environments (development)
    console.log('Would open URL:', url);
    // In a web environment, we could use window.open
    // window.open(url, '_blank', 'noopener,noreferrer');
  }
}

/**
 * Props for the ParsedTextWithLinks component
 */
interface ParsedTextWithLinksProps {
  children: string;
  className?: string;
  style?: React.CSSProperties;
}

/**
 * Component that renders text with clickable links
 * Preserves whitespace and formatting while making URLs clickable
 * @param children - The text content to parse and render
 * @param className - CSS classes to apply to the container
 * @param style - Inline styles to apply to the container
 */
export function ParsedTextWithLinks({ children, className, style }: ParsedTextWithLinksProps) {
  const segments = parseTextSegments(children);

  return (
    <span className={className} style={style}>
      {segments.map((segment, index) => {
        if (segment.type === 'link' && segment.url) {
          return (
            <a
              key={index}
              href="#"
              onClick={(e) => {
                e.preventDefault();
                handleLinkClick(segment.url!);
              }}
              className="text-blue-500 hover:text-blue-700 underline cursor-pointer"
              style={{
                WebkitAppRegion: 'no-drag',
                textDecoration: 'underline',
                color: 'rgb(59 130 246)', // blue-500
              } as React.CSSProperties & { WebkitAppRegion?: string }}
              title={`Open ${segment.url} in browser`}
            >
              {segment.content}
            </a>
          );
        } else {
          return (
            <span key={index}>
              {segment.content}
            </span>
          );
        }
      })}
    </span>
  );
}

/**
 * Utility function to parse text with links (for use in other components)
 * @param text - The text to parse
 * @param className - Optional CSS classes for the container
 * @param style - Optional inline styles for the container
 * @returns JSX element with parsed text and clickable links
 */
export function parseTextWithLinks(
  text: string, 
  className?: string, 
  style?: React.CSSProperties
): JSX.Element {
  return (
    <ParsedTextWithLinks className={className} style={style}>
      {text}
    </ParsedTextWithLinks>
  );
}

import mammoth from 'mammoth';
import * as XLSX from 'xlsx';
import csvParser from 'csv-parser';
import { parse as parseHtml } from 'node-html-parser';
import ICAL from 'ical.js';
import { parseString as parseXml } from 'xml2js';
import rtfParser from 'rtf-parser';

export interface ParsedDocument {
  text: string;
  metadata?: {
    title?: string;
    author?: string;
    pages?: number;
    sheets?: string[];
    format?: string;
    success?: boolean;
    error?: string;
    fallbackUsed?: boolean;
    processingTime?: number;
    [key: string]: unknown;
  };
}

export interface DocumentParsingStats {
  totalAttempts: number;
  successfulParses: number;
  failedParses: number;
  fallbacksUsed: number;
  averageProcessingTime: number;
  errorsByType: Record<string, number>;
}

export class DocumentParserService {
  private stats: DocumentParsingStats = {
    totalAttempts: 0,
    successfulParses: 0,
    failedParses: 0,
    fallbacksUsed: 0,
    averageProcessingTime: 0,
    errorsByType: {}
  };

  /**
   * Get current parsing statistics
   */
  getStats(): DocumentParsingStats {
    return { ...this.stats };
  }

  /**
   * Reset parsing statistics
   */
  resetStats(): void {
    this.stats = {
      totalAttempts: 0,
      successfulParses: 0,
      failedParses: 0,
      fallbacksUsed: 0,
      averageProcessingTime: 0,
      errorsByType: {}
    };
  }

  /**
   * Main entry point for parsing any supported document format
   */
  async parseDocument(file: File): Promise<ParsedDocument> {
    const startTime = Date.now();
    const fileExtension = this.getFileExtension(file.name).toLowerCase();
    const mimeType = file.type;

    this.stats.totalAttempts++;
    console.log(`üìÑ Parsing document: ${file.name} (${fileExtension}, ${mimeType})`);
    console.log(`üìÑ File size: ${Math.round(file.size / 1024)}KB, Last modified: ${file.lastModified ? new Date(file.lastModified).toISOString() : 'Unknown'}`);

    try {
      let result: ParsedDocument;

      switch (fileExtension) {
        case '.docx':
        case '.doc':
          result = await this.parseWordDocument(file);
          break;

        case '.xlsx':
        case '.xls':
        case '.ods':
          result = await this.parseSpreadsheet(file);
          break;

        case '.csv':
          result = await this.parseCsv(file);
          break;

        case '.html':
        case '.htm':
          result = await this.parseHtml(file);
          break;

        case '.ics':
          result = await this.parseIcs(file);
          break;

        case '.json':
          result = await this.parseJson(file);
          break;

        case '.rtf':
          result = await this.parseRtf(file);
          break;

        case '.xml':
          result = await this.parseXml(file);
          break;

        case '.pptx':
        case '.ppt':
          result = await this.parsePowerPoint(file);
          break;

        case '.pdf':
          result = await this.parsePdf(file);
          break;

        case '.txt':
          result = await this.parseTextFile(file);
          break;

        case '.md':
          result = await this.parseMarkdownFile(file);
          break;

        default:
          // Create a fallback document for unsupported formats
          console.log('üìÑ Unsupported format, creating fallback document...');
          result = await this.createFallbackDocument(file, 'Unsupported file format');
          this.stats.fallbacksUsed++;
          break;
      }

      // Success - update statistics
      const processingTime = Date.now() - startTime;
      this.stats.successfulParses++;
      this.updateAverageProcessingTime(processingTime);

      result.metadata = {
        ...result.metadata,
        success: true,
        processingTime
      };

      console.log(`‚úÖ Successfully parsed ${file.name} in ${processingTime}ms`);
      return result;

    } catch (error) {
      // Error handling with fallback
      const processingTime = Date.now() - startTime;
      this.stats.failedParses++;

      const errorMessage = error instanceof Error ? error.message : 'Unknown error';
      const errorType = this.categorizeError(error);

      this.stats.errorsByType[errorType] = (this.stats.errorsByType[errorType] || 0) + 1;

      console.error(`‚ùå Error parsing document ${file.name}:`, error);

      // Try fallback approach
      try {
        console.log(`üîÑ Attempting fallback parsing for ${file.name}...`);
        const fallbackResult = await this.createFallbackDocument(file, errorMessage);
        this.stats.fallbacksUsed++;

        fallbackResult.metadata = {
          ...fallbackResult.metadata,
          success: false,
          error: errorMessage,
          fallbackUsed: true,
          processingTime
        };

        console.log(`‚ö†Ô∏è Fallback parsing completed for ${file.name}`);
        return fallbackResult;
      } catch (fallbackError) {
        console.error(`‚ùå Fallback parsing also failed for ${file.name}:`, fallbackError);
        throw new Error(`Failed to parse document: ${errorMessage}. Fallback also failed: ${fallbackError instanceof Error ? fallbackError.message : 'Unknown error'}`);
      }
    }
  }

  /**
   * Parse Word documents (.docx, .doc)
   */
  private async parseWordDocument(file: File): Promise<ParsedDocument> {
    try {
      // First, try with ArrayBuffer (more compatible)
      const arrayBuffer = await file.arrayBuffer();
      const result = await mammoth.extractRawText({ arrayBuffer });

      return {
        text: result.value,
        metadata: {
          format: 'Word Document',
          title: file.name,
          warnings: result.messages?.length > 0 ? result.messages.map(m => m.message) : undefined
        }
      };
    } catch (error) {
      console.log('üìÑ Mammoth failed with ArrayBuffer, trying Buffer approach...', error);

      try {
        // Fallback: try with Buffer
        const buffer = await this.fileToBuffer(file);
        const result = await mammoth.extractRawText({ buffer });

        return {
          text: result.value,
          metadata: {
            format: 'Word Document',
            title: file.name,
            warnings: result.messages?.length > 0 ? result.messages.map(m => m.message) : undefined
          }
        };
      } catch (bufferError) {
        console.log('üìÑ Mammoth failed with Buffer approach too...', bufferError);

        // Final fallback: create a fallback document with detailed error info
        const errorMessage = error instanceof Error ? error.message : 'Unknown error';
        const bufferErrorMessage = bufferError instanceof Error ? bufferError.message : 'Unknown error';

        return await this.createFallbackDocument(
          file,
          `Word document parsing failed. Primary error: ${errorMessage}. Buffer fallback error: ${bufferErrorMessage}. The file may be corrupted, password-protected, or in an unsupported format.`
        );
      }
    }
  }

  /**
   * Parse spreadsheet files (.xlsx, .xls, .ods)
   */
  private async parseSpreadsheet(file: File): Promise<ParsedDocument> {
    const buffer = await this.fileToBuffer(file);
    
    try {
      const workbook = XLSX.read(buffer, { type: 'buffer' });
      const sheets: string[] = [];
      let combinedText = '';

      workbook.SheetNames.forEach((sheetName) => {
        sheets.push(sheetName);
        const worksheet = workbook.Sheets[sheetName];
        const csvData = XLSX.utils.sheet_to_csv(worksheet);
        
        combinedText += `\n\n=== Sheet: ${sheetName} ===\n`;
        combinedText += csvData;
      });

      return {
        text: combinedText.trim(),
        metadata: {
          format: 'Spreadsheet',
          title: file.name,
          sheets: sheets,
          sheetCount: sheets.length
        }
      };
    } catch {
      console.log('üìä XLSX failed, creating fallback document...');
      return await this.createFallbackDocument(file, 'Spreadsheet parsing failed - file may be corrupted or in an unsupported format');
    }
  }

  /**
   * Parse CSV files
   */
  private async parseCsv(file: File): Promise<ParsedDocument> {
    const text = await this.fileToText(file);
    
    return new Promise((resolve, reject) => {
      const rows: Record<string, string>[] = [];
      // eslint-disable-next-line @typescript-eslint/no-require-imports
      const stream = require('stream');
      const readable = new stream.Readable();
      readable.push(text);
      readable.push(null);

      readable
        .pipe(csvParser())
        .on('data', (row: Record<string, string>) => rows.push(row))
        .on('end', () => {
          const headers = Object.keys(rows[0] || {});
          let csvText = `Headers: ${headers.join(', ')}\n\n`;
          
          rows.forEach((row, index) => {
            csvText += `Row ${index + 1}:\n`;
            headers.forEach(header => {
              csvText += `  ${header}: ${row[header]}\n`;
            });
            csvText += '\n';
          });

          resolve({
            text: csvText,
            metadata: {
              format: 'CSV',
              title: file.name,
              rows: rows.length,
              columns: headers.length,
              headers: headers
            }
          });
        })
        .on('error', reject);
    });
  }

  /**
   * Parse HTML files
   */
  private async parseHtml(file: File): Promise<ParsedDocument> {
    const htmlContent = await this.fileToText(file);
    const root = parseHtml(htmlContent);
    
    // Extract title
    const title = root.querySelector('title')?.text || file.name;
    
    // Remove script and style tags
    root.querySelectorAll('script, style').forEach(el => el.remove());
    
    // Extract text content
    const text = root.text;
    
    return {
      text: text,
      metadata: {
        format: 'HTML',
        title: title,
        originalTitle: file.name
      }
    };
  }

  /**
   * Parse ICS calendar files
   */
  private async parseIcs(file: File): Promise<ParsedDocument> {
    const icsContent = await this.fileToText(file);
    
    try {
      const jcalData = ICAL.parse(icsContent);
      const comp = new ICAL.Component(jcalData);
      const events = comp.getAllSubcomponents('vevent');
      
      let calendarText = 'Calendar Events:\n\n';
      
      events.forEach((event, index) => {
        const vevent = new ICAL.Event(event);
        calendarText += `Event ${index + 1}:\n`;
        calendarText += `  Title: ${vevent.summary}\n`;
        calendarText += `  Start: ${vevent.startDate?.toJSDate()}\n`;
        calendarText += `  End: ${vevent.endDate?.toJSDate()}\n`;
        if (vevent.description) {
          calendarText += `  Description: ${vevent.description}\n`;
        }
        if (vevent.location) {
          calendarText += `  Location: ${vevent.location}\n`;
        }
        calendarText += '\n';
      });

      return {
        text: calendarText,
        metadata: {
          format: 'Calendar (ICS)',
          title: file.name,
          eventCount: events.length
        }
      };
    } catch (error) {
      throw new Error(`Failed to parse ICS file: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Parse JSON files
   */
  private async parseJson(file: File): Promise<ParsedDocument> {
    const jsonContent = await this.fileToText(file);
    
    try {
      const jsonData = JSON.parse(jsonContent);
      const formattedJson = JSON.stringify(jsonData, null, 2);
      
      return {
        text: formattedJson,
        metadata: {
          format: 'JSON',
          title: file.name,
          type: Array.isArray(jsonData) ? 'Array' : typeof jsonData,
          size: Array.isArray(jsonData) ? jsonData.length : Object.keys(jsonData).length
        }
      };
    } catch (error) {
      throw new Error(`Invalid JSON file: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Parse RTF files
   */
  private async parseRtf(file: File): Promise<ParsedDocument> {
    const rtfContent = await this.fileToText(file);
    
    try {
      const doc = await rtfParser.parseRTF(rtfContent);
      const text = this.extractTextFromRtfDoc(doc);
      
      return {
        text: text,
        metadata: {
          format: 'RTF',
          title: file.name
        }
      };
    } catch (error) {
      throw new Error(`Failed to parse RTF file: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  /**
   * Parse XML files
   */
  private async parseXml(file: File): Promise<ParsedDocument> {
    const xmlContent = await this.fileToText(file);

    return new Promise((resolve, reject) => {
      parseXml(xmlContent, (err, result) => {
        if (err) {
          reject(new Error(`Failed to parse XML: ${err.message}`));
          return;
        }

        const formattedXml = JSON.stringify(result, null, 2);

        resolve({
          text: formattedXml,
          metadata: {
            format: 'XML',
            title: file.name,
            rootElement: Object.keys(result)[0]
          }
        });
      });
    });
  }

  /**
   * Parse plain text files (.txt)
   */
  private async parseTextFile(file: File): Promise<ParsedDocument> {
    const textContent = await this.fileToText(file);

    return {
      text: textContent,
      metadata: {
        format: 'Plain Text',
        title: file.name,
        characterCount: textContent.length,
        lineCount: textContent.split('\n').length
      }
    };
  }

  /**
   * Parse Markdown files (.md)
   */
  private async parseMarkdownFile(file: File): Promise<ParsedDocument> {
    const markdownContent = await this.fileToText(file);

    // Extract basic markdown metadata
    const lines = markdownContent.split('\n');
    const headings = lines.filter(line => line.trim().startsWith('#'));
    const firstHeading = headings.length > 0 ? headings[0].replace(/^#+\s*/, '') : undefined;

    return {
      text: markdownContent,
      metadata: {
        format: 'Markdown',
        title: firstHeading || file.name,
        originalTitle: file.name,
        characterCount: markdownContent.length,
        lineCount: lines.length,
        headingCount: headings.length,
        headings: headings.slice(0, 10).map(h => h.replace(/^#+\s*/, '')) // First 10 headings
      }
    };
  }

  /**
   * Parse PowerPoint files (.pptx, .ppt)
   */
  private async parsePowerPoint(file: File): Promise<ParsedDocument> {
    // PowerPoint parsing is not currently supported in browser environment
    console.log('üìä PowerPoint parsing not available, creating fallback document...');
    return await this.createFallbackDocument(file, 'PowerPoint parsing is not currently supported in browser environment. Please convert to PDF or extract text manually.');
  }

  /**
   * Parse PDF files (.pdf)
   */
  private async parsePdf(file: File): Promise<ParsedDocument> {
    try {
      console.log('üìÑ Parsing PDF file using Electron main process:', file.name);

      // Check if we're in a browser environment with Electron API access
      if (typeof window !== 'undefined' && (window as unknown as { electronAPI?: { parsePdfFile?: (buffer: ArrayBuffer, name: string) => Promise<{ success: boolean; text?: string; metadata?: { pages?: number; [key: string]: unknown }; error?: string }> } }).electronAPI?.parsePdfFile) {
        const fileBuffer = await file.arrayBuffer();
        const result = await (window as unknown as { electronAPI: { parsePdfFile: (buffer: ArrayBuffer, name: string) => Promise<{ success: boolean; text?: string; metadata?: { pages?: number; [key: string]: unknown }; error?: string }> } }).electronAPI.parsePdfFile(fileBuffer, file.name);

        console.log('üìÑ PDF parsing result from Electron:', { success: result.success, textLength: result.text?.length });

        if (result.success && result.text) {
          // Check if this is actually the fallback error message
          if (result.text.includes('PDF parsing module could not be loaded') ||
              result.text.includes('PDF text extraction is not available')) {
            console.error('üìÑ PDF parsing failed - received fallback message');
            return await this.createFallbackDocument(file, 'PDF parsing module not available in Electron environment');
          }

          return {
            text: result.text,
            metadata: {
              format: 'PDF',
              title: file.name,
              pages: result.metadata?.pages || 1,
              success: true,
              processingTime: Date.now() - Date.now() // Will be overridden by caller
            }
          };
        } else {
          console.error('üìÑ PDF parsing failed:', result.error);
          return await this.createFallbackDocument(file, `PDF parsing failed: ${result.error || 'Unknown error'}`);
        }
      } else {
        // Fallback if electronAPI is not available
        console.log('üìÑ ElectronAPI not available for PDF parsing');
        return await this.createFallbackDocument(file, 'PDF parsing not available in this environment. Please use the Electron app for PDF support.');
      }
    } catch (error) {
      console.error('üìÑ PDF parsing error:', error);
      return await this.createFallbackDocument(file, `PDF parsing error: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }





  // Helper methods
  private getFileExtension(filename: string): string {
    return filename.substring(filename.lastIndexOf('.'));
  }

  private async fileToBuffer(file: File): Promise<Buffer> {
    const arrayBuffer = await file.arrayBuffer();
    return Buffer.from(arrayBuffer);
  }

  private async fileToText(file: File): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = error => reject(error);
      reader.readAsText(file);
    });
  }

  private extractTextFromRtfDoc(doc: unknown): string {
    // Simple text extraction from RTF document structure
    if (typeof doc === 'string') {
      return doc;
    }

    if (doc && typeof doc === 'object') {
      const docObj = doc as Record<string, unknown>;

      if (docObj.content) {
        if (Array.isArray(docObj.content)) {
          return docObj.content.map((item: unknown) => this.extractTextFromRtfDoc(item)).join('');
        }
        return this.extractTextFromRtfDoc(docObj.content);
      }

      if (docObj.text && typeof docObj.text === 'string') {
        return docObj.text;
      }
    }

    return '';
  }

  private updateAverageProcessingTime(newTime: number): void {
    const totalSuccessful = this.stats.successfulParses;
    if (totalSuccessful === 1) {
      this.stats.averageProcessingTime = newTime;
    } else {
      this.stats.averageProcessingTime =
        ((this.stats.averageProcessingTime * (totalSuccessful - 1)) + newTime) / totalSuccessful;
    }
  }

  private categorizeError(error: unknown): string {
    if (error instanceof Error) {
      const message = error.message.toLowerCase();

      if (message.includes('network') || message.includes('fetch')) {
        return 'network';
      } else if (message.includes('permission') || message.includes('access')) {
        return 'permission';
      } else if (message.includes('format') || message.includes('invalid')) {
        return 'format';
      } else if (message.includes('memory') || message.includes('size')) {
        return 'memory';
      } else if (message.includes('timeout')) {
        return 'timeout';
      } else {
        return 'parsing';
      }
    }
    return 'unknown';
  }

  private async createFallbackDocument(file: File, originalError: string): Promise<ParsedDocument> {
    const fileExtension = this.getFileExtension(file.name).toLowerCase();
    const fileSize = Math.round(file.size / 1024);

    // Try to extract basic file metadata
    let fallbackText = `[Document: ${file.name}]\n`;
    fallbackText += `File Type: ${file.type || 'Unknown'}\n`;
    fallbackText += `File Size: ${fileSize}KB\n`;
    fallbackText += `Extension: ${fileExtension}\n`;
    fallbackText += `\nParsing Error: ${originalError}\n`;

    // Add format-specific guidance
    if (fileExtension === '.docx' || fileExtension === '.doc') {
      fallbackText += `\nTroubleshooting for Word documents:\n`;
      fallbackText += `‚Ä¢ The document may be password-protected\n`;
      fallbackText += `‚Ä¢ The file might be corrupted or incomplete\n`;
      fallbackText += `‚Ä¢ Try saving as a newer .docx format\n`;
      fallbackText += `‚Ä¢ Consider converting to PDF for better compatibility\n`;
      fallbackText += `‚Ä¢ You can copy and paste the text content directly into the chat\n`;
    } else if (fileExtension === '.xlsx' || fileExtension === '.xls') {
      fallbackText += `\nTroubleshooting for Excel files:\n`;
      fallbackText += `‚Ä¢ The spreadsheet may be password-protected\n`;
      fallbackText += `‚Ä¢ Try saving as a newer .xlsx format\n`;
      fallbackText += `‚Ä¢ Consider exporting as CSV for text analysis\n`;
    } else if (fileExtension === '.pptx' || fileExtension === '.ppt') {
      fallbackText += `\nNote: PowerPoint parsing is limited in browser environments.\n`;
      fallbackText += `‚Ä¢ Try converting to PDF for better text extraction\n`;
      fallbackText += `‚Ä¢ You can copy slide content and paste it directly\n`;
    }

    fallbackText += `\nWhat you can do:\n`;
    fallbackText += `‚Ä¢ Describe the document content and ask specific questions\n`;
    fallbackText += `‚Ä¢ Convert the file to PDF, TXT, or another supported format\n`;
    fallbackText += `‚Ä¢ Copy and paste the text content directly into the chat\n`;
    fallbackText += `‚Ä¢ Share specific sections or data you'd like me to analyze\n`;

    // Try to read as plain text if it might be a text-based format
    if (['.txt', '.md', '.log', '.csv', '.json', '.xml', '.html', '.htm'].includes(fileExtension)) {
      try {
        const textContent = await this.fileToText(file);
        if (textContent && textContent.trim().length > 0) {
          fallbackText += `\n\nRaw Content (may contain formatting artifacts):\n${textContent.substring(0, 2000)}`;
          if (textContent.length > 2000) {
            fallbackText += `\n... (content truncated, showing first 2000 characters)`;
          }
        }
      } catch (textError) {
        console.log(`Could not read ${file.name} as text:`, textError);
      }
    }

    return {
      text: fallbackText,
      metadata: {
        format: 'Fallback',
        title: file.name,
        originalError,
        fallbackUsed: true,
        fileSize: file.size,
        fileType: file.type
      }
    };
  }
}

export const documentParserService = new DocumentParserService();

import * as lancedb from 'vectordb';
import { pipeline, FeatureExtractionPipeline } from '@xenova/transformers';
import * as path from 'path';
import * as fs from 'fs/promises';
import { documentParserService } from './DocumentParserService.js';

interface KnowledgeBaseRecord {
  id: string;
  text: string;
  vector: number[];
  source: string;
  metadata?: Record<string, unknown>;
  [key: string]: unknown;
}

interface ExportData {
  version: string;
  exportDate: string;
  records: KnowledgeBaseRecord[];
  metadata: {
    totalRecords: number;
    totalDocuments: number;
    exportSize: number;
  };
}

/**
 * Manages the local knowledge base for the application.
 * This service handles the creation, management, and querying of a local vector database,
 * allowing AI models to access a shared, user-enrichable knowledge source.
 */
export class KnowledgeBaseService {
  private static instance: KnowledgeBaseService;
    private db: lancedb.Connection | undefined;
  private table: lancedb.Table | undefined;
  private embedder: FeatureExtractionPipeline | undefined;

  private constructor() {}

  /**
   * Gets the singleton instance of the KnowledgeBaseService.
   * @returns {KnowledgeBaseService} The singleton instance.
   */
  public static getInstance(): KnowledgeBaseService {
    if (!KnowledgeBaseService.instance) {
      KnowledgeBaseService.instance = new KnowledgeBaseService();
    }
    return KnowledgeBaseService.instance;
  }

  /**
   * Checks if the knowledge base is initialized.
   * @returns {boolean} True if the knowledge base is initialized, false otherwise.
   */
  public isInitialized(): boolean {
    return this.db !== undefined && this.table !== undefined && this.embedder !== undefined;
  }

  /**
   * Initializes the connection to the LanceDB vector database.
   * Creates the database and necessary tables if they don't exist.
   * @param dbPath - The local file system path to store the database.
   */
    public async initialize(dbPath: string): Promise<void> {
    this.db = await lancedb.connect(dbPath);
    // Using a pre-trained model for local embeddings
    this.embedder = await pipeline('feature-extraction', 'Xenova/all-MiniLM-L6-v2');

    const tableNames = await this.db.tableNames();
    if (!tableNames.includes('vectors')) {
      // The first time we create the table, we need to provide data.
      // We'll use a dummy entry to define the schema.
      const initialData = [{ 
        vector: await this.createEmbedding('initial'), 
        text: 'initial', 
        source: 'system' 
      }];
      this.table = await this.db.createTable('vectors', initialData);
      console.log('Knowledge base table "vectors" created.');
    } else {
      this.table = await this.db.openTable('vectors');
      console.log('Connected to existing knowledge base table "vectors".');
    }
  }

  /**
   * Safely imports pdf-parse module with comprehensive error handling
   */
  public async importPdfParseSafely(): Promise<(buffer: Buffer) => Promise<{ text: string; numpages: number; info: Record<string, unknown> }>> {
    try {
      console.log('üìÑ Attempting to import pdf-parse with working directory fix...');

      // Change to the pdf-parse module directory to ensure test files are found
      const originalCwd = process.cwd();

      try {
        // Try to import pdf-parse with a timeout to prevent hanging
        const importPromise = import('pdf-parse');
        const timeoutPromise = new Promise((_, reject) =>
          setTimeout(() => reject(new Error('PDF parse import timeout')), 5000)
        );

        const pdfParseModule = await Promise.race([importPromise, timeoutPromise]) as {
          default?: (buffer: Buffer) => Promise<{ text: string; numpages: number; info: Record<string, unknown> }>;
        } & ((buffer: Buffer) => Promise<{ text: string; numpages: number; info: Record<string, unknown> }>);
        console.log('‚úÖ pdf-parse module imported successfully');

        // Handle both CommonJS and ES module exports
        const pdfParse = pdfParseModule.default;
        if (typeof pdfParse === 'function') {
          return pdfParse;
        } else if (typeof pdfParseModule === 'function') {
          return pdfParseModule as (buffer: Buffer) => Promise<{ text: string; numpages: number; info: Record<string, unknown> }>;
        } else {
          throw new Error('pdf-parse module does not export a function');
        }
      } finally {
        // Always restore the original working directory
        process.chdir(originalCwd);
      }
    } catch (error) {
      console.error('‚ùå Failed to import pdf-parse:', error);

      // Return a fallback function that provides basic PDF info without parsing
      return async (buffer: Buffer) => {
        console.log('üìÑ Using fallback PDF handler (no text extraction)');
        return {
          text: `[PDF Document - ${buffer.length} bytes]\nNote: PDF text extraction is not available. The PDF parsing module could not be loaded.`,
          numpages: 1,
          info: {
            Title: 'PDF Document',
            Creator: 'Unknown',
            Producer: 'Fallback Handler'
          } as Record<string, unknown>
        };
      };
    }
  }

  /**
   * Adds a document to the knowledge base.
   * The document is processed, chunked, embedded, and stored in the vector database.
   * @param filePath - The path to the file to be added (e.g., a PDF).
   * @param metadata - Optional metadata to include with the document.
   * @param progressCallback - Optional callback for progress updates.
   */
  public async addDocument(
    filePath: string,
    metadata?: Record<string, unknown>,
    progressCallback?: (progress: {step: string, message: string, current?: number, total?: number, chunkCount?: number}) => void
  ): Promise<void>;

  /**
   * Adds a document to the knowledge base from a File object.
   * The document is processed, chunked, embedded, and stored in the vector database.
   * @param file - The File object to be added.
   * @param metadata - Optional metadata to include with the document.
   * @param progressCallback - Optional callback for progress updates.
   */
  public async addDocument(
    file: File,
    metadata?: Record<string, unknown>,
    progressCallback?: (progress: {step: string, message: string, current?: number, total?: number, chunkCount?: number}) => void
  ): Promise<void>;

  public async addDocument(
    filePathOrFile: string | File,
    metadata?: Record<string, unknown>,
    progressCallback?: (progress: {step: string, message: string, current?: number, total?: number, chunkCount?: number}) => void
  ): Promise<void> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    try {
      let text: string;
      let documentSource: string;
      let documentMetadata: Record<string, unknown> = metadata || {};

      if (typeof filePathOrFile === 'string') {
        // Handle file path (existing PDF functionality)
        const filePath = filePathOrFile;
        documentSource = path.basename(filePath);
        progressCallback?.({step: 'reading', message: `Reading file: ${documentSource}`});
        console.log(`Starting to process document from path: ${filePath}`);

        // Check if file exists
        const fileBuffer = await fs.readFile(filePath);
        console.log(`File read successfully, size: ${fileBuffer.length} bytes`);

        // Determine file type and parse accordingly
        const fileExtension = path.extname(filePath).toLowerCase();

        progressCallback?.({step: 'parsing', message: `Parsing ${fileExtension.toUpperCase()} file: ${documentSource}`});

        if (fileExtension === '.pdf') {
          // Use existing PDF parsing logic
          try {
            const pdfParse = await this.importPdfParseSafely();
            const pdfData = await pdfParse(fileBuffer);
            text = pdfData.text;
            console.log(`PDF parsed successfully, text length: ${text.length} characters`);
            documentMetadata = { ...documentMetadata, format: 'PDF', pages: pdfData.numpages };
          } catch (pdfError) {
            console.error('PDF parsing failed:', pdfError);
            throw new Error(`Failed to parse PDF: ${pdfError instanceof Error ? pdfError.message : 'Unknown error'}`);
          }
        } else {
          // For non-PDF files from file path, read as text
          const textContent = await fs.readFile(filePath, 'utf-8');
          text = textContent;
          documentMetadata = { ...documentMetadata, format: 'Text', characterCount: text.length };
        }
      } else {
        // Handle File object (new functionality)
        const file = filePathOrFile;
        documentSource = file.name;
        progressCallback?.({step: 'reading', message: `Reading file: ${documentSource}`});
        console.log(`Starting to process document from File object: ${file.name}`);
        console.log(`File size: ${file.size} bytes, type: ${file.type}`);

        progressCallback?.({step: 'parsing', message: `Parsing file: ${documentSource}`});

        // Use DocumentParserService to parse the file
        const parsedDocument = await documentParserService.parseDocument(file);
        text = parsedDocument.text;
        documentMetadata = {
          ...documentMetadata,
          ...parsedDocument.metadata,
          fileSize: file.size,
          fileType: file.type,
          uploadDate: new Date().toISOString()
        };

        console.log(`Document parsed successfully, text length: ${text.length} characters`);
      }

      if (!text || text.trim().length === 0) {
        throw new Error('No text content found in the document');
      }

      progressCallback?.({step: 'chunking', message: `Chunking text for: ${documentSource}`});
      const chunks = this.chunkText(text);
      console.log(`Document chunked into ${chunks.length} pieces.`);
      progressCallback?.({step: 'chunking', message: `Generated ${chunks.length} chunks for: ${documentSource}`, chunkCount: chunks.length});

      const records = [];
      console.log(`Using document source: ${documentSource}`);

      progressCallback?.({step: 'embedding', message: `Creating embeddings for: ${documentSource}`, current: 0, total: chunks.length});

      for (let i = 0; i < chunks.length; i++) {
        const chunk = chunks[i];
        console.log(`Processing chunk ${i + 1}/${chunks.length}`);
        progressCallback?.({step: 'embedding', message: `Processing chunk ${i + 1}/${chunks.length} for: ${documentSource}`, current: i + 1, total: chunks.length});

        const embedding = await this.createEmbedding(chunk);
        records.push({
          vector: embedding,
          text: chunk,
          source: documentSource,
          metadata: documentMetadata,
          chunkIndex: i
        });
      }

      progressCallback?.({step: 'storing', message: `Storing ${records.length} records for: ${documentSource}`});
      console.log(`Adding ${records.length} records to the knowledge base...`);
      await this.table.add(records);
      console.log(`Successfully added ${records.length} records to the knowledge base for document: ${documentSource}`);

      // Verify the records were added by checking the count
      const dummyEmbedding = new Array(384).fill(0); // MiniLM-L6-v2 has 384 dimensions
      const allRecords = await this.table.search(dummyEmbedding).limit(10000).execute();
      const documentRecords = allRecords.filter(r => (r as {source: string}).source === documentSource);
      console.log(`Verification: Found ${documentRecords.length} records for document ${documentSource} in the database`);

      progressCallback?.({step: 'complete', message: `Successfully processed: ${documentSource}`, chunkCount: chunks.length});

    } catch (error) {
      const errorMessage = typeof filePathOrFile === 'string' ? filePathOrFile : filePathOrFile.name;
      console.error(`Error adding document ${errorMessage}:`, error);
      progressCallback?.({step: 'error', message: `Failed to process: ${errorMessage} - ${error instanceof Error ? error.message : 'Unknown error'}`});
      throw error;
    }
  }

  /**
   * Adds multiple documents to the knowledge base with real-time progress updates.
   * @param filePaths - Array of file paths to process.
   * @param progressCallback - Callback for real-time progress updates.
   * @returns Promise with batch processing results.
   */
  public async addDocumentsBatch(
    filePaths: string[],
    progressCallback?: (progress: {
      step: string;
      message: string;
      fileIndex: number;
      totalFiles: number;
      fileName: string;
      chunkCount?: number;
      status: 'processing' | 'success' | 'error';
      error?: string;
    }) => void
  ): Promise<{success: boolean, results: Array<{filePath: string, success: boolean, error?: string, chunkCount?: number}>, summary: string}> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    const results: Array<{filePath: string, success: boolean, error?: string, chunkCount?: number}> = [];
    let successCount = 0;
    let errorCount = 0;

    progressCallback?.({
      step: 'starting',
      message: `Starting batch processing of ${filePaths.length} documents`,
      fileIndex: 0,
      totalFiles: filePaths.length,
      fileName: '',
      status: 'processing'
    });

    for (let i = 0; i < filePaths.length; i++) {
      const filePath = filePaths[i];
      const fileName = path.basename(filePath);

      try {
        progressCallback?.({
          step: 'processing',
          message: `Processing file ${i + 1}/${filePaths.length}: ${fileName}`,
          fileIndex: i + 1,
          totalFiles: filePaths.length,
          fileName,
          status: 'processing'
        });

        // Process individual document with progress tracking
        let chunkCount = 0;
        await this.addDocument(filePath, undefined, (docProgress) => {
          if (docProgress.chunkCount) {
            chunkCount = docProgress.chunkCount;
          }

          progressCallback?.({
            step: docProgress.step,
            message: `File ${i + 1}/${filePaths.length}: ${docProgress.message}`,
            fileIndex: i + 1,
            totalFiles: filePaths.length,
            fileName,
            chunkCount: docProgress.chunkCount,
            status: 'processing'
          });
        });

        results.push({
          filePath,
          success: true,
          chunkCount
        });

        successCount++;

        progressCallback?.({
          step: 'complete',
          message: `Successfully processed ${fileName} (${chunkCount} chunks)`,
          fileIndex: i + 1,
          totalFiles: filePaths.length,
          fileName,
          chunkCount,
          status: 'success'
        });

      } catch (error) {
        const errorMessage = error instanceof Error ? error.message : 'Unknown error';

        results.push({
          filePath,
          success: false,
          error: errorMessage
        });

        errorCount++;

        progressCallback?.({
          step: 'error',
          message: `Failed to process ${fileName}: ${errorMessage}`,
          fileIndex: i + 1,
          totalFiles: filePaths.length,
          fileName,
          status: 'error',
          error: errorMessage
        });

        console.error(`Error processing file ${filePath}:`, error);
      }
    }

    const summary = `Batch processing completed: ${successCount} successful, ${errorCount} failed out of ${filePaths.length} total files`;

    progressCallback?.({
      step: 'finished',
      message: summary,
      fileIndex: filePaths.length,
      totalFiles: filePaths.length,
      fileName: '',
      status: successCount === filePaths.length ? 'success' : errorCount === filePaths.length ? 'error' : 'processing'
    });

    return {
      success: errorCount === 0,
      results,
      summary
    };
  }

  /**
   * Adds a document to the knowledge base from a Google Docs URL.
   * The document is processed, chunked, embedded, and stored in the vector database.
   * @param url - The Google Docs URL to import.
   * @param metadata - Optional metadata to include with the document.
   */
  public async addDocumentFromUrl(url: string, metadata?: Record<string, unknown>): Promise<void> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    try {
      console.log(`Starting to process document from URL: ${url}`);

      // Validate Google Docs URL
      if (!this.isValidGoogleDocsUrl(url)) {
        throw new Error('Invalid Google Docs URL. Please provide a valid Google Docs sharing URL.');
      }

      // Convert Google Docs URL to export format (plain text)
      const exportUrl = this.convertToGoogleDocsExportUrl(url);
      console.log(`Converted to export URL: ${exportUrl}`);

      // Fetch the document content
      const response = await fetch(exportUrl);
      if (!response.ok) {
        throw new Error(`Failed to fetch Google Docs content: ${response.status} ${response.statusText}. Make sure the document is publicly accessible.`);
      }

      const text = await response.text();
      console.log(`Google Docs content fetched successfully, text length: ${text.length} characters`);

      if (!text || text.trim().length === 0) {
        throw new Error('No text content found in the Google Docs document');
      }

      // Extract document title from URL or use a default
      const documentTitle = this.extractGoogleDocsTitle(url) || 'Google Docs Document';
      const documentSource = `${documentTitle}.txt`;

      const chunks = this.chunkText(text);
      console.log(`Document chunked into ${chunks.length} pieces.`);

      const documentMetadata = {
        ...metadata,
        format: 'Google Docs',
        sourceUrl: url,
        characterCount: text.length,
        uploadDate: new Date().toISOString(),
        title: documentTitle
      };

      const records = [];
      console.log(`Using document source: ${documentSource}`);

      for (let i = 0; i < chunks.length; i++) {
        const chunk = chunks[i];
        console.log(`Processing chunk ${i + 1}/${chunks.length}`);
        const embedding = await this.createEmbedding(chunk);
        records.push({
          vector: embedding,
          text: chunk,
          source: documentSource,
          metadata: documentMetadata,
          chunkIndex: i
        });
      }

      console.log(`Adding ${records.length} records to the knowledge base...`);
      await this.table.add(records);
      console.log(`Successfully added ${records.length} records to the knowledge base for document: ${documentSource}`);

      // Verify the records were added by checking the count
      const dummyEmbedding = new Array(384).fill(0); // MiniLM-L6-v2 has 384 dimensions
      const allRecords = await this.table.search(dummyEmbedding).limit(10000).execute();
      const documentRecords = allRecords.filter(r => (r as {source: string}).source === documentSource);
      console.log(`Verification: Found ${documentRecords.length} records for document ${documentSource} in the database`);

    } catch (error) {
      console.error(`Error adding document from URL ${url}:`, error);
      throw error;
    }
  }

  /**
   * Validates if a URL is a valid Google Docs URL
   */
  private isValidGoogleDocsUrl(url: string): boolean {
    const googleDocsPattern = /^https:\/\/docs\.google\.com\/document\/d\/[a-zA-Z0-9-_]+/;
    return googleDocsPattern.test(url);
  }

  /**
   * Converts a Google Docs sharing URL to an export URL for plain text
   */
  private convertToGoogleDocsExportUrl(url: string): string {
    // Extract document ID from the URL
    const match = url.match(/\/document\/d\/([a-zA-Z0-9-_]+)/);
    if (!match) {
      throw new Error('Could not extract document ID from Google Docs URL');
    }

    const documentId = match[1];
    return `https://docs.google.com/document/d/${documentId}/export?format=txt`;
  }

  /**
   * Attempts to extract a title from the Google Docs URL
   */
  private extractGoogleDocsTitle(url: string): string | null {
    // Try to extract title from URL if it contains one
    const titleMatch = url.match(/\/document\/d\/[^/]+\/edit.*[?&]title=([^&]+)/);
    if (titleMatch) {
      return decodeURIComponent(titleMatch[1]);
    }
    return null;
  }

  /**
   * Generates a vector embedding for a given text.
   * @param text - The text to embed.
   * @returns {Promise<number[]>} A promise that resolves to the vector embedding.
   */
    /**
   * Splits a long text into smaller, overlapping chunks.
   * @param text - The text to chunk.
   * @param chunkSize - The size of each chunk.
   * @param overlap - The overlap between consecutive chunks.
   * @returns {string[]} An array of text chunks.
   */
  private chunkText(text: string, chunkSize = 1000, overlap = 200): string[] {
    const chunks: string[] = [];
    for (let i = 0; i < text.length; i += chunkSize - overlap) {
      chunks.push(text.substring(i, i + chunkSize));
    }
    return chunks;
  }

  private async createEmbedding(text: string): Promise<number[]> {
    if (!this.embedder) {
      throw new Error('Embedding pipeline is not initialized.');
    }
    const result = await this.embedder(text, { pooling: 'mean', normalize: true });
    return Array.from(result.data);
  }

  /**
   * Searches the knowledge base for content relevant to a given query.
   * @param queryText - The text to search for.
   * @param limit - The maximum number of relevant chunks to return.
   * @returns {Promise<Array<{text: string, source: string, score: number}>>} A promise that resolves to an array of relevant document chunks.
   */
    public async search(queryText: string, limit = 5): Promise<Array<{text: string, source: string, score: number}>> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    const queryEmbedding = await this.createEmbedding(queryText);

    const results = await this.table
      .search(queryEmbedding)
      .limit(limit)
      .execute();

    return results.map(r => ({
      text: String(r.text),
      source: String(r.source),
      score: Number(r.score)
    }));
  }

  /**
   * Removes a document from the knowledge base.
   * @param documentId - The identifier of the document to remove (typically the file path or name).
   * @returns {Promise<void>} A promise that resolves when the document is removed.
   */
  public async removeDocument(documentId: string): Promise<void> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    // Remove all chunks that belong to this document
    await this.table.delete(`source = '${documentId}'`);
    console.log(`Removed document: ${documentId}`);
  }

  /**
   * Gets a list of all documents in the knowledge base.
   * @returns {Promise<string[]>} A promise that resolves to an array of document identifiers.
   */
  public async getDocuments(): Promise<string[]> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    try {
      // Use search with a dummy embedding to get all records
      // This is the correct way to get all records from a LanceDB table
      const dummyEmbedding = new Array(384).fill(0); // MiniLM-L6-v2 has 384 dimensions
      const results = await this.table
        .search(dummyEmbedding)
        .limit(10000)
        .execute();

      console.log(`Retrieved ${results.length} records from knowledge base`);

      // Extract unique sources and filter out system entries
      const sourceSet = new Set(results.map(r => (r as {source: string}).source as string));
      const uniqueSources = Array.from(sourceSet).filter(source => source !== 'system');

      console.log(`Found ${uniqueSources.length} unique documents:`, uniqueSources);
      return uniqueSources;
    } catch (error) {
      console.error('Error getting documents:', error);
      return [];
    }
  }

  /**
   * Gets a list of all documents in the knowledge base with their metadata.
   * @returns {Promise<Array<{source: string, metadata: Record<string, unknown>, chunkCount: number}>>} A promise that resolves to an array of documents with metadata.
   */
  public async getDocumentsWithMetadata(): Promise<Array<{source: string, metadata: Record<string, unknown>, chunkCount: number, addedAt?: string}>> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    try {
      // Use search with a dummy embedding to get all records
      const dummyEmbedding = new Array(384).fill(0); // MiniLM-L6-v2 has 384 dimensions
      const results = await this.table
        .search(dummyEmbedding)
        .limit(10000)
        .execute();

      console.log(`Retrieved ${results.length} records from knowledge base`);

      // Group records by source and extract metadata
      const documentMap = new Map<string, {metadata: Record<string, unknown>, chunks: number}>();

      for (const record of results) {
        const source = (record as {source: string}).source;
        const metadata = (record as {metadata?: Record<string, unknown>}).metadata || {};

        // Skip system entries
        if (source === 'system') continue;

        if (documentMap.has(source)) {
          documentMap.get(source)!.chunks++;
        } else {
          documentMap.set(source, { metadata, chunks: 1 });
        }
      }

      // Convert to array format
      const documentsWithMetadata = Array.from(documentMap.entries()).map(([source, data]) => ({
        source,
        metadata: data.metadata,
        chunkCount: data.chunks,
        addedAt: data.metadata.uploadDate as string || new Date().toISOString()
      }));

      console.log(`Found ${documentsWithMetadata.length} unique documents with metadata`);
      return documentsWithMetadata;
    } catch (error) {
      console.error('Error getting documents with metadata:', error);
      return [];
    }
  }

  /**
   * Exports the entire knowledge base to a portable JSON format.
   * @param progressCallback - Optional callback to report export progress.
   * @returns {Promise<{data: ExportData, stats: {totalRecords: number, totalDocuments: number, exportSize: number, exportTime: number}}>} The exported data and statistics.
   */
  public async exportKnowledgeBase(progressCallback?: (progress: {step: string, current: number, total: number, message: string}) => void): Promise<{data: ExportData, stats: {totalRecords: number, totalDocuments: number, exportSize: number, exportTime: number}}> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    const startTime = Date.now();
    progressCallback?.({step: 'initializing', current: 0, total: 100, message: 'Starting knowledge base export...'});

    try {
      // Get all records from the database
      progressCallback?.({step: 'fetching', current: 10, total: 100, message: 'Fetching all records from database...'});
      const dummyEmbedding = new Array(384).fill(0); // MiniLM-L6-v2 has 384 dimensions
      const allRecords = await this.table.search(dummyEmbedding).limit(50000).execute();

      progressCallback?.({step: 'processing', current: 30, total: 100, message: `Processing ${allRecords.length} records...`});

      // Filter out system entries and organize data
      const validRecords = allRecords.filter(r => (r as {source: string}).source !== 'system');

      // Create export structure
      const exportData = {
        version: '1.0.0',
        exportDate: new Date().toISOString(),
        records: validRecords.map(record => ({
          id: (record as {id?: string}).id || `${Date.now()}-${Math.random()}`,
          vector: (record as {vector: number[]}).vector,
          text: (record as {text: string}).text,
          source: (record as {source: string}).source,
          metadata: (record as {metadata?: Record<string, unknown>}).metadata || {},
          chunkIndex: (record as {chunkIndex?: number}).chunkIndex || 0
        })),
        metadata: {
          totalRecords: 0, // Will be calculated below
          totalDocuments: 0, // Will be calculated below
          exportSize: 0 // Will be calculated below
        }
      };

      progressCallback?.({step: 'finalizing', current: 80, total: 100, message: 'Finalizing export data...'});

      // Calculate statistics
      const uniqueSources = new Set(exportData.records.map(r => r.source));
      const exportTime = Date.now() - startTime;

      // Update metadata with calculated values
      exportData.metadata = {
        totalRecords: exportData.records.length,
        totalDocuments: uniqueSources.size,
        exportSize: JSON.stringify(exportData).length
      };

      const stats = {
        totalRecords: exportData.records.length,
        totalDocuments: uniqueSources.size,
        exportSize: exportData.metadata.exportSize,
        exportTime
      };

      progressCallback?.({step: 'complete', current: 100, total: 100, message: `Export completed: ${stats.totalDocuments} documents, ${stats.totalRecords} chunks`});

      console.log(`‚úÖ Knowledge base exported: ${stats.totalDocuments} documents, ${stats.totalRecords} records, ${Math.round(stats.exportSize / 1024)}KB`);

      return { data: exportData, stats };
    } catch (error) {
      console.error('‚ùå Failed to export knowledge base:', error);
      throw error;
    }
  }

  /**
   * Imports knowledge base data from a previously exported format.
   * @param importData - The data to import.
   * @param options - Import options including merge vs replace mode.
   * @param progressCallback - Optional callback to report import progress.
   * @returns {Promise<{success: boolean, stats: {importedRecords: number, importedDocuments: number, skippedRecords: number, importTime: number}}>} Import results and statistics.
   */
  public async importKnowledgeBase(
    importData: ExportData | { records: KnowledgeBaseRecord[] },
    options: {mode: 'replace' | 'merge', validateEmbeddings?: boolean} = {mode: 'replace', validateEmbeddings: true},
    progressCallback?: (progress: {step: string, current: number, total: number, message: string}) => void
  ): Promise<{success: boolean, stats: {importedRecords: number, importedDocuments: number, skippedRecords: number, importTime: number}}> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    const startTime = Date.now();
    progressCallback?.({step: 'validating', current: 0, total: 100, message: 'Validating import data...'});

    try {
      // Validate import data structure
      if (!this.validateImportData(importData)) {
        throw new Error('Invalid import data format. Please ensure you are importing a valid knowledge base export file.');
      }

      const records = importData.records || [];
      progressCallback?.({step: 'preparing', current: 10, total: 100, message: `Preparing to import ${records.length} records...`});

      // Clear existing data if replace mode
      if (options.mode === 'replace') {
        progressCallback?.({step: 'clearing', current: 20, total: 100, message: 'Clearing existing knowledge base...'});
        await this.clearKnowledgeBase();
      }

      // Process records in batches for better performance
      const batchSize = 100;
      let importedRecords = 0;
      let skippedRecords = 0;
      const uniqueSources = new Set<string>();

      for (let i = 0; i < records.length; i += batchSize) {
        const batch = records.slice(i, i + batchSize);
        const progress = 30 + ((i / records.length) * 60);

        progressCallback?.({
          step: 'importing',
          current: Math.round(progress),
          total: 100,
          message: `Importing batch ${Math.floor(i / batchSize) + 1}/${Math.ceil(records.length / batchSize)}...`
        });

        try {
          // Validate and prepare batch records
          const validBatch = batch.filter((record: KnowledgeBaseRecord) => {
            if (options.validateEmbeddings && (!record.vector || record.vector.length !== 384)) {
              skippedRecords++;
              return false;
            }
            if (!record.text || !record.source) {
              skippedRecords++;
              return false;
            }
            return true;
          });

          if (validBatch.length > 0) {
            await this.table.add(validBatch);
            importedRecords += validBatch.length;
            validBatch.forEach((record: KnowledgeBaseRecord) => uniqueSources.add(record.source));
          }
        } catch (batchError) {
          console.error(`‚ùå Failed to import batch ${Math.floor(i / batchSize) + 1}:`, batchError);
          skippedRecords += batch.length;
        }
      }

      const importTime = Date.now() - startTime;
      const stats = {
        importedRecords,
        importedDocuments: uniqueSources.size,
        skippedRecords,
        importTime
      };

      progressCallback?.({step: 'complete', current: 100, total: 100, message: `Import completed: ${stats.importedDocuments} documents, ${stats.importedRecords} records imported`});

      console.log(`‚úÖ Knowledge base imported: ${stats.importedDocuments} documents, ${stats.importedRecords} records imported, ${stats.skippedRecords} skipped`);

      return { success: true, stats };
    } catch (error) {
      console.error('‚ùå Failed to import knowledge base:', error);
      throw error;
    }
  }

  /**
   * Validates the structure of import data.
   * @param data - The data to validate.
   * @returns {boolean} True if the data is valid for import.
   */
  private validateImportData(data: unknown): data is ExportData | { records: KnowledgeBaseRecord[] } {
    if (!data || typeof data !== 'object') {
      return false;
    }

    const typedData = data as Record<string, unknown>;

    // Check required fields
    if (!typedData.version || !typedData.records || !Array.isArray(typedData.records)) {
      return false;
    }

    // Check version compatibility
    if (typedData.version !== '1.0.0') {
      console.warn(`‚ö†Ô∏è Import data version ${typedData.version} may not be fully compatible with current version 1.0.0`);
    }

    // Validate a sample of records
    const sampleSize = Math.min(10, typedData.records.length);
    for (let i = 0; i < sampleSize; i++) {
      const record = (typedData.records as unknown[])[i] as Record<string, unknown>;
      if (!record.text || !record.source || !Array.isArray(record.vector)) {
        return false;
      }
    }

    return true;
  }

  /**
   * Clears all data from the knowledge base.
   * @returns {Promise<void>} A promise that resolves when the knowledge base is cleared.
   */
  public async clearKnowledgeBase(): Promise<void> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    try {
      // Get all records to delete them
      const dummyEmbedding = new Array(384).fill(0);
      const allRecords = await this.table.search(dummyEmbedding).limit(50000).execute();

      // Delete all non-system records
      for (const record of allRecords) {
        const source = (record as {source: string}).source;
        if (source !== 'system') {
          await this.table.delete(`source = '${source}'`);
        }
      }

      console.log('‚úÖ Knowledge base cleared successfully');
    } catch (error) {
      console.error('‚ùå Failed to clear knowledge base:', error);
      throw error;
    }
  }

  /**
   * Gets statistics about the current knowledge base.
   * @returns {Promise<{totalRecords: number, totalDocuments: number, databaseSize: number}>} Knowledge base statistics.
   */
  public async getKnowledgeBaseStats(): Promise<{totalRecords: number, totalDocuments: number, databaseSize: number}> {
    if (!this.table) {
      throw new Error('Knowledge base is not initialized.');
    }

    try {
      const dummyEmbedding = new Array(384).fill(0);
      const allRecords = await this.table.search(dummyEmbedding).limit(50000).execute();

      const validRecords = allRecords.filter(r => (r as {source: string}).source !== 'system');
      const uniqueSources = new Set(validRecords.map(r => (r as {source: string}).source));

      // Estimate database size (rough calculation)
      const estimatedSize = validRecords.length * (384 * 4 + 500); // 4 bytes per float + estimated text size

      return {
        totalRecords: validRecords.length,
        totalDocuments: uniqueSources.size,
        databaseSize: estimatedSize
      };
    } catch (error) {
      console.error('Error getting knowledge base stats:', error);
      return { totalRecords: 0, totalDocuments: 0, databaseSize: 0 };
    }
  }
}

