  // Force refresh API keys and clear any cached data
  async forceRefreshApiKeys(): Promise<void> {
    console.log('üîÑ ChatService: Force refreshing API keys...');
    try {
      // Force reload API keys from secure storage
      if (secureApiKeyService) {
        await secureApiKeyService.forceReloadApiKeys();
      }

      // Clear model cache to force fresh fetch with new API keys
      this.clearModelCache();

      console.log('‚úÖ ChatService: API keys and cache refreshed successfully');
    } catch (error) {
      console.error('‚ùå ChatService: Failed to refresh API keys:', error);
    }
  },

  // Helper method to get provider instance
  getProviderInstance(providerId: string) {
    // Get the actual provider instance from ProviderFactory via llmService's adapter
    return llmService.getProviderAdapter().getProvider(providerId);
  },

  // Helper method to get provider config
  getProviderConfig(providerId: string) {
    const providers = llmService.getProviders();
    return providers.find(p => p.id === providerId);
  },

  // Generic file processing for non-Mistral providers
  async processFilesGeneric(files: File[], message: string, provider: string): Promise<Array<ContentItem>> {
    const contentArray: Array<ContentItem> = [
      {
        type: 'text',
        text: message || 'Please analyze the attached content.'
      }
    ];

    // Process each file
    for (const file of files) {
      console.log('Processing file:', file.name, file.type);

      if (file.type.startsWith('image/')) {
        // Convert image to base64 data URL
        console.log('Converting image to base64...');
        const base64 = await this.fileToBase64(file);
        console.log('Image converted, base64 length:', base64.length);

        contentArray.push({
          type: 'image_url',
          image_url: {
            url: base64
          }
        });
      } else {
        const nativelySupported = file.type === 'application/pdf' || file.type.startsWith('text/') || file.type === 'text/csv';

        // Check if provider supports this file type natively
        if (nativelySupported && provider === 'anthropic') {
          // Anthropic supports native document handling for PDFs and text files
          console.log('Sending document to Anthropic:', file.name);
          const base64 = await this.fileToBase64(file);
          const base64Data = base64.split(',')[1]; // Remove data URL prefix

          contentArray.push({
            type: 'document',
            document: {
              name: file.name,
              media_type: file.type,
              data: base64Data
            }
          } as ContentItem);
        } else {
          // For all other cases, extract text content using DocumentParserService
          console.log('Extracting text content for provider:', provider, file.name);
          const textContent = await this.extractTextFromFile(file);
          console.log('Text extracted, length:', textContent.length);
          contentArray[0].text += `\n\n[File: ${file.name}]\n${textContent}`;
        }
      }
    }

    return contentArray;
  }
};

/**
 * Filesystem Command Implementations
 * Implements DesktopCommanderMCP filesystem functionality as internal commands
 */

import {
  CommandResult,
  InternalCommandTool,
  ReadFileArgs,
  ReadMultipleFilesArgs,
  WriteFileArgs,
  CreateDirectoryArgs,
  ListDirectoryArgs,
  MoveFileArgs,
  SearchFilesArgs,
  SearchCodeArgs,
  GetFileInfoArgs,
  DeleteFileArgs
} from '../../types/internalCommands';
import { filesystemManager } from '../filesystemManager';
import { internalCommandService } from '../internalCommandService';
import { processManager } from '../processManager';

/**
 * Read file contents with optional offset and length
 */
async function readFile(args: unknown): Promise<CommandResult> {
  try {
    const { path, isUrl, offset, length } = args as ReadFileArgs;

    const result = await filesystemManager.readFile(path, isUrl, offset, length);

    if (result.isImage) {
      return {
        success: true,
        content: [
          {
            type: 'text',
            text: `Image file: ${path} (${result.mimeType})\n`
          },
          {
            type: 'image',
            data: result.content,
            mimeType: result.mimeType
          }
        ]
      };
    } else {
      return {
        success: true,
        content: [{
          type: 'text',
          text: result.content
        }]
      };
    }

  } catch (error) {
    return internalCommandService.createErrorResponse(
      error instanceof Error ? error.message : String(error)
    );
  }
}

/**
 * Read multiple files simultaneously
 */
async function readMultipleFiles(args: unknown): Promise<CommandResult> {
  try {
    const { paths } = args as ReadMultipleFilesArgs;

    const results = await filesystemManager.readMultipleFiles(paths);

    // Create summary
    const summary = results.map(result => {
      if (result.error) {
        return `${result.path}: Error - ${result.error}`;
      } else if (result.mimeType) {
        return `${result.path}: ${result.mimeType} ${result.isImage ? '(image)' : '(text)'}`;
      } else {
        return `${result.path}: Unknown type`;
      }
    }).join('\n');

    // Create content items
    const contentItems: Array<{type: 'text' | 'image', text?: string, data?: string, mimeType?: string}> = [];

    contentItems.push({ type: 'text', text: summary });

    // Add each file content
    for (const result of results) {
      if (!result.error && result.content !== undefined) {
        if (result.isImage && result.mimeType) {
          contentItems.push({
            type: 'image',
            data: result.content,
            mimeType: result.mimeType
          });
        } else {
          contentItems.push({
            type: 'text',
            text: `\n--- ${result.path} contents: ---\n${result.content}`
          });
        }
      }
    }

    return {
      success: true,
      content: contentItems
    };

  } catch (error) {
    return internalCommandService.createErrorResponse(
      error instanceof Error ? error.message : String(error)
    );
  }
}

/**
 * Write file contents
 */
async function writeFile(args: unknown): Promise<CommandResult> {
  try {
    const { path, content, mode } = args as WriteFileArgs;

    await filesystemManager.writeFile(path, content, mode);

    const lines = content.split('\n');
    const lineCount = lines.length;
    const modeMessage = mode === 'append' ? 'appended to' : 'wrote to';

    // Get line limit from configuration for performance tip
    const config = internalCommandService.getConfiguration();
    const maxLines = config.fileWriteLineLimit;

    let message = `Successfully ${modeMessage} ${path} (${lineCount} lines)`;

    if (lineCount > maxLines) {
      message += `\nüí° Performance tip: For optimal speed, consider chunking files into ‚â§${maxLines} line pieces in future operations.`;
    }

    return internalCommandService.createSuccessResponse(message);

  } catch (error) {
    return internalCommandService.createErrorResponse(
      error instanceof Error ? error.message : String(error)
    );
  }
}

