/**
 * Memory Cleanup Service for LiteLLM
 * Handles automatic memory maintenance, archiving, and optimization
 */

import { memoryService } from './memoryService';
import { MemoryEntry } from '../types/memory';

export interface CleanupConfig {
  maxMemories: number;
  maxAge: number; // days
  maxSize: number; // bytes
  archiveOldMemories: boolean;
  removeUnusedMemories: boolean;
  consolidateDuplicates: boolean;
  minAccessCount: number;
}

export interface CleanupResult {
  success: boolean;
  deleted: number;
  archived: number;
  consolidated: number;
  errors: string[];
  sizeBefore: number;
  sizeAfter: number;
}

class MemoryCleanupService {
  private readonly DEFAULT_CONFIG: CleanupConfig = {
    maxMemories: 1000,
    maxAge: 365, // 1 year
    maxSize: 50 * 1024 * 1024, // 50MB
    archiveOldMemories: true,
    removeUnusedMemories: false,
    consolidateDuplicates: true,
    minAccessCount: 0
  };

  /**
   * Perform automatic memory cleanup
   */
  async performCleanup(config: Partial<CleanupConfig> = {}): Promise<CleanupResult> {
    const finalConfig = { ...this.DEFAULT_CONFIG, ...config };
    const result: CleanupResult = {
      success: true,
      deleted: 0,
      archived: 0,
      consolidated: 0,
      errors: [],
      sizeBefore: 0,
      sizeAfter: 0
    };

    try {
      // Get current stats
      const statsResult = await memoryService.getMemoryStats();
      if (statsResult.success && statsResult.data) {
        result.sizeBefore = statsResult.data.totalSize;
      }

      // Get all memories for analysis
      const searchResult = await memoryService.searchMemories({
        query: { limit: 10000 }
      });

      if (!searchResult.success || !searchResult.data) {
        result.success = false;
        result.errors.push('Failed to retrieve memories for cleanup');
        return result;
      }

      const memories = searchResult.data.results.map(r => r.entry);
      console.log(`üßπ Starting cleanup of ${memories.length} memories`);

      // 1. Remove old memories
      if (finalConfig.maxAge > 0) {
        const oldMemories = this.findOldMemories(memories, finalConfig.maxAge);
        for (const memory of oldMemories) {
          if (finalConfig.archiveOldMemories) {
            // Archive instead of delete
            await this.archiveMemory(memory);
            result.archived++;
          } else {
            const deleteResult = await memoryService.deleteMemory({ id: memory.id });
            if (deleteResult.success) {
              result.deleted++;
            } else {
              result.errors.push(`Failed to delete old memory ${memory.id}: ${deleteResult.error}`);
            }
          }
        }
      }

      // 2. Remove unused memories
      if (finalConfig.removeUnusedMemories) {
        const unusedMemories = this.findUnusedMemories(memories, finalConfig.minAccessCount);
        for (const memory of unusedMemories) {
          const deleteResult = await memoryService.deleteMemory({ id: memory.id });
          if (deleteResult.success) {
            result.deleted++;
          } else {
            result.errors.push(`Failed to delete unused memory ${memory.id}: ${deleteResult.error}`);
          }
        }
      }

      // 3. Consolidate duplicates
      if (finalConfig.consolidateDuplicates) {
        const duplicateGroups = this.findDuplicateMemories(memories);
        for (const group of duplicateGroups) {
          const consolidateResult = await this.consolidateDuplicates(group);
          if (consolidateResult.success) {
            result.consolidated += consolidateResult.consolidated;
          } else {
            result.errors.push(`Failed to consolidate duplicates: ${consolidateResult.error}`);
          }
        }
      }

      // 4. Enforce memory limits
      if (finalConfig.maxMemories > 0) {
        const remainingMemories = memories.length - result.deleted;
        if (remainingMemories > finalConfig.maxMemories) {
          const excessCount = remainingMemories - finalConfig.maxMemories;
          const oldestMemories = this.findOldestMemories(memories, excessCount);

          for (const memory of oldestMemories) {
            if (finalConfig.archiveOldMemories) {
              await this.archiveMemory(memory);
              result.archived++;
            } else {
              const deleteResult = await memoryService.deleteMemory({ id: memory.id });
              if (deleteResult.success) {
                result.deleted++;
              } else {
                result.errors.push(`Failed to delete excess memory ${memory.id}: ${deleteResult.error}`);
              }
            }
          }
        }
      }

      // Get final stats
      const finalStatsResult = await memoryService.getMemoryStats();
      if (finalStatsResult.success && finalStatsResult.data) {
        result.sizeAfter = finalStatsResult.data.totalSize;
      }

      console.log(`üßπ Cleanup completed: ${result.deleted} deleted, ${result.archived} archived, ${result.consolidated} consolidated`);

    } catch (error) {
      result.success = false;
      result.errors.push(`Cleanup failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }

    return result;
  }

  /**
   * Find memories older than specified days
   */
  private findOldMemories(memories: MemoryEntry[], maxAgeDays: number): MemoryEntry[] {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - maxAgeDays);

    return memories.filter(memory => {
      const memoryDate = new Date(memory.createdAt);
      return memoryDate < cutoffDate;
    });
  }

  /**
   * Find memories with low access counts
   */
  private findUnusedMemories(memories: MemoryEntry[], minAccessCount: number): MemoryEntry[] {
    return memories.filter(memory => {
      const accessCount = memory.metadata.accessCount || 0;
      return accessCount < minAccessCount;
    });
  }

  /**
   * Find oldest memories for removal
   */
  private findOldestMemories(memories: MemoryEntry[], count: number): MemoryEntry[] {
    return memories
      .sort((a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime())
      .slice(0, count);
  }

  /**
   * Find duplicate memories based on content similarity
   */
  private findDuplicateMemories(memories: MemoryEntry[]): MemoryEntry[][] {
    const duplicateGroups: MemoryEntry[][] = [];
    const processed = new Set<string>();

    for (const memory of memories) {
      if (processed.has(memory.id)) continue;

      const duplicates = memories.filter(other => 
        other.id !== memory.id && 
        !processed.has(other.id) &&
        this.areSimilar(memory, other)
      );

      if (duplicates.length > 0) {
        const group = [memory, ...duplicates];
        duplicateGroups.push(group);

        // Mark all as processed
        group.forEach(m => processed.add(m.id));
      }
    }

    return duplicateGroups;
  }

  /**
   * Check if two memories are similar enough to be considered duplicates
   */
  private areSimilar(memory1: MemoryEntry, memory2: MemoryEntry): boolean {
    // Same type and similar title
    if (memory1.type === memory2.type && 
        this.calculateSimilarity(memory1.title, memory2.title) > 0.8) {
      return true;
    }

    // Similar content
    if (this.calculateSimilarity(memory1.content, memory2.content) > 0.9) {
      return true;
    }

    return false;
  }

  /**
   * Calculate text similarity (simple implementation)
   */
  private calculateSimilarity(text1: string, text2: string): number {
    const words1 = text1.toLowerCase().split(/\s+/);
    const words2 = text2.toLowerCase().split(/\s+/);

    const set1 = new Set(words1);
    const set2 = new Set(words2);

    const intersection = new Set(Array.from(set1).filter(x => set2.has(x)));
    const union = new Set([...Array.from(set1), ...Array.from(set2)]);

    return intersection.size / union.size;
  }

  /**
   * Consolidate duplicate memories
   */
  private async consolidateDuplicates(duplicates: MemoryEntry[]): Promise<{ success: boolean; consolidated: number; error?: string }> {
    try {
      if (duplicates.length < 2) {
        return { success: true, consolidated: 0 };
      }

      // Find the best memory to keep (most recent or most accessed)
      const keeper = duplicates.reduce((best, current) => {
        const bestAccess = best.metadata.accessCount || 0;
        const currentAccess = current.metadata.accessCount || 0;

        if (currentAccess > bestAccess) return current;
        if (currentAccess === bestAccess) {
          return new Date(current.updatedAt) > new Date(best.updatedAt) ? current : best;
        }
        return best;
      });

      // Merge tags from all duplicates
      const allTags = new Set<string>();
      duplicates.forEach(memory => {
        memory.metadata.tags.forEach(tag => allTags.add(tag));
      });

      // Update the keeper with merged information
      const updateResult = await memoryService.updateMemory({
        id: keeper.id,
        title: keeper.title,
        content: keeper.content,
        tags: Array.from(allTags),
        type: keeper.type
      });

      if (!updateResult.success) {
        return { success: false, consolidated: 0, error: updateResult.error };
      }

      // Delete the duplicates
      let consolidated = 0;
      for (const duplicate of duplicates) {
        if (duplicate.id !== keeper.id) {
          const deleteResult = await memoryService.deleteMemory({ id: duplicate.id });
          if (deleteResult.success) {
            consolidated++;
          }
        }
      }

      return { success: true, consolidated };
    } catch (error) {
      return {
        success: false,
        consolidated: 0,
        error: error instanceof Error ? error.message : 'Unknown error'
      };
    }
  }

  /**
   * Archive a memory (mark as archived instead of deleting)
   */
  private async archiveMemory(memory: MemoryEntry): Promise<boolean> {
    try {
      // Add archive tag and update
      const archiveTags = [...memory.metadata.tags, 'archived'];
      const updateResult = await memoryService.updateMemory({
        id: memory.id,
        title: `[ARCHIVED] ${memory.title}`,
        content: memory.content,
        tags: archiveTags,
        type: memory.type
      });

      return updateResult.success;
    } catch (error) {
      console.error('Failed to archive memory:', error);
      return false;
    }
  }

  /**
   * Get cleanup recommendations
   */
  async getCleanupRecommendations(): Promise<{
    oldMemories: number;
    unusedMemories: number;
    duplicateGroups: number;
    totalSize: number;
    recommendations: string[];
  }> {
    const recommendations: string[] = [];

    try {
      // Get all memories
      const searchResult = await memoryService.searchMemories({
        query: { limit: 10000 }
      });

      if (!searchResult.success || !searchResult.data) {
        return {
          oldMemories: 0,
          unusedMemories: 0,
          duplicateGroups: 0,
          totalSize: 0,
          recommendations: ['Unable to analyze memories for cleanup recommendations']
        };
      }

      const memories = searchResult.data.results.map(r => r.entry);

      // Analyze old memories
      const oldMemories = this.findOldMemories(memories, 365);
      if (oldMemories.length > 0) {
        recommendations.push(`${oldMemories.length} memories are older than 1 year and could be archived`);
      }

      // Analyze unused memories
      const unusedMemories = this.findUnusedMemories(memories, 1);
      if (unusedMemories.length > 0) {
        recommendations.push(`${unusedMemories.length} memories have never been accessed`);
      }

      // Analyze duplicates
      const duplicateGroups = this.findDuplicateMemories(memories);
      if (duplicateGroups.length > 0) {
        const totalDuplicates = duplicateGroups.reduce((sum, group) => sum + group.length - 1, 0);
        recommendations.push(`${duplicateGroups.length} groups of duplicates found (${totalDuplicates} duplicates total)`);
      }

      // Get size info
      const statsResult = await memoryService.getMemoryStats();
      const totalSize = statsResult.success ? statsResult.data!.totalSize : 0;

      if (totalSize > 10 * 1024 * 1024) { // 10MB
        recommendations.push(`Memory storage is ${Math.round(totalSize / 1024 / 1024)}MB - consider cleanup`);
      }

      if (memories.length > 500) {
        recommendations.push(`${memories.length} total memories - consider setting limits`);
      }

      if (recommendations.length === 0) {
        recommendations.push('No cleanup needed - memory system is well maintained');
      }

      return {
        oldMemories: oldMemories.length,
        unusedMemories: unusedMemories.length,
        duplicateGroups: duplicateGroups.length,
        totalSize,
        recommendations
      };
    } catch (error) {
      return {
        oldMemories: 0,
        unusedMemories: 0,
        duplicateGroups: 0,
        totalSize: 0,
        recommendations: [`Error analyzing memories: ${error instanceof Error ? error.message : 'Unknown error'}`]
      };
    }
  }
}

// Export singleton instance
export const memoryCleanupService = new MemoryCleanupService();

/**
 * Memory Context Service for LiteLLM
 * Provides memory-aware conversation enhancements
 */

import { memoryService } from './memoryService';
import { MemoryType, SearchQuery, MemoryEntry } from '../types/memory';

export interface ContextMemory {
  id: string;
  type: MemoryType;
  title: string;
  content: string;
  relevanceScore: number;
  tags: string[];
}

export interface MemoryContext {
  relevantMemories: ContextMemory[];
  contextSummary: string;
  totalMemories: number;
}

export interface ConversationAnalysis {
  topics: string[];
  intent: string;
  entities: string[];
  memoryTriggers: string[];
  shouldCreateMemory: boolean;
  suggestedMemoryType?: MemoryType;
}

class MemoryContextService {
  private readonly MAX_CONTEXT_MEMORIES = 5;
  private readonly MAX_CONTEXT_LENGTH = 2000; // characters
  private readonly MEMORY_RELEVANCE_THRESHOLD = 0.3;

  /**
   * Analyze user message to extract topics, intent, and memory triggers
   */
  analyzeMessage(message: string, conversationHistory: Array<{role: string, content: string}> = []): ConversationAnalysis {
    const messageText = message.toLowerCase();
    const recentHistory = conversationHistory.slice(-3); // Last 3 messages for context

    // Extract topics (simple keyword extraction)
    const topics = this.extractTopics(messageText);

    // Determine intent
    const intent = this.determineIntent(messageText);

    // Extract entities (names, projects, technologies)
    const entities = this.extractEntities(messageText);

    // Identify memory triggers
    const memoryTriggers = this.identifyMemoryTriggers(messageText);

    // Determine if we should create a memory
    const shouldCreateMemory = this.shouldCreateMemory(messageText, intent, recentHistory);

    // Suggest memory type if we should create one
    const suggestedMemoryType = shouldCreateMemory ? this.suggestMemoryType(messageText, intent) : undefined;

    return {
      topics,
      intent,
      entities,
      memoryTriggers,
      shouldCreateMemory,
      suggestedMemoryType
    };
  }

  /**
   * Get relevant memory context for a conversation
   */
  async getMemoryContext(
    message: string, 
    conversationId?: string, 
    projectId?: string,
    conversationHistory: Array<{role: string, content: string}> = []
  ): Promise<MemoryContext> {
    try {
      const analysis = this.analyzeMessage(message, conversationHistory);
      const searchQueries = this.buildSearchQueries(analysis, conversationId, projectId);

      const allMemories: Array<{
        id: string;
        type: MemoryType;
        title: string;
        content: string;
        relevanceScore: number;
        tags: string[];
      }> = [];

      // Execute multiple search queries and combine results
      for (const query of searchQueries) {
        const searchResult = await memoryService.searchMemories({ query });

        if (searchResult.success && searchResult.data) {
          for (const result of searchResult.data.results) {
            // Calculate relevance score based on multiple factors
            const relevanceScore = this.calculateRelevanceScore(
              result.entry,
              analysis,
              result.relevanceScore || 1.0
            );

            if (relevanceScore >= this.MEMORY_RELEVANCE_THRESHOLD) {
              allMemories.push({
                id: result.entry.id,
                type: result.entry.type,
                title: result.entry.title,
                content: result.entry.content,
                relevanceScore,
                tags: result.entry.metadata.tags
              });
            }
          }
        }
      }

      // Remove duplicates and sort by relevance
      const uniqueMemories = this.deduplicateMemories(allMemories);
      const sortedMemories = uniqueMemories
        .sort((a, b) => b.relevanceScore - a.relevanceScore)
        .slice(0, this.MAX_CONTEXT_MEMORIES);

      // Create context summary
      const contextSummary = this.createContextSummary(sortedMemories);

      return {
        relevantMemories: sortedMemories,
        contextSummary,
        totalMemories: uniqueMemories.length
      };
    } catch (error) {
      console.error('Error getting memory context:', error);
      return {
        relevantMemories: [],
        contextSummary: '',
        totalMemories: 0
      };
    }
  }

  /**
   * Create memory from conversation automatically
   */
  async createMemoryFromConversation(
    userMessage: string,
    aiResponse: string,
    analysis: ConversationAnalysis,
    conversationId?: string,
    projectId?: string
  ): Promise<boolean> {
    if (!analysis.shouldCreateMemory || !analysis.suggestedMemoryType) {
      return false;
    }

    try {
      // Extract key information for memory
      const memoryContent = this.extractMemoryContent(userMessage, aiResponse, analysis);
      const memoryTitle = this.generateMemoryTitle(userMessage, analysis);
      const memoryTags = this.generateMemoryTags(analysis);

      const storeResult = await memoryService.storeMemory({
        type: analysis.suggestedMemoryType,
        title: memoryTitle,
        content: memoryContent,
        tags: memoryTags,
        conversationId,
        projectId,
        source: 'auto_conversation'
      });

      if (storeResult.success) {
        console.log(`üß† Auto-created memory: ${memoryTitle} (${analysis.suggestedMemoryType})`);
        return true;
      } else {
        console.error('Failed to auto-create memory:', storeResult.error);
        return false;
      }
    } catch (error) {
      console.error('Error creating memory from conversation:', error);
      return false;
    }
  }

  /**
   * Build enhanced system prompt with memory context
   */
  buildMemoryEnhancedPrompt(originalPrompt: string, memoryContext: MemoryContext): string {
    if (memoryContext.relevantMemories.length === 0) {
      return originalPrompt;
    }

    const memorySection = `

üß† RELEVANT CONTEXT FROM PREVIOUS CONVERSATIONS üß†

You have access to the following information from previous interactions:

${memoryContext.relevantMemories.map((memory) =>
  `‚Ä¢ ${memory.content}`
).join('\n')}

Please use this context naturally when relevant to the conversation. You can reference this information when it helps answer questions or provide better assistance.`;

    return originalPrompt + memorySection;
  }

  // Private helper methods
  private extractTopics(message: string): string[] {
    const topics: string[] = [];

    // Technology keywords
    const techKeywords = ['react', 'javascript', 'typescript', 'python', 'node', 'api', 'database', 'ui', 'frontend', 'backend'];
    techKeywords.forEach(keyword => {
      if (message.includes(keyword)) topics.push(keyword);
    });

    // Action keywords
    const actionKeywords = ['create', 'build', 'fix', 'debug', 'implement', 'design', 'optimize'];
    actionKeywords.forEach(keyword => {
      if (message.includes(keyword)) topics.push(keyword);
    });

    return Array.from(new Set(topics)); // Remove duplicates
  }

  private determineIntent(message: string): string {
    if (message.includes('prefer') || message.includes('like') || message.includes('want')) {
      return 'preference';
    }
    if (message.includes('how') || message.includes('what') || message.includes('?')) {
      return 'question';
    }
    if (message.includes('create') || message.includes('build') || message.includes('make')) {
      return 'creation';
    }
    if (message.includes('fix') || message.includes('error') || message.includes('problem')) {
      return 'troubleshooting';
    }
    return 'general';
  }

  private extractEntities(message: string): string[] {
    const entities: string[] = [];

    // Simple entity extraction (can be enhanced with NLP)
    const words = message.split(/\s+/);
    words.forEach(word => {
      // Capitalized words might be entities
      if (word.length > 2 && word[0] === word[0].toUpperCase() && word.slice(1) === word.slice(1).toLowerCase()) {
        entities.push(word);
      }
    });

    return entities;
  }

  private identifyMemoryTriggers(message: string): string[] {
    const triggers: string[] = [];

    // Preference indicators
    if (message.includes('prefer') || message.includes('like') || message.includes('always')) {
      triggers.push('preference');
    }

    // Solution indicators
    if (message.includes('worked') || message.includes('solved') || message.includes('fixed')) {
      triggers.push('solution');
    }

    // Knowledge sharing
    if (message.includes('remember') || message.includes('note') || message.includes('important')) {
      triggers.push('knowledge');
    }

    return triggers;
  }

  private shouldCreateMemory(message: string, intent: string, history: Array<{role: string, content: string}>): boolean {
    // Create memory for preferences
    if (intent === 'preference') return true;

    // Create memory for successful solutions
    if (intent === 'troubleshooting' && history.some(h => h.content.includes('worked') || h.content.includes('solved'))) {
      return true;
    }

    // Create memory for important information
    if (message.includes('remember') || message.includes('important') || message.includes('note')) {
      return true;
    }

    return false;
  }

  private suggestMemoryType(message: string, intent: string): MemoryType {
    if (intent === 'preference') return 'user_preference';
    if (intent === 'troubleshooting') return 'solution';
    if (message.includes('code') || message.includes('function')) return 'code_snippet';
    if (message.includes('project')) return 'project_knowledge';
    return 'general';
  }

  private buildSearchQueries(analysis: ConversationAnalysis, conversationId?: string, projectId?: string): SearchQuery[] {
    const queries: SearchQuery[] = [];

    // Search by topics
    if (analysis.topics.length > 0) {
      queries.push({
        text: analysis.topics.join(' '),
        limit: 3
      });
    }

    // Search by entities
    if (analysis.entities.length > 0) {
      queries.push({
        text: analysis.entities.join(' '),
        limit: 2
      });
    }

    // Search by project
    if (projectId) {
      queries.push({
        projectId,
        limit: 3
      });
    }

    // Search by conversation
    if (conversationId) {
      queries.push({
        conversationId,
        limit: 2
      });
    }

    // Search preferences if intent suggests it
    if (analysis.intent === 'preference' || analysis.memoryTriggers.includes('preference')) {
      queries.push({
        type: 'user_preference',
        limit: 3
      });
    }

    return queries;
  }

  private calculateRelevanceScore(memory: MemoryEntry, analysis: ConversationAnalysis, baseScore: number): number {
    let score = baseScore;

    // Boost score for matching topics
    const memoryText = (memory.title + ' ' + memory.content + ' ' + memory.metadata.tags.join(' ')).toLowerCase();
    analysis.topics.forEach(topic => {
      if (memoryText.includes(topic)) score += 0.2;
    });

    // Boost score for matching entities
    analysis.entities.forEach(entity => {
      if (memoryText.includes(entity.toLowerCase())) score += 0.3;
    });

    // Boost score for recent memories
    const daysSinceCreated = (Date.now() - new Date(memory.createdAt).getTime()) / (1000 * 60 * 60 * 24);
    if (daysSinceCreated < 7) score += 0.1;

    // Boost score for frequently accessed memories
    if (memory.metadata.accessCount && memory.metadata.accessCount > 5) score += 0.1;

    return Math.min(score, 1.0); // Cap at 1.0
  }

  private deduplicateMemories(memories: ContextMemory[]): ContextMemory[] {
    const seen = new Set();
    return memories.filter(memory => {
      if (seen.has(memory.id)) return false;
      seen.add(memory.id);
      return true;
    });
  }

  private createContextSummary(memories: ContextMemory[]): string {
    if (memories.length === 0) return '';

    const summaryParts: string[] = [];

    // Group by type
    const byType = memories.reduce((acc, memory) => {
      if (!acc[memory.type]) acc[memory.type] = [];
      acc[memory.type].push(memory);
      return acc;
    }, {} as Record<string, ContextMemory[]>);

    Object.entries(byType).forEach(([type, mems]) => {
      const typeLabel = type.replace('_', ' ').toUpperCase();
      summaryParts.push(`${typeLabel}: ${mems.map(m => m.title).join(', ')}`);
    });

    return summaryParts.join(' | ');
  }

  private extractMemoryContent(userMessage: string, aiResponse: string, analysis: ConversationAnalysis): string {
    // Create meaningful memory content based on the conversation
    if (analysis.suggestedMemoryType === 'user_preference') {
      return `User preference: ${userMessage}`;
    }

    if (analysis.suggestedMemoryType === 'solution') {
      return `Problem: ${userMessage}\n\nSolution: ${aiResponse}`;
    }

    if (analysis.suggestedMemoryType === 'code_snippet') {
      // Extract code from AI response
      const codeMatch = aiResponse.match(/```[\s\S]*?```/);
      return codeMatch ? codeMatch[0] : aiResponse;
    }

    return `${userMessage}\n\nResponse: ${aiResponse}`;
  }

  private generateMemoryTitle(userMessage: string, analysis: ConversationAnalysis): string {
    const words = userMessage.split(' ').slice(0, 6).join(' ');
    const type = analysis.suggestedMemoryType?.replace('_', ' ') || 'conversation';
    return `${type}: ${words}${words.length < userMessage.length ? '...' : ''}`;
  }

  private generateMemoryTags(analysis: ConversationAnalysis): string[] {
    const tags = [...analysis.topics, ...analysis.entities.map(e => e.toLowerCase())];
    tags.push(analysis.intent);
    return Array.from(new Set(tags)).filter(tag => tag.length > 1);
  }
}

