// Tool execution functions
export async function executeMemoryTool(toolName: string, args: unknown): Promise<MemoryToolResponse<unknown>> {
  console.log(`üß† Executing memory tool: ${toolName} with args:`, args);

  try {
    switch (toolName) {
      case 'memory-store':
        return await memoryService.storeMemory(args as MemoryStoreRequest);

      case 'memory-search':
        return await memoryService.searchMemories({ query: args } as MemorySearchRequest);

      case 'memory-retrieve':
        return await memoryService.retrieveMemory(args as MemoryRetrieveRequest);

      case 'memory-update':
        return await memoryService.updateMemory(args as MemoryUpdateRequest);

      case 'memory-delete':
        return await memoryService.deleteMemory(args as MemoryDeleteRequest);

      default:
        return {
          success: false,
          error: `Unknown memory tool: ${toolName}`
        };
    }
  } catch (error) {
    console.error(`‚ùå Memory tool ${toolName} failed:`, error);
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error occurred'
    };
  }
}

// Helper function to get memory tools in MCP format
export function getMemoryMCPTools() {
  return memoryMCPTools.map(tool => ({
    type: 'function',
    function: {
      name: tool.name,
      description: tool.description,
      parameters: tool.inputSchema
    }
  }));
}

// Helper function to check if a tool is a memory tool
export function isMemoryTool(toolName: string): boolean {
  return memoryMCPTools.some(tool => tool.name === toolName);
}

/**
 * Memory Service for LiteLLM
 * Handles persistent memory storage, retrieval, and search functionality
 */

import { 
  MemoryEntry, 
  MemoryIndex, 
  MemoryIndexEntry, 
  MemoryType, 
  SearchQuery, 
  SearchResult, 
  SearchResponse,
  MemoryStats,
  MemoryStoreRequest,
  MemorySearchRequest,
  MemoryRetrieveRequest,
  MemoryUpdateRequest,
  MemoryDeleteRequest,
  MemoryToolResponse
} from '../types/memory';

// Memory operations will be available on the existing ElectronAPI interface

class MemoryService {
  private memoryIndex: MemoryIndex | null = null;
  private initialized = false;
  private readonly MEMORY_VERSION = '1.0.0';

  async initialize(): Promise<void> {
    if (this.initialized) return;

    try {
      // Load memory index from disk
      if (typeof window !== 'undefined' && window.electronAPI) {
        const index = await window.electronAPI.loadMemoryIndex();
        
        if (index) {
          this.memoryIndex = {
            ...index,
            lastUpdated: new Date(index.lastUpdated),
            entries: index.entries.map((entry: MemoryIndexEntry & {timestamp: string | Date}) => ({
              ...entry,
              timestamp: new Date(entry.timestamp)
            }))
          };
          console.log(`Loaded ${this.memoryIndex?.entries.length || 0} memory entries from disk`);
        } else {
          // Create new index if none exists
          this.memoryIndex = {
            entries: [],
            lastUpdated: new Date(),
            totalEntries: 0,
            version: this.MEMORY_VERSION
          };
          await this.saveIndex();
          console.log('Created new memory index');
        }
      } else {
        // Fallback for non-Electron environments
        this.memoryIndex = {
          entries: [],
          lastUpdated: new Date(),
          totalEntries: 0,
          version: this.MEMORY_VERSION
        };
      }
    } catch (error) {
      console.error('Failed to initialize memory service:', error);
      this.memoryIndex = {
        entries: [],
        lastUpdated: new Date(),
        totalEntries: 0,
        version: this.MEMORY_VERSION
      };
    }

    this.initialized = true;
  }

  private async saveIndex(): Promise<boolean> {
    if (!this.memoryIndex) return false;

    try {
      if (typeof window !== 'undefined' && window.electronAPI?.saveMemoryIndex) {
        const serializedIndex = {
          ...this.memoryIndex,
          lastUpdated: this.memoryIndex.lastUpdated.toISOString(),
          entries: this.memoryIndex.entries.map(entry => ({
            ...entry,
            timestamp: entry.timestamp.toISOString()
          }))
        };

        const success = await window.electronAPI.saveMemoryIndex(serializedIndex as unknown as MemoryIndex);
        if (success) {
          console.log('Memory index saved successfully');
        } else {
          console.error('Failed to save memory index');
        }
        return success;
      }
    } catch (error) {
      console.error('Error saving memory index:', error);
    }
    return false;
  }

  private generateId(): string {
    return `mem_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  private createSearchableText(title: string, content: string, tags: string[]): string {
    return [title, content, ...tags].join(' ').toLowerCase();
  }

  async storeMemory(request: MemoryStoreRequest): Promise<MemoryToolResponse<MemoryEntry>> {
    await this.initialize();

    try {
      const id = this.generateId();
      const now = new Date();
      
      const entry: MemoryEntry = {
        id,
        type: request.type,
        title: request.title,
        content: request.content,
        metadata: {
          tags: request.tags || [],
          timestamp: now,
          projectId: request.projectId,
          conversationId: request.conversationId,
          source: request.source,
          accessCount: 0
        },
        searchableText: this.createSearchableText(request.title, request.content, request.tags || []),
        createdAt: now,
        updatedAt: now
      };

      // Save entry to file
      if (typeof window !== 'undefined' && window.electronAPI?.saveMemoryEntry) {
        const success = await window.electronAPI.saveMemoryEntry(entry);
        if (!success) {
          return {
            success: false,
            error: 'Failed to save memory entry to disk'
          };
        }
      }

      // Add to index
      const indexEntry: MemoryIndexEntry = {
        id: entry.id,
        type: entry.type,
        title: entry.title,
        tags: entry.metadata.tags,
        timestamp: entry.metadata.timestamp,
        relevanceScore: entry.metadata.relevanceScore,
        projectId: entry.metadata.projectId,
        conversationId: entry.metadata.conversationId,
        fileSize: JSON.stringify(entry).length
      };

      this.memoryIndex!.entries.unshift(indexEntry);
      this.memoryIndex!.totalEntries = this.memoryIndex!.entries.length;
      this.memoryIndex!.lastUpdated = now;

      // Keep only last 1000 entries in index for performance
      if (this.memoryIndex!.entries.length > 1000) {
        this.memoryIndex!.entries = this.memoryIndex!.entries.slice(0, 1000);
      }

      await this.saveIndex();

      return {
        success: true,
        data: entry,
        message: `Memory stored successfully with ID: ${id}`
      };
    } catch (error) {
      console.error('Error storing memory:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error occurred'
      };
    }
  }

  async retrieveMemory(request: MemoryRetrieveRequest): Promise<MemoryToolResponse<MemoryEntry>> {
    await this.initialize();

    try {
      if (typeof window !== 'undefined' && window.electronAPI?.loadMemoryEntry) {
        const entry = await window.electronAPI.loadMemoryEntry(request.id);
        
        if (entry) {
          // Update access tracking
          entry.metadata.lastAccessed = new Date();
          entry.metadata.accessCount = (entry.metadata.accessCount || 0) + 1;
          
          // Save updated entry
          await window.electronAPI.saveMemoryEntry(entry);

          return {
            success: true,
            data: {
              ...entry,
              createdAt: new Date(entry.createdAt),
              updatedAt: new Date(entry.updatedAt),
              metadata: {
                ...entry.metadata,
                timestamp: new Date(entry.metadata.timestamp),
                lastAccessed: entry.metadata.lastAccessed
              }
            }
          };
        } else {
          return {
            success: false,
            error: `Memory entry with ID ${request.id} not found`
          };
        }
      }

      return {
        success: false,
        error: 'Memory retrieval not available in this environment'
      };
    } catch (error) {
      console.error('Error retrieving memory:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error occurred'
      };
    }
  }

  async searchMemories(request: MemorySearchRequest): Promise<MemoryToolResponse<SearchResponse>> {
    await this.initialize();

    try {
      const query = request.query;
      const startTime = performance.now();

      if (!this.memoryIndex) {
        return {
          success: false,
          error: 'Memory index not available'
        };
      }

      // Filter entries based on query criteria
      let filteredEntries = this.memoryIndex.entries;

      // Filter by type
      if (query.type) {
        filteredEntries = filteredEntries.filter(entry => entry.type === query.type);
      }

      // Filter by tags
      if (query.tags && query.tags.length > 0) {
        filteredEntries = filteredEntries.filter(entry =>
          query.tags!.some(tag => entry.tags.includes(tag))
        );
      }

      // Filter by project ID
      if (query.projectId) {
        filteredEntries = filteredEntries.filter(entry => entry.projectId === query.projectId);
      }

      // Filter by conversation ID
      if (query.conversationId) {
        filteredEntries = filteredEntries.filter(entry => entry.conversationId === query.conversationId);
      }

      // Filter by date range
      if (query.dateRange) {
        filteredEntries = filteredEntries.filter(entry => {
          const entryDate = entry.timestamp;
          return entryDate >= query.dateRange!.start && entryDate <= query.dateRange!.end;
        });
      }

      // Enhanced text search - search in title, tags, AND content
      if (query.text) {
        const searchText = query.text.toLowerCase();
        const searchTerms = searchText.split(/\s+/).filter(term => term.length > 0);

        filteredEntries = filteredEntries.filter(entry => {
          // Load full entry to search content
          const entryText = (entry.title + ' ' + entry.tags.join(' ')).toLowerCase();

          // Check if any search term matches title or tags
          const titleTagMatch = searchTerms.some(term =>
            entryText.includes(term)
          );

          if (titleTagMatch) return true;

          // For more thorough search, we need to load the full entry content
          // This is a simplified approach - in production, you'd want to index content
          return false; // Will be enhanced with content search below
        });

        // Enhanced search: Load full entries and search content
        const enhancedResults = [];
        for (const indexEntry of filteredEntries) {
          if (typeof window !== 'undefined' && window.electronAPI?.loadMemoryEntry) {
            const fullEntry = await window.electronAPI.loadMemoryEntry(indexEntry.id);
            if (fullEntry) {
              const fullText = (fullEntry.title + ' ' + fullEntry.content + ' ' + fullEntry.metadata.tags.join(' ')).toLowerCase();
              const hasMatch = searchTerms.some(term => fullText.includes(term));
              if (hasMatch) {
                enhancedResults.push(indexEntry);
              }
            }
          }
        }

        // Also search entries that didn't match title/tags but might match content
        const remainingEntries = this.memoryIndex.entries.filter(entry => !filteredEntries.includes(entry));
        for (const indexEntry of remainingEntries.slice(0, 50)) { // Limit to avoid performance issues
          if (typeof window !== 'undefined' && window.electronAPI?.loadMemoryEntry) {
            const fullEntry = await window.electronAPI.loadMemoryEntry(indexEntry.id);
            if (fullEntry) {
              const fullText = (fullEntry.title + ' ' + fullEntry.content + ' ' + fullEntry.metadata.tags.join(' ')).toLowerCase();
              const hasMatch = searchTerms.some(term => fullText.includes(term));
              if (hasMatch) {
                enhancedResults.push(indexEntry);
              }
            }
          }
        }

        filteredEntries = enhancedResults;
      }

      // Sort by relevance (timestamp for now, can be enhanced)
      filteredEntries.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());

      // Apply pagination
      const offset = query.offset || 0;
      const limit = query.limit || 50;
      const paginatedEntries = filteredEntries.slice(offset, offset + limit);

      // Load full entries for results
      const results: SearchResult[] = [];

      for (const indexEntry of paginatedEntries) {
        if (typeof window !== 'undefined' && window.electronAPI?.loadMemoryEntry) {
          const fullEntry = await window.electronAPI.loadMemoryEntry(indexEntry.id);
          if (fullEntry) {
            results.push({
              entry: {
                ...fullEntry,
                createdAt: new Date(fullEntry.createdAt),
                updatedAt: new Date(fullEntry.updatedAt),
                metadata: {
                  ...fullEntry.metadata,
                  timestamp: new Date(fullEntry.metadata.timestamp),
                  lastAccessed: fullEntry.metadata.lastAccessed ? new Date(fullEntry.metadata.lastAccessed) : undefined
                }
              },
              relevanceScore: indexEntry.relevanceScore || 1.0,
              matchedFields: this.getMatchedFields(fullEntry, query)
            });
          }
        }
      }

      const executionTime = performance.now() - startTime;

      return {
        success: true,
        data: {
          results,
          totalCount: filteredEntries.length,
          query,
          executionTime
        }
      };
    } catch (error) {
      console.error('Error searching memories:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error occurred'
      };
    }
  }

  private getMatchedFields(entry: MemoryEntry, query: SearchQuery): string[] {
    const matchedFields: string[] = [];

    if (query.text) {
      const searchText = query.text.toLowerCase();
      const searchTerms = searchText.split(/\s+/).filter(term => term.length > 0);

      // Check title matches
      const titleText = entry.title.toLowerCase();
      if (searchTerms.some(term => titleText.includes(term))) {
        matchedFields.push('title');
      }

      // Check content matches
      const contentText = entry.content.toLowerCase();
      if (searchTerms.some(term => contentText.includes(term))) {
        matchedFields.push('content');
      }

      // Check tag matches
      const tagText = entry.metadata.tags.join(' ').toLowerCase();
      if (searchTerms.some(term => tagText.includes(term))) {
        matchedFields.push('tags');
      }

      // Add fuzzy matching for better results
      if (matchedFields.length === 0) {
        // Try partial word matching
        const allText = (titleText + ' ' + contentText + ' ' + tagText);
        for (const term of searchTerms) {
          if (term.length >= 3) { // Only for terms 3+ chars
            // Check if any word starts with the search term
            const words = allText.split(/\s+/);
            if (words.some(word => word.startsWith(term))) {
              matchedFields.push('partial_match');
              break;
            }
          }
        }
      }
    }

    return matchedFields;
  }

  async updateMemory(request: MemoryUpdateRequest): Promise<MemoryToolResponse<MemoryEntry>> {
    await this.initialize();

    try {
      if (typeof window !== 'undefined' && window.electronAPI?.loadMemoryEntry) {
        const existingEntry = await window.electronAPI.loadMemoryEntry(request.id);

        if (!existingEntry) {
          return {
            success: false,
            error: `Memory entry with ID ${request.id} not found`
          };
        }

        // Update entry fields
        const updatedEntry: MemoryEntry = {
          ...existingEntry,
          title: request.title || existingEntry.title,
          content: request.content || existingEntry.content,
          type: request.type || existingEntry.type,
          metadata: {
            ...existingEntry.metadata,
            tags: request.tags || existingEntry.metadata.tags,
            timestamp: new Date(existingEntry.metadata.timestamp)
          },
          updatedAt: new Date(),
          createdAt: new Date(existingEntry.createdAt)
        };

        // Regenerate searchable text
        updatedEntry.searchableText = this.createSearchableText(
          updatedEntry.title,
          updatedEntry.content,
          updatedEntry.metadata.tags
        );

        // Save updated entry
        const success = await window.electronAPI.saveMemoryEntry(updatedEntry);
        if (!success) {
          return {
            success: false,
            error: 'Failed to save updated memory entry'
          };
        }

        // Update index entry
        const indexEntryIndex = this.memoryIndex!.entries.findIndex(e => e.id === request.id);
        if (indexEntryIndex !== -1) {
          this.memoryIndex!.entries[indexEntryIndex] = {
            ...this.memoryIndex!.entries[indexEntryIndex],
            title: updatedEntry.title,
            type: updatedEntry.type,
            tags: updatedEntry.metadata.tags,
            fileSize: JSON.stringify(updatedEntry).length
          };

          this.memoryIndex!.lastUpdated = new Date();
          await this.saveIndex();
        }

        return {
          success: true,
          data: updatedEntry,
          message: `Memory entry ${request.id} updated successfully`
        };
      }

      return {
        success: false,
        error: 'Memory update not available in this environment'
      };
    } catch (error) {
      console.error('Error updating memory:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error occurred'
      };
    }
  }

  async deleteMemory(request: MemoryDeleteRequest): Promise<MemoryToolResponse<void>> {
    await this.initialize();

    try {
      if (typeof window !== 'undefined' && window.electronAPI?.deleteMemoryEntry) {
        const success = await window.electronAPI.deleteMemoryEntry(request.id);

        if (success) {
          // Remove from index
          const indexEntryIndex = this.memoryIndex!.entries.findIndex(e => e.id === request.id);
          if (indexEntryIndex !== -1) {
            this.memoryIndex!.entries.splice(indexEntryIndex, 1);
            this.memoryIndex!.totalEntries = this.memoryIndex!.entries.length;
            this.memoryIndex!.lastUpdated = new Date();
            await this.saveIndex();
          }

          return {
            success: true,
            message: `Memory entry ${request.id} deleted successfully`
          };
        } else {
          return {
            success: false,
            error: `Failed to delete memory entry ${request.id}`
          };
        }
      }

      return {
        success: false,
        error: 'Memory deletion not available in this environment'
      };
    } catch (error) {
      console.error('Error deleting memory:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error occurred'
      };
    }
  }

  async getMemoryStats(): Promise<MemoryToolResponse<MemoryStats>> {
    await this.initialize();

    try {
      if (!this.memoryIndex) {
        return {
          success: false,
          error: 'Memory index not available'
        };
      }

      const entriesByType: Record<string, number> = {};
      let oldestEntry: Date | undefined;
      let newestEntry: Date | undefined;
      let mostAccessedEntry: MemoryIndexEntry | undefined;

      for (const entry of this.memoryIndex.entries) {
        // Count by type
        entriesByType[entry.type] = (entriesByType[entry.type] || 0) + 1;

        // Find oldest and newest
        if (!oldestEntry || entry.timestamp < oldestEntry) {
          oldestEntry = entry.timestamp;
        }
        if (!newestEntry || entry.timestamp > newestEntry) {
          newestEntry = entry.timestamp;
        }

        // For now, use the most recent entry as "most accessed"
        // TODO: Implement proper access tracking in the future
        if (!mostAccessedEntry || entry.timestamp > mostAccessedEntry.timestamp) {
          mostAccessedEntry = entry;
        }
      }

      // Get total size from Electron API
      let totalSize = 0;
      if (typeof window !== 'undefined' && window.electronAPI?.getMemoryStats) {
        const stats = await window.electronAPI.getMemoryStats();
        totalSize = stats.totalSize;
      }

      const memoryStats: MemoryStats = {
        totalEntries: this.memoryIndex.totalEntries,
        entriesByType: entriesByType as Record<MemoryType, number>,
        totalSize,
        oldestEntry,
        newestEntry,
        mostAccessedEntry
      };

      return {
        success: true,
        data: memoryStats
      };
    } catch (error) {
      console.error('Error getting memory stats:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error occurred'
      };
    }
  }
}

// Export singleton instance
export const memoryService = new MemoryService();

// Mistral File Upload and Document AI Service
// Handles file uploads, OCR, and document processing for Mistral AI

export interface MistralFileUploadResponse {
  id: string;
  object: string;
  bytes: number;
  created_at: number;
  filename: string;
  purpose: string;
  sample_type?: string;
  num_lines?: number;
  source: string;
}

export interface MistralFileListResponse {
  data: MistralFileUploadResponse[];
  object: string;
}

export interface MistralOCRResponse {
  content: string;
  metadata?: {
    pages?: number;
    language?: string;
    confidence?: number;
  };
}

export type MistralFilePurpose = 'fine-tune' | 'batch' | 'ocr';

export class MistralFileService {
  private baseUrl: string;
  private apiKey: string;

  constructor(apiKey: string, baseUrl = 'https://api.mistral.ai/v1') {
    this.apiKey = apiKey;
    this.baseUrl = baseUrl;
  }

  /**
   * Upload a file to Mistral's file storage
   * Maximum file size: 512 MB (50 MB for OCR)
   * Supported purposes: fine-tune (.jsonl only), batch, ocr
   */
  async uploadFile(
    file: File,
    purpose: MistralFilePurpose = 'batch'
  ): Promise<MistralFileUploadResponse> {
    // Validate file size based on purpose
    let maxSize = 512 * 1024 * 1024; // 512 MB for general uploads
    if (purpose === 'ocr') {
      maxSize = 50 * 1024 * 1024; // 50 MB for OCR
    }

    if (file.size > maxSize) {
      const limitMB = Math.round(maxSize / 1024 / 1024);
      throw new Error(`File size ${Math.round(file.size / 1024 / 1024)}MB exceeds Mistral's ${limitMB}MB limit for ${purpose}`);
    }

    // Validate file type for fine-tuning
    if (purpose === 'fine-tune' && !file.name.endsWith('.jsonl')) {
      throw new Error('Fine-tuning only supports .jsonl files');
    }

    console.log(`üì§ Uploading file to Mistral: ${file.name} (${Math.round(file.size / 1024)}KB) for ${purpose}`);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('purpose', purpose);

    try {
      const response = await fetch(`${this.baseUrl}/files`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'User-Agent': 'LittleLLM/1.0'
        },
        body: formData
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå Mistral file upload failed (${response.status}):`, errorText);
        
        let errorMessage = errorText;
        try {
          const errorObj = JSON.parse(errorText);
          errorMessage = errorObj.message || errorObj.error?.message || errorText;
        } catch {
          // Keep original error if not JSON
        }

        throw new Error(`Mistral file upload failed: ${errorMessage}`);
      }

      const result = await response.json() as MistralFileUploadResponse;
      console.log(`‚úÖ File uploaded to Mistral successfully:`, result);
      return result;

    } catch (error) {
      console.error('‚ùå Mistral file upload error:', error);
      throw error;
    }
  }

  /**
   * List all uploaded files
   */
  async listFiles(
    page = 0,
    pageSize = 100,
    purpose?: MistralFilePurpose
  ): Promise<MistralFileListResponse> {
    const params = new URLSearchParams({
      page: page.toString(),
      page_size: pageSize.toString()
    });

    if (purpose) {
      params.append('purpose', purpose);
    }

    try {
      const response = await fetch(`${this.baseUrl}/files?${params}`, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'User-Agent': 'LittleLLM/1.0'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to list files: ${errorText}`);
      }

      return await response.json() as MistralFileListResponse;
    } catch (error) {
      console.error('‚ùå Mistral list files error:', error);
      throw error;
    }
  }

  /**
   * Retrieve file information
   */
  async getFile(fileId: string): Promise<MistralFileUploadResponse> {
    try {
      const response = await fetch(`${this.baseUrl}/files/${fileId}`, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'User-Agent': 'LittleLLM/1.0'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to get file: ${errorText}`);
      }

      return await response.json() as MistralFileUploadResponse;
    } catch (error) {
      console.error('‚ùå Mistral get file error:', error);
      throw error;
    }
  }

  /**
   * Delete a file
   */
  async deleteFile(fileId: string): Promise<void> {
    try {
      const response = await fetch(`${this.baseUrl}/files/${fileId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'User-Agent': 'LittleLLM/1.0'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to delete file: ${errorText}`);
      }

      console.log(`‚úÖ File ${fileId} deleted successfully`);
    } catch (error) {
      console.error('‚ùå Mistral delete file error:', error);
      throw error;
    }
  }

  /**
   * Download file content
   */
  async downloadFile(fileId: string): Promise<Blob> {
    try {
      const response = await fetch(`${this.baseUrl}/files/${fileId}/download`, {
        headers: {
          'Authorization': `Bearer ${this.apiKey}`,
          'User-Agent': 'LittleLLM/1.0'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to download file: ${errorText}`);
      }

      return await response.blob();
    } catch (error) {
      console.error('‚ùå Mistral download file error:', error);
      throw error;
    }
  }

  /**
   * Process document with OCR using Mistral's Document AI
   * This uses the /v1/ocr endpoint for PDFs and documents
   */
  async processDocumentOCR(file: File): Promise<MistralOCRResponse> {
    console.log(`üîç Processing document with Mistral OCR: ${file.name} (${file.type}, ${Math.round(file.size/1024)}KB)`);

    // Validate file size for OCR (50MB limit)
    const maxSize = 50 * 1024 * 1024; // 50 MB
    if (file.size > maxSize) {
      throw new Error(`File size ${Math.round(file.size / 1024 / 1024)}MB exceeds Mistral's 50MB OCR limit`);
    }

    try {
      // Convert file to base64 for OCR endpoint
      const base64 = await this.fileToBase64(file);

      // Determine document type and format
      let documentType: 'image_url' | 'document_url';
      let documentUrl: string;

      if (file.type.startsWith('image/')) {
        documentType = 'image_url';
        documentUrl = base64; // Keep full data URL for images
      } else if (file.type === 'application/pdf' || file.type.includes('document') || file.type.includes('word') || file.type.includes('excel')) {
        documentType = 'document_url';
        documentUrl = base64; // Keep full data URL for documents
      } else {
        throw new Error(`File type ${file.type} not supported for OCR`);
      }

      console.log(`üì° Calling Mistral OCR endpoint: ${this.baseUrl}/ocr`);

      const response = await fetch(`${this.baseUrl}/ocr`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${this.apiKey}`,
          'User-Agent': 'LittleLLM/1.0'
        },
        body: JSON.stringify({
          model: 'mistral-ocr-latest',
          document: {
            type: documentType,
            [documentType === 'image_url' ? 'image_url' : 'document_url']: documentUrl
          },
          include_image_base64: false
        })
      });

      console.log(`üì° OCR response status: ${response.status} ${response.statusText}`);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå Mistral OCR failed (${response.status}):`, errorText);
        throw new Error(`Mistral OCR failed (${response.status}): ${errorText}`);
      }

      const result = await response.json() as MistralOCRResponse;
      console.log(`‚úÖ Document processed with Mistral OCR successfully:`, {
        contentLength: result.content?.length || 0,
        hasMetadata: !!result.metadata
      });
      return result;

    } catch (error) {
      console.error('‚ùå Mistral OCR error:', error);
      throw error;
    }
  }

  /**
   * Convert file to base64 for vision models
   * This is used when sending files directly to vision-capable models
   */
  async fileToBase64(file: File): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => {
        const result = reader.result as string;
        resolve(result);
      };
      reader.onerror = error => reject(error);
      reader.readAsDataURL(file);
    });
  }

  /**
   * Prepare file for Mistral models (vision or OCR)
   * Returns the appropriate format for including in chat completions
   */
  async prepareFileForVision(file: File): Promise<{ type: string; image_url?: { url: string }; text?: string }> {
    console.log(`üîç Preparing file for Mistral: ${file.name} (${file.type})`);

    if (file.type.startsWith('image/')) {
      // For images, use vision models with proper image format
      console.log(`üñºÔ∏è Processing image for vision: ${file.name}`);
      const base64 = await this.fileToBase64(file);

      // Ensure proper image format for vision models
      if (!base64.startsWith('data:image/')) {
        throw new Error(`Invalid image format. Expected data:image/*, got: ${base64.substring(0, 20)}...`);
      }

      return {
        type: 'image_url',
        image_url: { url: base64 }
      };
    } else if (file.type === 'application/pdf' ||
               file.type.includes('document') ||
               file.type.includes('word') ||
               file.type.includes('excel') ||
               file.type.includes('powerpoint')) {
      // For PDFs and documents, use OCR to extract text
      console.log(`üìÑ Processing document with OCR: ${file.name}`);
      try {
        const ocrResult = await this.processDocumentOCR(file);
        return {
          type: 'text',
          text: `[Document: ${file.name}]\n${ocrResult.content}`
        };
      } catch (ocrError) {
        console.warn(`‚ö†Ô∏è OCR failed for ${file.name}, providing file info:`, ocrError);
        return {
          type: 'text',
          text: `[Document: ${file.name}]\nFile type: ${file.type}\nSize: ${Math.round(file.size / 1024)}KB\nNote: OCR processing failed: ${ocrError instanceof Error ? ocrError.message : String(ocrError)}`
        };
      }
    } else if (file.type.startsWith('text/') || file.type === 'text/csv' || file.type === 'application/json') {
      // For text files, read content directly
      console.log(`üìù Reading text file: ${file.name}`);
      const text = await this.readTextFile(file);
      return {
        type: 'text',
        text: `[Text File: ${file.name}]\n${text}`
      };
    } else {
      // For other file types, provide a descriptive message
      console.log(`‚ùì Unsupported file type: ${file.name} (${file.type})`);
      return {
        type: 'text',
        text: `[File: ${file.name}]\nFile type: ${file.type}\nSize: ${Math.round(file.size / 1024)}KB\nNote: This file type cannot be processed for content extraction, but has been included in the conversation.`
      };
    }
  }

  /**
   * Read text file content (fallback method)
   */
  private async readTextFile(file: File): Promise<string> {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = error => reject(error);
      reader.readAsText(file);
    });
  }

  /**
   * Validate if file is supported by Mistral
   */
  static isFileSupported(file: File): { supported: boolean; reason?: string } {
    // Check file size based on type
    let maxSize = 512 * 1024 * 1024; // 512 MB for general files
    let sizeLimit = '512MB';

    // OCR files have 50MB limit
    if (file.type === 'application/pdf' ||
        file.type.includes('document') ||
        file.type.includes('word') ||
        file.type.includes('excel') ||
        file.type.includes('powerpoint')) {
      maxSize = 50 * 1024 * 1024; // 50 MB for OCR
      sizeLimit = '50MB';
    }

    if (file.size > maxSize) {
      return {
        supported: false,
        reason: `File size ${Math.round(file.size / 1024 / 1024)}MB exceeds ${sizeLimit} limit`
      };
    }

    const supportedTypes = [
      // Images (Vision models)
      'image/png',
      'image/jpeg',
      'image/jpg',
      'image/webp',
      'image/gif',
      // Documents (OCR)
      'application/pdf',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document', // .docx
      'application/vnd.openxmlformats-officedocument.presentationml.presentation', // .pptx
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', // .xlsx
      'application/msword', // .doc
      'application/vnd.ms-powerpoint', // .ppt
      'application/vnd.ms-excel', // .xls
      // Text files
      'text/plain',
      'text/csv',
      'text/markdown',
      'application/json'
    ];

    // Check if file type is supported
    const isSupported = supportedTypes.includes(file.type) ||
                       file.type.startsWith('text/') ||
                       file.type.includes('document') ||
                       file.type.includes('word') ||
                       file.type.includes('excel') ||
                       file.type.includes('powerpoint');

    if (!isSupported) {
      return {
        supported: false,
        reason: `File type ${file.type} not supported. Supported: images, PDFs, Office documents, text files`
      };
    }

    return { supported: true };
  }
}

