// Default pricing for unknown models
export const DEFAULT_PRICING: ModelPricing = {
  inputPrice: 1.00,
  outputPrice: 3.00,
  currency: 'USD',
  lastUpdated: '2025-01-01'
};

export class PricingService {
  /**
   * Calculate the cost for a given provider, model, and token usage
   */
  static calculateCost(
    provider: string,
    model: string,
    promptTokens: number,
    completionTokens: number
  ): CostCalculation {
    const providerPricing = PROVIDER_PRICING[provider.toLowerCase()];
    let modelPricing: ModelPricing;

    if (providerPricing) {
      // Try exact model match first
      modelPricing = providerPricing[model] || 
                   // Try without version suffix (e.g., gpt-4-1106-preview -> gpt-4)
                   providerPricing[model.split('-').slice(0, 2).join('-')] ||
                   // Try first part only (e.g., gpt-4-turbo -> gpt-4)
                   providerPricing[model.split('-')[0]] ||
                   // Use default for this provider if available
                   providerPricing['default'] ||
                   DEFAULT_PRICING;
    } else {
      modelPricing = DEFAULT_PRICING;
    }

    // Calculate costs (prices are per 1M tokens)
    const inputCost = (promptTokens / 1_000_000) * modelPricing.inputPrice;
    const outputCost = (completionTokens / 1_000_000) * modelPricing.outputPrice;
    const totalCost = inputCost + outputCost;

    return {
      inputCost: Number(inputCost.toFixed(6)),
      outputCost: Number(outputCost.toFixed(6)),
      totalCost: Number(totalCost.toFixed(6)),
      currency: modelPricing.currency,
      provider,
      model
    };
  }

  /**
   * Check if a provider is local (no API costs)
   */
  static isLocalProvider(provider: string): boolean {
    const localProviders = ['ollama', 'lmstudio'];
    return localProviders.includes(provider.toLowerCase());
  }

  /**
   * Format cost for display
   */
  static formatCost(cost: number, currency = 'USD'): string {
    if (cost < 0.000001) {
      return `<$0.000001`;
    }
    if (cost < 0.01) {
      return `$${cost.toFixed(6)}`;
    }
    return `$${cost.toFixed(4)}`;
  }

  /**
   * Get pricing information for a specific model
   */
  static getModelPricing(provider: string, model: string): ModelPricing | null {
    const providerPricing = PROVIDER_PRICING[provider.toLowerCase()];
    if (!providerPricing) return null;

    return providerPricing[model] || 
           providerPricing[model.split('-').slice(0, 2).join('-')] ||
           providerPricing[model.split('-')[0]] ||
           providerPricing['default'] ||
           null;
  }

  /**
   * Get all available models for a provider with their pricing
   */
  static getProviderModels(provider: string): { [model: string]: ModelPricing } {
    return PROVIDER_PRICING[provider.toLowerCase()] || {};
  }
}

/**
 * Process Manager for Internal Commands
 * Handles terminal process execution and management
 */

import { spawn, ChildProcess } from 'child_process';
import { ProcessSession, ProcessInfo } from '../types/internalCommands';
import { EventEmitter } from 'events';

class ProcessManager extends EventEmitter {
  private sessions: Map<number, ProcessSession> = new Map();
  private nextPid = 1000; // Start with a high number to avoid conflicts

  constructor() {
    super();
    this.setupCleanup();
  }

  /**
   * Start a new process
   */
  async startProcess(command: string, shell?: string, timeout?: number): Promise<ProcessInfo> {
    const pid = this.nextPid++;
    const startTime = new Date();

    try {
      // Determine shell to use
      const useShell = shell || this.getDefaultShell();

      // Parse command and arguments
      const { cmd, args } = this.parseCommand(command, useShell);

      // Spawn the process
      const childProcess = spawn(cmd, args, {
        stdio: ['pipe', 'pipe', 'pipe'],
        shell: useShell === 'bash' || useShell === 'sh',
        env: { ...process.env },
        cwd: process.cwd()
      });

      // Create session
      const session: ProcessSession = {
        pid,
        command,
        shell: useShell,
        startTime,
        lastActivity: startTime,
        status: 'running',
        blocked: false,
        process: childProcess
      };

      this.sessions.set(pid, session);

      // Set up process event handlers
      this.setupProcessHandlers(session, childProcess);

      // Set up timeout if specified
      if (timeout && timeout > 0) {
        setTimeout(() => {
          if (this.sessions.has(pid) && this.sessions.get(pid)!.status === 'running') {
            this.forceTerminate(pid);
          }
        }, timeout);
      }

      console.log(`üöÄ Started process ${pid}: ${command}`);

      return {
        pid,
        command,
        status: 'running',
        startTime,
        blocked: false,
        runtime: 0
      };

    } catch (error) {
      console.error(`‚ùå Failed to start process: ${command}`, error);
      throw new Error(`Failed to start process: ${error instanceof Error ? error.message : String(error)}`);
    }
  }

  /**
   * Read output from a process
   */
  async readProcessOutput(pid: number, timeoutMs?: number): Promise<string> {
    const session = this.sessions.get(pid);
    if (!session) {
      throw new Error(`Process ${pid} not found`);
    }

    if (!session.process) {
      throw new Error(`Process ${pid} has no associated child process`);
    }

    return new Promise((resolve, reject) => {
      let output = '';
      let hasResolved = false;

      const timeout = setTimeout(() => {
        if (!hasResolved) {
          hasResolved = true;
          resolve(output || 'Process timed out or no output available');
        }
      }, timeoutMs || 5000);

      // Collect stdout data
      const onData = (data: Buffer) => {
        output += data.toString();
        session.lastActivity = new Date();
      };

      // Handle process completion
      const onClose = (code: number | null) => {
        clearTimeout(timeout);
        if (!hasResolved) {
          hasResolved = true;
          session.status = code === 0 ? 'finished' : 'error';
          resolve(output);
        }
      };

      // Handle errors
      const onError = (error: Error) => {
        clearTimeout(timeout);
        if (!hasResolved) {
          hasResolved = true;
          session.status = 'error';
          reject(error);
        }
      };

      const childProcess = session.process as ChildProcess;
      childProcess.stdout?.on('data', onData);
      childProcess.stderr?.on('data', onData);
      childProcess.on('close', onClose);
      childProcess.on('error', onError);

      // Clean up listeners after resolution
      const cleanup = () => {
        childProcess.stdout?.off('data', onData);
        childProcess.stderr?.off('data', onData);
        childProcess.off('close', onClose);
        childProcess.off('error', onError);
      };

      // Ensure cleanup happens
      setTimeout(cleanup, (timeoutMs || 5000) + 1000);
    });
  }

  /**
   * Send input to a process
   */
  async interactWithProcess(pid: number, input: string, timeoutMs?: number): Promise<string> {
    const session = this.sessions.get(pid);
    if (!session) {
      throw new Error(`Process ${pid} not found`);
    }

    const childProcess = session.process as ChildProcess;
    if (!childProcess || !childProcess.stdin) {
      throw new Error(`Process ${pid} is not accepting input`);
    }

    // Send input to the process
    childProcess.stdin.write(input + '\n');
    session.lastActivity = new Date();

    // Wait for output
    return this.readProcessOutput(pid, timeoutMs);
  }

  /**
   * Force terminate a process
   */
  async forceTerminate(pid: number): Promise<boolean> {
    const session = this.sessions.get(pid);
    if (!session) {
      return false;
    }

    try {
      if (session.process) {
        const childProcess = session.process as ChildProcess;
        childProcess.kill('SIGTERM');

        // If process doesn't terminate gracefully, force kill
        setTimeout(() => {
          if (session.process && !childProcess.killed) {
            childProcess.kill('SIGKILL');
          }
        }, 5000);
      }

      session.status = 'finished';
      this.sessions.delete(pid);

      console.log(`üõë Terminated process ${pid}`);
      return true;

    } catch (error) {
      console.error(`‚ùå Failed to terminate process ${pid}:`, error);
      return false;
    }
  }

  /**
   * List all active sessions
   */
  listSessions(): ProcessInfo[] {
    return Array.from(this.sessions.values()).map(session => ({
      pid: session.pid,
      command: session.command,
      status: session.status,
      startTime: session.startTime,
      blocked: session.blocked,
      runtime: Date.now() - session.startTime.getTime()
    }));
  }

  /**
   * Kill a process by PID (system-wide)
   */
  async killProcess(pid: number): Promise<boolean> {
    try {
      process.kill(pid, 'SIGTERM');
      return true;
    } catch (error) {
      console.error(`‚ùå Failed to kill process ${pid}:`, error);
      return false;
    }
  }

  /**
   * Get default shell based on platform
   */
  private getDefaultShell(): string {
    switch (process.platform) {
      case 'win32':
        return 'powershell';
      case 'darwin':
      case 'linux':
      default:
        return 'bash';
    }
  }

  /**
   * Parse command for shell execution
   */
  private parseCommand(command: string, shell: string): { cmd: string; args: string[] } {
    if (shell === 'powershell') {
      return {
        cmd: 'powershell',
        args: ['-Command', command]
      };
    } else {
      return {
        cmd: shell,
        args: ['-c', command]
      };
    }
  }

  /**
   * Set up process event handlers
   */
  private setupProcessHandlers(session: ProcessSession, childProcess: ChildProcess): void {
    childProcess.on('close', (code) => {
      session.status = code === 0 ? 'finished' : 'error';
      console.log(`üìã Process ${session.pid} closed with code ${code}`);
    });

    childProcess.on('error', (error) => {
      session.status = 'error';
      console.error(`‚ùå Process ${session.pid} error:`, error);
    });

    // Detect if process is blocked (waiting for input)
    let lastOutputTime = Date.now();
    childProcess.stdout?.on('data', () => {
      lastOutputTime = Date.now();
      session.blocked = false;
    });

    // Check periodically if process seems blocked
    const checkBlocked = setInterval(() => {
      if (session.status === 'running' && Date.now() - lastOutputTime > 2000) {
        session.blocked = true;
      }
    }, 1000);

    childProcess.on('close', () => {
      clearInterval(checkBlocked);
    });
  }

  /**
   * Set up cleanup on process exit
   */
  private setupCleanup(): void {
    const cleanup = () => {
      console.log('üßπ Cleaning up process manager...');
      for (const [pid] of this.sessions) {
        this.forceTerminate(pid);
      }
    };

    process.on('exit', cleanup);
    process.on('SIGINT', cleanup);
    process.on('SIGTERM', cleanup);
  }
}

