  private customPrompts: Prompt[] = [];
  private initialized = false;

  constructor() {
    this.loadDefaultPrompts();
    // Only load custom prompts on client side
    if (typeof window !== 'undefined') {
      this.loadCustomPrompts();
      this.initialized = true;
    }
  }

  private ensureInitialized() {
    if (!this.initialized && typeof window !== 'undefined') {
      // Load custom prompts asynchronously but don't block
      this.loadCustomPrompts().catch(error => {
        console.error('Failed to load custom prompts:', error);
      });
      this.initialized = true;
    }
  }

  private loadDefaultPrompts() {
    this.prompts = (promptsData as PromptsData).prompts;
  }

  private async loadCustomPrompts() {
    try {
      const stored = await getStorageItem('custom-prompts');
      if (stored) {
        this.customPrompts = JSON.parse(stored);
      }
    } catch (error) {
      console.error('Failed to load custom prompts:', error);
      this.customPrompts = [];
    }
  }

  private async saveCustomPrompts() {
    try {
      await setStorageItem('custom-prompts', JSON.stringify(this.customPrompts));
    } catch (error) {
      console.error('Failed to save custom prompts:', error);
    }
  }

  getAllPrompts(): Prompt[] {
    this.ensureInitialized();
    return [...this.prompts, ...this.customPrompts];
  }

  getCustomPrompts(): Prompt[] {
    this.ensureInitialized();
    return [...this.customPrompts];
  }

  getPromptsByCategory(category: string): Prompt[] {
    const allPrompts = this.getAllPrompts();
    return allPrompts.filter(prompt => prompt.category === category);
  }

  getPromptById(id: string): Prompt | undefined {
    const allPrompts = this.getAllPrompts();
    return allPrompts.find(prompt => prompt.id === id);
  }

  getCategories(): string[] {
    const allPrompts = this.getAllPrompts();
    const categories = new Set(allPrompts.map(prompt => prompt.category));
    return Array.from(categories).sort();
  }

  processPrompt(promptId: string, content: string): string {
    const prompt = this.getPromptById(promptId);
    if (!prompt) {
      throw new Error(`Prompt with id "${promptId}" not found`);
    }

    return prompt.prompt.replace('{content}', content);
  }

  async addCustomPrompt(prompt: Omit<Prompt, 'id'>): Promise<Prompt> {
    const newPrompt: Prompt = {
      ...prompt,
      id: `custom_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
    };

    this.customPrompts.push(newPrompt);
    await this.saveCustomPrompts();
    return newPrompt;
  }

  async updateCustomPrompt(id: string, updates: Partial<Omit<Prompt, 'id'>>): Promise<boolean> {
    const index = this.customPrompts.findIndex(prompt => prompt.id === id);
    if (index === -1) {
      return false;
    }

    this.customPrompts[index] = { ...this.customPrompts[index], ...updates };
    await this.saveCustomPrompts();
    return true;
  }

  async updateBuiltinPrompt(id: string, updates: Partial<Omit<Prompt, 'id'>>): Promise<boolean> {
    const index = this.prompts.findIndex(prompt => prompt.id === id);
    if (index === -1) {
      return false;
    }

    this.prompts[index] = { ...this.prompts[index], ...updates };
    // Note: Built-in prompts are modified in memory only
    // They will revert to original values on app restart
    return true;
  }

  async updatePrompt(id: string, updates: Partial<Omit<Prompt, 'id'>>): Promise<boolean> {
    // Check if it's a custom prompt first
    if (this.isCustomPrompt(id)) {
      return this.updateCustomPrompt(id, updates);
    } else {
      // It's a built-in prompt
      return this.updateBuiltinPrompt(id, updates);
    }
  }

  async deleteCustomPrompt(id: string): Promise<boolean> {
    const index = this.customPrompts.findIndex(prompt => prompt.id === id);
    if (index === -1) {
      return false;
    }

    this.customPrompts.splice(index, 1);
    await this.saveCustomPrompts();
    return true;
  }

  isCustomPrompt(id: string): boolean {
    return this.customPrompts.some(prompt => prompt.id === id);
  }

  /**
   * Find existing custom copy of a built-in prompt
   * This helps prevent duplicate custom prompts when editing built-in ones
   */
  findCustomCopyOfBuiltinPrompt(builtinPrompt: Prompt): Prompt | undefined {
    if (this.isCustomPrompt(builtinPrompt.id)) {
      return undefined; // Already a custom prompt
    }

    // Look for custom prompts that might be copies of this built-in prompt
    // We'll match based on the original name (removing " (Custom)" suffix if present)
    const originalName = builtinPrompt.name;
    const customCopyName = `${originalName} (Custom)`;

    return this.customPrompts.find(customPrompt =>
      customPrompt.name === customCopyName ||
      customPrompt.name === originalName ||
      // Also check if the prompt content is very similar (in case user renamed it)
      (customPrompt.prompt === builtinPrompt.prompt && customPrompt.category === builtinPrompt.category)
    );
  }

  exportPrompts(): string {
    return JSON.stringify({
      prompts: this.customPrompts
    }, null, 2);
  }

  async importPrompts(jsonData: string): Promise<boolean> {
    try {
      const data = JSON.parse(jsonData);
      if (data.prompts && Array.isArray(data.prompts)) {
        // Validate prompt structure
        const validPrompts = data.prompts.filter((prompt: unknown) => {
          const p = prompt as Record<string, unknown>;
          return p.name && p.description && p.prompt && p.category;
        });

        // Add unique IDs if missing
        validPrompts.forEach((prompt: unknown) => {
          const p = prompt as Record<string, unknown>;
          if (!p.id) {
            p.id = `imported_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
          }
          if (!p.icon) {
            p.icon = 'üìù';
          }
        });

        this.customPrompts.push(...validPrompts);
        await this.saveCustomPrompts();
        return true;
      }
      return false;
    } catch (error) {
      console.error('Failed to import prompts:', error);
      return false;
    }
  }
}

export const promptsService = new PromptsService();

// Anthropic provider implementation

import { BaseProvider } from './BaseProvider';
import { 
  LLMSettings, 
  LLMResponse, 
  MessageContent, 
  ContentItem, 
  LLMProvider,
  ToolObject,
  ProviderCapabilities
} from './types';

import { ToolNameUtils } from './utils';
import { ANTHROPIC_SYSTEM_PROMPT } from './prompts/anthropic';
import { PricingService } from '../pricingService';

export class AnthropicProvider extends BaseProvider {
  readonly id = 'anthropic';
  readonly name = 'Anthropic';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: true,
    supportsTools: true,
    supportsStreaming: true,
    supportsSystemMessages: true,
    supportsPromptCaching: true,
    promptCachingType: 'manual', // Requires cache_control parameters
    maxToolNameLength: 64,
    toolFormat: 'anthropic'
  };

  // Injected methods from main service
  private _executeMultipleToolsParallel?: (
    toolCalls: Array<{ id?: string; name: string; arguments: Record<string, unknown> }>,
    provider?: string
  ) => Promise<Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>>;

  private _summarizeToolResultsForModel?: (
    results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
  ) => string;

  // Injected tool executor method
  public executeMCPTool?: (toolName: string, args: Record<string, unknown>) => Promise<string>;

  private _aggregateToolResults?: (
    results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
  ) => string;

  private _formatToolResult?: (toolName: string, result: unknown) => string;

  private _getMCPToolsForProvider?: (providerId: string, settings: LLMSettings) => Promise<unknown[]>;

  // Anthropic-specific tool calling methods
  private async getAnthropicTools(settings: LLMSettings): Promise<unknown[]> {
    try {
      console.log(`üîç Getting tools for Anthropic provider`);
      console.log(`üîç Tool calling enabled:`, settings?.toolCallingEnabled !== false);

      // Check if tool calling is disabled
      if (settings?.toolCallingEnabled === false) {
        console.log(`üö´ Tool calling is disabled, returning empty tools array`);
        return [];
      }

      // Get raw tools from the centralized service
      console.log(`üîß AnthropicProvider: Calling _getMCPToolsForProvider for anthropic`);
      console.log(`üîß AnthropicProvider: _getMCPToolsForProvider method exists:`, typeof this._getMCPToolsForProvider === 'function');

      const rawTools = await this._getMCPToolsForProvider!('anthropic', settings);
      console.log(`üîß AnthropicProvider: Received ${rawTools.length} raw tools from _getMCPToolsForProvider`);
      console.log(`üìã Raw tools received (${rawTools.length} tools):`, (rawTools as Array<{ function?: { name?: string }, serverId?: string }>).map(t => ({
        name: t.function?.name,
        serverId: t.serverId,
        isInternal: t.serverId === 'internal-commands'
      })));

      // Format tools specifically for Anthropic
      const formattedTools = this.formatToolsForAnthropic(rawTools as Array<{ type?: string; function?: { name?: string; description?: string; parameters?: unknown } }>);
      console.log(`üîß Formatted ${formattedTools.length} tools for Anthropic`);

      return formattedTools;
    } catch (error) {
      console.error('‚ùå Failed to get Anthropic tools:', error);
      return [];
    }
  }

  private formatToolsForAnthropic(rawTools: Array<{ type?: string; function?: { name?: string; description?: string; parameters?: unknown } }>): unknown[] {
    return rawTools.map(tool => {
      // All tools now come in unified format with type: 'function' and function object
      if (tool.type === 'function' && tool.function) {
        // Convert to Anthropic's custom tool format
        return {
          type: 'custom',
          name: tool.function.name || 'unknown_tool',
          description: tool.function.description || 'No description',
          input_schema: tool.function.parameters || {
            type: 'object',
            properties: {},
            required: []
          }
        };
      }
      
      console.warn(`‚ö†Ô∏è Skipping invalid tool (not in unified format):`, tool);
      return null;
    }).filter(tool => tool !== null);
  }

  // Method to inject dependencies from main service
  injectDependencies(dependencies: {
    executeMultipleToolsParallel?: (
      toolCalls: Array<{ id?: string; name: string; arguments: Record<string, unknown> }>,
      provider?: string
    ) => Promise<Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>>;
    summarizeToolResultsForModel?: (
      results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
    ) => string;
    aggregateToolResults?: (
      results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
    ) => string;
    formatToolResult?: (toolName: string, result: unknown) => string;
    getMCPToolsForProvider?: (providerId: string, settings: LLMSettings) => Promise<unknown[]>;
  }) {
    this._executeMultipleToolsParallel = dependencies.executeMultipleToolsParallel;
    this._summarizeToolResultsForModel = dependencies.summarizeToolResultsForModel;
    this._aggregateToolResults = dependencies.aggregateToolResults;
    this._formatToolResult = dependencies.formatToolResult;
    this._getMCPToolsForProvider = dependencies.getMCPToolsForProvider;
  }

  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    // Initialize caching setting early
    const cachingEnabled = settings.promptCachingEnabled ?? true;

    // Debug API key details
    console.log('üîç Anthropic API key debug:', {
      hasApiKey: !!settings.apiKey,
      keyLength: settings.apiKey?.length || 0,
      keyStart: settings.apiKey?.substring(0, 10) || 'undefined',
      keyType: typeof settings.apiKey,
      startsWithSkAnt: settings.apiKey?.startsWith('sk-ant-') || false,
      cachingEnabled
    });

    // Validate API key format
    if (!settings.apiKey || !settings.apiKey.startsWith('sk-ant-')) {
      console.error('‚ùå Anthropic API key validation failed:', {
        apiKey: settings.apiKey,
        hasApiKey: !!settings.apiKey,
        startsWithSkAnt: settings.apiKey?.startsWith('sk-ant-')
      });
      throw new Error('Invalid Anthropic API key format. Key should start with "sk-ant-"');
    }

    // Adjust max_tokens based on Claude model limits
    let maxTokens = settings.maxTokens;
    if (settings.model.includes('claude-3-5-haiku')) {
      maxTokens = Math.min(maxTokens, 8192);
    } else if (settings.model.includes('claude-3-opus') || settings.model.includes('claude-3-sonnet') || settings.model.includes('claude-3-haiku')) {
      // Claude 3 models have 4096 max output tokens
      maxTokens = Math.min(maxTokens, 4096);
    } else if (settings.model.includes('claude-3-5-sonnet')) {
      maxTokens = Math.min(maxTokens, 8192);
    } else {
      // Default Claude limit
      maxTokens = Math.min(maxTokens, 4096);
    }

    const messages = [];

    // Add conversation history - filter out empty messages for Anthropic
    for (const historyMessage of conversationHistory) {
      let content: string;
      if (typeof historyMessage.content === 'string') {
        content = historyMessage.content.trim();
      } else if (Array.isArray(historyMessage.content)) {
        // Extract text from array format
        content = historyMessage.content.map((item: ContentItem | string) => {
          if (typeof item === 'string') return item;
          if (item.type === 'text') return item.text;
          return '[Non-text content]';
        }).join(' ').trim();
      } else {
        content = String(historyMessage.content).trim();
      }

      // Only add messages with non-empty content
      if (content) {
        messages.push({
          role: historyMessage.role,
          content: content
        });
      } else {
        console.warn(`‚ö†Ô∏è Skipping empty message in Anthropic conversation history:`, historyMessage);
      }
    }

    // Add current message
    if (typeof message === 'string') {
      // For large string messages, add cache_control if caching is enabled
      if (cachingEnabled && message.length > 4096) { // ~1024 tokens
        const contentWithCaching = [
          {
            type: 'text',
            text: message,
            cache_control: { type: 'ephemeral' }
          }
        ];
        messages.push({ role: 'user', content: contentWithCaching });
        console.log(`üîß Anthropic: Added cache_control to large user message (${message.length} chars)`);
      } else {
        messages.push({ role: 'user', content: message });
      }
    } else if (Array.isArray(message)) {
      // Handle ContentItem array format (from chatService.ts)
      const anthropicContent = message.map((item: ContentItem, index) => {
        if (item.type === 'text') {
          // Add cache_control to large text content and make it the last cacheable item
          const textItem = { type: 'text', text: item.text };
          if (cachingEnabled && item.text && item.text.length > 4096 && index === message.length - 1) {
            return { ...textItem, cache_control: { type: 'ephemeral' } };
          }
          return textItem;
        } else if (item.type === 'image_url') {
          // Convert OpenAI format to Anthropic format
          const imageUrl = item.image_url?.url || '';

          // Determine media type from data URL
          const mediaType = imageUrl.includes('data:image/png') ? 'image/png' :
                           imageUrl.includes('data:image/gif') ? 'image/gif' :
                           imageUrl.includes('data:image/webp') ? 'image/webp' : 'image/jpeg';

          return {
            type: 'image',
            source: {
              type: 'base64',
              media_type: mediaType,
              data: imageUrl.split(',')[1] // Remove data:image/jpeg;base64, prefix
            }
          };
        } else if (item.type === 'document') {
          // Handle document format for Anthropic
          return {
            type: 'document',
            source: {
              type: 'base64',
              media_type: item.document?.media_type || 'application/pdf',
              data: item.document?.data || ''
            }
          };
        }
        return item; // Pass through other types as-is
      });

      messages.push({ role: 'user', content: anthropicContent });
    } else {
      // Handle legacy vision format (for backward compatibility)
      const messageWithImages = message as { text: string; images: string[] };
      const content = [];
      content.push({ type: 'text', text: messageWithImages.text });

      // Add images in Anthropic format
      for (const imageUrl of messageWithImages.images) {
        // Determine media type from data URL
        const mediaType = imageUrl.includes('data:image/png') ? 'image/png' :
                         imageUrl.includes('data:image/gif') ? 'image/gif' :
                         imageUrl.includes('data:image/webp') ? 'image/webp' : 'image/jpeg';

        content.push({
          type: 'image',
          source: {
            type: 'base64',
            media_type: mediaType,
            data: imageUrl.split(',')[1] // Remove data:image/jpeg;base64, prefix
          }
        });
      }
      messages.push({ role: 'user', content });
    }

    // Get Anthropic-specific formatted tools
    const anthropicTools = await this.getAnthropicTools(settings);

    // Use behavioral system prompt only (no tool descriptions)
    // Tools are sent separately in the tools parameter
    // Check for meaningful system prompt, not just empty string or generic default
    const hasCustomSystemPrompt = settings.systemPrompt &&
      settings.systemPrompt.trim() &&
      settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

    const systemPrompt = hasCustomSystemPrompt ? settings.systemPrompt! : this.getSystemPrompt();

    console.log(`üîç Anthropic system prompt source:`, {
      hasCustom: hasCustomSystemPrompt,
      usingCustom: hasCustomSystemPrompt,
      promptLength: systemPrompt?.length || 0,
      promptStart: systemPrompt?.substring(0, 100) + '...',
      cachingEnabled
    });

    // Add cache_control to system prompt if caching is enabled and content is large enough
    let systemWithCaching: unknown = systemPrompt;
    if (cachingEnabled && systemPrompt && systemPrompt.length > 4096) { // ~1024 tokens
      systemWithCaching = [
        {
          type: 'text',
          text: systemPrompt,
          cache_control: { type: 'ephemeral' }
        }
      ];
      console.log(`üîß Anthropic: Added cache_control to system prompt (${systemPrompt.length} chars)`);
    }

    const requestBody: Record<string, unknown> = {
      model: settings.model,
      max_tokens: maxTokens,
      temperature: settings.temperature,
      system: systemWithCaching || undefined,
      messages: messages,
      stream: !!onStream
    };

    // Add tools if available
    if (anthropicTools.length > 0) {
      requestBody.tools = anthropicTools;

      // For Claude 3.7 Sonnet, encourage parallel tool use
      if (settings.model.includes('claude-3-7-sonnet') || settings.model.includes('claude-sonnet-3-7')) {
        // Don't disable parallel tool use to encourage multiple tool calls
      }

      // Use auto tool choice to allow Claude to decide when to use tools
      requestBody.tool_choice = { type: "auto" };

      console.log(`üöÄ Anthropic API call with ${anthropicTools.length} tools:`, {
        model: settings.model,
        toolCount: anthropicTools.length,
        toolChoice: requestBody.tool_choice
      });
    } else {
      console.log(`üöÄ Anthropic API call without tools (no tools available)`);
    }

    console.log('üîç Anthropic request body:', JSON.stringify(requestBody, null, 2));

    const response = await fetch(`${provider.baseUrl}/messages`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'x-api-key': settings.apiKey,
        'anthropic-version': '2023-06-01'
      },
      body: JSON.stringify(requestBody),
      signal
    });

    console.log('üîç Anthropic response status:', response.status, response.statusText);

    if (!response.ok) {
      const error = await response.text();
      console.error('‚ùå Anthropic API error response:', error);
      if (response.status === 401) {
        throw new Error(`Anthropic API authentication failed. Please check your API key in Settings. The key may be expired or invalid. Error: ${error}`);
      }
      throw new Error(`Anthropic API error: ${error}`);
    }

    if (onStream) {
      return this.handleStreamResponse(response, onStream, settings, provider, conversationHistory, signal);
    } else {
      return this.handleNonStreamResponse(response, settings, conversationHistory, conversationId);
    }
  }

  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.error('‚ùå No Anthropic API key provided - cannot fetch models');
      throw new Error('Anthropic API key is required to fetch available models. Please add your API key in settings.');
    }

    try {
      console.log('üîç Fetching Anthropic models from API...');
      const response = await fetch('https://api.anthropic.com/v1/models', {
        headers: {
          'x-api-key': apiKey,
          'anthropic-version': '2023-06-01',
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå Anthropic API error: ${response.status} ${response.statusText}`, errorText);
        throw new Error(`Failed to fetch Anthropic models: ${response.status} ${response.statusText} - ${errorText}`);
      }

      const data = await response.json() as { data: Array<{ id: string; display_name: string }> };
      const models = data.data?.map((model) => model.id)?.sort() || [];

      console.log(`‚úÖ Fetched ${models.length} Anthropic models from API:`, models);

      if (models.length === 0) {
        throw new Error('No Anthropic models returned from API. This may indicate an API issue or insufficient permissions.');
      }

      return models;
    } catch (error) {
      console.error('‚ùå Failed to fetch Anthropic models from API:', error);
      throw error instanceof Error ? error : new Error(`Failed to fetch Anthropic models: ${String(error)}`);
    }
  }

  formatTools(tools: ToolObject[]): unknown[] {
    // Anthropic format with name length validation (max 64 characters)
    return tools.map(tool => {
      const originalName = tool.name || tool.function?.name || '';
      const truncatedName = originalName.length > 64
        ? ToolNameUtils.truncateToolNameForAnthropic(originalName)
        : originalName;

      if (originalName !== truncatedName) {
        console.warn(`‚ö†Ô∏è Truncated tool name for Anthropic: "${originalName}" -> "${truncatedName}"`);
      }

      return {
        name: truncatedName,
        description: tool.description || tool.function?.description,
        input_schema: tool.parameters || tool.function?.parameters || {
          type: 'object',
          properties: {},
          required: []
        }
      };
    });
  }

  getSystemPrompt(): string {
    return ANTHROPIC_SYSTEM_PROMPT;
  }



  enhanceSystemPromptWithTools(basePrompt: string, tools: ToolObject[]): string {
    if (tools.length === 0) {
      return basePrompt;
    }

    // Anthropic uses structured tool calling with tools parameter and tool_choice
    // Don't add XML tool instructions as they conflict with native function calling
    console.log(`üîß Anthropic using structured tools, skipping XML tool instructions`);
    return basePrompt;
  }

  validateToolCall(toolCall: { id?: string; name: string; arguments: Record<string, unknown> }): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!toolCall.name || typeof toolCall.name !== 'string') {
      errors.push('Tool call must have a valid name');
    }

    // Anthropic uses tool_use blocks with specific format
    if (toolCall.arguments && typeof toolCall.arguments !== 'object') {
      errors.push(`Anthropic tool call arguments must be object: ${toolCall.name}`);
    }

    return { valid: errors.length === 0, errors };
  }

  validateTool(tool: unknown): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!tool || typeof tool !== 'object') {
      errors.push('Tool must be an object');
      return { valid: false, errors };
    }

    const toolObj = tool as Record<string, unknown>;

    if (!toolObj.name) {
      errors.push('Anthropic tools must have name property');
    }

    if (!toolObj.description) {
      errors.push('Anthropic tools must have description property');
    }

    return { valid: errors.length === 0, errors };
  }

  // Legacy methods for backward compatibility (now use injected dependencies)
  private async getMCPToolsForProvider(providerId: string, settings: LLMSettings): Promise<unknown[]> {
    return this._getMCPToolsForProvider ? await this._getMCPToolsForProvider(providerId, settings) : [];
  }

  private async executeMultipleToolsParallel(
    toolCalls: Array<{ id?: string; name: string; arguments: Record<string, unknown> }>,
    provider?: string
  ): Promise<Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>> {
    return this._executeMultipleToolsParallel ? await this._executeMultipleToolsParallel(toolCalls, provider) : [];
  }

  private summarizeToolResultsForModel(
    results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
  ): string {
    return this._summarizeToolResultsForModel ? this._summarizeToolResultsForModel(results) : '';
  }

  private aggregateToolResults(
    results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
  ): string {
    return this._aggregateToolResults ? this._aggregateToolResults(results) : '';
  }

  private formatToolResult(toolName: string, result: unknown): string {
    return this._formatToolResult ? this._formatToolResult(toolName, result) : JSON.stringify(result);
  }

  private static streamingCallCount = 0;
  private static readonly MAX_STREAMING_CALLS = 5;

  private async handleStreamResponse(
    response: Response,
    onStream: (chunk: string) => void,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    signal?: AbortSignal
  ): Promise<LLMResponse> {
    // Prevent infinite recursion
    AnthropicProvider.streamingCallCount++;
    if (AnthropicProvider.streamingCallCount > AnthropicProvider.MAX_STREAMING_CALLS) {
      console.error('‚ùå CRITICAL: Too many streaming calls detected - preventing infinite loop');
      AnthropicProvider.streamingCallCount = 0;
      throw new Error('Maximum streaming calls exceeded - preventing infinite loop');
    }

    try {
    const reader = response.body?.getReader();
    if (!reader) {
      throw new Error('No response body');
    }

    let fullContent = '';
    let usage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined = undefined;
    const decoder = new TextDecoder();
    const toolCalls: Array<{ id?: string; name?: string; arguments?: unknown; result?: string; isError?: boolean; parseError?: string }> = [];
    const toolInputBuffers: { [index: number]: string } = {};
    const currentToolBlocks: { [index: number]: Record<string, unknown> } = {};
    const assistantContent: Array<Record<string, unknown>> = [];

    try {
      // eslint-disable-next-line no-constant-condition
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('event: ')) {
            // Skip event type lines
            continue;
          }

          if (line.startsWith('data: ')) {
            const data = line.slice(6).trim();
            if (!data || data === '[DONE]') continue;

            try {
              const parsed = JSON.parse(data);

              // Handle content_block_start for text
              if (parsed.type === 'content_block_start' && parsed.content_block?.type === 'text') {
                assistantContent.push({ type: 'text', text: '' });
              }

              // Handle content_block_start for tool_use
              if (parsed.type === 'content_block_start' && parsed.content_block?.type === 'tool_use') {
                console.log(`üîß Anthropic streaming tool use started:`, parsed.content_block);
                currentToolBlocks[parsed.index] = parsed.content_block;
                toolInputBuffers[parsed.index] = '';
                assistantContent.push({
                  type: 'tool_use',
                  id: parsed.content_block.id,
                  name: parsed.content_block.name,
                  input: {}
                });

                // Show tool usage in chat
                const toolMessage = `\n\nüîß **Using tool: ${parsed.content_block.name}**\n`;
                fullContent += toolMessage;
                onStream(toolMessage);
              }

              // Handle content_block_delta events with text_delta
              if (parsed.type === 'content_block_delta' &&
                  parsed.delta?.type === 'text_delta' &&
                  parsed.delta?.text) {
                const content = parsed.delta.text;
                fullContent += content;
                onStream(content);

                // Update assistant content
                if (assistantContent[parsed.index] && assistantContent[parsed.index].type === 'text') {
                  assistantContent[parsed.index].text += content;
                }
              }

              // Handle content_block_delta events with input_json_delta (tool parameters)
              if (parsed.type === 'content_block_delta' &&
                  parsed.delta?.type === 'input_json_delta' &&
                  parsed.delta?.partial_json !== undefined) {
                const index = parsed.index;
                toolInputBuffers[index] += parsed.delta.partial_json;
                console.log(`üîß Anthropic streaming tool input:`, { index, partial: parsed.delta.partial_json });
              }

              // Handle content_block_stop for tool_use
              if (parsed.type === 'content_block_stop' && currentToolBlocks[parsed.index]?.type === 'tool_use') {
                const index = parsed.index;
                const toolBlock = currentToolBlocks[index];
                const inputJson = toolInputBuffers[index];

                console.log(`üîß Anthropic streaming tool use completed:`, { toolBlock, inputJson });

                try {
                  const toolInput = JSON.parse(inputJson);
                  console.log(`üîß Collected streaming tool for parallel execution:`, toolBlock.name, toolInput);

                  // Update assistant content with final input
                  if (assistantContent[index] && assistantContent[index].type === 'tool_use') {
                    assistantContent[index].input = toolInput;
                  }

                  // Collect tool for parallel execution (don't execute yet)
                  toolCalls.push({
                    id: toolBlock.id as string,
                    name: toolBlock.name as string,
                    arguments: toolInput
                  });

                  // Show that we're preparing the tool (don't execute yet)
                  const preparingMessage = `‚öôÔ∏è Preparing ${toolBlock.name}...\n`;
                  fullContent += preparingMessage;
                  onStream(preparingMessage);

                } catch (error) {
                  console.error(`‚ùå Anthropic streaming tool input parsing failed:`, error);

                  // Show parsing error in chat
                  const errorMessage = `‚ùå Tool ${toolBlock.name} input parsing failed: ${error instanceof Error ? error.message : String(error)}\n`;
                  fullContent += errorMessage;
                  onStream(errorMessage);

                  // Still collect the tool call for potential execution
                  toolCalls.push({
                    id: toolBlock.id as string,
                    name: toolBlock.name as string,
                    arguments: {},
                    parseError: error instanceof Error ? error.message : String(error)
                  });
                }

                // Clean up
                delete currentToolBlocks[index];
                delete toolInputBuffers[index];
              }

              // Handle message_delta events with usage data
              if (parsed.type === 'message_delta' && parsed.usage) {
                usage = {
                  prompt_tokens: parsed.usage.input_tokens,
                  completion_tokens: parsed.usage.output_tokens,
                  total_tokens: parsed.usage.input_tokens + parsed.usage.output_tokens
                };
              }
            } catch (e) {
              // Skip invalid JSON
              console.warn('Failed to parse streaming event:', e);

              // Prevent infinite loops from debug logger errors
              if (e instanceof Error && e.message.includes('debugLogger') && e.message.includes('is not a function')) {
                console.error('‚ùå Critical: Debug logger method missing - breaking streaming loop to prevent infinite recursion');
                break; // Exit the streaming loop
              }
            }
          }
        }
      }
    } finally {
      reader.releaseLock();
      // Reset counter when streaming completes normally
      AnthropicProvider.streamingCallCount = Math.max(0, AnthropicProvider.streamingCallCount - 1);
    }

    // If we have tool calls, execute them in parallel and make a follow-up streaming call
    console.log(`üîç Anthropic tool execution check:`, {
      toolCallsCount: toolCalls.length,
      hasExecuteMultipleToolsParallel: !!this._executeMultipleToolsParallel,
      hasSummarizeToolResultsForModel: !!this._summarizeToolResultsForModel,
      hasAggregateToolResults: !!this._aggregateToolResults,
      hasFormatToolResult: !!this._formatToolResult
    });

    if (toolCalls.length > 0 && this._executeMultipleToolsParallel && this._summarizeToolResultsForModel && this._aggregateToolResults && this._formatToolResult) {
      console.log(`üöÄ Executing ${toolCalls.length} Anthropic tools in parallel before follow-up`);

      // Show parallel execution message
      const parallelMessage = `\nüöÄ Executing ${toolCalls.length} tools in parallel...\n`;
      fullContent += parallelMessage;
      onStream(parallelMessage);

      // Filter and prepare tool calls for parallel execution
      const validToolCalls = toolCalls
        .filter(tc => tc.id && tc.name && tc.arguments !== undefined)
        .map(tc => ({
          id: tc.id!,
          name: tc.name!,
          arguments: tc.arguments as Record<string, unknown>
        }));

      // Execute all tools in parallel
      const parallelResults = await this._executeMultipleToolsParallel(validToolCalls, 'anthropic');

      // Show completion message
      const successCount = parallelResults.filter(r => r.success).length;
      const completionMessage = `‚úÖ Parallel execution completed: ${successCount}/${parallelResults.length} successful\n\n`;
      fullContent += completionMessage;
      onStream(completionMessage);

      // Add user-friendly summary for the model to work with (not the detailed debug output)
      const toolSummary = this._summarizeToolResultsForModel(parallelResults);

      // Log detailed results for debugging (not shown to user)
      console.log('üîß Detailed tool execution results:', this._aggregateToolResults(parallelResults));

      // Only add the clean summary to the content stream
      fullContent += toolSummary;
      onStream(toolSummary);

      // Log tool execution for debugging
      console.log(`üîç Executed tools:`, parallelResults.map(r => r.name));
      console.log(`üîç Tool execution completed, proceeding with follow-up call`);

      console.log(`üîÑ Making follow-up Anthropic streaming call with ${parallelResults.length} tool results`);

      // Reconstruct the conversation with tool results
      const messages = conversationHistory ? [...conversationHistory] : [];

      // Add the assistant's message with tool calls (proper Anthropic format)
      messages.push({
        role: 'assistant',
        content: assistantContent as unknown as Array<ContentItem>
      });

      // Add tool results as user message using parallel execution results (proper Anthropic format)
      const toolResults = parallelResults.map(result => {
        let content = result.result;

        // Parse JSON results and format them properly for Claude
        try {
          const parsedResult = JSON.parse(result.result);
          content = this._formatToolResult!(result.name, parsedResult);
        } catch {
          // If not JSON, use as-is but clean up quotes
          content = result.result.replace(/^"|"$/g, '');
        }

        return {
          type: 'tool_result',
          tool_use_id: result.id || '',
          content: content,
          is_error: !result.success
        };
      });

      messages.push({
        role: 'user',
        content: toolResults as unknown as Array<ContentItem>
      });

      // Get tools for continued agentic behavior
      const anthropicTools = await this.getAnthropicTools(settings);
      console.log(`üîß Anthropic follow-up call with ${anthropicTools.length} tools available for continued agentic behavior`);

      // Use behavioral system prompt only for follow-up (no tool descriptions)
      // Tools are sent separately in the tools parameter
      const hasCustomSystemPromptFollowUp = settings.systemPrompt &&
        settings.systemPrompt.trim() &&
        settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

      const baseSystemPrompt = hasCustomSystemPromptFollowUp ? settings.systemPrompt! : this.getSystemPrompt();
      const followUpSystemPrompt = baseSystemPrompt +
        `\n\n## Follow-up Context\n\nBased on the tool results provided above, analyze the information and provide a complete, helpful response to the user's original question. Use the tool results to give specific, detailed information. If you need additional tools to provide a better answer, use them, but always conclude with a final response to the user.`;

      // Make follow-up streaming call with tools enabled for agentic behavior
      const followUpRequestBody = {
        model: settings.model,
        max_tokens: settings.maxTokens,
        temperature: settings.temperature,
        system: followUpSystemPrompt,
        messages: messages,
        stream: true,
        // Include tools to allow continued agentic behavior
        ...(anthropicTools.length > 0 && {
          tools: anthropicTools,
          tool_choice: { type: "auto" }
        })
      };

      const followUpResponse = await fetch(`${provider.baseUrl}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-api-key': settings.apiKey,
          'anthropic-version': '2023-06-01'
        },
        body: JSON.stringify(followUpRequestBody),
        signal
      });

      if (followUpResponse.ok) {
        console.log(`‚úÖ Starting follow-up streaming response`);

        try {
          // Stream the follow-up response with updated conversation history for agentic behavior
          const followUpResult = await this.handleStreamResponse(
            followUpResponse,
            (chunk: string) => {
              console.log(`üîÑ Anthropic streaming follow-up chunk:`, chunk.substring(0, 50) + '...');
              // DISABLED: debugLogger.logStreaming('Anthropic', chunk, true);
              onStream(chunk);
            },
            settings,
            provider,
            messages, // Use updated messages that include tool results
            signal
          );

          console.log(`‚úÖ Follow-up streaming completed:`, {
            contentLength: followUpResult.content?.length || 0,
            hasUsage: !!followUpResult.usage,
            hasToolCalls: !!followUpResult.toolCalls
          });

          // Combine tool calls from initial response AND follow-up response
          const initialToolCalls = toolCalls
            .filter(tc => tc.id && tc.name)
            .map(tc => ({
              id: tc.id!,
              name: tc.name!,
              arguments: tc.arguments as Record<string, unknown>
            }));

          const followUpToolCalls = followUpResult.toolCalls || [];
          const allToolCalls = [...initialToolCalls, ...followUpToolCalls];

          console.log(`üîß Combined tool calls: ${initialToolCalls.length} initial + ${followUpToolCalls.length} follow-up = ${allToolCalls.length} total`);

          return {
            content: fullContent + followUpResult.content,
            usage: followUpResult.usage ? {
              promptTokens: (usage?.prompt_tokens || 0) + (followUpResult.usage?.promptTokens || 0),
              completionTokens: (usage?.completion_tokens || 0) + (followUpResult.usage?.completionTokens || 0),
              totalTokens: (usage?.total_tokens || 0) + (followUpResult.usage?.totalTokens || 0)
            } : usage ? {
              promptTokens: usage.prompt_tokens || 0,
              completionTokens: usage.completion_tokens || 0,
              totalTokens: usage.total_tokens || 0
            } : undefined,
            toolCalls: allToolCalls
          };
        } catch (error) {
          console.error(`‚ùå Anthropic follow-up streaming failed:`, error);
          console.error(`‚ùå Error details:`, {
            errorType: typeof error,
            errorMessage: error instanceof Error ? error.message : String(error),
            errorStack: error instanceof Error ? error.stack : undefined
          });

          // Reset streaming counter on error to prevent permanent lockout
          AnthropicProvider.streamingCallCount = 0;

          // Prevent infinite loops by not retrying on specific errors
          if (error instanceof Error && error.message.includes('debugLogger') && error.message.includes('is not a function')) {
            console.error('‚ùå Critical: Debug logger method missing - this would cause infinite loops. Stopping retry attempts.');
            // Don't retry, just provide fallback response
          }

          // Provide a fallback response with tool results
          const fallbackMessage = `\n\n**Tool execution completed successfully, but follow-up response failed. Here are the tool results:**\n\n`;
          onStream(fallbackMessage);

          // Stream the tool summary as fallback
          const toolSummary = this._summarizeToolResultsForModel!(parallelResults);
          onStream(toolSummary);

          // Check if it's a rate limit error and try local execution
          if (error && typeof error === 'object' && 'error' in error) {
            const apiError = error as { error?: { type?: string } };
            if (apiError.error?.type === 'rate_limit_error') {
              console.warn('‚ö†Ô∏è Anthropic: Rate limit exceeded, attempting local tool execution');

              // Try to execute tools locally if methods are available
              if (this.executeMCPTool && toolCalls.length > 0) {
                try {
                  const localResults = await this.executeToolsLocally(toolCalls, fullContent, usage);
                  return localResults as unknown as LLMResponse;
                } catch (localError) {
                  console.error('‚ùå Local tool execution also failed:', localError);
                }
              }
            }
          }

          // Fall through to return original response
        }
      } else {
        const errorText = await followUpResponse.text();
        console.error(`‚ùå Anthropic follow-up streaming call failed:`, followUpResponse.status, followUpResponse.statusText);
        console.error(`‚ùå Follow-up error details:`, errorText);

        // Provide a fallback response with tool results
        const fallbackMessage = `\n\n**Tool execution completed successfully, but follow-up request failed (${followUpResponse.status}). Here are the tool results:**\n\n`;
        onStream(fallbackMessage);

        // Stream the tool summary as fallback
        const toolSummary = this._summarizeToolResultsForModel!(parallelResults);
        onStream(toolSummary);
      }
    }

    // If we reach here, either no tool calls or tool execution methods not available
    if (toolCalls.length > 0) {
      console.warn(`‚ö†Ô∏è Anthropic: Tool calls detected but execution methods not available. Tool calls will not be executed.`);
      console.warn(`‚ö†Ô∏è Anthropic: Returning tool calls for external handling.`);
    }

    return {
      content: fullContent,
      usage: usage ? {
        promptTokens: usage.prompt_tokens || 0,
        completionTokens: usage.completion_tokens || 0,
        totalTokens: usage.total_tokens || 0
      } : undefined,
      toolCalls: toolCalls.length > 0 ? toolCalls
        .filter(tc => tc.id && tc.name)
        .map(tc => ({
          id: tc.id!,
          name: tc.name!,
          arguments: tc.arguments as Record<string, unknown>
        })) : undefined
    };
    } catch (error) {
      // Reset streaming counter on error to prevent permanent lockout
      AnthropicProvider.streamingCallCount = 0;
      console.error('‚ùå Anthropic streaming error:', error);
      throw error;
    }
  }

  /**
   * Execute tools locally when API calls fail (e.g., rate limits)
   */
  private async executeToolsLocally(
    toolCalls: Array<Record<string, unknown>>,
    content: string,
    usage: Record<string, unknown> | undefined
  ): Promise<Record<string, unknown>> {
    console.log(`üîß Executing ${toolCalls.length} tools locally due to API limitations`);

    const toolResults = [];
    for (const toolCall of toolCalls) {
      try {
        console.log(`üîß Executing local tool: ${toolCall.name} with args:`, toolCall.arguments);
        const result = await this.executeMCPTool!(String(toolCall.name), toolCall.arguments as Record<string, unknown>);
        toolResults.push({
          toolCallId: toolCall.id,
          toolName: toolCall.name,
          result: result
        });
        console.log(`‚úÖ Local tool execution successful for ${toolCall.name}`);
      } catch (error) {
        console.error(`‚ùå Local tool execution failed for ${toolCall.name}:`, error);
        toolResults.push({
          toolCallId: toolCall.id,
          toolName: toolCall.name,
          result: `Error: ${error instanceof Error ? error.message : String(error)}`
        });
      }
    }

    // Format results for display
    const resultsText = toolResults.map(tr =>
      `**${tr.toolName}**: ${tr.result.substring(0, 500)}${tr.result.length > 500 ? '...' : ''}`
    ).join('\n\n');

    return {
      content: `${content}\n\n**Tool Execution Results:**\n\n${resultsText}`,
      usage: usage ? {
        promptTokens: usage.prompt_tokens || 0,
        completionTokens: usage.completion_tokens || 0,
        totalTokens: usage.total_tokens || 0
      } : undefined,
      toolCalls: undefined // Clear tool calls since they've been executed
    };
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleNonStreamResponse(
    response: Response,
    _settings: LLMSettings,
    _conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    _conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    const data = await response.json();
    console.log('üîç Anthropic raw response:', JSON.stringify(data, null, 2));

    // Handle tool calls in Anthropic format
    let content = '';
    const toolUseBlocks = [];

    // First pass: collect text content and tool use blocks
    for (const contentBlock of data.content) {
      if (contentBlock.type === 'text') {
        content += contentBlock.text;
      } else if (contentBlock.type === 'tool_use') {
        toolUseBlocks.push(contentBlock);
      }
    }

    // Create toolCalls array for compatibility
    const toolCalls = toolUseBlocks.map(block => ({
      id: block.id,
      name: block.name,
      arguments: block.input || {}
    }));

    const { usage, cost } = this.createUsageAndCost(settings.model, data.usage);
    return {
      content,
      usage,
      cost,
      toolCalls: toolCalls.length > 0 ? toolCalls : undefined
    };
  }

  /**
   * Create usage and cost information from Anthropic API response
   */
  private createUsageAndCost(model: string, usage?: { input_tokens?: number; output_tokens?: number }) {
    if (!usage) return { usage: undefined, cost: undefined };

    const usageInfo = {
      promptTokens: usage.input_tokens || 0,
      completionTokens: usage.output_tokens || 0,
      totalTokens: (usage.input_tokens || 0) + (usage.output_tokens || 0)
    };

    const costInfo = PricingService.calculateCost('anthropic', model, usageInfo.promptTokens, usageInfo.completionTokens);

    return { usage: usageInfo, cost: costInfo };
  }
}

// DeepSeek provider implementation

import { BaseProvider } from './BaseProvider';
import {
  LLMSettings,
  LLMResponse,
  MessageContent,
  ContentItem,
  LLMProvider,
  ToolObject,
  ProviderCapabilities,
  APIResponseData
} from './types';

import { DEEPSEEK_SYSTEM_PROMPT } from './prompts/deepseek';
import { OpenAICompatibleStreaming } from './shared/OpenAICompatibleStreaming';

export class DeepSeekProvider extends BaseProvider {
  readonly id = 'deepseek';
  readonly name = 'DeepSeek';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: false,
    supportsTools: false, // DeepSeek has limited tool support
    supportsStreaming: true,
    supportsSystemMessages: true,
    maxToolNameLength: undefined,
    toolFormat: 'openai'
  };

  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    // DeepSeek uses OpenAI-compatible API
    const messages = [];

    const systemPrompt = settings.systemPrompt || this.getSystemPrompt();
    if (systemPrompt) {
      messages.push({ role: 'system', content: systemPrompt });
    }

    // Add conversation history
    messages.push(...conversationHistory);

    // Add current message
    if (typeof message === 'string') {
      messages.push({ role: 'user', content: message });
    } else if (Array.isArray(message)) {
      // Handle ContentItem array format (from chatService.ts)
      // DeepSeek uses OpenAI-compatible format, so we can pass through as-is
      messages.push({ role: 'user', content: message });
    } else {
      // Handle legacy vision format (convert to OpenAI format)
      const messageWithImages = message as { text: string; images: string[] };
      const content: ContentItem[] = [{ type: 'text', text: messageWithImages.text }];

      for (const imageUrl of messageWithImages.images) {
        content.push({
          type: 'image_url',
          image_url: { url: imageUrl }
        });
      }
      messages.push({ role: 'user', content });
    }

    // Get MCP tools for DeepSeek (limited support)
    const mcpTools = await this.getMCPToolsForProvider('deepseek', settings);

    const requestBody: Record<string, unknown> = {
      model: settings.model,
      messages: messages,
      temperature: settings.temperature,
      max_tokens: settings.maxTokens,
      stream: !!onStream
    };

    // Add tools if available (DeepSeek has limited tool support)
    if (mcpTools.length > 0) {
      requestBody.tools = mcpTools;
      requestBody.tool_choice = 'auto';
      console.log(`üöÄ DeepSeek API call with ${mcpTools.length} tools:`, {
        model: settings.model,
        toolCount: mcpTools.length,
        tools: mcpTools
      });
    } else {
      console.log(`üöÄ DeepSeek API call without tools (no MCP tools available)`);
    }

    const response = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`
      },
      body: JSON.stringify(requestBody),
      signal
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`DeepSeek API error: ${error}`);
    }

    if (onStream) {
      return this.handleStreamResponse(response, onStream, settings, provider, conversationHistory, signal);
    } else {
      return this.handleNonStreamResponse(response, settings, conversationHistory, conversationId);
    }
  }

  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.error('‚ùå No DeepSeek API key provided - cannot fetch models');
      throw new Error('DeepSeek API key is required to fetch available models. Please add your API key in settings.');
    }

    try {
      // DeepSeek models endpoint (OpenAI-compatible)
      const response = await fetch('https://api.deepseek.com/v1/models', {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå DeepSeek API error: ${response.status}`, errorText);
        throw new Error(`Failed to fetch DeepSeek models: ${response.status} - ${errorText}`);
      }

      const data = await response.json() as APIResponseData;
      const models = data.data?.map((model) => model.id)?.sort() || [];

      if (models.length === 0) {
        throw new Error('No DeepSeek models returned from API. This may indicate an API issue or insufficient permissions.');
      }

      return models;
    } catch (error) {
      console.error('‚ùå Failed to fetch DeepSeek models:', error);
      throw error instanceof Error ? error : new Error(`Failed to fetch DeepSeek models: ${String(error)}`);
    }
  }

  formatTools(tools: ToolObject[]): unknown[] {
    // DeepSeek uses OpenAI-compatible format (limited support)
    return tools.map(tool => ({
      type: 'function',
      function: {
        name: tool.name || tool.function?.name,
        description: tool.description || tool.function?.description,
        parameters: tool.parameters || tool.function?.parameters || {
          type: 'object',
          properties: {},
          required: []
        }
      }
    }));
  }

  getSystemPrompt(): string {
    return DEEPSEEK_SYSTEM_PROMPT;
  }

  enhanceSystemPromptWithTools(basePrompt: string, tools: ToolObject[]): string {
    if (tools.length === 0) {
      return basePrompt;
    }

    // DeepSeek uses structured tool calling with tools parameter and tool_choice
    // Don't add XML tool instructions as they conflict with native function calling
    console.log(`üîß DeepSeek using structured tools, skipping XML tool instructions`);
    return basePrompt;
  }

  validateToolCall(toolCall: { id?: string; name: string; arguments: Record<string, unknown> }): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!toolCall.id) {
      errors.push(`DeepSeek tool call missing required id: ${toolCall.name}`);
    }

    if (!toolCall.name || typeof toolCall.name !== 'string') {
      errors.push('Tool call must have a valid name');
    }

    if (!toolCall.arguments || typeof toolCall.arguments !== 'object') {
      errors.push('Tool call must have valid arguments object');
    }

    return { valid: errors.length === 0, errors };
  }

  validateTool(tool: unknown): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!tool || typeof tool !== 'object') {
      errors.push('Tool must be an object');
      return { valid: false, errors };
    }

    const toolObj = tool as Record<string, unknown>;

    if (!toolObj.type || toolObj.type !== 'function') {
      errors.push('DeepSeek tools must have type: "function"');
    }

    if (!toolObj.function || typeof toolObj.function !== 'object') {
      errors.push('DeepSeek tools must have function object');
    } else {
      const func = toolObj.function as Record<string, unknown>;
      if (!func.name) {
        errors.push('DeepSeek tools must have function.name');
      }
    }

    return { valid: errors.length === 0, errors };
  }

  // Private helper methods
  // This method is injected by the ProviderAdapter from the LLMService
  private getMCPToolsForProvider!: (providerId: string, settings: LLMSettings) => Promise<unknown[]>;

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleStreamResponse(
    response: Response,
    onStream: (chunk: string) => void,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    signal?: AbortSignal
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    return OpenAICompatibleStreaming.handleStreamResponse(
      response,
      onStream,
      settings,
      provider,
      conversationHistory,
      'DeepSeek',
      this.executeToolsAndFollowUp.bind(this)
    );
  }

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private async executeMCPTool(_toolName: string, _args: Record<string, unknown>): Promise<string> {
    // This will be injected by the main service
    return JSON.stringify({ error: 'Tool execution not available' });
  }

  private async executeToolsAndFollowUp(
    toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }>,
    initialContent: string,
    initialUsage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    return OpenAICompatibleStreaming.executeToolsAndFollowUp(
      toolCalls,
      initialContent,
      initialUsage,
      settings,
      provider,
      conversationHistory,
      onStream,
      'DeepSeek',
      this.executeMCPTool.bind(this),
      {},
      () => this.getMCPToolsForProvider(provider.id, settings)
      // No enhanced system prompt function - tools sent separately in tools parameter
    );
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleNonStreamResponse(
    response: Response,
    settings: LLMSettings,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    const data = await response.json();
    console.log('üîç DeepSeek non-streaming response:', {
      hasUsage: !!data.usage,
      usage: data.usage,
      responseKeys: Object.keys(data)
    });

    const choice = data.choices[0];
    const message = choice.message;

    // Handle tool calls (same as OpenAI format) - execute immediately like Anthropic
    if (message.tool_calls && message.tool_calls.length > 0) {
      console.log(`üîß DeepSeek response contains ${message.tool_calls.length} tool calls:`, message.tool_calls);

      // Check if we have the parallel execution method injected
      if ((this as unknown as {executeMultipleToolsParallel?: unknown, summarizeToolResultsForModel?: unknown}).executeMultipleToolsParallel && (this as unknown as {executeMultipleToolsParallel?: unknown, summarizeToolResultsForModel?: unknown}).summarizeToolResultsForModel) {
        console.log(`üöÄ Executing ${message.tool_calls.length} DeepSeek tools immediately`);

        // Format tool calls for execution
        const toolCallsForExecution = message.tool_calls.map((toolCall: { id: string; function: { name: string; arguments: string } }) => ({
          id: toolCall.id,
          name: toolCall.function.name,
          arguments: JSON.parse(toolCall.function.arguments)
        }));

        // Execute tools in parallel immediately
        const executeMultipleToolsParallel = (this as unknown as {executeMultipleToolsParallel: unknown}).executeMultipleToolsParallel;
        const summarizeToolResultsForModel = (this as unknown as {summarizeToolResultsForModel: unknown}).summarizeToolResultsForModel;
        
        try {
          const parallelResults = await (executeMultipleToolsParallel as (calls: unknown[], provider: string) => Promise<Array<{success: boolean}>>)(toolCallsForExecution, 'deepseek');
          console.log(`‚úÖ DeepSeek tool execution completed: ${parallelResults.filter(r => r.success).length}/${parallelResults.length} successful`);

          // Get tool results summary for the model
          const toolSummary = (summarizeToolResultsForModel as (results: unknown[]) => string)(parallelResults);
          
          // Return response with tool results included
          return {
            content: (message.content || '') + '\n\n' + toolSummary,
            usage: data.usage ? {
              promptTokens: data.usage.prompt_tokens,
              completionTokens: data.usage.completion_tokens,
              totalTokens: data.usage.total_tokens
            } : undefined
          };
        } catch (error) {
          console.error(`‚ùå DeepSeek tool execution failed:`, error);
          // Fall back to returning tool calls for external handling
          return {
            content: message.content || '',
            usage: data.usage ? {
              promptTokens: data.usage.prompt_tokens,
              completionTokens: data.usage.completion_tokens,
              totalTokens: data.usage.total_tokens
            } : undefined,
            toolCalls: toolCallsForExecution
          };
        }
      } else {
        console.warn(`‚ö†Ô∏è DeepSeek provider missing tool execution methods - falling back to external handling`);
        // Fall back to external handling if methods not injected
        return {
          content: message.content || '',
          usage: data.usage ? {
            promptTokens: data.usage.prompt_tokens,
            completionTokens: data.usage.completion_tokens,
            totalTokens: data.usage.total_tokens
          } : undefined,
          toolCalls: message.tool_calls.map((tc: { id: string; function: { name: string; arguments: string } }) => ({
            id: tc.id,
            name: tc.function.name,
            arguments: JSON.parse(tc.function.arguments)
          }))
        };
      }
    }

    return {
      content: message.content,
      usage: data.usage ? {
        promptTokens: data.usage.prompt_tokens,
        completionTokens: data.usage.completion_tokens,
        totalTokens: data.usage.total_tokens
      } : undefined
    };
  }
}

// Deepinfra provider implementation
// Uses OpenAI-compatible API at https://api.deepinfra.com/v1/openai

import { BaseProvider } from './BaseProvider';
import {
  LLMSettings,
  LLMResponse,
  MessageContent,
  ContentItem,
  LLMProvider,
  ToolObject,
  ProviderCapabilities
} from './types';
// No fallback models - providers now properly throw errors instead of masking failures
import { DEEPINFRA_SYSTEM_PROMPT } from './prompts/deepinfra';
import { OpenAICompatibleStreaming } from './shared/OpenAICompatibleStreaming';

export class DeepinfraProvider extends BaseProvider {
  readonly id = 'deepinfra';
  readonly name = 'Deepinfra';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: true, // Many models support multimodal
    supportsTools: true, // Function calling supported for Llama, Mixtral, Mistral models
    supportsStreaming: true,
    supportsSystemMessages: true,
    maxToolNameLength: 64,
    toolFormat: 'openai'
  };

  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    const messages = [];

    // Get MCP tools for this provider
    const mcpTools = await this.getMCPToolsForProvider('deepinfra', settings);

    // Use behavioral system prompt
    const hasCustomSystemPrompt = settings.systemPrompt &&
      settings.systemPrompt.trim() &&
      settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

    const systemPrompt = hasCustomSystemPrompt ? settings.systemPrompt! : this.getSystemPrompt();

    console.log(`üîç Deepinfra system prompt source:`, {
      hasCustom: hasCustomSystemPrompt,
      usingCustom: hasCustomSystemPrompt,
      promptLength: systemPrompt?.length || 0,
      promptStart: systemPrompt?.substring(0, 100) + '...'
    });

    if (systemPrompt) {
      messages.push({ role: 'system', content: systemPrompt });
    }

    // Add conversation history
    messages.push(...conversationHistory);

    // Add current message (handle both string and array formats)
    if (typeof message === 'string') {
      messages.push({ role: 'user', content: message });
    } else if (Array.isArray(message)) {
      // Handle ContentItem array format (images, text)
      messages.push({ role: 'user', content: message });
    } else {
      // Handle legacy vision format (convert to OpenAI format)
      const messageWithImages = message as { text: string; images: string[] };
      const content: ContentItem[] = [{ type: 'text', text: messageWithImages.text }];

      for (const imageUrl of messageWithImages.images) {
        content.push({
          type: 'image_url',
          image_url: { url: imageUrl }
        });
      }
      messages.push({ role: 'user', content });
    }

    const requestBody: Record<string, unknown> = {
      model: settings.model,
      messages,
      temperature: settings.temperature,
      max_tokens: settings.maxTokens,
      stream: !!onStream
    };

    // Add tools if available
    if (mcpTools.length > 0) {
      requestBody.tools = mcpTools;
      requestBody.tool_choice = 'auto';
      console.log(`üöÄ Deepinfra API call with ${mcpTools.length} tools:`, {
        model: settings.model,
        toolCount: mcpTools.length,
        conversationId: conversationId || 'none'
      });
    } else {
      console.log(`üöÄ Deepinfra API call without tools`);
    }

    console.log('üîç Deepinfra request body:', JSON.stringify(requestBody, null, 2));

    const response = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`
      },
      body: JSON.stringify(requestBody),
      signal
    });

    console.log('üîç Deepinfra response status:', response.status, response.statusText);

    if (!response.ok) {
      const error = await response.text();
      console.error('‚ùå Deepinfra API error response:', error);

      if (response.status === 401) {
        throw new Error(`Deepinfra API authentication failed. Please check your API key in Settings. Error: ${error}`);
      }

      throw new Error(`Deepinfra API error: ${error}`);
    }

    if (onStream) {
      return OpenAICompatibleStreaming.handleStreamResponse(
        response,
        onStream,
        settings,
        provider,
        conversationHistory,
        'Deepinfra',
        this.executeToolsAndFollowUp.bind(this)
      );
    } else {
      return this.handleNonStreamResponse(response, settings, conversationHistory);
    }
  }

  private async handleNonStreamResponse(
    response: Response,
    settings: LLMSettings,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>
  ): Promise<LLMResponse> {
    const data = await response.json();
    console.log('üîç Deepinfra non-stream response:', JSON.stringify(data, null, 2));

    const choice = data.choices?.[0];
    if (!choice) {
      throw new Error('No choices in Deepinfra response');
    }

    const message = choice.message;
    const content = message?.content || '';
    const toolCalls = message?.tool_calls || [];

    // Handle tool calls if present
    if (toolCalls.length > 0) {
      console.log(`üîß Deepinfra response contains ${toolCalls.length} tool calls`);
      return this.executeToolsAndFollowUp(
        toolCalls,
        content,
        data.usage,
        settings,
        { baseUrl: 'https://api.deepinfra.com/v1/openai' } as LLMProvider,
        conversationHistory,
        () => {} // No-op function for non-streaming tool execution
      );
    }

    return {
      content,
      usage: {
        promptTokens: data.usage?.prompt_tokens || 0,
        completionTokens: data.usage?.completion_tokens || 0,
        totalTokens: data.usage?.total_tokens || 0
      }
    };
  }

  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.log('‚ùå No Deepinfra API key provided - cannot fetch models');
      throw new Error('Deepinfra API key is required to fetch models');
    }

    try {
      const response = await fetch('https://api.deepinfra.com/v1/openai/models', {
        headers: {
          'Authorization': `Bearer ${apiKey}`
        }
      });

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå Deepinfra API error: ${response.status}`, errorText);
        throw new Error(`Failed to fetch Deepinfra models: ${response.status} - ${errorText}`);
      }

      const data = await response.json();
      const models = data.data?.map((model: { id: string }) => model.id)?.sort() || [];

      if (models.length === 0) {
        throw new Error('No Deepinfra models returned from API. This may indicate an API issue or insufficient permissions.');
      }

      console.log(`‚úÖ Fetched ${models.length} Deepinfra models (sorted alphabetically)`);
      return models;
    } catch (error) {
      console.error('‚ùå Failed to fetch Deepinfra models:', error);
      throw error instanceof Error ? error : new Error(`Failed to fetch Deepinfra models: ${String(error)}`);
    }
  }

  formatTools(tools: ToolObject[]): unknown[] {
    // OpenAI format - array of tool objects with type: 'function'
    return tools.map(tool => ({
      type: 'function',
      function: {
        name: tool.name || tool.function?.name,
        description: tool.description || tool.function?.description,
        parameters: tool.parameters || tool.function?.parameters || {
          type: 'object',
          properties: {},
          required: []
        }
      }
    }));
  }

  getSystemPrompt(): string {
    return DEEPINFRA_SYSTEM_PROMPT;
  }

  enhanceSystemPromptWithTools(basePrompt: string, tools: ToolObject[]): string {
    if (tools.length === 0) {
      return basePrompt;
    }

    const toolDescriptions = tools.map(tool => {
      const name = tool.name || tool.function?.name || 'unknown';
      const description = tool.description || tool.function?.description || 'No description';
      return `- ${name}: ${description}`;
    }).join('\n');

    return `${basePrompt}

## Available Tools

You have access to the following tools:
${toolDescriptions}

Use these tools when they can help provide better, more accurate, or more current information to answer the user's questions.`;
  }

  // Private helper methods
  // These methods are injected by the ProviderAdapter from the LLMService
  private getMCPToolsForProvider!: (providerId: string, settings: LLMSettings) => Promise<unknown[]>;

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private async executeMCPTool(_toolName: string, _args: Record<string, unknown>): Promise<string> {
    // This will be injected by the main service
    return JSON.stringify({ error: 'Tool execution not available' });
  }

  private async executeToolsAndFollowUp(
    toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }>,
    initialContent: string,
    initialUsage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    return OpenAICompatibleStreaming.executeToolsAndFollowUp(
      toolCalls,
      initialContent,
      initialUsage,
      settings,
      provider,
      conversationHistory,
      onStream,
      'Deepinfra',
      this.executeMCPTool.bind(this),
      {}, // No additional headers needed
      () => this.getMCPToolsForProvider('deepinfra', settings),
      (tools: unknown[]) => this.enhanceSystemPromptWithTools(this.getSystemPrompt(), tools as ToolObject[])
    );
  }
}

// Google Gemini provider implementation

import { BaseProvider } from './BaseProvider';
import {
  LLMSettings,
  LLMResponse,
  MessageContent,
  ContentItem,
  LLMProvider,
  ToolObject,
  ProviderCapabilities
} from './types';

import { GEMINI_SYSTEM_PROMPT } from './prompts/gemini';
import { PricingService } from '../pricingService';

export class GeminiProvider extends BaseProvider {
  readonly id = 'gemini';
  readonly name = 'Google Gemini';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: true,
    supportsTools: true,
    supportsStreaming: true,
    supportsSystemMessages: true,
    supportsPromptCaching: true,
    promptCachingType: 'both', // Supports both implicit (automatic) and explicit (manual) caching
    maxToolNameLength: undefined,
    toolFormat: 'gemini'
  };

  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    console.log('üß† Gemini: Integrating memory context');

    // Use behavioral system prompt only (no tool descriptions)
    // System instruction will be set in request body, not as user/model messages
    // Check for meaningful system prompt, not just empty string or generic default
    const hasCustomSystemPrompt = settings.systemPrompt &&
      settings.systemPrompt.trim() &&
      settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

    const systemPrompt = hasCustomSystemPrompt ? settings.systemPrompt! : this.getSystemPrompt();
    const cachingEnabled = settings.promptCachingEnabled ?? true;

    console.log(`üîç Gemini system prompt source:`, {
      hasCustom: hasCustomSystemPrompt,
      usingCustom: hasCustomSystemPrompt,
      promptLength: systemPrompt?.length || 0,
      promptStart: systemPrompt?.substring(0, 100) + '...',
      cachingEnabled,
      implicitCachingEligible: systemPrompt && systemPrompt.length > 8192 ? 'yes' : 'no' // 2048 tokens for Pro
    });

    const contents = [];

    // NO system prompt in contents - it goes in systemInstruction parameter

    // Add conversation history
    for (const msg of conversationHistory) {
      contents.push({
        role: msg.role === 'assistant' ? 'model' : 'user',
        parts: [{ text: typeof msg.content === 'string' ? msg.content : JSON.stringify(msg.content) }]
      });
    }

    // Add current message
    if (typeof message === 'string') {
      // For large string messages, log caching eligibility
      if (cachingEnabled && message.length > 8192) { // ~2048 tokens for Gemini Pro
        console.log(`üîß Gemini: User message eligible for implicit caching (${message.length} chars, ‚â•2048 tokens)`);
      }
      contents.push({
        role: 'user',
        parts: [{ text: message }]
      });
    } else if (Array.isArray(message)) {
      // Handle ContentItem array format (from chatService.ts)
      const parts: Array<{ text: string; cache_control?: { type: 'ephemeral' } } | { inline_data: { mime_type: string; data: string } }> = [];

      for (const item of message as ContentItem[]) {
        if (item.type === 'text') {
          // Add explicit cache_control for large text content if caching enabled
          const textPart: { text: string; cache_control?: { type: 'ephemeral' } } = { text: item.text || '' };
          if (cachingEnabled && item.cache_control) {
            textPart.cache_control = item.cache_control;
            console.log(`üîß Gemini: Added explicit cache_control to text content (${item.text?.length || 0} chars)`);
          } else if (cachingEnabled && item.text && item.text.length > 8192) {
            console.log(`üîß Gemini: Large text content eligible for implicit caching (${item.text.length} chars, ‚â•2048 tokens)`);
          }
          parts.push(textPart);
        } else if (item.type === 'image_url') {
          // Convert OpenAI format to Gemini format
          const imageUrl = item.image_url?.url || '';

          // Determine mime type from data URL
          const mimeType = imageUrl.includes('data:image/png') ? 'image/png' :
                          imageUrl.includes('data:image/gif') ? 'image/gif' :
                          imageUrl.includes('data:image/webp') ? 'image/webp' : 'image/jpeg';

          parts.push({
            inline_data: {
              mime_type: mimeType,
              data: imageUrl.split(',')[1] // Remove data:image/jpeg;base64, prefix
            }
          });
        } else if (item.type === 'document') {
          // For documents, convert to text or handle as needed
          // Gemini doesn't have native document support like Anthropic
          parts.push({
            text: `[Document: ${item.document?.name || 'document'}]`
          });
        }
      }

      contents.push({ role: 'user', parts });
    } else {
      // Handle vision format
      const messageWithImages = message as { text: string; images: string[] };
      const parts: Array<{ text: string } | { inline_data: { mime_type: string; data: string } }> = [{ text: messageWithImages.text }];

      // Add images in Gemini format
      for (const imageUrl of messageWithImages.images) {
        // Determine mime type from data URL
        const mimeType = imageUrl.includes('data:image/png') ? 'image/png' :
                        imageUrl.includes('data:image/gif') ? 'image/gif' :
                        imageUrl.includes('data:image/webp') ? 'image/webp' : 'image/jpeg';

        parts.push({
          inline_data: {
            mime_type: mimeType,
            data: imageUrl.split(',')[1] // Remove data:image/jpeg;base64, prefix
          }
        } as { inline_data: { mime_type: string; data: string } });
      }
      contents.push({ role: 'user', parts });
    }

    // Get MCP tools for Gemini
    console.log(`üîß GeminiProvider: Calling getMCPToolsForProvider for gemini`);
    console.log(`üîß GeminiProvider: getMCPToolsForProvider method exists:`, typeof this.getMCPToolsForProvider === 'function');

    const rawMcpTools = await this.getMCPToolsForProvider('gemini', settings);
    console.log(`üîß GeminiProvider: Received ${rawMcpTools.length} raw tools from getMCPToolsForProvider`);
    console.log(`üîß GeminiProvider: Raw tools:`, rawMcpTools.map((t: any) => ({
      name: t.function?.name || t.name,
      type: t.type,
      serverId: t.serverId,
      isInternal: t.serverId === 'internal-commands'
    })));

    // Format tools for Gemini (clean schemas)
    const formattedTools = rawMcpTools.length > 0 ? this.formatTools(rawMcpTools as ToolObject[]) : [];

    const endpoint = onStream ? 'streamGenerateContent?alt=sse' : 'generateContent';
    const url = `${provider.baseUrl}/models/${settings.model}:${endpoint}${onStream ? '' : '?key=' + settings.apiKey}`;

    const requestBody: Record<string, unknown> = {
      contents,
      generationConfig: {
        temperature: settings.temperature,
        maxOutputTokens: settings.maxTokens
      }
    };

    // Add system instruction with potential caching
    if (systemPrompt) {
      if (cachingEnabled && systemPrompt.length > 8192) {
        // For large system prompts, add explicit cache_control
        requestBody.systemInstruction = {
          parts: [
            {
              text: systemPrompt,
              cache_control: { type: 'ephemeral' }
            }
          ]
        };
        console.log(`üîß Gemini: Added explicit cache_control to system instruction (${systemPrompt.length} chars)`);
      } else {
        // Regular system instruction (may still benefit from implicit caching)
        requestBody.systemInstruction = {
          parts: [{ text: systemPrompt }]
        };
        if (cachingEnabled && systemPrompt.length > 8192) {
          console.log(`üîß Gemini: System instruction eligible for implicit caching (${systemPrompt.length} chars, ‚â•2048 tokens)`);
        }
      }
    }

    // Add tools if available (Gemini uses its own format)
    if (formattedTools.length > 0) {
      requestBody.tools = formattedTools;
      console.log(`üöÄ Gemini API call with ${rawMcpTools.length} raw tools formatted to ${formattedTools.length} Gemini tools:`, {
        model: settings.model,
        rawToolCount: rawMcpTools.length,
        formattedToolCount: formattedTools.length,
        tools: formattedTools
      });
    } else {
      console.log(`üöÄ Gemini API call without tools (no MCP tools available)`);
    }

    // Set system instruction (behavioral prompt only - no tool descriptions)
    if (systemPrompt) {
      requestBody.system_instruction = {
        parts: [{ text: systemPrompt }]
      };
      console.log(`üîß Gemini system instruction set:`, {
        length: systemPrompt.length,
        preview: systemPrompt.substring(0, 100) + '...'
      });
    }

    console.log('üîç Gemini request body:', JSON.stringify(requestBody, null, 2));

    const headers: Record<string, string> = {
      'Content-Type': 'application/json'
    };

    if (onStream) {
      headers['x-goog-api-key'] = settings.apiKey;
    }

    const response = await fetch(url, {
      method: 'POST',
      headers,
      body: JSON.stringify(requestBody),
      signal
    });

    console.log('üîç Gemini response status:', response.status, response.statusText);

    if (!response.ok) {
      const error = await response.text();
      console.error('‚ùå Gemini API error response:', error);
      throw new Error(`Gemini API error: ${error}`);
    }

    if (onStream) {
      return this.handleStreamResponse(response, onStream, settings, provider, conversationHistory, signal);
    } else {
      return this.handleNonStreamResponse(response, settings, conversationHistory, conversationId);
    }
  }

  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.error('‚ùå No Gemini API key provided - cannot fetch models');
      throw new Error('Gemini API key is required to fetch available models. Please add your API key in settings.');
    }

    try {
      // Google Gemini models endpoint
      const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models?key=${apiKey}`);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå Gemini API error: ${response.status}`, errorText);
        throw new Error(`Failed to fetch Gemini models: ${response.status} - ${errorText}`);
      }

      const data = await response.json() as { models?: Array<{ name: string; supportedGenerationMethods?: string[] }> };
      const models = data.models
        ?.filter((model) => model.name.includes('gemini') && model.supportedGenerationMethods?.includes('generateContent'))
        ?.map((model) => model.name.replace('models/', ''))
        ?.sort() || [];

      if (models.length === 0) {
        throw new Error('No Gemini models returned from API. This may indicate an API issue or insufficient permissions.');
      }

      return models;
    } catch (error) {
      console.error('‚ùå Failed to fetch Gemini models:', error);
      throw error instanceof Error ? error : new Error(`Failed to fetch Gemini models: ${String(error)}`);
    }
  }

  formatTools(tools: ToolObject[]): unknown[] {
    console.log(`üîß Gemini formatTools received ${tools.length} tools:`, tools.map(t => ({
      keys: Object.keys(t),
      name: t.name,
      functionName: t.function?.name,
      type: (t as {type?: string}).type,
      structure: JSON.stringify(t, null, 2).substring(0, 200) + '...'
    })));

    // Gemini format - single array of function declarations with cleaned schemas
    const formattedTools = [{
      functionDeclarations: tools.map(tool => {
        // Try multiple ways to get the tool name
        const originalName = tool.name || tool.function?.name || (tool as {function_name?: string}).function_name || (tool as {toolName?: string}).toolName || 'unknown_tool';
        const sanitizedName = this.sanitizeToolNameForGemini(originalName);

        // Try multiple ways to get the description
        const description = tool.description || tool.function?.description || (tool as {function_description?: string}).function_description || `Tool: ${sanitizedName}`;

        // Try multiple ways to get the parameters
        const parameters = tool.parameters || tool.function?.parameters || (tool as {input_schema?: unknown}).input_schema || (tool as {parameters?: unknown}).parameters;
        const cleanedParameters = this.cleanSchemaForGemini(parameters);

        console.log(`üîß Gemini tool formatting - ${originalName} -> ${sanitizedName}:`, {
          originalTool: tool,
          cleanedParameters: JSON.stringify(cleanedParameters, null, 2)
        });

        return {
          name: sanitizedName,
          description: description,
          parameters: cleanedParameters
        };
      })
    }];

    console.log(`üîß Gemini formatted tools:`, JSON.stringify(formattedTools, null, 2));
    return formattedTools;
  }

  private sanitizeToolNameForGemini(name: string): string {
    if (!name) return 'unknown_tool';

    // Gemini requirements: Must start with letter or underscore, alphanumeric + _ . -, max 64 chars
    let sanitized = name;

    // Replace invalid characters with underscores
    sanitized = sanitized.replace(/[^a-zA-Z0-9_.-]/g, '_');

    // Ensure it starts with letter or underscore
    if (!/^[a-zA-Z_]/.test(sanitized)) {
      sanitized = '_' + sanitized;
    }

    // Truncate to 64 characters
    if (sanitized.length > 64) {
      sanitized = sanitized.substring(0, 64);
    }

    // Ensure it's not empty
    if (!sanitized) {
      sanitized = 'tool';
    }

    console.log(`üîß Gemini tool name sanitization: "${name}" -> "${sanitized}"`);
    return sanitized;
  }

  private safeParseJSON(jsonString: string): unknown {
    try {
      return JSON.parse(jsonString);
    } catch {
      console.warn(`üö´ Gemini: Failed to parse JSON "${jsonString}", returning as string`);
      return { result: jsonString };
    }
  }

