  private async handleTextBasedToolCallingFromContent(
    fullContent: string,
    usage: { promptTokens?: number; completionTokens?: number; totalTokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    console.log(`üîç LM Studio parsing complete response for tool calls`);

    // Handle empty responses
    if (!fullContent || fullContent.trim().length === 0) {
      console.warn(`‚ö†Ô∏è LM Studio returned empty response. This might indicate:`);
      console.warn(`   - Model failed to generate content`);
      console.warn(`   - Network/connection issues`);
      console.warn(`   - Model overloaded or timeout`);
      console.warn(`   - Model not properly loaded in LM Studio`);

      return {
        content: "I apologize, but I didn't receive a proper response from the LM Studio model. This could be due to the model being overloaded, not properly loaded, or a connection issue. Please check that the model is running in LM Studio and try again.",
        usage: usage ? {
          promptTokens: usage.promptTokens || 0,
          completionTokens: usage.completionTokens || 0,
          totalTokens: usage.totalTokens || 0
        } : undefined
      };
    }

    // Remove thinking content before parsing for tool calls
    console.log(`üîç RAW CONTENT BEFORE CLEANING:`, fullContent);

    // Check if content appears to contain incomplete tool calls
    const hasIncompleteToolCall = fullContent.includes('to=') && !fullContent.match(/to=[a-zA-Z_][a-zA-Z0-9_-]+\s*json\{.*\}/);
    if (hasIncompleteToolCall) {
      console.log(`‚ö†Ô∏è Detected incomplete tool call in content - this suggests parsing during streaming`);
      console.log(`üîç Content snippet:`, fullContent.substring(fullContent.lastIndexOf('to='), fullContent.lastIndexOf('to=') + 50));
    }

    const contentWithoutThinking = this.removeThinkingContent(fullContent);
    console.log(`üß† Content after removing thinking tags:`, contentWithoutThinking);

    // Parse the response for tool calls (excluding thinking content)
    const toolCalls = this.parseToolCallsFromText(contentWithoutThinking);

    console.log(`üîç Text parsing result: found ${toolCalls.length} tool calls:`, toolCalls);

    if (toolCalls.length > 0) {
      console.log(`üîß LM Studio found ${toolCalls.length} tool calls in complete response`);
      return this.executeTextBasedTools(toolCalls, fullContent, usage, settings, provider, conversationHistory, onStream);
    }

    // Return the original content (with thinking) for UI display
    // The UI component will handle parsing and displaying thinking content
    const result = {
      content: fullContent, // Keep original content with thinking for UI
      usage: usage ? {
        promptTokens: usage.promptTokens || 0,
        completionTokens: usage.completionTokens || 0,
        totalTokens: usage.totalTokens || 0
      } : undefined
    };

    console.log(`üîç LM Studio returning result without tool calls:`, {
      contentLength: result.content.length,
      hasUsage: !!result.usage,
      hasToolCalls: false
    });

    return result;
  }

  private async executeNativeToolCalls(
    toolCalls: Array<{ id: string; type: string; function: { name: string; arguments: string } }>,
    initialContent: string,
    usage: { promptTokens?: number; completionTokens?: number; totalTokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    console.log(`üîß LM Studio executing ${toolCalls.length} native tool calls`);

    // Execute all tool calls
    const toolResults: Array<{ id: string; name: string; result: string; error?: boolean }> = [];

    for (const toolCall of toolCalls) {
      try {
        // Safely parse arguments with fallback
        let args: Record<string, unknown> = {};
        if (toolCall.function.arguments) {
          try {
            args = JSON.parse(toolCall.function.arguments);
          } catch (parseError) {
            console.warn(`‚ö†Ô∏è Failed to parse tool arguments: ${toolCall.function.arguments}`, parseError);
            // Try to extract arguments using fallback parsing
            args = this.parseArgumentsFromText(toolCall.function.arguments);
          }
        }

        console.log(`üîß Executing LM Studio native tool: ${toolCall.function.name} with args:`, args);

        const result = await this.executeMCPTool(toolCall.function.name, args);
        toolResults.push({
          id: toolCall.id,
          name: toolCall.function.name,
          result: typeof result === 'string' ? result : JSON.stringify(result),
          error: false
        });
        console.log(`‚úÖ LM Studio native tool ${toolCall.function.name} executed successfully`);
      } catch (error) {
        console.error(`‚ùå LM Studio native tool ${toolCall.function.name} failed:`, error);
        const userFriendlyError = this.formatToolError(toolCall.function.name, error);
        toolResults.push({
          id: toolCall.id,
          name: toolCall.function.name,
          result: userFriendlyError,
          error: true
        });
      }
    }

    // Build proper conversation history following official LM Studio format
    const properConversationHistory = this.buildProperConversationHistory(
      conversationHistory,
      toolResults,
      toolCalls
    );

    console.log(`üîÑ LM Studio making native follow-up call with proper conversation format`);

    // Make a follow-up call without tools (following official LM Studio pattern)
    const followUpResponse = await this.makeDirectFollowUpCall(
      properConversationHistory as Array<{role: string, content: string | Array<ContentItem>}>,
      settings,
      onStream
    );

    return {
      content: followUpResponse.content,
      usage: followUpResponse.usage,
      toolCalls: toolCalls.map(tc => {
        // Safely parse arguments for the return value
        let parsedArgs: Record<string, unknown> = {};
        if (tc.function.arguments) {
          try {
            parsedArgs = JSON.parse(tc.function.arguments);
          } catch (parseError) {
            console.warn(`‚ö†Ô∏è Failed to parse tool arguments for return: ${tc.function.arguments}`, parseError);
            parsedArgs = this.parseArgumentsFromText(tc.function.arguments);
          }
        }

        // Attach execution results for UI parity with text-based tools
        const matched = toolResults.find(tr => tr.id === tc.id);

        return {
          id: tc.id,
          name: tc.function.name,
          arguments: parsedArgs,
          result: matched ? matched.result : undefined,
          error: matched ? matched.error : undefined
        };
      })
    };
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleNonStreamResponse(
    response: Response,
    settings: LLMSettings,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    const data = await response.json();
    console.log(`üîç LMStudio raw response:`, JSON.stringify(data, null, 2));
    const message = data.choices[0].message;
    console.log(`üîç LMStudio message:`, message);

    // Handle tool calls if present - execute immediately like Anthropic
    if (message.tool_calls && message.tool_calls.length > 0) {
      console.log(`üîß LMStudio response contains ${message.tool_calls.length} tool calls:`, message.tool_calls);

      // Check if we have the parallel execution method injected
      if ((this as unknown as {executeMultipleToolsParallel?: unknown, summarizeToolResultsForModel?: unknown}).executeMultipleToolsParallel && (this as unknown as {executeMultipleToolsParallel?: unknown, summarizeToolResultsForModel?: unknown}).summarizeToolResultsForModel) {
        console.log(`üöÄ Executing ${message.tool_calls.length} LMStudio tools immediately`);
        
        // Format tool calls for execution
        const toolCallsForExecution = message.tool_calls.map((toolCall: { id: string; function: { name: string; arguments: string } }) => {
          // Safely parse arguments
          let parsedArgs: Record<string, unknown> = {};
          if (toolCall.function.arguments) {
            try {
              parsedArgs = JSON.parse(toolCall.function.arguments);
            } catch (parseError) {
              console.warn(`‚ö†Ô∏è Failed to parse tool arguments in non-stream: ${toolCall.function.arguments}`, parseError);
              parsedArgs = this.parseArgumentsFromText(toolCall.function.arguments);
            }
          }

          return {
            id: toolCall.id,
            name: toolCall.function.name,
            arguments: parsedArgs
          };
        });

        // Execute tools in parallel immediately
        const executeMultipleToolsParallel = (this as unknown as {executeMultipleToolsParallel: unknown}).executeMultipleToolsParallel;
        const summarizeToolResultsForModel = (this as unknown as {summarizeToolResultsForModel: unknown}).summarizeToolResultsForModel;
        
        try {
          const parallelResults = await (executeMultipleToolsParallel as (calls: unknown[], provider: string) => Promise<Array<{success: boolean}>>)(toolCallsForExecution, 'lmstudio');
          console.log(`‚úÖ LMStudio tool execution completed: ${parallelResults.filter(r => r.success).length}/${parallelResults.length} successful`);

          // Get tool results summary for the model
          const toolSummary = (summarizeToolResultsForModel as (results: unknown[]) => string)(parallelResults);
          
          // Return response with tool results included
          return {
            content: (message.content || '') + '\n\n' + toolSummary,
            usage: data.usage ? {
              promptTokens: data.usage.prompt_tokens,
              completionTokens: data.usage.completion_tokens,
              totalTokens: data.usage.total_tokens
            } : undefined
          };
        } catch (error) {
          console.error(`‚ùå LMStudio tool execution failed:`, error);
          // Fall back to returning tool calls for external handling
          return {
            content: message.content || '',
            usage: data.usage ? {
              promptTokens: data.usage.prompt_tokens,
              completionTokens: data.usage.completion_tokens,
              totalTokens: data.usage.total_tokens
            } : undefined,
            toolCalls: toolCallsForExecution
          };
        }
      } else {
        console.warn(`‚ö†Ô∏è LMStudio provider missing tool execution methods - falling back to external handling`);
        // Fall back to external handling if methods not injected
        return {
          content: message.content || '',
          usage: data.usage ? {
            promptTokens: data.usage.prompt_tokens,
            completionTokens: data.usage.completion_tokens,
            totalTokens: data.usage.total_tokens
          } : undefined,
          toolCalls: message.tool_calls.map((tc: { id: string; function: { name: string; arguments: string } }) => {
            // Safely parse arguments
            let parsedArgs: Record<string, unknown> = {};
            if (tc.function.arguments) {
              try {
                parsedArgs = JSON.parse(tc.function.arguments);
              } catch (parseError) {
                console.warn(`‚ö†Ô∏è Failed to parse tool arguments in non-stream: ${tc.function.arguments}`, parseError);
                parsedArgs = this.parseArgumentsFromText(tc.function.arguments);
              }
            }

            return {
              id: tc.id,
              name: tc.function.name,
              arguments: parsedArgs
            };
          })
        };
      }
    }

    // Use text-based tool calling for non-stream responses too
    const content = message.content || '';
    const toolCalls = this.parseToolCallsFromText(content);

    if (toolCalls.length > 0) {
      console.log(`üîß LM Studio found ${toolCalls.length} tool calls in non-stream response`);
      // For non-stream, we can't use onStream, so pass a no-op function
      return this.executeTextBasedTools(toolCalls, content, data.usage ? {
        promptTokens: data.usage.prompt_tokens,
        completionTokens: data.usage.completion_tokens,
        totalTokens: data.usage.total_tokens
      } : undefined, settings, { id: 'lmstudio', name: 'LM Studio' } as LLMProvider, conversationHistory, () => {});
    }

    return {
      content: content,
      usage: data.usage ? {
        promptTokens: data.usage.prompt_tokens,
        completionTokens: data.usage.completion_tokens,
        totalTokens: data.usage.total_tokens
      } : undefined
    };
  }

  private parseToolCallsFromText(content: string): Array<{ name: string; arguments: Record<string, unknown> }> {
    console.log(`üö® PARSETEXT METHOD CALLED WITH CONTENT: "${content}"`);
    const toolCalls: Array<{ name: string; arguments: Record<string, unknown> }> = [];

    // Get available tool names from MCP tools
    const availableTools = this.getAvailableToolNames();

    console.log(`üîç LM Studio parsing text for tools. Available tools:`, availableTools);
    console.log(`üîç Content to parse:`, content);

    // DEBUG: Test if content matches our expected pattern
    const testRegex = /to=([a-zA-Z_][a-zA-Z0-9_-]*)\s*json/gi;
    const testMatch = testRegex.exec(content);
    console.log(`üîç DEBUG: Simple regex test result:`, testMatch);

    // STEP 1: Look for structured tool call formats first

    // Pattern 0: Fenced tool/function_call blocks (Harmony-style variations)
    // ```tool {"name":"web_search","arguments":{"query":"..."}} ```
    // ```function_call {"name":"...","arguments":{...}} ```
    // Also accept multiple JSON objects inside a single fenced block
    try {
      const fencedRegex = /```\s*(tool|tool_call|call|function_call)[^\n]*\n([\s\S]*?)```/gi;
      let fencedMatch: RegExpExecArray | null;
      while ((fencedMatch = fencedRegex.exec(content)) !== null) {
        const block = fencedMatch[2].trim();
        // Try to parse the block directly as JSON or detect multiple JSON objects
        // 1) Direct JSON object
        const tryParseSingle = (jsonStr: string) => {
          try {
            const obj = JSON.parse(jsonStr);
            if (obj.tool_call && obj.tool_call.name) {
              const tName = obj.tool_call.name;
              const tArgsRaw = obj.tool_call.arguments;
              const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : (tArgsRaw || {});
              if (availableTools.includes(tName)) {
                toolCalls.push({ name: tName, arguments: tArgs });
              }
              return true;
            } else if (obj.name && obj.arguments) {
              const tName = obj.name;
              const tArgsRaw = obj.arguments;
              const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : (tArgsRaw || {});
              if (availableTools.includes(tName)) {
                toolCalls.push({ name: tName, arguments: tArgs });
              }
              return true;
            } else if (Array.isArray(obj)) {
              // Array of tool calls
              for (const item of obj) {
                const tName = item?.tool_call?.name || item?.name;
                const tArgsRaw = item?.tool_call?.arguments ?? item?.arguments ?? {};
                const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : (tArgsRaw || {});
                if (tName && availableTools.includes(tName)) {
                  toolCalls.push({ name: tName, arguments: tArgs });
                }
              }
              return true;
            }
          } catch {
            // ignore
          }
          return false;
        };

        if (!tryParseSingle(block)) {
          // 2) Scan the fenced block for multiple JSON tool_call objects
          const toolCallPattern = /\{\s*"tool_call"\s*:\s*\{/gi;
          let dm: RegExpExecArray | null;
          while ((dm = toolCallPattern.exec(block)) !== null) {
            const start = dm.index;
            const extracted = JSONUtils.extractCompleteJSON(block, start);
            if (extracted) {
              try {
                const parsed = JSON.parse(extracted.jsonStr);
                if (parsed.tool_call?.name) {
                  const tName = parsed.tool_call.name;
                  const tArgsRaw = parsed.tool_call.arguments ?? {};
                  const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : tArgsRaw;
                  if (availableTools.includes(tName)) {
                    toolCalls.push({ name: tName, arguments: tArgs });
                  }
                }
              } catch {
                // ignore individual failures
              }
            }
          }
        }
      }
    } catch (e) {
      console.log('‚ö†Ô∏è Fenced tool block parsing failed:', e);
    }

    if (toolCalls.length > 0) {
      const unique = this.deduplicateToolCalls(toolCalls);
      console.log(`‚úÖ Found ${unique.length} fenced tool calls`);
      return unique;
    }

    // Pattern 1: New model format with optional commentary prefix and to=tool_name and JSON arguments
    // Example: "commentary to=web_search json{"query":"dad joke", "topn":5}" or "to=list_directoryjson{...}"
    // Updated to handle nested JSON, multiple tool calls, hyphens, function namespace prefixes, and optional space before json
    // Made more robust to handle underscores and longer tool names
    const newModelFormatRegex = /(?:commentary\s+)?to=(?:functions\.)?([a-zA-Z_][a-zA-Z0-9_-]+)\s*json(\{(?:[^{}]|{[^{}]*})*\})/gi;

    // Pattern 1b: Nested function call format - to=functions json{"name":"tool_name","arguments":{...}}
    // CHECK THIS FIRST before the general pattern to avoid conflicts
    const nestedFunctionFormatRegex = /(?:commentary\s+)?to=functions\s*json\{[^}]*"name"\s*:\s*"([^"]+)"[^}]*"arguments"\s*:\s*(\{[^}]*\})[^}]*\}/gi;

    // Handle nested function format FIRST
    console.log(`üîç Testing nested format regex against content: "${content}"`);
    console.log(`üîç Nested format regex: ${nestedFunctionFormatRegex}`);

    let nestedMatch;
    while ((nestedMatch = nestedFunctionFormatRegex.exec(content)) !== null) {
      try {
        const rawToolName = nestedMatch[1];
        const jsonArgs = nestedMatch[2];

        // Only parse tools that actually exist - no guessing or mapping
        if (availableTools.includes(rawToolName)) {
          try {
            const args = JSON.parse(jsonArgs);
            toolCalls.push({ name: rawToolName, arguments: args });
            console.log(`‚úÖ Found valid nested function tool call: ${rawToolName} with args:`, args);
          } catch (error) {
            console.log(`‚ö†Ô∏è Failed to parse JSON arguments for nested ${rawToolName}:`, jsonArgs, error);
          }
        } else {
          console.log(`‚ö†Ô∏è Nested function tool "${rawToolName}" not found. Available tools:`, availableTools.slice(0, 10), '...');
          // Return an error response that the LLM can see and correct
          return [{
            name: 'error_response',
            arguments: {
              error: `Tool "${rawToolName}" does not exist. Available tools include: ${availableTools.slice(0, 10).join(', ')}, and ${availableTools.length - 10} more. Please use an exact tool name from the available list.`
            }
          }];
        }
      } catch (error) {
        console.log(`‚ö†Ô∏è Failed to parse nested function format tool call:`, nestedMatch[0], error);
      }
    }

    // If we found nested function calls, deduplicate and return them
    if (toolCalls.length > 0) {
      const uniqueToolCalls = this.deduplicateToolCalls(toolCalls);
      console.log(`‚úÖ Found ${toolCalls.length} nested function format tool calls, deduplicated to ${uniqueToolCalls.length}`);
      return uniqueToolCalls;
    }

    let newModelMatch;
    console.log(`üîç Testing regular format regex against content: "${content}"`);
    console.log(`üîç Regular format regex: ${newModelFormatRegex}`);

    while ((newModelMatch = newModelFormatRegex.exec(content)) !== null) {
      try {
        console.log(`üîç Regular format match found:`, newModelMatch);
        const rawToolName = newModelMatch[1];
        const jsonArgs = newModelMatch[2];
        console.log(`üîç Extracted tool name: "${rawToolName}", args: "${jsonArgs}"`);
        console.log(`üîç Full regex match:`, newModelMatch);
        console.log(`üîç Available tools containing "${rawToolName}":`, availableTools.filter(t => t.includes(rawToolName)));

        // Only parse tools that actually exist - no guessing or mapping
        if (availableTools.includes(rawToolName)) {
          try {
            // Handle malformed empty JSON like {"":""}
            let cleanJsonArgs = jsonArgs;
            if (jsonArgs === '{"":""}' || jsonArgs === '{"": ""}') {
              cleanJsonArgs = '{}';
              console.log(`üîß Fixed malformed empty JSON: ${jsonArgs} -> ${cleanJsonArgs}`);
            }

            const args = JSON.parse(cleanJsonArgs);
            toolCalls.push({ name: rawToolName, arguments: args });
            console.log(`‚úÖ Found valid tool call: ${rawToolName} with args:`, args);
          } catch (error) {
            console.log(`‚ö†Ô∏è Failed to parse JSON arguments for ${rawToolName}:`, jsonArgs, error);
          }
        } else {
          console.log(`‚ö†Ô∏è Tool "${rawToolName}" not found. Available tools:`, availableTools.slice(0, 10), '...');
          // Return an error response that the LLM can see and correct
          return [{
            name: 'error_response',
            arguments: {
              error: `Tool "${rawToolName}" does not exist. Available tools include: ${availableTools.slice(0, 10).join(', ')}, and ${availableTools.length - 10} more. Please use an exact tool name from the available list.`
            }
          }];
        }
      } catch (error) {
        console.log(`‚ö†Ô∏è Failed to parse new model format tool call:`, newModelMatch[0], error);
      }
    }

    // If we found any new model format tool calls, deduplicate and return them
    if (toolCalls.length > 0) {
      const uniqueToolCalls = this.deduplicateToolCalls(toolCalls);
      console.log(`‚úÖ Found ${toolCalls.length} new model format tool calls, deduplicated to ${uniqueToolCalls.length}`);
      return uniqueToolCalls;
    }

    // Pattern 2: Enhanced tool_call format with ```json wrapper (Option 2)
    // ```json { "tool_call": { "name": "web_search", "arguments": {...} } } ```
    const jsonWrappedToolCallRegex = /```json\s*(\{[\s\S]*?"tool_call"[\s\S]*?\})\s*```/gi;
    const match = jsonWrappedToolCallRegex.exec(content);
    if (match) {
      try {
        const jsonObj = JSON.parse(match[1]);
        if (Array.isArray(jsonObj)) {
          for (const item of jsonObj) {
            const tName = item?.tool_call?.name || item?.name;
            if (!tName) continue;
            const tArgsRaw = item?.tool_call?.arguments ?? item?.arguments ?? {};
            const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : tArgsRaw;
            if (availableTools.includes(tName)) {
              toolCalls.push({ name: tName, arguments: tArgs });
            }
          }
          if (toolCalls.length > 0) {
            const unique = this.deduplicateToolCalls(toolCalls);
            console.log(`‚úÖ Found ${unique.length} JSON-wrapped array tool calls`);
            return unique;
          }
        } else if (jsonObj.tool_call && jsonObj.tool_call.name) {
          const tName = jsonObj.tool_call.name;
          const tArgsRaw = jsonObj.tool_call.arguments ?? {};
          const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : tArgsRaw;
          toolCalls.push({ name: tName, arguments: tArgs });
          console.log(`‚úÖ Found JSON-wrapped tool call: ${tName} with args:`, tArgs);
          return toolCalls; // Return early if we found the structured format
        }
      } catch {
        console.log(`‚ö†Ô∏è Failed to parse JSON-wrapped tool call:`, match[1]);
      }
    }

    // Pattern 3: Direct JSON tool_call format (Option 1)
    // { "tool_call": { "name": "web_search", "arguments": {...} } }
    // Robust iterative scan for direct tool_call objects, including multiple occurrences
    const toolCallPattern = /\{\s*"tool_call"\s*:\s*\{/gi;
    let directMatch: RegExpExecArray | null;
    while ((directMatch = toolCallPattern.exec(content)) !== null) {
      try {
        const startIndex = directMatch.index;
        const extracted = JSONUtils.extractCompleteJSON(content, startIndex);
        if (extracted) {
          const parsed = JSON.parse(extracted.jsonStr);
          if (parsed.tool_call?.name) {
            const tName = parsed.tool_call.name;
            const tArgsRaw = parsed.tool_call.arguments ?? {};
            const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : tArgsRaw;
            toolCalls.push({ name: tName, arguments: tArgs });
          }
        }
      } catch (e) {
        console.log(`‚ö†Ô∏è Failed to parse direct tool_call JSON:`, e);
      }
    }
    if (toolCalls.length > 0) {
      const unique = this.deduplicateToolCalls(toolCalls);
      console.log(`‚úÖ Found ${unique.length} direct tool_call objects`);
      return unique;
    }

    // Pattern 4: Look for any JSON blocks and check if they contain tool calls
    const jsonBlockRegex = /```json\s*([\s\S]*?)\s*```/gi;
    let jsonMatch;
    while ((jsonMatch = jsonBlockRegex.exec(content)) !== null) {
      try {
        const jsonText = jsonMatch[1].trim();
        const jsonObj = JSON.parse(jsonText);
        if (Array.isArray(jsonObj)) {
          for (const item of jsonObj) {
            const tName = item?.tool_call?.name || item?.name;
            if (!tName) continue;
            const tArgsRaw = item?.tool_call?.arguments ?? item?.arguments ?? {};
            const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : tArgsRaw;
            if (availableTools.includes(tName)) {
              toolCalls.push({ name: tName, arguments: tArgs });
            }
          }
        } else if (jsonObj.tool_call && jsonObj.tool_call.name) {
          const tName = jsonObj.tool_call.name;
          const tArgsRaw = jsonObj.tool_call.arguments ?? {};
          const tArgs = typeof tArgsRaw === 'string' ? (JSON.parse(tArgsRaw) as Record<string, unknown>) : tArgsRaw;
          toolCalls.push({
            name: tName,
            arguments: tArgs
          });
          console.log(`‚úÖ Found JSON block tool call: ${tName} with args:`, tArgs);
        }
      } catch {
        console.log(`‚ö†Ô∏è Failed to parse JSON block:`, jsonMatch[1]);
      }
    }

    // If we found structured tool calls, return them
    if (toolCalls.length > 0) {
      console.log(`‚úÖ Found ${toolCalls.length} structured tool calls, returning them`);
      return toolCalls;
    }

    // STEP 2: If no structured tool calls found, search for traces of tool usage in text
    console.log(`üîç No structured tool calls found, searching for tool usage traces in text...`);

    return this.parseToolTracesFromText(content, availableTools);
  }

  private parseToolTracesFromText(content: string, availableTools: string[]): Array<{ name: string; arguments: Record<string, unknown> }> {
    const toolCalls: Array<{ name: string; arguments: Record<string, unknown> }> = [];

    console.log(`üîç Searching for tool usage traces in content...`);

    for (const toolName of availableTools) {
      // Pattern 1: Direct tool mentions with intent to use
      // "I'll use web_search to find..." or "Let me search using web_search"
      const intentPatterns = [
        new RegExp(`I(?:'ll|\\s+will)\\s+use\\s+${toolName}\\s+(?:to\\s+|with\\s+|for\\s+)([^.!?]+)`, 'gi'),
        new RegExp(`Let\\s+me\\s+(?:use\\s+)?${toolName}\\s+(?:to\\s+|with\\s+|for\\s+)([^.!?]+)`, 'gi'),
        new RegExp(`Using\\s+${toolName}\\s+(?:to\\s+|with\\s+|for\\s+)([^.!?]+)`, 'gi'),
        new RegExp(`I\\s+(?:need\\s+to\\s+|should\\s+)?${toolName}\\s+(?:to\\s+|with\\s+|for\\s+)([^.!?]+)`, 'gi'),
        new RegExp(`I\\s+should\\s+use\\s+${toolName}\\s+(?:to\\s+|with\\s+|for\\s+)([^.!?]+)`, 'gi'),
        new RegExp(`(?:So,?\\s+)?I'll\\s+call\\s+${toolName}\\s+(?:with\\s+|to\\s+|for\\s+)([^.!?]+)`, 'gi'),
        new RegExp(`The\\s+best\\s+approach\\s+is\\s+to\\s+use\\s+${toolName}\\s+(?:with\\s+|to\\s+|for\\s+)([^.!?]+)`, 'gi')
      ];

      for (const pattern of intentPatterns) {
        const match = pattern.exec(content);
        if (match) {
          const intent = match[1].trim();
          const args = this.extractArgumentsFromIntent(toolName, intent);
          if (Object.keys(args).length > 0) {
            toolCalls.push({ name: toolName, arguments: args });
            console.log(`‚úÖ Found tool usage intent: ${toolName} with intent "${intent}" -> args:`, args);
            break; // Found this tool, move to next
          }
        }
      }

      // Pattern 2: Function call style mentions
      // "web_search('weather Paris')" or "web_search(query='weather Paris')"
      const functionCallPattern = new RegExp(`${toolName}\\s*\\(([^)]+)\\)`, 'gi');
      const funcMatch = functionCallPattern.exec(content);
      if (funcMatch && !toolCalls.find(tc => tc.name === toolName)) {
        const argsText = funcMatch[1].trim();
        const args = this.parseArgumentsFromText(argsText);
        if (Object.keys(args).length > 0) {
          toolCalls.push({ name: toolName, arguments: args });
          console.log(`‚úÖ Found function call: ${toolName}(${argsText}) -> args:`, args);
          continue;
        }
      }

      // Pattern 3: Tool name mentioned with quoted parameters
      // "web_search with query 'weather Paris'" or 'web_search "current weather"'
      const quotedParamsPattern = new RegExp(`${toolName}\\s+(?:with\\s+|using\\s+)?(?:query\\s+)?['"]([^'"]+)['"]`, 'gi');
      const quotedMatch = quotedParamsPattern.exec(content);
      if (quotedMatch && !toolCalls.find(tc => tc.name === toolName)) {
        const query = quotedMatch[1].trim();
        const args = this.createArgumentsForTool(toolName, query);
        if (Object.keys(args).length > 0) {
          toolCalls.push({ name: toolName, arguments: args });
          console.log(`‚úÖ Found quoted params: ${toolName} with "${query}" -> args:`, args);
          continue;
        }
      }
    }

    console.log(`üîç Tool trace parsing found ${toolCalls.length} tool calls:`, toolCalls);
    return toolCalls;
  }

  private extractArgumentsFromIntent(toolName: string, intent: string): Record<string, unknown> {
    // Extract meaningful arguments from natural language intent
    const args: Record<string, unknown> = {};

    // Common patterns for different tools
    if (toolName === 'web_search' || toolName === 'search') {
      // Extract search query from intent
      const searchPatterns = [
        /(?:search\s+for\s+|find\s+|look\s+up\s+|get\s+)(.+)/i,
        /(.+?)(?:\s+information|\s+details|\s+data)?$/i
      ];

      for (const pattern of searchPatterns) {
        const match = pattern.exec(intent);
        if (match) {
          args.query = match[1].trim();
          break;
        }
      }
    } else if (toolName === 'read_file') {
      // Extract file path from intent
      const filePatterns = [
        /(?:read\s+|open\s+|check\s+)(?:the\s+file\s+)?['"]?([^'"]+)['"]?/i,
        /file\s+['"]?([^'"]+)['"]?/i
      ];

      for (const pattern of filePatterns) {
        const match = pattern.exec(intent);
        if (match) {
          args.path = match[1].trim();
          break;
        }
      }
    } else if (toolName === 'write_file') {
      // Extract file path and content hints
      const writePatterns = [
        /(?:write\s+to\s+|save\s+to\s+|create\s+)(?:file\s+)?['"]?([^'"]+)['"]?/i
      ];

      for (const pattern of writePatterns) {
        const match = pattern.exec(intent);
        if (match) {
          args.path = match[1].trim();
          break;
        }
      }
    }

    // If no specific patterns matched, use the intent as a general query/input
    if (Object.keys(args).length === 0 && intent.length > 0) {
      // Use common parameter names based on tool
      if (toolName.includes('search')) {
        args.query = intent;
      } else if (toolName.includes('file') || toolName.includes('read')) {
        args.path = intent;
      } else {
        args.input = intent;
      }
    }

    return args;
  }

  private createArgumentsForTool(toolName: string, value: string): Record<string, unknown> {
    const args: Record<string, unknown> = {};

    // Map tool names to their expected parameter names
    if (toolName === 'web_search' || toolName === 'search') {
      args.query = value;
    } else if (toolName === 'read_file' || toolName === 'write_file' || toolName === 'edit_file') {
      args.path = value;
    } else if (toolName === 'fetch' || toolName === 'fetch_content') {
      args.url = value;
    } else {
      // Default to common parameter names
      args.query = value;
    }

    return args;
  }

  private parseArgumentsFromText(argsText: string): Record<string, unknown> {
    try {
      // Try to parse as JSON first
      return JSON.parse(argsText);
    } catch {
      console.log(`‚ö†Ô∏è JSON parsing failed for: ${argsText}, trying fallback parsing`);

      // If JSON parsing fails, try to extract key-value pairs
      const args: Record<string, unknown> = {};

      // Enhanced regex patterns for different value types
      const patterns = [
        // String values with quotes
        /["']?(\w+)["']?\s*[:=]\s*["']([^"']+)["']/g,
        // Number values
        /["']?(\w+)["']?\s*[:=]\s*(\d+(?:\.\d+)?)/g,
        // Boolean values
        /["']?(\w+)["']?\s*[:=]\s*(true|false)/g,
        // Array values (simplified)
        /["']?(\w+)["']?\s*[:=]\s*\[([^\]]+)\]/g,
        // General fallback
        /["']?(\w+)["']?\s*[:=]\s*([^,}\]]+)/g
      ];

      for (const pattern of patterns) {
        let match;
        while ((match = pattern.exec(argsText)) !== null) {
          const key = match[1];
          let value: unknown = match[2];

          // Skip if we already have this key
          if (args[key] !== undefined) continue;

          // Type conversion
          if (value === 'true') value = true;
          else if (value === 'false') value = false;
          else if (typeof value === 'string' && !isNaN(Number(value))) value = Number(value);
          else if (typeof value === 'string' && value.includes(',')) {
            // Try to parse as array
            value = value.split(',').map(v => v.trim().replace(/['"]/g, ''));
          }

          args[key] = value;
        }
      }

      if (Object.keys(args).length === 0) {
        // Last resort: attempt to salvage from malformed JSON
        const recovered = JSONUtils.extractArgumentsFromMalformedJson(argsText);
        if (Object.keys(recovered).length > 0) {
          console.log(`‚úÖ Recovered arguments from malformed JSON:`, recovered);
          return recovered;
        }
      }

      console.log(`‚úÖ Fallback parsing extracted:`, args);
      return args;
    }
  }

  private getAvailableToolNames(): string[] {
    // Return injected tool names if available, otherwise fallback to common ones
    if (this.availableToolNames.length > 0) {
      return this.availableToolNames;
    }

    // Fallback to common tool names
    return [
      'web_search', 'search', 'google_search',
      'get_weather', 'weather', 'weather_search',
      'calculator', 'calculate', 'math',
      'file_read', 'read_file', 'get_file',
      'file_write', 'write_file', 'save_file',
      'get_datetime', 'current_time', 'date_time'
    ];
  }

  private async executeTextBasedTools(
    toolCalls: Array<{ name: string; arguments: Record<string, unknown> }>,
    originalContent: string,
    usage: { promptTokens?: number; completionTokens?: number; totalTokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    console.log(`üîß LM Studio executing ${toolCalls.length} text-based tool calls`);

    // STEP 1: Show tool execution start but CONTINUE streaming thinking content
    console.log(`üîß LM Studio: Starting tool execution while preserving thinking stream`);

    // Show tool execution start
    const toolExecutionHeader = `\n\n<tool_execution>\nüîß **Tool Execution Started**\n\nExecuting ${toolCalls.length} tool${toolCalls.length !== 1 ? 's' : ''}:\n${toolCalls.map(tc => `- ${tc.name}`).join('\n')}\n</tool_execution>\n\n`;
    onStream(toolExecutionHeader);

    // Execute all tool calls
    const toolResults: Array<{ name: string; result: string; error?: boolean }> = [];

    // STEP 2: Execute ALL tools to completion WITHOUT streaming
    console.log(`üîß LM Studio: Executing ${toolCalls.length} tools to completion...`);

    for (const toolCall of toolCalls) {
      try {
        // Handle error responses from tool parsing
        if (toolCall.name === 'error_response') {
          const errVal = (toolCall.arguments as Record<string, unknown>)?.error;
          console.log(`‚ö†Ô∏è Tool parsing error:`, errVal);
          const errStr = typeof errVal === 'string' ? errVal : JSON.stringify(errVal ?? 'Tool parsing error');
          toolResults.push({
            name: 'error_response',
            result: errStr,
            error: true
          });
          continue;
        }

        console.log(`üîß Executing LM Studio tool: ${toolCall.name} with args:`, toolCall.arguments);

        const result = await this.executeMCPTool(toolCall.name, toolCall.arguments);
        const resultString = typeof result === 'string' ? result : JSON.stringify(result);

        console.log(`üîç DEBUG: Tool ${toolCall.name} returned result:`, resultString.substring(0, 200) + '...');

        toolResults.push({
          name: toolCall.name,
          result: resultString,
          error: false
        });

        console.log(`‚úÖ LM Studio tool ${toolCall.name} executed successfully`);
      } catch (error) {
        console.error(`‚ùå LM Studio tool ${toolCall.name} failed:`, error);
        const userFriendlyError = this.formatToolError(toolCall.name, error);

        toolResults.push({
          name: toolCall.name,
          result: userFriendlyError,
          error: true
        });
      }
    }

    // STEP 3: Show tool execution completion and prepare for fresh LLM call
    const successCount = toolResults.filter(tr => !tr.error).length;
    const failureCount = toolResults.filter(tr => tr.error).length;

    console.log(`üèÅ LM Studio: Tool execution complete - ${successCount} successful, ${failureCount} failed`);

    // Show completion in UI
    const completionMessage = `<tool_execution>\nüèÅ **Tool Execution Complete**\n\n‚úÖ ${successCount} successful, ‚ùå ${failureCount} failed\n\nStarting fresh LLM call with results...\n</tool_execution>\n\n`;
    onStream(completionMessage);

    // For text-based tools, create a simulated proper conversation history
    // Since text-based tools don't have structured IDs, we'll create them
    const simulatedToolCalls = toolCalls.map((tc, index) => ({
      id: `text_tool_${index}`,
      type: 'function',
      function: {
        name: tc.name,
        arguments: JSON.stringify(tc.arguments)
      }
    }));

    const simulatedToolResults = toolResults.map((tr, index) => ({
      id: `text_tool_${index}`,
      name: tr.name,
      result: tr.result,
      error: tr.error
    }));

    // Build proper conversation history for text-based tools
    const properConversationHistory = this.buildProperConversationHistory(
      conversationHistory,
      simulatedToolResults,
      simulatedToolCalls
    );

    // STEP 4: Make completely fresh LLM call with clean conversation history
    console.log(`üîÑ LM Studio: Starting FRESH LLM call with tool results (no prompt contamination)`);
    console.log(`üßπ LM Studio: Clean conversation history prepared with ${properConversationHistory.length} messages`);

    // Make a follow-up call without tools (following official LM Studio pattern)
    const followUpResponse = await this.makeDirectFollowUpCall(
      properConversationHistory as Array<{role: string, content: string | Array<ContentItem>}>,
      settings,
      onStream
    );

    // According to LM Studio docs, we should return ONLY the final assistant response
    // Tool execution details are handled by the UI separately via toolCalls
    console.log(`üéØ LM Studio: Final response (clean):`, followUpResponse.content);

    return {
      content: followUpResponse.content || '',
      usage: followUpResponse.usage,
      toolCalls: toolResults.map((tr, index) => ({
        id: `text_tool_${index}`,
        name: tr.name,
        arguments: toolCalls.find(tc => tc.name === tr.name)?.arguments || {},
        result: tr.result,
        error: tr.error
      }))
    };
  }

  private getLastUserMessage(conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>): string {
    const userMessages = conversationHistory.filter(msg => msg.role === 'user');
    if (userMessages.length === 0) return 'Please help me with the information provided.';

    const lastMessage = userMessages[userMessages.length - 1];
    return typeof lastMessage.content === 'string' ? lastMessage.content : 'Please help me with the information provided.';
  }

  /**
   * Builds proper conversation history following official LM Studio format
   * Format: User message ‚Üí Assistant tool call message ‚Üí Tool result messages ‚Üí Final response
   * IMPORTANT: Filters out previous tool results to prevent prompt poisoning
   */
  private buildProperConversationHistory(
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    toolCalls: Array<{ id: string; name: string; result: string; error?: boolean }>,
    originalToolCallsFormat: Array<{ id: string; type: string; function: { name: string; arguments: string } }>
  ): Array<Record<string, unknown>> {

    // Include conversation history but filter out previous tool results to prevent prompt poisoning
    const cleanedHistory = conversationHistory.filter(msg => {
      // Keep user messages and assistant messages that don't contain tool execution results
      if (msg.role === 'user') return true;
      if (msg.role === 'assistant') {
        const content = typeof msg.content === 'string' ? msg.content : '';
        // Filter out messages that contain tool execution results from previous turns
        return !content.includes('<tool_execution>') && !content.includes('Tool Execution');
      }
      // Filter out tool role messages from previous turns
      return msg.role !== 'tool';
    });

    console.log(`üßπ LM Studio: Conversation history filtering:`, {
      original: conversationHistory.length,
      cleaned: cleanedHistory.length,
      filtered: conversationHistory.length - cleanedHistory.length
    });

    // Log the content of recent messages for debugging
    cleanedHistory.slice(-3).forEach((msg, index) => {
      const content = typeof msg.content === 'string' ? msg.content : JSON.stringify(msg.content);
      console.log(`üîç Recent message ${index}: [${msg.role}] ${content.substring(0, 100)}...`);
    });

    const messages: Array<Record<string, unknown>> = [...cleanedHistory];

    // Add the assistant message with tool calls (following official format)
    const assistantToolCallMessage = {
      role: 'assistant',
      content: '',
      tool_calls: originalToolCallsFormat.map(tc => ({
        id: tc.id,
        type: tc.type,
        function: tc.function
      }))
    };
    messages.push(assistantToolCallMessage);

    // Add tool result messages (following official format)
    for (const toolResult of toolCalls) {
      const toolResultMessage = {
        role: 'tool',
        content: toolResult.result,
        tool_call_id: toolResult.id,
        name: toolResult.name
      };
      messages.push(toolResultMessage);
    }

    // Add a final instruction prompting the model to use the tool outputs
    const finalInstruction = {
      role: 'user',
      content: `Use the tool outputs above to produce the final answer to your previous request: "${this.getLastUserMessage(conversationHistory)}". Do not call tools again. Provide a concise answer grounded in those results.`
    };
    messages.push(finalInstruction);

    console.log(`üîß Built cleaned conversation history with ${messages.length} messages (filtered previous tool results + ${toolCalls.length} current tool results)`);
    return messages;
  }

  private async makeDirectFollowUpCall(
    messages: Array<{role: string, content: string | Array<ContentItem>}>,
    settings: LLMSettings,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    console.log(`üîÑ Making follow-up call without tools (following official LM Studio pattern)`);

    // According to official LM Studio documentation, the follow-up call should NOT include tools
    // This prevents recursive tool calling and ensures proper synchronization
    console.log(`‚úÖ LM Studio follow-up call without tools - following official pattern for final response`);

    // Use the original system prompt for follow-up calls (maintain consistency)
    const finalResponsePrompt = settings.systemPrompt || this.getSystemPrompt();

    // Always update system message for final response (no tools)
    const systemMessageIndex = messages.findIndex(msg => msg.role === 'system');
    if (systemMessageIndex >= 0) {
      messages[systemMessageIndex].content = finalResponsePrompt;
    } else {
      messages.unshift({ role: 'system', content: finalResponsePrompt });
    }

    const requestBody = {
      model: settings.model,
      messages: messages,
      stream: true,
      temperature: settings.temperature || 0.7,
      max_tokens: settings.maxTokens || 4000
      // No tools included - following official LM Studio pattern for final response
    };

    // Sanitize and log a small preview of the follow-up payload to verify tool outputs are included
    try {
      const msgs = requestBody.messages as Array<Record<string, any>>;
      const preview = {
        model: requestBody.model,
        messageCount: msgs.length,
        roles: msgs.map(m => m.role),
        assistantToolCallIncluded: msgs.some(m => Array.isArray((m as any).tool_calls) && (m as any).tool_calls.length > 0),
        toolResultSummaries: msgs
          .map((m, idx) => ({ idx, role: m.role, name: (m as any).name, id: (m as any).tool_call_id, content: (m as any).content }))
          .filter(s => s.role === 'tool')
          .map(s => ({
            idx: s.idx,
            name: s.name,
            id: s.id,
            contentLen: typeof s.content === 'string' ? s.content.length : JSON.stringify(s.content || '').length,
            contentPreview: typeof s.content === 'string' ? s.content.slice(0, 160) : '[non-string]'
          })),
        finalInstructionPreview: (() => {
          const last = msgs[msgs.length - 1];
          const txt = typeof last?.content === 'string' ? last.content : JSON.stringify(last?.content || '');
          return txt.slice(0, 200);
        })()
      };
      console.log('üîé [LM STUDIO DEBUG] Follow-up payload preview:', preview);
    } catch (e) {
      console.warn('‚ö†Ô∏è [LM STUDIO DEBUG] Failed to build follow-up payload preview:', e);
    }

    // Construct the correct URL - baseUrl might already include /v1
    const baseUrl = settings.baseUrl || 'http://localhost:1234';
    const endpoint = baseUrl.endsWith('/v1') ? '/chat/completions' : '/v1/chat/completions';
    const fullUrl = `${baseUrl}${endpoint}`;

    console.log(`üîó Follow-up call URL: ${fullUrl}`);

    const response = await fetch(fullUrl, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer not-needed'
      },
      body: JSON.stringify(requestBody)
    });

    if (!response.ok) {
      throw new Error(`LM Studio follow-up API error (${response.status}): ${await response.text()}`);
    }

    if (response.body) {
      // Handle the streaming response directly without recursion
      return this.handleFollowUpStreamResponse(response, onStream);
    } else {
      throw new Error('No response body received from LM Studio follow-up call');
    }
  }

  private async handleFollowUpStreamResponse(
    response: Response,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    console.log(`üîÑ Processing follow-up stream response`);

    const reader = response.body!.getReader();
    const decoder = new TextDecoder();
    let fullContent = '';

    try {
      // eslint-disable-next-line no-constant-condition
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value, { stream: true });
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const data = line.slice(6);
            if (data === '[DONE]') continue;

            try {
              const parsed = JSON.parse(data);
              const delta = parsed.choices?.[0]?.delta;

              if (delta?.content) {
                fullContent += delta.content;
                onStream(delta.content);
              }
            } catch {
              // Skip invalid JSON lines
              continue;
            }
          }
        }
      }
    } finally {
      reader.releaseLock();
    }

    console.log(`‚úÖ Follow-up response completed. Content length: ${fullContent.length}`);

    // No recursive tool calling - following official LM Studio pattern
    // The follow-up call should provide the final response without calling more tools
    console.log(`üéØ LM Studio final response ready - no additional tool calls needed`);

    return {
      content: fullContent,
      usage: undefined // Follow-up calls don't need usage tracking
    };
  }

  private formatToolError(toolName: string, error: unknown): string {
    const errorStr = error instanceof Error ? error.message : String(error);
    const errorLower = errorStr.toLowerCase();

    // Check for common error patterns and provide user-friendly messages
    if (errorLower.includes('timeout') || errorLower.includes('timed out')) {
      return `‚è∞ ${toolName} timed out. The tool took too long to respond. Please try again.`;
    }

    if (errorLower.includes('not found') || errorLower.includes('unknown tool')) {
      return `üîß ${toolName} is not available. The tool might be disabled or not properly configured.`;
    }

    if (errorLower.includes('network') || errorLower.includes('connection')) {
      return `üåê Network error while executing ${toolName}. Please check your connection and try again.`;
    }

    if (errorLower.includes('invalid') && errorLower.includes('argument')) {
      return `üìù ${toolName} received invalid arguments. Please check the parameters and try again.`;
    }

    if (errorLower.includes('rate limit') || errorLower.includes('too many requests')) {
      return `‚è±Ô∏è ${toolName} rate limit exceeded. Please wait a moment before trying again.`;
    }

    if (errorLower.includes('unauthorized') || errorLower.includes('forbidden')) {
      return `üîê Access denied for ${toolName}. Please check your permissions or API credentials.`;
    }

    // Default error message with the original error for debugging
    return `‚ùå ${toolName} execution failed: ${errorStr}`;
  }

  private removeThinkingContent(content: string): string {
    // Remove various thinking patterns and model template tags from content before parsing for tool calls
    let cleanedContent = content;

    // Remove <think>...</think> blocks
    cleanedContent = cleanedContent.replace(/<think>[\s\S]*?<\/think>/gi, '');

    // Remove <thinking>...</thinking> blocks
    cleanedContent = cleanedContent.replace(/<thinking>[\s\S]*?<\/thinking>/gi, '');

    // Remove unclosed thinking tags (in case they're at the end)
    cleanedContent = cleanedContent.replace(/<think>[\s\S]*$/gi, '');
    cleanedContent = cleanedContent.replace(/<thinking>[\s\S]*$/gi, '');

    // Remove model-specific template tags more aggressively
    // New model format tags: <|start|>, <|message|>, <|channel|>, <|end|>, <|constrain|>
    cleanedContent = cleanedContent.replace(/<\|start\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|message\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|channel\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|end\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|constrain\|>/gi, '');

    // Qwen3 format tags: <|im_start|>, <|im_end|>
    cleanedContent = cleanedContent.replace(/<\|im_start\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|im_end\|>/gi, '');

    // Remove role indicators that might appear after template tags (only at start of lines or after colons)
    cleanedContent = cleanedContent.replace(/^(system|user|assistant):\s*/gim, '');
    cleanedContent = cleanedContent.replace(/\n(system|user|assistant):\s*/gi, '\n');

    // Remove channel indicators that might appear after <|channel|> tags (only at start of lines or after colons)
    cleanedContent = cleanedContent.replace(/^(final|analysis|commentary):\s*/gim, '');
    cleanedContent = cleanedContent.replace(/\n(final|analysis|commentary):\s*/gi, '\n');

    // Clean up any remaining template-like patterns
    cleanedContent = cleanedContent.replace(/<\|[^|]*\|>/gi, '');

    // Remove template sequences that span multiple tags
    cleanedContent = cleanedContent.replace(/<\|end\|><\|start\|>assistant<\|channel\|>commentary/gi, '');
    cleanedContent = cleanedContent.replace(/<\|constrain\|>json<\|message\|>/gi, ' json');

    // Remove tool execution blocks that might be mixed in the response
    cleanedContent = cleanedContent.replace(/<tool_execution>[\s\S]*?<\/tool_execution>/gi, '');

    // Remove any remaining tool execution indicators
    cleanedContent = cleanedContent.replace(/üîß\s*\*\*Tool Execution Started\*\*/gi, '');
    cleanedContent = cleanedContent.replace(/üèÅ\s*\*\*Tool Execution Complete\*\*/gi, '');
    cleanedContent = cleanedContent.replace(/Executing \d+ tools?:/gi, '');
    cleanedContent = cleanedContent.replace(/‚úÖ \d+ successful, ‚ùå \d+ failed/gi, '');

    // Clean up any extra whitespace
    cleanedContent = cleanedContent.trim();

    return cleanedContent;
  }

  private deduplicateToolCalls(toolCalls: Array<{ name: string; arguments: Record<string, unknown> }>): Array<{ name: string; arguments: Record<string, unknown> }> {
    const seen = new Set<string>();
    const unique: Array<{ name: string; arguments: Record<string, unknown> }> = [];

    for (const toolCall of toolCalls) {
      const key = `${toolCall.name}:${JSON.stringify(toolCall.arguments)}`;
      if (!seen.has(key)) {
        seen.add(key);
        unique.push(toolCall);
      } else {
        console.log(`üîß Removed duplicate tool call: ${toolCall.name}`);
      }
    }

    return unique;
  }
}

// Mistral AI provider implementation

import { BaseProvider } from './BaseProvider';
import {
  LLMSettings,
  LLMResponse,
  MessageContent,
  ContentItem,
  LLMProvider,
  ToolObject,
  ProviderCapabilities,
  APIResponseData
} from './types';

import { MISTRAL_SYSTEM_PROMPT } from './prompts/mistral';
import { PricingService } from '../pricingService';
import { MistralFileService } from '../mistralFileService';

export class MistralProvider extends BaseProvider {
  readonly id = 'mistral';
  readonly name = 'Mistral AI';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: true,
    supportsTools: true,
    supportsStreaming: true,
    supportsSystemMessages: true,
    maxToolNameLength: 64,
    toolFormat: 'openai'
  };

  private fileService?: MistralFileService;

  // Mistral-specific tool calling methods
  private async getMistralTools(settings: LLMSettings): Promise<unknown[]> {
    try {
      console.log(`üîç Getting tools for Mistral provider`);
      console.log(`üîç Tool calling enabled:`, settings?.toolCallingEnabled !== false);

      // Check if tool calling is disabled
      if (settings?.toolCallingEnabled === false) {
        console.log(`üö´ Tool calling is disabled, returning empty tools array`);
        return [];
      }

      // Get raw tools from the centralized service (temporarily)
      const rawTools = await this.getMCPToolsForProvider('mistral', settings);
      console.log(`üìã Raw tools received (${rawTools.length} tools):`, (rawTools as Array<{name?: string, function?: {name?: string}}>).map(t => t.name || t.function?.name));

      // Format tools specifically for Mistral (uses OpenAI format)
      const formattedTools = this.formatToolsForMistral(rawTools);
      console.log(`üîß Formatted ${formattedTools.length} tools for Mistral`);

      return formattedTools;
    } catch (error) {
      console.error('‚ùå Failed to get Mistral tools:', error);
      return [];
    }
  }

  private formatToolsForMistral(rawTools: unknown[]): unknown[] {
    return rawTools.map(tool => {
      const typedTool = tool as {type?: string, function?: {name?: string, description?: string, parameters?: unknown}, name?: string, description?: string, inputSchema?: unknown};

      // All tools now come in unified format with type: 'function' and function object
      if (typedTool.type === 'function' && typedTool.function) {
        return {
          type: 'function',
          function: {
            name: typedTool.function.name || 'unknown_tool',
            description: typedTool.function.description || 'No description',
            parameters: typedTool.function.parameters || {
              type: 'object',
              properties: {},
              required: []
            }
          }
        };
      }

      // Handle MCP tools (need conversion to OpenAI format)
      if (typedTool.name && typedTool.description) {
        return {
          type: 'function',
          function: {
            name: typedTool.name,
            description: typedTool.description,
            parameters: typedTool.inputSchema || {
              type: 'object',
              properties: {},
              required: []
            }
          }
        };
      }

      console.warn(`‚ö†Ô∏è Skipping invalid tool:`, tool);
      return null;
    }).filter(tool => tool !== null);
  }

  /* eslint-disable @typescript-eslint/no-explicit-any */
  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-explicit-any */
    // Initialize file service if not already done
    if (!this.fileService && settings.apiKey) {
      this.fileService = new MistralFileService(settings.apiKey, provider.baseUrl);
    }

    // Mistral uses OpenAI-compatible API
    const messages = [];

    // Get Mistral-specific formatted tools
    const mistralTools = await this.getMistralTools(settings);

    // Use behavioral system prompt only (no tool descriptions)
    // Tools are sent separately in the tools parameter (OpenAI-compatible)
    // Check for meaningful system prompt, not just empty string or generic default
    const hasCustomSystemPrompt = settings.systemPrompt &&
      settings.systemPrompt.trim() &&
      settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

    const systemPrompt = hasCustomSystemPrompt ? settings.systemPrompt! : this.getSystemPrompt();

    console.log(`üîç Mistral system prompt source:`, {
      hasCustom: hasCustomSystemPrompt,
      usingCustom: hasCustomSystemPrompt,
      promptLength: systemPrompt?.length || 0,
      promptStart: systemPrompt?.substring(0, 100) + '...'
    });

    if (systemPrompt) {
      messages.push({ role: 'system', content: systemPrompt });
    }

    // Add conversation history (clean for Mistral requirements)
    const cleanedHistory = conversationHistory.map(msg => {
      const typedMsg = msg as {role: string, tool_calls?: unknown, content?: unknown};
      if (typedMsg.role === 'assistant' && typedMsg.tool_calls && typedMsg.content) {
        // Mistral requires assistant messages to have EITHER content OR tool_calls, not both
        // Keep only tool_calls for assistant messages that have both
        return {
          role: typedMsg.role,
          tool_calls: typedMsg.tool_calls
        };
      }
      return msg;
    });
    messages.push(...cleanedHistory);

    // Check if model supports vision when images are present
    const hasImages = (Array.isArray(message) && message.some((item: ContentItem) => item.type === 'image_url')) ||
                     (typeof message === 'object' && message && 'images' in message && ((message as {images?: unknown[]}).images?.length ?? 0) > 0);

    if (hasImages) {
      const visionModels = [
        'mistral-medium-latest', 'mistral-medium-2505',
        'pixtral-large-latest', 'pixtral-large-2411',
        'pixtral-12b-latest', 'pixtral-12b-2409',
        'mistral-small-2503'
      ];

      if (!visionModels.includes(settings.model)) {
        throw new Error(`Mistral model "${settings.model}" does not support images. Please use a vision-capable model like "mistral-medium-latest", "pixtral-large-latest", or "pixtral-12b-2409" for image analysis.`);
      }
    }

    // Add current message
    if (typeof message === 'string') {
      messages.push({ role: 'user', content: message });
    } else if (Array.isArray(message)) {
      // Handle ContentItem array format (from chatService.ts)
      // Mistral uses OpenAI-compatible format, so we can pass through most content as-is
      const mistralContent = await Promise.all(message.map(async (item: ContentItem) => {
        if (item.type === 'text') {
          return { type: 'text', text: item.text };
        } else if (item.type === 'image_url') {
          // Mistral uses OpenAI-compatible format
          return {
            type: 'image_url',
            image_url: { url: item.image_url?.url || '' }
          };
        } else if (item.type === 'document') {
          // For documents, use Mistral's native capabilities
          if (this.fileService && item.document?.data) {
            try {
              // Convert base64 back to file for processing
              const binaryString = atob(item.document.data);
              const bytes = new Uint8Array(binaryString.length);
              for (let i = 0; i < binaryString.length; i++) {
                bytes[i] = binaryString.charCodeAt(i);
              }
              const file = new File([bytes], item.document.name || 'document', {
                type: item.document.media_type || 'application/pdf'
              });

              // Use Mistral's file service to prepare the document
              const preparedFile = await this.fileService.prepareFileForVision(file);
              return preparedFile;
            } catch (error) {
              console.error('‚ùå Error processing document with Mistral:', error);
              return {
                type: 'text',
                text: `[Document: ${item.document?.name || 'document'} - Processing failed: ${error}]`
              };
            }
          } else {
            return {
              type: 'text',
              text: `[Document: ${item.document?.name || 'document'}]`
            };
          }
        }
        return item; // Pass through other types as-is
      }));

      messages.push({ role: 'user', content: mistralContent });
    } else {
      // Handle legacy vision format (for backward compatibility)
      const messageWithImages = message as { text: string; images: string[] };
      const content: ContentItem[] = [{ type: 'text', text: messageWithImages.text }];

      for (const imageUrl of messageWithImages.images) {
        // Mistral uses OpenAI-compatible format for images
        content.push({
          type: 'image_url',
          image_url: { url: imageUrl }
        });
      }
      messages.push({ role: 'user', content });
    }

    const requestBody: Record<string, unknown> = {
      model: settings.model,
      messages: messages,
      temperature: settings.temperature,
      max_tokens: settings.maxTokens,
      stream: onStream && typeof onStream === 'function'
    };

    // Add tools if available
    if (mistralTools.length > 0) {
      requestBody.tools = mistralTools;
      requestBody.tool_choice = 'auto';
      console.log(`üöÄ Mistral API call with ${mistralTools.length} tools:`, {
        model: settings.model,
        toolCount: mistralTools.length,
        tools: mistralTools
      });
    } else {
      console.log(`üöÄ Mistral API call without tools (no tools available)`);
    }

    // Log the full request for debugging
    console.log(`üîç Mistral API request:`, {
      url: `${provider.baseUrl}/chat/completions`,
      model: settings.model,
      messageCount: messages.length,
      hasImages: messages.some(msg => 'content' in msg && Array.isArray(msg.content) && msg.content.some((c: {type?: string}) => c.type === 'image_url')),
      requestBody: JSON.stringify(requestBody, null, 2)
    });

    const response = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`,
        'User-Agent': 'LittleLLM/1.0'
      },
      body: JSON.stringify(requestBody),
      signal
    });

    if (!response.ok) {
      const error = await response.text();
      console.error(`‚ùå Mistral API error (${response.status} ${response.statusText}):`, error);
      console.error(`‚ùå Request details:`, {
        url: `${provider.baseUrl}/chat/completions`,
        model: settings.model,
        messageCount: messages.length,
        hasTools: mistralTools.length > 0,
        hasImages: messages.some(msg => 'content' in msg && Array.isArray(msg.content) && msg.content.some((c: {type?: string}) => c.type === 'image_url'))
      });

      // Try to parse error as JSON for better error messages
      let errorMessage = error;
      let errorDetails = '';
      try {
        const errorObj = JSON.parse(error);
        errorMessage = errorObj.message || errorObj.error?.message || error;

        // Add specific guidance for common errors
        if (response.status === 503) {
          errorDetails = '\n\nTroubleshooting tips for 503 Service Unavailable:\n' +
            '1. Check if your API key is valid and active\n' +
            '2. Verify the model name is correct (try "mistral-small-latest")\n' +
            '3. Check Mistral AI status page for service outages\n' +
            '4. Try again in a few moments - this may be temporary\n' +
            '5. Ensure you have sufficient API credits/quota';
        } else if (response.status === 401) {
          errorDetails = '\n\nAuthentication error - please check your API key';
        } else if (response.status === 429) {
          errorDetails = '\n\nRate limit exceeded - please wait before retrying';
        }
      } catch {
        // Keep original error if not JSON
      }

      throw new Error(`Mistral API error: ${errorMessage}${errorDetails}`);
    }

    if (onStream && typeof onStream === 'function') {
      return this.handleStreamResponse(response, onStream, settings, provider, conversationHistory, signal);
    } else {
      return this.handleNonStreamResponse(response, settings, conversationHistory, conversationId);
    }
  }

  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.error('‚ùå No Mistral API key provided - cannot fetch models');
      throw new Error('Mistral API key is required to fetch available models. Please add your API key in settings.');
    }

    try {
      // Test API connectivity first
      console.log('üîç Testing Mistral API connectivity...');

      // Mistral AI models endpoint - correct API endpoint from their documentation
      const response = await fetch('https://api.mistral.ai/v1/models', {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json',
          'User-Agent': 'LittleLLM/1.0'
        }
      });

      console.log(`üîç Mistral models API response: ${response.status} ${response.statusText}`);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå Mistral API error: ${response.status} ${response.statusText}`, errorText);

        // Try to parse error for better debugging
        let errorDetails = errorText;
        try {
          const errorObj = JSON.parse(errorText);
          console.error('Mistral API error details:', errorObj);
          errorDetails = errorObj.message || errorObj.error || errorText;
        } catch {
          console.error('Mistral API raw error:', errorText);
        }

        throw new Error(`Failed to fetch Mistral models: ${response.status} ${response.statusText} - ${errorDetails}`);
      }

      const data = await response.json() as APIResponseData;
      console.log('‚úÖ Mistral API models response:', data);

      // Mistral API returns models in data array with id field
      const models = data.data?.map((model) => model.id)?.sort() || [];

      console.log(`‚úÖ Fetched ${models.length} Mistral models:`, models);

      if (models.length === 0) {
        throw new Error('No Mistral models returned from API. This may indicate an API issue or insufficient permissions.');
      }

      return models;
    } catch (error) {
      console.error('‚ùå Failed to fetch Mistral models:', error);
      throw error instanceof Error ? error : new Error(`Failed to fetch Mistral models: ${String(error)}`);
    }
  }

  formatTools(tools: ToolObject[]): unknown[] {
    // Mistral uses OpenAI-compatible format
    return tools.map(tool => ({
      type: 'function',
      function: {
        name: tool.name || tool.function?.name,
        description: tool.description || tool.function?.description,
        parameters: tool.parameters || tool.function?.parameters || {
          type: 'object',
          properties: {},
          required: []
        }
      }
    }));
  }

  getSystemPrompt(): string {
    return MISTRAL_SYSTEM_PROMPT;
  }

  enhanceSystemPromptWithTools(basePrompt: string, tools: ToolObject[]): string {
    if (tools.length === 0) {
      return basePrompt;
    }

    // Mistral uses structured tool calling with tools parameter and tool_choice
    // Don't add XML tool instructions as they conflict with native function calling
    console.log(`üîß Mistral using structured tools, skipping XML tool instructions`);
    return basePrompt;
  }

