    ];
  }

  private async executeTextBasedTools(
    toolCalls: Array<{ name: string; arguments: Record<string, unknown> }>,
    originalContent: string,
    usage: { promptTokens?: number; completionTokens?: number; totalTokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    console.log(`üîß Ollama executing ${toolCalls.length} text-based tool calls`);

    // STEP 1: STOP all streaming and execute tools completely
    console.log(`üõë Ollama: Stopping stream to execute tools cleanly`);

    // Show tool execution start (this is the ONLY streaming during tool execution)
    const toolExecutionHeader = `\n\n<tool_execution>\nüîß **Tool Execution Started**\n\nExecuting ${toolCalls.length} tool${toolCalls.length !== 1 ? 's' : ''}:\n${toolCalls.map(tc => `- ${tc.name}`).join('\n')}\n</tool_execution>\n\n`;
    onStream(toolExecutionHeader);

    // STEP 2: Execute ALL tools to completion WITHOUT streaming
    console.log(`üîß Ollama: Executing ${toolCalls.length} tools to completion...`);
    const toolResults: Array<{ name: string; result: string; error?: boolean }> = [];

    for (const toolCall of toolCalls) {
      try {
        console.log(`üîß Executing Ollama tool: ${toolCall.name} with args:`, toolCall.arguments);

        const result = await this.executeMCPTool(toolCall.name, toolCall.arguments);
        const resultString = typeof result === 'string' ? result : JSON.stringify(result);

        toolResults.push({
          name: toolCall.name,
          result: resultString,
          error: false
        });

        console.log(`‚úÖ Ollama tool ${toolCall.name} executed successfully`);
      } catch (error) {
        console.error(`‚ùå Ollama tool ${toolCall.name} failed:`, error);
        const userFriendlyError = this.formatToolError(toolCall.name, error);

        toolResults.push({
          name: toolCall.name,
          result: userFriendlyError,
          error: true
        });
      }
    }

    // Create follow-up prompt with tool results
    const toolResultsText = toolResults.map(tr =>
      `Tool: ${tr.name}\nResult: ${tr.result}\n`
    ).join('\n');

    const followUpPrompt = `Based on the tool results below, please provide a helpful response to the user's question.

Tool Results:
${toolResultsText}

Original Question: ${this.getLastUserMessage(conversationHistory)}

Please provide a natural, helpful response based on the tool results.`;

    console.log(`üîÑ Ollama making follow-up call with tool results`);

    // Clean conversation history to remove any malformed tool results
    const cleanedHistory = conversationHistory.filter(msg => {
      // Remove messages that contain tool execution errors
      if (typeof msg.content === 'string' && msg.content.includes('Tool Results:') && msg.content.includes('Error:')) {
        console.log(`üßπ Removing malformed tool result message from history`);
        return false;
      }
      return true;
    });

    // Make follow-up call
    const followUpMessages = [
      ...cleanedHistory,
      { role: 'user', content: followUpPrompt }
    ];

    const followUpResponse = await this.sendMessage(
      followUpPrompt,
      settings,
      provider,
      followUpMessages,
      onStream
    );

    return {
      content: followUpResponse.content,
      usage: followUpResponse.usage,
      toolCalls: toolResults.map((tr, index) => ({
        id: `text_tool_${index}`,
        name: tr.name,
        arguments: toolCalls.find(tc => tc.name === tr.name)?.arguments || {},
        result: tr.result,
        error: tr.error
      }))
    };
  }



  private formatToolError(toolName: string, error: unknown): string {
    const errorStr = error instanceof Error ? error.message : String(error);
    const errorLower = errorStr.toLowerCase();

    // Check for common error patterns and provide user-friendly messages
    if (errorLower.includes('timeout') || errorLower.includes('timed out')) {
      return `‚è∞ ${toolName} timed out. The tool took too long to respond. Please try again.`;
    }

    if (errorLower.includes('not found') || errorLower.includes('unknown tool')) {
      return `üîß ${toolName} is not available. The tool might be disabled or not properly configured.`;
    }

    if (errorLower.includes('network') || errorLower.includes('connection')) {
      return `üåê Network error while executing ${toolName}. Please check your connection and try again.`;
    }

    if (errorLower.includes('invalid') && errorLower.includes('argument')) {
      return `üìù ${toolName} received invalid arguments. Please check the parameters and try again.`;
    }

    if (errorLower.includes('rate limit') || errorLower.includes('too many requests')) {
      return `‚è±Ô∏è ${toolName} rate limit exceeded. Please wait a moment before trying again.`;
    }

    if (errorLower.includes('unauthorized') || errorLower.includes('forbidden')) {
      return `üîê Access denied for ${toolName}. Please check your permissions or API credentials.`;
    }

    // Default error message with the original error for debugging
    return `‚ùå ${toolName} execution failed: ${errorStr}`;
  }

  private removeThinkingContent(content: string): string {
    // Remove various thinking patterns and model template tags from content before parsing for tool calls
    let cleanedContent = content;

    // Remove <think>...</think> blocks
    cleanedContent = cleanedContent.replace(/<think>[\s\S]*?<\/think>/gi, '');

    // Remove <thinking>...</thinking> blocks
    cleanedContent = cleanedContent.replace(/<thinking>[\s\S]*?<\/thinking>/gi, '');

    // Remove unclosed thinking tags (in case they're at the end)
    cleanedContent = cleanedContent.replace(/<think>[\s\S]*$/gi, '');
    cleanedContent = cleanedContent.replace(/<thinking>[\s\S]*$/gi, '');

    // Remove model-specific template tags
    // New model format tags: <|start|>, <|message|>, <|channel|>, <|end|>, <|constrain|>
    cleanedContent = cleanedContent.replace(/<\|start\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|message\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|channel\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|end\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|constrain\|>/gi, '');

    // Qwen3 format tags: <|im_start|>, <|im_end|>
    cleanedContent = cleanedContent.replace(/<\|im_start\|>/gi, '');
    cleanedContent = cleanedContent.replace(/<\|im_end\|>/gi, '');

    // Remove role indicators that might appear after template tags
    cleanedContent = cleanedContent.replace(/^(system|user|assistant)\s*/gim, '');

    // Remove channel indicators that might appear after <|channel|> tags
    cleanedContent = cleanedContent.replace(/^(final|analysis|commentary)\s*/gim, '');

    // Clean up any remaining template-like patterns
    cleanedContent = cleanedContent.replace(/<\|[^|]*\|>/gi, '');

    // Clean up any extra whitespace
    cleanedContent = cleanedContent.trim();

    return cleanedContent;
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleNonStreamResponse(
    response: Response,
    settings: LLMSettings,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    const data = await response.json();
    console.log(`üîç Ollama raw response:`, JSON.stringify(data, null, 2));
    const message = data.choices[0].message;

    // Handle tool calls if present
    if (message.tool_calls && message.tool_calls.length > 0) {
      console.log(`üîß Ollama response contains ${message.tool_calls.length} tool calls:`, message.tool_calls);

      // Tool execution will be handled by the main service
      return {
        content: message.content || '',
        usage: data.usage ? {
          promptTokens: data.usage.prompt_tokens,
          completionTokens: data.usage.completion_tokens,
          totalTokens: data.usage.total_tokens
        } : undefined,
        toolCalls: message.tool_calls.map((tc: { id: string; function: { name: string; arguments: string } }) => ({
          id: tc.id,
          name: tc.function.name,
          arguments: JSON.parse(tc.function.arguments)
        }))
      };
    }

    return {
      content: message.content,
      usage: data.usage ? {
        promptTokens: data.usage.prompt_tokens,
        completionTokens: data.usage.completion_tokens,
        totalTokens: data.usage.total_tokens
      } : undefined
    };
  }

  private hasThinkingContent(content: string): boolean {
    // Check for various thinking patterns
    const thinkingPatterns = [
      /<think>/i,
      /<thinking>/i,
      /\*\*thinking\*\*/i,
      /\*thinking\*/i,
      /thinking:/i,
      /let me think/i,
      /i need to think/i,
      /first, let me/i,
      /step by step/i
    ];

    return thinkingPatterns.some(pattern => pattern.test(content));
  }

  private isThinkingComplete(content: string): boolean {
    // Check if thinking tags are properly closed
    const hasOpenThink = /<think>/i.test(content);
    const hasCloseThink = /<\/think>/i.test(content);

    const hasOpenThinking = /<thinking>/i.test(content);
    const hasCloseThinking = /<\/thinking>/i.test(content);

    // If we have opening tags, we need closing tags
    if (hasOpenThink && !hasCloseThink) return false;
    if (hasOpenThinking && !hasCloseThinking) return false;

    // Check for incomplete reasoning patterns
    const incompletePatterns = [
      /thinking\.\.\.$/i,
      /let me think\.\.\.$/i,
      /step \d+:?\s*$/i,
      /first,?\s*$/i,
      /so,?\s*$/i,
      /therefore,?\s*$/i
    ];

    if (incompletePatterns.some(pattern => pattern.test(content.trim()))) {
      return false;
    }

    // If content ends with reasoning indicators, it might be incomplete
    const reasoningEndings = [
      /\.\.\.$/, // ends with ...
      /:\s*$/, // ends with :
      /,\s*$/, // ends with ,
      /-\s*$/, // ends with -
    ];

    if (reasoningEndings.some(pattern => pattern.test(content.trim()))) {
      return false;
    }

    return true;
  }
}

// OpenAI provider implementation

/* eslint-disable @typescript-eslint/no-explicit-any, @typescript-eslint/no-unused-vars */
import { BaseProvider } from './BaseProvider';
import {
  LLMSettings,
  LLMResponse,
  MessageContent,
  ContentItem,
  LLMProvider,
  ToolObject,
  ProviderCapabilities
} from './types';

import { OPENAI_SYSTEM_PROMPT } from './prompts/openai';
import { OpenAIFileService } from '../OpenAIFileService';
import { PricingService } from '../pricingService';
// import { RAGService } from '../RAGService'; // Moved to Electron main process, accessed via IPC

export class OpenAIProvider extends BaseProvider {
  readonly id = 'openai';
  readonly name = 'OpenAI';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: true,
    supportsTools: true,
    supportsStreaming: true,
    supportsSystemMessages: true,
    supportsPromptCaching: true,
    promptCachingType: 'automatic', // Automatic caching for prompts ‚â•1024 tokens
    maxToolNameLength: 64,
    toolFormat: 'openai'
  };

  private fileService?: OpenAIFileService;
  private assistantId?: string;
  private vectorStoreId?: string;
  private threadMap = new Map<string, string>(); // Maps conversationId to threadId

  /* eslint-disable @typescript-eslint/no-unused-vars */
  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    // Smart routing: Use Assistants API for file uploads, Chat Completions API for everything else
    const hasFileUploads = this.hasDocumentUploads(message);

    if (hasFileUploads) {
      console.log('üîß OpenAI: Using Assistants API for file upload handling');
      return this.sendMessageWithAssistants(message, settings, provider, conversationHistory, onStream, signal, conversationId);
    } else {
      console.log('üîß OpenAI: Using Chat Completions API for fast tool calling');
      return this.sendMessageWithChatCompletions(message, settings, provider, conversationHistory, onStream, signal, conversationId);
    }
  }

  private hasDocumentUploads(message: MessageContent): boolean {
    console.log(`üîç OpenAI checking for file uploads in message:`, {
      isArray: Array.isArray(message),
      messageType: typeof message,
      messageContent: Array.isArray(message) ? message.map(item => {
        const extendedItem = item as ContentItem & {file?: unknown, document?: unknown, attachment?: unknown};
        return {
          type: item.type,
          hasFile: !!extendedItem.file,
          hasDocument: !!extendedItem.document,
          hasAttachment: !!extendedItem.attachment
        };
      }) : 'not array'
    });

    if (Array.isArray(message)) {
      const hasFiles = message.some(item => {
        const extendedItem = item as ContentItem & {file?: unknown, document?: unknown, attachment?: unknown};
        const extendedType = (extendedItem as unknown as {type?: string}).type;
        return extendedType === 'document' ||
               extendedType === 'file' ||
               extendedType === 'attachment' ||
               !!extendedItem.file ||
               !!extendedItem.document ||
               !!extendedItem.attachment;
      });

      console.log(`üîç OpenAI file detection result:`, { hasFiles });
      return hasFiles;
    }

    // Check if message has file properties (legacy format)
    const hasLegacyFiles = !!(message as {files?: unknown, attachments?: unknown}).files || !!(message as {files?: unknown, attachments?: unknown}).attachments;
    console.log(`üîç OpenAI legacy file detection result:`, { hasLegacyFiles });
    return hasLegacyFiles;
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async sendMessageWithAssistants(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    // Initialize file service if not already done
    if (!this.fileService && settings.apiKey) {
      this.fileService = new OpenAIFileService(settings.apiKey, provider.baseUrl);
    }

    // Assistants API workflow for file uploads
    if (!this.assistantId || !this.vectorStoreId) {
      const { assistantId, vectorStoreId } = await this.getOrCreateAssistantAndVectorStore(settings);
      this.assistantId = assistantId;
      this.vectorStoreId = vectorStoreId;
    }

    const threadId = await this.getOrCreateThread(conversationId, settings);

    let textContent = '';
    const attachments: { file_id: string; tools: { type: string; }[] }[] = [];

    if (typeof message === 'string') {
      textContent = message; // RAG integration now handled in chatService
    } else if (Array.isArray(message)) {
      for (const item of message) {
        if (item.type === 'text') {
          textContent += item.text + '\n';
        } else if (item.type === 'document' && this.fileService && item.document?.data) {
          try {
            const binaryString = atob(item.document.data);
            const bytes = new Uint8Array(binaryString.length);
            for (let i = 0; i < binaryString.length; i++) {
              bytes[i] = binaryString.charCodeAt(i);
            }
            const file = new File([bytes], item.document.name || 'document', {
              type: item.document.media_type || 'application/octet-stream'
            });
            // Upload the file and add it to the vector store
            const uploadedFile = await this.fileService.uploadFile(file, 'assistants');
            await this.addFileToVectorStore(this.vectorStoreId!, uploadedFile.id, settings);
            attachments.push({ file_id: uploadedFile.id, tools: [{ type: 'file_search' }] });
          } catch (error) {
            console.error('Error uploading document to OpenAI:', error);
            textContent += `\n[Error uploading document: ${item.document.name}]`;
          }
        }
      }
    }

    await this.addMessageToThread(threadId, textContent.trim(), attachments, settings);

    const run = await this.createAndPollRun(threadId, this.assistantId!, settings);

    if (run.status !== 'completed') {
      throw new Error(`Run failed with status: ${run.status}`);
    }

    const responseContent = await this.getAssistantResponse(threadId, settings);

    return {
      content: responseContent,
      usage: {
        promptTokens: run.usage?.prompt_tokens || 0,
        completionTokens: run.usage?.completion_tokens || 0,
        totalTokens: run.usage?.total_tokens || 0
      }
    };
  }

  private async sendMessageWithChatCompletions(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    const messages = [];

    // Get MCP tools for this provider first
    const mcpTools = await this.getMCPToolsForProvider('openai', settings);

    // Use behavioral system prompt only (no tool descriptions)
    // Tools are sent separately in the tools parameter
    // Check for meaningful system prompt, not just empty string or generic default
    const hasCustomSystemPrompt = settings.systemPrompt &&
      settings.systemPrompt.trim() &&
      settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

    const systemPrompt = hasCustomSystemPrompt ? settings.systemPrompt! : this.getSystemPrompt();
    const cachingEnabled = settings.promptCachingEnabled ?? true;

    console.log(`üîç OpenAI Chat Completions system prompt source:`, {
      hasCustom: hasCustomSystemPrompt,
      usingCustom: hasCustomSystemPrompt,
      promptLength: systemPrompt?.length || 0,
      promptStart: systemPrompt?.substring(0, 100) + '...',
      cachingEnabled,
      automaticCaching: systemPrompt && systemPrompt.length > 4096 ? 'eligible' : 'too_small'
    });

    if (systemPrompt) {
      messages.push({ role: 'system', content: systemPrompt });
      if (cachingEnabled && systemPrompt.length > 4096) {
        console.log(`üîß OpenAI: System prompt eligible for automatic caching (${systemPrompt.length} chars, ‚â•1024 tokens)`);
      }
    }

    // Add conversation history
    messages.push(...conversationHistory);

    // Add current message (handle both string and array formats)
    if (typeof message === 'string') {
      messages.push({ role: 'user', content: message });
      if (cachingEnabled && message.length > 4096) {
        console.log(`üîß OpenAI: User message eligible for automatic caching (${message.length} chars, ‚â•1024 tokens)`);
      }
    } else if (Array.isArray(message)) {
      // Handle ContentItem array format (images, text)
      messages.push({ role: 'user', content: message });
      if (cachingEnabled) {
        const totalTextLength = message.filter(item => item.type === 'text').reduce((sum, item) => sum + (item.text?.length || 0), 0);
        if (totalTextLength > 4096) {
          console.log(`üîß OpenAI: User message content eligible for automatic caching (${totalTextLength} chars total text, ‚â•1024 tokens)`);
        }
      }
    } else {
      // Handle legacy vision format (convert to OpenAI format)
      const messageWithImages = message as { text: string; images: string[] };
      const content: ContentItem[] = [{ type: 'text', text: messageWithImages.text }];

      for (const imageUrl of messageWithImages.images) {
        content.push({
          type: 'image_url',
          image_url: { url: imageUrl }
        });
      }
      messages.push({ role: 'user', content });
    }

    const requestBody: Record<string, unknown> = {
      model: settings.model,
      messages,
      temperature: settings.temperature,
      max_tokens: settings.maxTokens,
      stream: !!onStream
    };

    // Add tools if available
    if (mcpTools.length > 0) {
      requestBody.tools = mcpTools;
      requestBody.tool_choice = 'auto';
      console.log(`üöÄ OpenAI Chat Completions API call with ${mcpTools.length} tools:`, {
        model: settings.model,
        toolCount: mcpTools.length,
        conversationId: conversationId || 'none'
      });
    } else {
      console.log(`üöÄ OpenAI Chat Completions API call without tools`);
    }

    console.log('üîç OpenAI Chat Completions request body:', JSON.stringify(requestBody, null, 2));

    const response = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`
      },
      body: JSON.stringify(requestBody),
      signal
    });

    console.log('üîç OpenAI Chat Completions response status:', response.status, response.statusText);

    if (!response.ok) {
      const error = await response.text();
      console.error('‚ùå OpenAI Chat Completions API error response:', error);

      if (response.status === 401) {
        throw new Error(`OpenAI API authentication failed. Please check your API key in Settings. Error: ${error}`);
      }

      // Check if it's a token limit error - fallback to Assistants API
      if (response.status === 429 && error.includes('Request too large')) {
        console.log('üîÑ OpenAI Chat Completions request too large, falling back to Assistants API');
        return this.sendMessageWithAssistants(message, settings, provider, conversationHistory, onStream, signal, conversationId);
      }

      throw new Error(`OpenAI API error: ${error}`);
    }

    if (onStream) {
      return this.handleChatCompletionsStreamResponse(response, settings, provider, conversationHistory, onStream, signal);
    } else {
      return this.handleChatCompletionsNonStreamResponse(response, settings, conversationHistory);
    }
  }

  private async handleChatCompletionsStreamResponse(
    response: Response,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void,
    signal?: AbortSignal
  ): Promise<LLMResponse> {
    const reader = response.body?.getReader();
    if (!reader) {
      throw new Error('No response body');
    }

    let fullContent = '';
    let usage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined;
    const toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }> = [];
    // Removed unused currentToolCall variable

    try {
      // eslint-disable-next-line no-constant-condition
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = new TextDecoder().decode(value);
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const data = line.slice(6);
            if (data === '[DONE]') continue;

            try {
              const parsed = JSON.parse(data);
              const delta = parsed.choices?.[0]?.delta;

              if (delta?.content) {
                fullContent += delta.content;
                onStream(delta.content);
              }

              // Handle tool calls in streaming
              if (delta?.tool_calls) {
                for (const toolCall of delta.tool_calls) {
                  if (toolCall.index !== undefined) {
                    if (!toolCalls[toolCall.index]) {
                      toolCalls[toolCall.index] = { id: toolCall.id, type: toolCall.type };
                    }
                    if (toolCall.function) {
                      if (!toolCalls[toolCall.index].function) {
                        toolCalls[toolCall.index].function = { name: '', arguments: '' };
                      }
                      if (toolCall.function.name) {
                        toolCalls[toolCall.index].function!.name += toolCall.function.name;
                      }
                      if (toolCall.function.arguments) {
                        toolCalls[toolCall.index].function!.arguments += toolCall.function.arguments;
                      }
                    }
                  }
                }
              }

              if (parsed.usage) {
                usage = parsed.usage;
              }
            } catch {
              // Skip invalid JSON
            }
          }
        }
      }
    } finally {
      reader.releaseLock();
    }

    // Handle tool calls if present
    if (toolCalls.length > 0) {
      console.log(`üîß OpenAI Chat Completions detected ${toolCalls.length} tool calls`);

      // Execute tools and make follow-up call
      return this.executeToolsAndFollowUpChatCompletions(
        toolCalls,
        fullContent,
        usage,
        settings,
        provider,
        conversationHistory,
        onStream,
        signal
      );
    }

    const { usage: usageInfo, cost } = this.createUsageAndCost(settings.model, usage);
    return {
      content: fullContent,
      usage: usageInfo,
      cost
    };
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleChatCompletionsNonStreamResponse(
    response: Response,
    settings: LLMSettings,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    const data = await response.json();
    console.log('üîç OpenAI Chat Completions raw response:', JSON.stringify(data, null, 2));

    const message = data.choices?.[0]?.message;
    const content = message?.content || '';
    const usage = data.usage;

    // Handle tool calls if present
    if (message?.tool_calls && message.tool_calls.length > 0) {
      console.log(`üîß OpenAI Chat Completions detected ${message.tool_calls.length} tool calls`);
      // For non-streaming, we would need to implement tool execution here
      // For now, return the response as-is
    }

    return {
      content,
      usage: usage ? {
        promptTokens: usage.prompt_tokens || 0,
        completionTokens: usage.completion_tokens || 0,
        totalTokens: usage.total_tokens || 0
      } : undefined,
      toolCalls: message?.tool_calls?.map((tc: any) => ({
        id: tc.id,
        name: tc.function?.name,
        arguments: JSON.parse(tc.function?.arguments || '{}')
      }))
    };
  }

  private async executeToolsAndFollowUpChatCompletions(
    toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }>,
    initialContent: string,
    initialUsage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void,
    signal?: AbortSignal
  ): Promise<LLMResponse> {
    console.log(`üöÄ Executing ${toolCalls.length} OpenAI Chat Completions tools in parallel`);

    // Check if parallel execution methods are available
    if (!(this as any).executeMultipleToolsParallel) {
      console.error('‚ùå executeMultipleToolsParallel method not available');
      throw new Error('Tool execution method not available');
    }

    // Execute tools in parallel using the centralized service
    const executeMultipleToolsParallel = (this as any).executeMultipleToolsParallel;
    const parallelResults = await executeMultipleToolsParallel(
      toolCalls.map(tc => ({
        id: tc.id,
        name: tc.function?.name || '',
        arguments: JSON.parse(tc.function?.arguments || '{}')
      })),
      'openai'
    );

    console.log(`üèÅ OpenAI Chat Completions tool execution completed: ${parallelResults.filter((r: {success: boolean}) => r.success).length}/${parallelResults.length} successful`);

    // Build messages for follow-up call
    const userMessages = conversationHistory.filter(msg => msg.role !== 'system');

    // Convert tool calls to OpenAI format for follow-up
    const openaiToolCalls = toolCalls.map(tc => ({
      id: tc.id || '',
      type: 'function',
      function: {
        name: tc.function?.name || '',
        arguments: tc.function?.arguments || '{}'
      }
    }));

    // Convert tool results to OpenAI message format
    const toolResults = parallelResults.map((result: {id?: string, result: string}) => ({
      role: 'tool',
      tool_call_id: result.id || '',
      content: result.result
    }));

    // Use same behavioral system prompt as initial call (for consistency and caching)
    const hasCustomSystemPromptFollowUp = settings.systemPrompt &&
      settings.systemPrompt.trim() &&
      settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

    const baseSystemPrompt = hasCustomSystemPromptFollowUp ? settings.systemPrompt! : this.getSystemPrompt();
    const followUpSystemPrompt = baseSystemPrompt +
      `\n\n## Follow-up Context\n\nBased on the tool results provided above, continue the conversation naturally. If you need to use additional tools to better answer the user's question, feel free to do so.`;

    // Build proper assistant message - if no initial content, use a descriptive message
    const assistantContent = initialContent.trim() ||
      `I'll help you with that. Let me use the appropriate tools to get the information you need.`;

    const followUpMessages = [
      { role: 'system', content: followUpSystemPrompt },
      ...userMessages,
      { role: 'assistant', content: assistantContent, tool_calls: openaiToolCalls },
      ...toolResults
    ];

    console.log(`üîç OpenAI Chat Completions follow-up messages:`, {
      messageCount: followUpMessages.length,
      systemPromptLength: followUpSystemPrompt.length,
      assistantContent: assistantContent.substring(0, 100) + '...',
      toolResultsCount: toolResults.length,
      toolResultsPreview: toolResults.map((tr: {tool_call_id: string, content: string}) => ({
        tool_call_id: tr.tool_call_id,
        contentLength: tr.content.length,
        contentPreview: tr.content.substring(0, 100) + '...'
      }))
    });

    // Get tools for continued agentic behavior in follow-up call
    const followUpTools = await this.getMCPToolsForProvider('openai', settings);
    console.log(`üîÑ Making OpenAI Chat Completions follow-up call with ${followUpTools.length} tools available for continued agentic behavior`);

    const followUpRequestBody = {
      model: settings.model,
      messages: followUpMessages,
      temperature: settings.temperature,
      max_tokens: settings.maxTokens,
      stream: true,
      // Include tools to allow continued agentic behavior
      ...(followUpTools.length > 0 && {
        tools: followUpTools,
        tool_choice: 'auto'
      })
    };

    const followUpResponse = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`
      },
      body: JSON.stringify(followUpRequestBody),
      signal
    });

    console.log(`üîç OpenAI Chat Completions follow-up response status:`, followUpResponse.status, followUpResponse.statusText);

    if (followUpResponse.ok) {
      console.log(`‚úÖ Starting OpenAI Chat Completions follow-up streaming response`);

      try {
        // Stream the follow-up response with updated conversation history for agentic behavior
        const followUpResult = await this.handleChatCompletionsStreamResponse(
          followUpResponse,
          settings,
          provider,
          followUpMessages, // Use updated messages that include tool results
          onStream,
          signal
        );

        console.log(`‚úÖ OpenAI Chat Completions follow-up streaming completed:`, {
          contentLength: followUpResult.content?.length || 0,
          hasUsage: !!followUpResult.usage,
          hasToolCalls: !!followUpResult.toolCalls,
          content: followUpResult.content?.substring(0, 100) + '...'
        });

        // Combine tool calls from initial response AND follow-up response
        const initialToolCalls = toolCalls
          .filter(tc => tc.id && tc.function?.name)
          .map(tc => ({
            id: tc.id!,
            name: tc.function!.name!,
            arguments: JSON.parse(tc.function!.arguments || '{}')
          }));

        const followUpToolCalls = followUpResult.toolCalls || [];
        const allToolCalls = [...initialToolCalls, ...followUpToolCalls];

        console.log(`üîß Combined OpenAI Chat Completions tool calls: ${initialToolCalls.length} initial + ${followUpToolCalls.length} follow-up = ${allToolCalls.length} total`);

        return {
          content: initialContent + followUpResult.content,
          usage: followUpResult.usage ? {
            promptTokens: (initialUsage?.prompt_tokens || 0) + (followUpResult.usage?.promptTokens || 0),
            completionTokens: (initialUsage?.completion_tokens || 0) + (followUpResult.usage?.completionTokens || 0),
            totalTokens: (initialUsage?.total_tokens || 0) + (followUpResult.usage?.totalTokens || 0)
          } : initialUsage ? {
            promptTokens: initialUsage.prompt_tokens || 0,
            completionTokens: initialUsage.completion_tokens || 0,
            totalTokens: initialUsage.total_tokens || 0
          } : undefined,
          toolCalls: allToolCalls
        };
      } catch (error) {
        console.error(`‚ùå OpenAI Chat Completions follow-up streaming failed:`, error);
        console.error(`‚ùå Falling back to original response without tool results`);
        // Fall through to return original response
      }
    } else {
      const errorText = await followUpResponse.text();
      console.error(`‚ùå OpenAI Chat Completions follow-up call failed (${followUpResponse.status}):`, errorText);
      console.error(`‚ùå Falling back to original response without tool results`);
    }

    // Return original response with tool calls if follow-up failed
    console.log(`‚ö†Ô∏è OpenAI Chat Completions returning original response without tool results:`, {
      contentLength: initialContent?.length || 0,
      toolCallsCount: toolCalls.length,
      content: initialContent?.substring(0, 100) + '...'
    });

    return {
      content: initialContent,
      usage: initialUsage ? {
        promptTokens: initialUsage.prompt_tokens || 0,
        completionTokens: initialUsage.completion_tokens || 0,
        totalTokens: initialUsage.total_tokens || 0
      } : undefined,
      toolCalls: toolCalls
        .filter(tc => tc.id && tc.function?.name)
        .map(tc => ({
          id: tc.id!,
          name: tc.function!.name!,
          arguments: JSON.parse(tc.function!.arguments || '{}')
        }))
    };
  }

  // OpenAI-specific tool calling methods
  private async getOpenAITools(settings: LLMSettings): Promise<unknown[]> {
    try {
      console.log(`üîç Getting tools for OpenAI provider`);
      console.log(`üîç Tool calling enabled:`, settings?.toolCallingEnabled !== false);

      // Check if tool calling is disabled
      if (settings?.toolCallingEnabled === false) {
        console.log(`üö´ Tool calling is disabled, returning empty tools array`);
        return [];
      }

      // Get raw tools from the centralized service (temporarily)
      const rawTools = await this.getMCPToolsForProvider('openai', settings);
      console.log(`üìã Raw tools received (${rawTools.length} tools):`, rawTools.map((t: any) => t.name || t.function?.name));

      // Format tools specifically for OpenAI Assistants API
      const formattedTools = this.formatToolsForOpenAI(rawTools);
      console.log(`üîß Formatted ${formattedTools.length} tools for OpenAI Assistants API`);

      return formattedTools;
    } catch (error) {
      console.error('‚ùå Failed to get OpenAI tools:', error);
      return [];
    }
  }

  private formatToolsForOpenAI(rawTools: any[]): unknown[] {
    return rawTools.map(tool => {
      // All tools now come in unified format with type: 'function' and function object
      if (tool.type === 'function' && tool.function) {
        // Sanitize tool name for OpenAI (replace hyphens with underscores)
        const sanitizedName = tool.function.name.replace(/-/g, '_');
        return {
          type: 'function',
          function: {
            name: sanitizedName,
            description: tool.function.description || 'No description',
            parameters: tool.function.parameters || {
              type: 'object',
              properties: {},
              required: []
            }
          }
        };
      }
      
      console.warn(`‚ö†Ô∏è Skipping invalid tool (not in unified format):`, tool);
      return null;
    }).filter(tool => tool !== null);
  }

  private async getOrCreateAssistantAndVectorStore(settings: LLMSettings): Promise<{ assistantId: string, vectorStoreId: string }> {
    // Get OpenAI-specific formatted tools
    const openAITools = await this.getOpenAITools(settings);
    
    // Ensure we're using a compatible model for assistants
    let assistantModel = settings.model;
    const compatibleModels = ['gpt-4o', 'gpt-4o-mini', 'gpt-4-turbo', 'gpt-4', 'gpt-3.5-turbo'];
    if (!compatibleModels.some(model => assistantModel.includes(model))) {
      console.warn(`‚ö†Ô∏è Model ${assistantModel} may not be compatible with Assistants API, using gpt-4o-mini as fallback`);
      assistantModel = 'gpt-4o-mini';
    }
    
    // Create a vector store
    console.log('Creating a new vector store...');
    const vectorStoreResponse = await fetch('https://api.openai.com/v1/vector_stores', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`,
        'OpenAI-Beta': 'assistants=v2'
      },
      body: JSON.stringify({ name: 'Document Store' })
    });
    if (!vectorStoreResponse.ok) {
      const errorText = await vectorStoreResponse.text();
      console.error('‚ùå Vector store creation failed:', errorText);
      throw new Error(`Failed to create vector store: ${vectorStoreResponse.status} - ${errorText}`);
    }
    const vectorStore = await vectorStoreResponse.json();
    console.log(`Vector store created with ID: ${vectorStore.id}`);

    // Prepare tools array - start with file_search
    const tools: Array<{ type: string; function?: { name: string; description: string; parameters: any } }> = [{ type: 'file_search' }];
    
    // Add formatted OpenAI tools
    if (openAITools.length > 0) {
      console.log(`üîß Adding ${openAITools.length} formatted tools to OpenAI assistant`);
      tools.push(...openAITools as any[]);
    }

    // Create an assistant linked to the vector store with OpenAI tools
    console.log(`Creating a new assistant with file search and ${tools.length - 1} OpenAI tools...`);
    console.log('üîß Tools being sent to OpenAI:', JSON.stringify(tools, null, 2));
    
    const assistantPayload = {
      name: 'AI Assistant with MCP Tools',
      instructions: 'You are a helpful assistant that can search documents and use various tools to help users.',
      tools: tools,
      tool_resources: { file_search: { vector_store_ids: [vectorStore.id] } },
      model: assistantModel
    };
    
    console.log('üîß Assistant payload:', JSON.stringify(assistantPayload, null, 2));
    
    const assistantResponse = await fetch('https://api.openai.com/v1/assistants', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`,
        'OpenAI-Beta': 'assistants=v2'
      },
      body: JSON.stringify(assistantPayload)
    });
    
    if (!assistantResponse.ok) {
      const errorText = await assistantResponse.text();
      console.error('‚ùå OpenAI API Error:', {
        status: assistantResponse.status,
        statusText: assistantResponse.statusText,
        body: errorText,
        model: assistantModel,
        toolCount: tools.length
      });
      throw new Error(`Failed to create assistant: ${assistantResponse.status} ${assistantResponse.statusText} - ${errorText}`);
    }
    const assistant = await assistantResponse.json();
    console.log(`Assistant created with ID: ${assistant.id}`);

    return { assistantId: assistant.id, vectorStoreId: vectorStore.id };
  }

  private async getOrCreateThread(conversationId: string | undefined, settings: LLMSettings): Promise<string> {
    if (conversationId && this.threadMap.has(conversationId)) {
      return this.threadMap.get(conversationId)!;
    }

    const response = await fetch('https://api.openai.com/v1/threads', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`,
        'OpenAI-Beta': 'assistants=v2'
      }
    });
    const thread = await response.json();
    if (conversationId) {
      this.threadMap.set(conversationId, thread.id);
    }
    return thread.id;
  }

