  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.log('‚ùå No OpenAI API key provided - cannot fetch models');
      return [];
    }

    try {
      const response = await fetch('https://api.openai.com/v1/models', {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        console.warn(`‚ùå OpenAI API error: ${response.status} - check API key`);
        return [];
      }

      const data = await response.json() as { data: Array<{ id: string }> };
      const models = data.data
        .filter((model) => model.id.includes('gpt'))
        .map((model) => model.id)
        .sort();

      return models;
    } catch (error) {
      console.warn('‚ùå Failed to fetch OpenAI models:', error);
      return [];
    }
  }

  formatTools(tools: ToolObject[]): unknown[] {
    // OpenAI format - array of tool objects with type: 'function'
    return tools.map(tool => ({
      type: 'function',
      function: {
        name: tool.name || tool.function?.name,
        description: tool.description || tool.function?.description,
        parameters: tool.parameters || tool.function?.parameters || {
          type: 'object',
          properties: {},
          required: []
        }
      }
    }));
  }

  getSystemPrompt(): string {
    return OPENAI_SYSTEM_PROMPT;
  }

  enhanceSystemPromptWithTools(basePrompt: string, tools: ToolObject[]): string {
    if (tools.length === 0) {
      return basePrompt;
    }

    // OpenAI uses structured tool calling with tools parameter and tool_choice
    // Don't add XML tool instructions as they conflict with native function calling
    console.log(`üîß OpenAI using structured tools, skipping XML tool instructions`);
    return basePrompt;
  }

  validateToolCall(toolCall: { id?: string; name: string; arguments: Record<string, unknown> }): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!toolCall.id) {
      errors.push(`OpenAI tool call missing required id: ${toolCall.name}`);
    }

    if (!toolCall.name || typeof toolCall.name !== 'string') {
      errors.push('Tool call must have a valid name');
    }

    if (!toolCall.arguments || typeof toolCall.arguments !== 'object') {
      errors.push('Tool call must have valid arguments object');
    }

    return { valid: errors.length === 0, errors };
  }

  validateTool(tool: unknown): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!tool || typeof tool !== 'object') {
      errors.push('Tool must be an object');
      return { valid: false, errors };
    }

    const toolObj = tool as Record<string, unknown>;

    if (!toolObj.type || toolObj.type !== 'function') {
      errors.push('OpenAI tools must have type: "function"');
    }

    if (!toolObj.function || typeof toolObj.function !== 'object') {
      errors.push('OpenAI tools must have function object');
    } else {
      const func = toolObj.function as Record<string, unknown>;
      if (!func.name) {
        errors.push('OpenAI tools must have function.name');
      }
      if (func.name && typeof func.name === 'string' && func.name.length > 64) {
        errors.push('OpenAI function names must be ‚â§64 characters');
      }
    }

    return { valid: errors.length === 0, errors };
  }

  // Private helper methods
  // Note: These methods are injected by the ProviderAdapter from the LLMService
  private getMCPToolsForProvider!: (providerId: string, settings: LLMSettings) => Promise<unknown[]>;
  private shouldSendTools!: (conversationId: string | undefined, tools: ToolObject[]) => Promise<boolean>;
  private executeMCPTool!: (toolName: string, args: Record<string, unknown>) => Promise<string>;

  private async handleStreamResponse(
    response: Response,
    onStream: (chunk: string) => void,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    conversationId?: string
  ): Promise<LLMResponse> {
    console.log(`üîç Starting OpenAI stream response handling...`);
    const reader = response.body?.getReader();
    if (!reader) {
      throw new Error('No response body');
    }

    let fullContent = '';
    let usage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined = undefined;
    let chunkCount = 0;
    const toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }> = [];
    const decoder = new TextDecoder();

    try {
      // eslint-disable-next-line no-constant-condition
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        chunkCount++;
        if (chunkCount <= 3) {
          console.log(`üîç OpenAI stream chunk ${chunkCount}:`, chunk.substring(0, 200) + (chunk.length > 200 ? '...' : ''));
        }
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const data = line.slice(6);
            if (data === '[DONE]') continue;

            try {
              const parsed = JSON.parse(data);
              if (chunkCount <= 5) {
                console.log(`üîç OpenAI parsed chunk ${chunkCount}:`, JSON.stringify(parsed, null, 2));
              }

              const choice = parsed.choices?.[0];
              const delta = choice?.delta;
              const content = delta?.content || '';

              if (content) {
                fullContent += content;
                onStream(content);
                console.log(`üìù OpenAI content chunk: "${content}"`);
              }

              // Check for tool calls and assemble them
              if (delta?.tool_calls) {
                console.log(`üîß OpenAI tool calls detected:`, delta.tool_calls);

                for (const toolCall of delta.tool_calls) {
                  const index = toolCall.index;

                  // Initialize tool call if not exists
                  if (!toolCalls[index]) {
                    toolCalls[index] = {
                      id: toolCall.id || '',
                      type: toolCall.type || 'function',
                      function: {
                        name: toolCall.function?.name || '',
                        arguments: ''
                      }
                    };
                  }

                  // Append arguments
                  if (toolCall.function?.arguments && toolCalls[index].function) {
                    toolCalls[index].function!.arguments += toolCall.function.arguments;
                  }

                  // Set name if provided
                  if (toolCall.function?.name && toolCalls[index].function) {
                    toolCalls[index].function!.name = toolCall.function.name;
                  }

                  // Set id if provided
                  if (toolCall.id) {
                    toolCalls[index].id = toolCall.id;
                  }
                }
              }

              // Capture usage data if available
              if (parsed.usage) {
                usage = parsed.usage;
              }
            } catch (error) {
              console.error(`‚ùå OpenAI error parsing chunk:`, error, `Data: ${data.substring(0, 100)}...`);
            }
          }
        }
      }
    } finally {
      reader.releaseLock();
    }

    // Filter out empty tool calls and log final state
    const validToolCalls = toolCalls.filter(tc => tc && tc.function?.name);

    console.log('üîç OpenAI stream response completed:', {
      contentLength: fullContent.length,
      hasUsage: !!usage,
      usage: usage,
      toolCallsCount: validToolCalls.length
    });

    if (validToolCalls.length > 0) {
      console.log(`üîß OpenAI assembled ${validToolCalls.length} tool calls:`, validToolCalls.map(tc => ({
        name: tc.function?.name,
        arguments: tc.function?.arguments
      })));

      // Execute tools and make follow-up call
      return this.executeToolsAndFollowUp(validToolCalls, fullContent, usage, settings, provider, conversationHistory, onStream, conversationId);
    }

    const { usage: usageInfo, cost } = this.createUsageAndCost(settings.model, usage);
    return {
      content: fullContent,
      usage: usageInfo,
      cost,
      toolCalls: validToolCalls
        .filter(tc => tc.id && tc.function?.name) // Only include tool calls with valid id and name
        .map(tc => ({
          id: tc.id!,
          name: tc.function!.name!,
          arguments: JSON.parse(tc.function!.arguments || '{}')
        }))
    };
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async executeToolsAndFollowUp(
    toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }>,
    initialContent: string,
    initialUsage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    console.log(`üîß OpenAI streaming detected ${toolCalls.length} tool calls, executing...`);

    // Check if we have parallel execution method injected (like Anthropic/Mistral)
    if ((this as any).executeMultipleToolsParallel && (this as any).summarizeToolResultsForModel) {
      console.log(`üöÄ Using parallel execution for ${toolCalls.length} OpenAI tools`);
      
      // Format tool calls for parallel execution
      const toolCallsForExecution = toolCalls.map(tc => ({
        id: tc.id || '',
        name: tc.function?.name || '',
        arguments: JSON.parse(tc.function?.arguments || '{}')
      }));

      try {
        // Execute tools in parallel immediately
        const executeMultipleToolsParallel = (this as any).executeMultipleToolsParallel;
        const summarizeToolResultsForModel = (this as any).summarizeToolResultsForModel;
        
        const parallelResults = await executeMultipleToolsParallel(toolCallsForExecution, 'openai');
        console.log(`‚úÖ OpenAI parallel execution completed: ${parallelResults.filter((r: any) => r.success).length}/${parallelResults.length} successful`);
        
        // Get tool results summary for the model
        const toolSummary = summarizeToolResultsForModel(parallelResults);
        
        // Stream the tool results to user
        onStream('\n\n' + toolSummary);
        
        // Return response with tool results included
        return {
          content: initialContent + '\n\n' + toolSummary,
          usage: initialUsage ? {
            promptTokens: initialUsage.prompt_tokens || 0,
            completionTokens: initialUsage.completion_tokens || 0,
            totalTokens: initialUsage.total_tokens || 0
          } : undefined
        };
      } catch (error) {
        console.error(`‚ùå OpenAI parallel tool execution failed, falling back to sequential:`, error);
        // Fall back to sequential execution below
      }
    }

    // Fallback: Execute all tool calls sequentially (old method)
    console.log(`‚ö†Ô∏è Using sequential execution for ${toolCalls.length} OpenAI tools`);
    const toolResults = [];
    for (const toolCall of toolCalls) {
      try {
        console.log(`üîß Executing OpenAI tool call: ${toolCall.function?.name}`);
        const toolName = toolCall.function?.name || '';
        const toolArgs = JSON.parse(toolCall.function?.arguments || '{}');
        const toolResult = await (this as any).executeMCPTool(toolName, toolArgs);
        toolResults.push({
          role: 'tool',
          tool_call_id: toolCall.id,
          content: JSON.stringify(toolResult)
        });
      } catch (error) {
        console.error(`‚ùå OpenAI tool execution failed:`, error);
        toolResults.push({
          role: 'tool',
          tool_call_id: toolCall.id,
          content: JSON.stringify({ error: error instanceof Error ? error.message : String(error) })
        });
      }
    }

    // Make follow-up call with tool results
    const userMessages = conversationHistory.filter(msg => msg.role !== 'system');

    // Convert tool calls to OpenAI format for follow-up
    const openaiToolCalls = toolCalls.map(tc => ({
      id: tc.id || '',
      type: 'function',
      function: {
        name: tc.function?.name || '',
        arguments: tc.function?.arguments || '{}'
      }
    }));

    // Use same behavioral system prompt as initial call (for consistency and caching)
    const hasCustomSystemPromptFollowUp = settings.systemPrompt &&
      settings.systemPrompt.trim() &&
      settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

    const baseSystemPrompt = hasCustomSystemPromptFollowUp ? settings.systemPrompt! : this.getSystemPrompt();
    const followUpSystemPrompt = baseSystemPrompt +
      `\n\n## Follow-up Context\n\nBased on the tool results provided above, continue the conversation naturally. If you need to use additional tools to better answer the user's question, feel free to do so.`;

    const followUpMessages = [
      { role: 'system', content: followUpSystemPrompt },
      ...userMessages,
      { role: 'assistant', content: initialContent, tool_calls: openaiToolCalls },
      ...toolResults
    ];

    // Get tools for continued agentic behavior in follow-up call
    const followUpTools = await this.getMCPToolsForProvider('openai', settings);
    console.log(`üîÑ Making OpenAI follow-up call with ${followUpTools.length} tools available for continued agentic behavior`);

    const followUpRequestBody = {
      model: settings.model,
      messages: followUpMessages,
      temperature: settings.temperature,
      max_tokens: settings.maxTokens,
      stream: false,
      // Include tools to allow continued agentic behavior
      ...(followUpTools.length > 0 && {
        tools: followUpTools,
        tool_choice: 'auto'
      })
    };

    const followUpResponse = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`
      },
      body: JSON.stringify(followUpRequestBody)
    });

    if (followUpResponse.ok) {
      const followUpData = await followUpResponse.json();
      const followUpMessage = followUpData.choices[0]?.message;

      // Combine responses
      const combinedUsage = {
        promptTokens: (initialUsage?.prompt_tokens || 0) + (followUpData.usage?.prompt_tokens || 0),
        completionTokens: (initialUsage?.completion_tokens || 0) + (followUpData.usage?.completion_tokens || 0),
        totalTokens: (initialUsage?.total_tokens || 0) + (followUpData.usage?.total_tokens || 0)
      };

      // Stream the follow-up content
      if (followUpMessage?.content) {
        onStream(followUpMessage.content);
      }

      return {
        content: followUpMessage?.content || 'Tool execution completed.',
        usage: combinedUsage,
        toolCalls: toolCalls
          .filter(tc => tc.id && tc.function?.name)
          .map(tc => ({
            id: tc.id!,
            name: tc.function!.name!,
            arguments: JSON.parse(tc.function!.arguments || '{}')
          }))
      };
    } else {
      const errorText = await followUpResponse.text();
      console.error(`‚ùå OpenAI follow-up call failed (${followUpResponse.status}):`, errorText);

      // Return original response with tool calls
      return {
        content: initialContent,
        usage: initialUsage ? {
          promptTokens: initialUsage.prompt_tokens || 0,
          completionTokens: initialUsage.completion_tokens || 0,
          totalTokens: initialUsage.total_tokens || 0
        } : undefined,
        toolCalls: toolCalls
          .filter(tc => tc.id && tc.function?.name)
          .map(tc => ({
            id: tc.id!,
            name: tc.function!.name!,
            arguments: JSON.parse(tc.function!.arguments || '{}')
          }))
      };
    }
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleNonStreamResponse(
    response: Response,
    settings: LLMSettings,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    const data = await response.json();
    const choice = data.choices[0];
    const message = choice.message;

    // Handle tool calls if present
    if (message.tool_calls && message.tool_calls.length > 0) {
      console.log(`üîß OpenAI response contains ${message.tool_calls.length} tool calls:`, message.tool_calls);
      const content = message.content || '';

      // Tool execution will be handled by the main service
      // For now, just return the content with tool calls
      return {
        content,
        usage: data.usage ? {
          promptTokens: data.usage.prompt_tokens,
          completionTokens: data.usage.completion_tokens,
          totalTokens: data.usage.total_tokens
        } : undefined,
        toolCalls: message.tool_calls.map((tc: { id: string; function: { name: string; arguments: string } }) => ({
          id: tc.id,
          name: tc.function.name,
          arguments: JSON.parse(tc.function.arguments)
        }))
      };
    }

    const { usage, cost } = this.createUsageAndCost(settings.model, data.usage);
    return {
      content: message.content,
      usage,
      cost
    };
  }

  /**
   * Calculate cost for OpenAI API usage
   */
  private calculateCost(model: string, promptTokens: number, completionTokens: number) {
    return PricingService.calculateCost('openai', model, promptTokens, completionTokens);
  }

  /**
   * Create usage and cost information from OpenAI API response
   */
  private createUsageAndCost(model: string, usage?: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number }) {
    if (!usage) return { usage: undefined, cost: undefined };

    const usageInfo = {
      promptTokens: usage.prompt_tokens || 0,
      completionTokens: usage.completion_tokens || 0,
      totalTokens: usage.total_tokens || 0
    };

    const costInfo = this.calculateCost(model, usageInfo.promptTokens, usageInfo.completionTokens);

    return { usage: usageInfo, cost: costInfo };
  }
}

// OpenRouter provider implementation

import { BaseProvider } from './BaseProvider';
import {
  LLMSettings,
  LLMResponse,
  MessageContent,
  ContentItem,
  LLMProvider,
  ToolObject,
  ProviderCapabilities,
  APIResponseData
} from './types';

import { OPENROUTER_SYSTEM_PROMPT, generateOpenRouterToolPrompt } from './prompts/openrouter';
// import { RAGService } from '../RAGService'; // Moved to Electron main process, accessed via IPC

export class OpenRouterProvider extends BaseProvider {
  readonly id = 'openrouter';
  readonly name = 'OpenRouter';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: true,
    supportsTools: true,
    supportsStreaming: true,
    supportsSystemMessages: true,
    supportsPromptCaching: true,
    promptCachingType: 'both', // Supports both automatic (OpenAI/Grok/DeepSeek) and manual (Anthropic/Gemini)
    maxToolNameLength: 64,
    toolFormat: 'openai'
  };

  // OpenRouter-specific tool calling methods
  private async getOpenRouterTools(settings: LLMSettings): Promise<unknown[]> {
    try {
      console.log(`üîç Getting tools for OpenRouter provider`);
      console.log(`üîç Tool calling enabled:`, settings?.toolCallingEnabled !== false);

      // Check if tool calling is disabled
      if (settings?.toolCallingEnabled === false) {
        console.log(`üö´ Tool calling is disabled, returning empty tools array`);
        return [];
      }

      // Get raw tools from the centralized service (temporarily)
      const rawTools = await this.getMCPToolsForProvider('openrouter', settings);
      console.log(`üìã Raw tools received (${rawTools.length} tools):`, rawTools.map((t: unknown) => (t as {name?: string, function?: {name?: string}}).name || (t as {name?: string, function?: {name?: string}}).function?.name));

      // Format tools specifically for OpenRouter (uses OpenAI format)
      const formattedTools = this.formatToolsForOpenRouter(rawTools);
      console.log(`üîß Formatted ${formattedTools.length} tools for OpenRouter`);

      return formattedTools;
    } catch (error) {
      console.error('‚ùå Failed to get OpenRouter tools:', error);
      return [];
    }
  }

  private formatToolsForOpenRouter(rawTools: unknown[]): unknown[] {
    return rawTools.map(tool => {
      const typedTool = tool as {type?: string, function?: {name?: string, description?: string, parameters?: unknown}, name?: string, description?: string, inputSchema?: unknown};

      // All tools now come in unified format with type: 'function' and function object
      if (typedTool.type === 'function' && typedTool.function) {
        return {
          type: 'function',
          function: {
            name: typedTool.function.name || 'unknown_tool',
            description: typedTool.function.description || 'No description',
            parameters: typedTool.function.parameters || {
              type: 'object',
              properties: {},
              required: []
            }
          }
        };
      }

      // Handle MCP tools (need conversion to OpenAI format)
      if (typedTool.name && typedTool.description) {
        return {
          type: 'function',
          function: {
            name: typedTool.name,
            description: typedTool.description,
            parameters: typedTool.inputSchema || {
              type: 'object',
              properties: {},
              required: []
            }
          }
        };
      }

      console.warn(`‚ö†Ô∏è Skipping invalid tool:`, tool);
      return null;
    }).filter(tool => tool !== null);
  }

  private detectUnderlyingProvider(model: string): string {
    // Detect underlying provider from OpenRouter model name
    if (model.startsWith('openai/')) return 'openai';
    if (model.startsWith('anthropic/')) return 'anthropic';
    if (model.startsWith('google/')) return 'google';
    if (model.startsWith('meta-llama/')) return 'meta';
    if (model.startsWith('mistral/') || model.startsWith('mistralai/')) return 'mistral';
    if (model.startsWith('cohere/')) return 'cohere';
    if (model.startsWith('perplexity/')) return 'perplexity';

    // Default to OpenAI format for unknown models (safest)
    console.log(`‚ö†Ô∏è Unknown OpenRouter model prefix for "${model}", defaulting to OpenAI format`);
    return 'openai';
  }

  private modelSupportsStructuredTools(underlyingProvider: string): boolean {
    // Check if underlying provider supports structured tool calling
    switch (underlyingProvider) {
      case 'openai':
      case 'anthropic':
      case 'google':
      case 'mistral':
        return true;
      case 'meta':
      case 'cohere':
      case 'perplexity':
      default:
        return false; // Use text-based tool descriptions
    }
  }

  private modelSupportsManualCaching(underlyingProvider: string): boolean {
    // Check if underlying provider supports manual cache_control parameters
    switch (underlyingProvider) {
      case 'anthropic':
      case 'google': // Gemini models
        return true;
      case 'openai':
      case 'grok':
      case 'deepseek':
        return false; // These use automatic caching
      default:
        return false;
    }
  }

  private addCacheControlToSystemPrompt(systemPrompt: string, underlyingProvider: string, cachingEnabled: boolean): unknown {
    // Add cache_control to system prompt for providers that support manual caching
    if (!cachingEnabled || !this.modelSupportsManualCaching(underlyingProvider)) {
      return systemPrompt;
    }

    if (underlyingProvider === 'anthropic') {
      // Anthropic format: array of content objects with cache_control
      return [
        {
          type: 'text',
          text: systemPrompt,
          cache_control: { type: 'ephemeral' }
        }
      ];
    } else if (underlyingProvider === 'google') {
      // For Gemini via OpenRouter, system prompt caching is handled differently
      // Return as string but we'll add cache_control to messages instead
      return systemPrompt;
    }

    return systemPrompt;
  }

  private addCacheControlToLargeContent(content: Array<ContentItem>, enableCaching: boolean): Array<ContentItem> {
    if (!enableCaching) {
      return content;
    }

    // Add cache_control to large text content (>1024 tokens estimated)
    return content.map((item, index) => {
      if (item.type === 'text' && item.text) {
        // Rough token estimation: ~4 characters per token
        const estimatedTokens = item.text.length / 4;

        // Add cache_control to large content (>1024 tokens) and make it the last cacheable item
        if (estimatedTokens > 1024 && index === content.length - 1) {
          return {
            ...item,
            cache_control: { type: 'ephemeral' as const }
          };
        }
      }
      return item;
    });
  }

  private async buildProviderSpecificRequest(
    underlyingProvider: string,
    settings: LLMSettings,
    systemPrompt: string,
    tools: unknown[],
    message: MessageContent,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream?: (chunk: string) => void
  ): Promise<{ requestBody: Record<string, unknown>; messages: unknown[] }> {

    const supportsStructuredTools = this.modelSupportsStructuredTools(underlyingProvider);
    const supportsManualCaching = this.modelSupportsManualCaching(underlyingProvider);
    const cachingEnabled = settings.promptCachingEnabled ?? false;

    console.log(`üîß OpenRouter underlying provider "${underlyingProvider}" supports structured tools: ${supportsStructuredTools}`);
    console.log(`üîß OpenRouter underlying provider "${underlyingProvider}" supports manual caching: ${supportsManualCaching}, enabled: ${cachingEnabled}`);

    // Build messages based on provider format
    let messages: unknown[];
    let requestBody: Record<string, unknown>;

    if (underlyingProvider === 'anthropic') {
      // Anthropic format: system parameter + messages without system role
      messages = await this.constructMessagesWithFiles(message, conversationHistory, '', cachingEnabled && supportsManualCaching);
      const systemWithCaching = this.addCacheControlToSystemPrompt(systemPrompt, underlyingProvider, cachingEnabled);
      requestBody = {
        model: settings.model,
        system: systemWithCaching, // System parameter with optional caching
        messages,
        temperature: settings.temperature,
        max_tokens: settings.maxTokens,
        stream: !!onStream
      };
    } else {
      // OpenAI format (default): system message first in messages array
      messages = await this.constructMessagesWithFiles(message, conversationHistory, systemPrompt, cachingEnabled && supportsManualCaching);
      requestBody = {
        model: settings.model,
        messages,
        temperature: settings.temperature,
        max_tokens: settings.maxTokens,
        stream: !!onStream
      };
    }

    // Add tools based on provider capabilities
    if (supportsStructuredTools && tools.length > 0) {
      requestBody.tools = tools;
      requestBody.tool_choice = 'auto';
      console.log(`üöÄ OpenRouter API call with ${tools.length} structured tools for ${underlyingProvider} model`);
    } else if (tools.length > 0) {
      // For models without structured tools, use enhanced system prompt with proper tool calling instructions
      const enhancedSystemPrompt = this.enhanceSystemPromptWithTools(systemPrompt, tools as ToolObject[], underlyingProvider);
      console.log(`üîß OpenRouter enhanced system prompt for ${underlyingProvider} (${enhancedSystemPrompt.length - systemPrompt.length} chars added)`);

      if (underlyingProvider === 'anthropic') {
        requestBody.system = enhancedSystemPrompt;
      } else {
        // Update system message in messages array
        const systemMessageIndex = (messages as Array<{role: string, content: string}>).findIndex(m => m.role === 'system');
        if (systemMessageIndex >= 0) {
          (messages as Array<{role: string, content: string}>)[systemMessageIndex].content = enhancedSystemPrompt;
        }
      }
      console.log(`üöÄ OpenRouter API call with ${tools.length} text-based tools for ${underlyingProvider} model (enhanced system prompt)`);
    } else {
      console.log(`üöÄ OpenRouter API call without tools for ${underlyingProvider} model`);
    }

    return { requestBody, messages };
  }

  private formatToolsAsText(tools: ToolObject[]): string {
    return tools.map(tool => {
      const name = tool.name || tool.function?.name || 'unknown_tool';
      const description = tool.description || tool.function?.description || 'No description';
      const parameters = tool.parameters || tool.function?.parameters || {};

      return `- ${name}: ${description}\n  Parameters: ${JSON.stringify(parameters, null, 2)}`;
    }).join('\n\n');
  }

  /**
   * Parse text-based tool calls from model response content
   * Handles various formats that models might use when structured tool calling isn't supported
   */
  private parseTextBasedToolCalls(content: string, availableTools: string[] = []): Array<{ name: string; arguments: Record<string, unknown> }> {
    const toolCalls: Array<{ name: string; arguments: Record<string, unknown> }> = [];

    console.log(`üîç OpenRouter parsing text for tool calls in content:`, content.substring(0, 200) + '...');

    // Pattern 0: Simple functions.tool_name:id format (most common)
    // functions.list_processes:0, functions.write_file:1, etc.
    const simpleFunctionRegex = /functions\.([a-zA-Z_][a-zA-Z0-9_]*):(\d+)/gi;
    let simpleMatch = simpleFunctionRegex.exec(content);
    while (simpleMatch) {
      const toolName = simpleMatch[1];
      const toolId = simpleMatch[2];

      // Check if this tool is available
      if (availableTools.length === 0 || availableTools.includes(toolName)) {
        // For simple format, we need to extract arguments from context or use empty object
        const args = this.extractArgumentsForSimpleToolCall(content, toolName, toolId);
        toolCalls.push({ name: toolName, arguments: args });
        console.log(`‚úÖ Found simple function format tool call: ${toolName}:${toolId} with args:`, args);
      } else {
        console.log(`‚ö†Ô∏è Tool ${toolName} not in available tools list:`, availableTools);
      }
      simpleMatch = simpleFunctionRegex.exec(content);
    }

    // Pattern 1: Custom OpenRouter/model-specific format with delimiters
    // <|tool_calls_section_begin|><|tool_call_begin|>functions.tool_name:1<|tool_call_argument_begin|>{"arg": "value"}<|tool_call_end|><|tool_calls_section_end|>
    const customFormatRegex = /<\|tool_call_begin\|>(?:functions\.)?([^:]+):\d+<\|tool_call_argument_begin\|>(\{[\s\S]*?\})<\|tool_call_end\|>/gi;
    let match = customFormatRegex.exec(content);
    while (match) {
      try {
        const toolName = match[1];
        let args = JSON.parse(match[2]);

        // Handle nested input structure: {"input": {"path": "..."}} -> {"path": "..."}
        if (args.input && typeof args.input === 'object' && Object.keys(args).length === 1) {
          args = args.input;
          console.log(`üîß Unwrapped nested input structure for ${toolName}`);
        }

        toolCalls.push({ name: toolName, arguments: args });
        console.log(`‚úÖ Found custom format tool call: ${toolName} with args:`, args);
      } catch (error) {
        console.warn(`‚ö†Ô∏è Failed to parse custom format tool call:`, match[0], error);
      }
      match = customFormatRegex.exec(content);
    }

    // Pattern 2: Enhanced tool_call format with ```json wrapper (Option 2)
    // ```json { "tool_call": { "name": "web_search", "arguments": {...} } } ```
    const jsonWrappedToolCallRegex = /```json\s*(\{[\s\S]*?"tool_call"[\s\S]*?\})\s*```/gi;
    match = jsonWrappedToolCallRegex.exec(content);
    if (match) {
      try {
        const jsonObj = JSON.parse(match[1]);
        if (jsonObj.tool_call && jsonObj.tool_call.name && jsonObj.tool_call.arguments) {
          toolCalls.push({
            name: jsonObj.tool_call.name,
            arguments: jsonObj.tool_call.arguments
          });
          console.log(`‚úÖ Found JSON-wrapped tool call: ${jsonObj.tool_call.name} with args:`, jsonObj.tool_call.arguments);
          return toolCalls; // Return early if we found the structured format
        }
      } catch (error) {
        console.log(`‚ö†Ô∏è Failed to parse JSON-wrapped tool call:`, match[1], error);
      }
    }

    // Pattern 3: Direct JSON tool_call format (Option 1)
    // { "tool_call": { "name": "web_search", "arguments": {...} } }
    const directToolCallRegex = /\{\s*"tool_call"\s*:\s*\{\s*"name"\s*:\s*"([^"]+)"\s*,\s*"arguments"\s*:\s*(\{[\s\S]*?\})\s*\}\s*\}/gi;
    match = directToolCallRegex.exec(content);
    if (match) {
      try {
        const toolName = match[1];
        const args = JSON.parse(match[2]);
        toolCalls.push({ name: toolName, arguments: args });
        console.log(`‚úÖ Found direct tool call: ${toolName} with args:`, args);
        return toolCalls; // Return early if we found the structured format
      } catch (error) {
        console.log(`‚ö†Ô∏è Failed to parse direct tool call arguments:`, match[2], error);
        // Try fallback parsing
        const toolName = match[1];
        const args = this.parseArgumentsFromText(match[2]);
        if (Object.keys(args).length > 0) {
          toolCalls.push({ name: toolName, arguments: args });
          console.log(`‚úÖ Found direct tool call with fallback parsing: ${toolName} with args:`, args);
          return toolCalls;
        }
      }
    }

    // If we found structured tool calls, return them
    if (toolCalls.length > 0) {
      console.log(`‚úÖ Found ${toolCalls.length} structured tool calls, returning them`);
      return toolCalls;
    }

    // STEP 2: If no structured tool calls found, search for traces of tool usage in text
    console.log(`üîç No structured tool calls found, searching for tool usage traces in text...`);

    return this.parseToolTracesFromText(content, availableTools);
  }

  /**
   * Extract arguments for simple tool calls (functions.tool_name:id format)
   * Looks for JSON objects or argument patterns near the tool call
   */
  private extractArgumentsForSimpleToolCall(content: string, toolName: string, toolId: string): Record<string, unknown> {
    // Look for JSON objects near the tool call
    const toolCallPattern = `functions\\.${toolName}:${toolId}`;
    const toolCallIndex = content.search(new RegExp(toolCallPattern));

    if (toolCallIndex === -1) {
      return {};
    }

    // Search for JSON objects in the surrounding context (500 chars before and after)
    const contextStart = Math.max(0, toolCallIndex - 500);
    const contextEnd = Math.min(content.length, toolCallIndex + 500);
    const context = content.substring(contextStart, contextEnd);

    // Look for JSON objects in the context
    const jsonRegex = /\{[^{}]*(?:\{[^{}]*\}[^{}]*)*\}/g;
    let match;
    while ((match = jsonRegex.exec(context)) !== null) {
      try {
        const parsed = JSON.parse(match[0]);
        // If it's a valid object with reasonable properties, use it
        if (typeof parsed === 'object' && parsed !== null && Object.keys(parsed).length > 0) {
          console.log(`üîß Extracted arguments for ${toolName} from context:`, parsed);
          return parsed;
        }
      } catch {
        // Continue searching
      }
    }

    // If no JSON found, return empty object (tool will use defaults)
    console.log(`üîß No arguments found for ${toolName}, using empty object`);
    return {};
  }

  /**
   * Parse tool usage traces from natural language text
   */
  private parseToolTracesFromText(content: string, availableTools: string[]): Array<{ name: string; arguments: Record<string, unknown> }> {
    const toolCalls: Array<{ name: string; arguments: Record<string, unknown> }> = [];

    console.log(`üîç Searching for tool usage traces in content...`);

    for (const toolName of availableTools) {
      // Pattern 1: Direct tool mentions with arguments
      // "I'll use web_search with query 'weather Paris'"
      const directMentionPattern = new RegExp(`(?:use|using|call|calling)\\s+${toolName}\\s+(?:with|for)\\s+([^.!?]+)`, 'gi');
      const directMatch = directMentionPattern.exec(content);
      if (directMatch && !toolCalls.find(tc => tc.name === toolName)) {
        const argsText = directMatch[1].trim();
        const args = this.parseArgumentsFromText(argsText);
        if (Object.keys(args).length > 0) {
          toolCalls.push({ name: toolName, arguments: args });
          console.log(`‚úÖ Found direct mention: ${toolName} with args:`, args);
          continue;
        }
      }

      // Pattern 2: Function call style mentions
      // "web_search('weather Paris')" or "web_search(query='weather Paris')"
      const functionCallPattern = new RegExp(`${toolName}\\s*\\(([^)]+)\\)`, 'gi');
      const funcMatch = functionCallPattern.exec(content);
      if (funcMatch && !toolCalls.find(tc => tc.name === toolName)) {
        const argsText = funcMatch[1].trim();
        const args = this.parseArgumentsFromText(argsText);
        if (Object.keys(args).length > 0) {
          toolCalls.push({ name: toolName, arguments: args });
          console.log(`‚úÖ Found function call: ${toolName}(${argsText}) -> args:`, args);
          continue;
        }
      }

      // Pattern 3: Action descriptions
      // "I'll search for weather in Paris" (for web_search tool)
      if (toolName === 'web_search' || toolName === 'web-search') {
        const searchPattern = /(?:search|find|look up|query)(?:\s+for)?\s+([^.!?]+)/gi;
        const searchMatch = searchPattern.exec(content);
        if (searchMatch && !toolCalls.find(tc => tc.name === toolName)) {
          const query = searchMatch[1].trim().replace(/['"]/g, '');
          if (query.length > 2) {
            toolCalls.push({ name: toolName, arguments: { query } });
            console.log(`‚úÖ Found search action: ${toolName} with query: "${query}"`);
            continue;
          }
        }
      }
    }

    console.log(`üîç Found ${toolCalls.length} tool usage traces`);
    return toolCalls;
  }

  /**
   * Parse arguments from text using various heuristics
   */
  private parseArgumentsFromText(text: string): Record<string, unknown> {
    const args: Record<string, unknown> = {};

    try {
      // Try to parse as JSON first
      const jsonMatch = text.match(/\{[\s\S]*\}/);
      if (jsonMatch) {
        return JSON.parse(jsonMatch[0]);
      }
    } catch {
      // Continue with other parsing methods
    }

    // Parse key=value pairs
    const keyValuePattern = /(\w+)\s*[=:]\s*['"]?([^'",]+)['"]?/g;
    let match;
    while ((match = keyValuePattern.exec(text)) !== null) {
      args[match[1]] = match[2].trim();
    }

    // If no key-value pairs found, treat as single query parameter
    if (Object.keys(args).length === 0) {
      const cleanText = text.replace(/['"]/g, '').trim();
      if (cleanText.length > 0) {
        args.query = cleanText;
      }
    }

    return args;
  }

  /**
   * Get available tool names for text-based parsing
   */
  private async getAvailableToolNames(settings: LLMSettings): Promise<string[]> {
    try {
      const tools = await this.getOpenRouterTools(settings);
      return tools.map((tool: unknown) => {
        const typedTool = tool as { function?: { name?: string } };
        return typedTool.function?.name || 'unknown_tool';
      }).filter(name => name !== 'unknown_tool');
    } catch (error) {
      console.error('‚ùå Failed to get tool names for parsing:', error);
      return [];
    }
  }

  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    // Detect underlying provider from model name
    const underlyingProvider = this.detectUnderlyingProvider(settings.model);
    const supportsStructuredTools = this.modelSupportsStructuredTools(underlyingProvider);
    console.log(`üîç OpenRouter model "${settings.model}" detected as underlying provider: ${underlyingProvider}`);
    console.log(`üîç OpenRouter structured tools support: ${supportsStructuredTools} for provider: ${underlyingProvider}`);

    // Get OpenRouter-specific formatted tools
    const openRouterTools = await this.getOpenRouterTools(settings);

    // Use behavioral system prompt only (no tool descriptions)
    // Check for meaningful system prompt, not just empty string or generic default
    const hasCustomSystemPrompt = settings.systemPrompt &&
      settings.systemPrompt.trim() &&
      settings.systemPrompt !== "You are a helpful AI assistant. Please provide concise and helpful responses.";

    const systemPrompt = hasCustomSystemPrompt ? settings.systemPrompt! : this.getSystemPrompt();

    console.log(`üîç OpenRouter system prompt source:`, {
      hasCustom: hasCustomSystemPrompt,
      usingCustom: hasCustomSystemPrompt,
      promptLength: systemPrompt?.length || 0,
      promptStart: systemPrompt?.substring(0, 100) + '...',
      underlyingProvider
    });

    // Build request based on underlying provider format
    const { requestBody } = await this.buildProviderSpecificRequest(
      underlyingProvider,
      settings,
      systemPrompt,
      openRouterTools,
      message,
      conversationHistory,
      onStream
    );




    const response = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`,
        'HTTP-Referer': 'https://littlellm.app',
        'X-Title': 'LittleLLM'
      },
      body: JSON.stringify(requestBody),
      signal
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`OpenRouter API error: ${error}`);
    }

    if (onStream) {
      return this.handleStreamResponse(response, onStream, settings, provider, conversationHistory, signal);
    } else {
      return this.handleNonStreamResponse(response, settings, conversationHistory, conversationId);
    }
  }

  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.log('‚ùå No OpenRouter API key provided - cannot fetch models');
      return [];
    }

    try {
      const response = await fetch('https://openrouter.ai/api/v1/models', {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        console.warn(`‚ùå OpenRouter API error: ${response.status} - check API key`);
        return [];
      }

      const data = await response.json() as APIResponseData;
      const models = data.data?.map((model) => model.id)?.sort() || [];

      return models;
    } catch (error) {
      console.warn('‚ùå Failed to fetch OpenRouter models:', error);
      return [];
    }
  }

  formatTools(tools: ToolObject[]): unknown[] {
    return tools.map(tool => ({
      type: 'function',
      function: {
        name: tool.name || tool.function?.name,
        description: tool.description || tool.function?.description,
        parameters: tool.parameters || tool.function?.parameters || {
          type: 'object',
          properties: {},
          required: []
        }
      }
    }));
  }

  getSystemPrompt(): string {
    return OPENROUTER_SYSTEM_PROMPT;
  }

  enhanceSystemPromptWithTools(basePrompt: string, tools: ToolObject[], underlyingProvider?: string): string {
    if (tools.length === 0) {
      return basePrompt;
    }

    // For structured tool calling providers, don't add XML tool instructions
    // The tools are sent via the tools parameter and the LLM should use native function calling
    const provider = underlyingProvider || 'openai'; // Default to openai if not specified
    const supportsStructuredTools = this.modelSupportsStructuredTools(provider);

    if (supportsStructuredTools) {
      // For structured tools, just return the base prompt
      // The LLM will use native function calling based on the tools parameter
      console.log(`üîß OpenRouter using structured tools for ${provider}, skipping XML tool instructions`);
      return basePrompt;
    } else {
      // For text-based tool calling, add the complex tool instructions with XML format
      console.log(`üîß OpenRouter using text-based tools for ${provider}, adding XML tool instructions`);
      const toolInstructions = generateOpenRouterToolPrompt(tools);
      return basePrompt + toolInstructions;
    }
  }

  validateToolCall(toolCall: { id?: string; name: string; arguments: Record<string, unknown> }): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!toolCall.name) {
      errors.push('Tool call must have a name');
    }

    if (toolCall.name && typeof this.capabilities.maxToolNameLength === 'number' && toolCall.name.length > this.capabilities.maxToolNameLength) {
      errors.push(`Tool name must be ‚â§${this.capabilities.maxToolNameLength} characters`);
    }

    return { valid: errors.length === 0, errors };
  }

  private async constructMessagesWithFiles(
    message: MessageContent,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    systemPrompt: string,
    enableCaching = false
  ): Promise<Array<{role: string, content: string | Array<ContentItem>}>> {
    const messages: Array<{role: string, content: string | Array<ContentItem>}> = [];

    if (systemPrompt) {
      messages.push({ role: 'system', content: systemPrompt });
    }

    messages.push(...conversationHistory);

    if (typeof message === 'string') {
      // For string messages, add cache_control if it's large content
      if (enableCaching && message.length > 4096) { // ~1024 tokens
        const contentWithCaching: Array<ContentItem> = [
          {
            type: 'text',
            text: message,
            cache_control: { type: 'ephemeral' }
          }
        ];
        messages.push({ role: 'user', content: contentWithCaching });
      } else {
        messages.push({ role: 'user', content: message });
      }
    } else if (Array.isArray(message)) {
      const userContent: Array<ContentItem> = [];
      for (const item of message) {
        if (item.type === 'file' && item.fileContent) {
          const fileExtension = item.fileName?.split('.').pop()?.toLowerCase() || '';
          let mimeType = 'application/octet-stream';
          if (fileExtension) {
            switch (fileExtension) {
              case 'pdf':
                mimeType = 'application/pdf';
                break;
              case 'doc':
              case 'docx':
                mimeType = 'application/vnd.openxmlformats-officedocument.wordprocessingml.document';
                break;
              case 'csv':
                mimeType = 'text/csv';
                break;
              case 'md':
                mimeType = 'text/markdown';
                break;
              case 'txt':
                mimeType = 'text/plain';
                break;
            }
          }
          userContent.push({
            type: 'image_url',
            image_url: {
              url: `data:${mimeType};base64,${item.fileContent}`
            }
          });
        } else {
          userContent.push(item);
        }
      }

      // Add cache_control to large content items
      const contentWithCaching = this.addCacheControlToLargeContent(userContent, enableCaching);
      messages.push({ role: 'user', content: contentWithCaching });
    }

    return messages;
  }

  validateTool(tool: unknown): { valid: boolean; errors: string[] } {
    const errors: string[] = [];

    if (!tool || typeof tool !== 'object') {
      errors.push('Tool must be an object');
      return { valid: false, errors };
    }

    const toolObj = tool as Record<string, unknown>;

    if (toolObj.type !== 'function') {
      errors.push('OpenRouter tools must have type "function"');
    }

    if (!toolObj.function || typeof toolObj.function !== 'object') {
      errors.push('OpenRouter tools must have function object');
    } else {
      const func = toolObj.function as Record<string, unknown>;
      if (!func.name) {
        errors.push('OpenRouter tools must have function.name');
      }
      if (func.name && typeof func.name === 'string' && func.name.length > 64) {
        errors.push('OpenRouter function names must be ‚â§64 characters');
      }
    }

    return { valid: errors.length === 0, errors };
  }

  // Private helper methods
  // These methods are injected by the ProviderAdapter from the LLMService
  private getMCPToolsForProvider!: (providerId: string, settings: LLMSettings) => Promise<unknown[]>;
  private executeMCPTool!: (toolName: string, args: Record<string, unknown>) => Promise<string>;

  private async executeToolsAndFollowUp(
    toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }>,
    initialContent: string,
    initialUsage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream?: (chunk: string) => void
  ): Promise<LLMResponse> {
    console.log(`üîß OpenRouter detected ${toolCalls.length} tool calls, executing...`);

    // Convert to standard format for tool execution
    const standardToolCalls = toolCalls
      .filter(tc => tc.id && tc.function?.name)
      .map(tc => ({
        id: tc.id!,
        name: tc.function!.name!,
        arguments: JSON.parse(tc.function!.arguments || '{}')
      }));

    // Execute all tool calls in parallel
    const toolResults = [];
    for (const toolCall of standardToolCalls) {
      try {
        console.log(`üîß Executing OpenRouter tool call: ${toolCall.name} with args:`, toolCall.arguments);
        console.log(`üîß OpenRouter executeMCPTool method available:`, typeof this.executeMCPTool);

        const toolResult = await this.executeMCPTool(toolCall.name, toolCall.arguments);
        console.log(`‚úÖ OpenRouter tool execution successful for ${toolCall.name}:`, toolResult?.substring(0, 100) + '...');

        toolResults.push({
          role: 'tool',
          tool_call_id: toolCall.id,
          name: toolCall.name,
          content: toolResult
        });
      } catch (error) {
        console.error(`‚ùå OpenRouter tool execution failed for ${toolCall.name}:`, error);
        console.error(`‚ùå Error details:`, {
          name: error instanceof Error ? error.name : 'Unknown',
          message: error instanceof Error ? error.message : String(error),
          stack: error instanceof Error ? error.stack : undefined
        });

        const errorMessage = `Tool execution failed: ${error instanceof Error ? error.message : String(error)}`;
        toolResults.push({
          role: 'tool',
          tool_call_id: toolCall.id,
          name: toolCall.name,
          content: JSON.stringify({ error: errorMessage })
        });
      }
    }

    // Build follow-up messages in OpenAI format
    const followUpMessages = [
      ...conversationHistory,
      {
        role: 'assistant',
        content: initialContent || null,
        tool_calls: toolCalls.map(tc => ({
          id: tc.id || '',
          type: 'function' as const,
          function: {
            name: tc.function?.name || '',
            arguments: tc.function?.arguments || '{}'
          }
        }))
      },
      ...toolResults,
      {
        role: 'user',
        content: 'Please provide a final response based on the tool execution results above. Analyze the data and answer the user\'s most recent question directly. Do not repeat previous responses or refer to earlier requests in this conversation.'
      }
    ];

    console.log(`üîÑ Making OpenRouter follow-up call to process tool results...`);

    // Get tools for follow-up call to maintain structured tool calling context
    const tools = await this.getOpenRouterTools(settings);
    const underlyingProvider = this.detectUnderlyingProvider(settings.model);
    const supportsStructuredTools = this.modelSupportsStructuredTools(underlyingProvider);

    const followUpRequestBody: Record<string, unknown> = {
      model: settings.model,
      messages: followUpMessages,
      temperature: settings.temperature,
      max_tokens: settings.maxTokens,
      stream: false
    };

    // Include tools in follow-up call to maintain structured tool calling context
    if (supportsStructuredTools && tools.length > 0) {
      followUpRequestBody.tools = tools;
      followUpRequestBody.tool_choice = 'auto';
      console.log(`üîß OpenRouter follow-up call includes ${tools.length} structured tools to maintain context`);
    }

    const followUpResponse = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`,
        'HTTP-Referer': 'https://littlellm.app',
        'X-Title': 'LittleLLM'
      },
      body: JSON.stringify(followUpRequestBody)
    });

    if (followUpResponse.ok) {
      const followUpData = await followUpResponse.json();
      const followUpMessage = followUpData.choices[0]?.message;

      // Combine usage statistics
      const combinedUsage = {
        promptTokens: (initialUsage?.prompt_tokens || 0) + (followUpData.usage?.prompt_tokens || 0),
        completionTokens: (initialUsage?.completion_tokens || 0) + (followUpData.usage?.completion_tokens || 0),
        totalTokens: (initialUsage?.total_tokens || 0) + (followUpData.usage?.total_tokens || 0)
      };

      // Stream the follow-up content if streaming is enabled
      if (onStream && followUpMessage?.content) {
        onStream(followUpMessage.content);
      }

      return {
        content: followUpMessage?.content || 'Tool execution completed.',
        usage: combinedUsage,
        toolCalls: standardToolCalls
      };
    } else {
      const errorText = await followUpResponse.text();
      console.error(`‚ùå OpenRouter follow-up call failed (${followUpResponse.status}):`, errorText);

      // Return original response with tool calls
      return {
        content: initialContent || 'Tool execution completed, but follow-up failed.',
        usage: initialUsage ? {
          promptTokens: initialUsage.prompt_tokens || 0,
          completionTokens: initialUsage.completion_tokens || 0,
          totalTokens: initialUsage.total_tokens || 0
        } : undefined,
        toolCalls: standardToolCalls
      };
    }
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleStreamResponse(
    response: Response,
    onStream: (chunk: string) => void,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    signal?: AbortSignal
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */

    console.log(`üîç Starting OpenRouter stream response handling...`);
    const reader = response.body?.getReader();
    if (!reader) {
      throw new Error('No response body');
    }

    let fullContent = '';
    let usage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined = undefined;
    let chunkCount = 0;
    const toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }> = [];
    const decoder = new TextDecoder();

    try {
      // eslint-disable-next-line no-constant-condition
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        const chunk = decoder.decode(value);
        chunkCount++;
        if (chunkCount <= 3) {
          console.log(`üîç OpenRouter stream chunk ${chunkCount}:`, chunk.substring(0, 200) + (chunk.length > 200 ? '...' : ''));
        }
        const lines = chunk.split('\n');

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const data = line.slice(6);
            if (data === '[DONE]') continue;

            try {
              const parsed = JSON.parse(data);
              const delta = parsed.choices?.[0]?.delta;

              if (delta?.content) {
                fullContent += delta.content;
                onStream(delta.content);
              }

              if (delta?.tool_calls) {
                for (const toolCall of delta.tool_calls) {
                  const index = toolCall.index || 0;
                  if (!toolCalls[index]) {
                    toolCalls[index] = {
                      id: toolCall.id,
                      type: toolCall.type,
                      function: { name: '', arguments: '' }
                    };
                  }

                  if (toolCall.function?.name) {
                    toolCalls[index].function!.name = toolCall.function.name;
                  }
                  if (toolCall.function?.arguments) {
                    toolCalls[index].function!.arguments += toolCall.function.arguments;
                  }
                }
              }

              if (parsed.usage) {
                usage = parsed.usage;
              }
            } catch (error) {
              console.error(`‚ùå OpenRouter error parsing chunk:`, error, `Data: ${data.substring(0, 100)}...`);
            }
          }
        }
      }
    } finally {
      reader.releaseLock();
    }

    // Filter out empty tool calls and log final state
    const validToolCalls = toolCalls.filter(tc => tc && tc.function?.name);

    console.log(`üîç OpenRouter stream response completed:`, {
      contentLength: fullContent.length,
      hasUsage: !!usage,
      usage: usage,
      toolCallsCount: validToolCalls.length
    });

    // Check for text-based tool calls if no structured tool calls found
    if (validToolCalls.length === 0 && fullContent && (fullContent.includes('tool_call') || fullContent.includes('<|tool_call') || fullContent.includes('functions.'))) {
      console.log(`üîç OpenRouter: No structured tool calls found in stream, checking for text-based tool calls...`);

      // Get available tool names for parsing
      const availableTools = await this.getAvailableToolNames(settings);
      const textBasedToolCalls = this.parseTextBasedToolCalls(fullContent, availableTools);

      if (textBasedToolCalls.length > 0) {
        console.log(`üîß OpenRouter: Found ${textBasedToolCalls.length} text-based tool calls in stream:`, textBasedToolCalls);

        // Convert to structured format and execute
        const structuredToolCalls = textBasedToolCalls.map((tc, index) => ({
          id: `call_${Date.now()}_${index}`,
          type: 'function' as const,
          function: {
            name: tc.name,
            arguments: JSON.stringify(tc.arguments)
          }
        }));

        // Execute tools and get follow-up response
        return this.executeToolsAndFollowUp(
          structuredToolCalls,
          fullContent,
          usage,
          settings,
          provider,
          conversationHistory,
          onStream
        );
      }
    }

    if (validToolCalls.length > 0) {
      console.log(`üîß OpenRouter assembled ${validToolCalls.length} structured tool calls:`, validToolCalls.map(tc => ({
        name: tc.function?.name,
        arguments: tc.function?.arguments
      })));

      // Execute tools and make follow-up call
      return this.executeToolsAndFollowUp(validToolCalls, fullContent, usage, settings, provider, conversationHistory, onStream);
    }

    return {
      content: fullContent,
      usage: usage ? {
        promptTokens: usage.prompt_tokens || 0,
        completionTokens: usage.completion_tokens || 0,
        totalTokens: usage.total_tokens || 0
      } : undefined,
      toolCalls: validToolCalls
        .filter(tc => tc.id && tc.function?.name)
        .map(tc => ({
          id: tc.id!,
          name: tc.function!.name!,
          arguments: JSON.parse(tc.function!.arguments || '{}')
        }))
    };
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleNonStreamResponse(
    response: Response,
    settings: LLMSettings,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    const data = await response.json();
    const choice = data.choices[0];
    const message = choice.message;

    console.log('üîç OpenRouter non-stream response:', {
      hasToolCalls: !!(message.tool_calls && message.tool_calls.length > 0),
      toolCallsCount: message.tool_calls?.length || 0,
      content: message.content,
      usage: data.usage
    });

    // Check for text-based tool calls if no structured tool calls found
    if (!message.tool_calls || message.tool_calls.length === 0) {
      if (message.content && (message.content.includes('tool_call') || message.content.includes('<|tool_call') || message.content.includes('functions.'))) {
        console.log(`üîç OpenRouter: No structured tool calls found, checking for text-based tool calls...`);

        // Get available tool names for parsing
        const availableTools = await this.getAvailableToolNames(settings);
        const textBasedToolCalls = this.parseTextBasedToolCalls(message.content, availableTools);

        if (textBasedToolCalls.length > 0) {
          console.log(`üîß OpenRouter: Found ${textBasedToolCalls.length} text-based tool calls:`, textBasedToolCalls);

          // Convert to structured format and execute
          const structuredToolCalls = textBasedToolCalls.map((tc, index) => ({
            id: `call_${Date.now()}_${index}`,
            type: 'function' as const,
            function: {
              name: tc.name,
              arguments: JSON.stringify(tc.arguments)
            }
          }));

          // Execute tools and get follow-up response
          return this.executeToolsAndFollowUp(
            structuredToolCalls,
            message.content || '',
            data.usage,
            settings,
            { id: 'openrouter', name: 'OpenRouter', baseUrl: 'https://openrouter.ai/api/v1' } as LLMProvider,
            conversationHistory
          );
        }
      }
    }

    // Handle tool calls if present (OpenAI format) - execute immediately like Anthropic
    if (message.tool_calls && message.tool_calls.length > 0) {
      console.log(`üîß OpenRouter response contains ${message.tool_calls.length} tool calls:`, message.tool_calls);

      // Check if we have the parallel execution method injected
      if ((this as unknown as {executeMultipleToolsParallel?: unknown, summarizeToolResultsForModel?: unknown}).executeMultipleToolsParallel && (this as unknown as {executeMultipleToolsParallel?: unknown, summarizeToolResultsForModel?: unknown}).summarizeToolResultsForModel) {
        console.log(`üöÄ Executing ${message.tool_calls.length} OpenRouter tools immediately`);

        // Format tool calls for execution
        const toolCallsForExecution = message.tool_calls.map((toolCall: { id: string; function: { name: string; arguments: string } }) => ({
          id: toolCall.id,
          name: toolCall.function.name,
          arguments: JSON.parse(toolCall.function.arguments)
        }));

        // Execute tools in parallel immediately
        const executeMultipleToolsParallel = (this as unknown as {executeMultipleToolsParallel: unknown}).executeMultipleToolsParallel;
        const summarizeToolResultsForModel = (this as unknown as {summarizeToolResultsForModel: unknown}).summarizeToolResultsForModel;
        
        try {
          const parallelResults = await (executeMultipleToolsParallel as (calls: unknown[], provider: string) => Promise<Array<{success: boolean}>>)(toolCallsForExecution, 'openrouter');
          console.log(`‚úÖ OpenRouter tool execution completed: ${parallelResults.filter(r => r.success).length}/${parallelResults.length} successful`);

          // Get tool results summary for the model
          const toolSummary = (summarizeToolResultsForModel as (results: unknown[]) => string)(parallelResults);
          
          // Return response with tool results included
          return {
            content: (message.content || '') + '\n\n' + toolSummary,
            usage: data.usage ? {
              promptTokens: data.usage.prompt_tokens,
              completionTokens: data.usage.completion_tokens,
              totalTokens: data.usage.total_tokens
            } : undefined
          };
        } catch (error) {
          console.error(`‚ùå OpenRouter tool execution failed:`, error);
          // Fall back to returning tool calls for external handling
          return {
            content: message.content || '',
            usage: data.usage ? {
              promptTokens: data.usage.prompt_tokens,
              completionTokens: data.usage.completion_tokens,
              totalTokens: data.usage.total_tokens
            } : undefined,
            toolCalls: toolCallsForExecution
          };
        }
      } else {
        console.warn(`‚ö†Ô∏è OpenRouter provider missing tool execution methods - falling back to external handling`);
        // Fall back to external handling if methods not injected
        return {
          content: message.content || '',
          usage: data.usage ? {
            promptTokens: data.usage.prompt_tokens,
            completionTokens: data.usage.completion_tokens,
            totalTokens: data.usage.total_tokens
          } : undefined,
          toolCalls: message.tool_calls.map((tc: { id: string; function: { name: string; arguments: string } }) => ({
            id: tc.id,
            name: tc.function.name,
            arguments: JSON.parse(tc.function.arguments)
          }))
        };
      }
    }

    return {
      content: message.content,
      usage: data.usage ? {
        promptTokens: data.usage.prompt_tokens,
        completionTokens: data.usage.completion_tokens,
        totalTokens: data.usage.total_tokens
      } : undefined
    };
  }
}

// Provider adapter to integrate new provider architecture with existing llmService

import { ILLMProvider } from './BaseProvider';
import { ProviderFactory } from './ProviderFactory';
import { 
  LLMSettings, 
  LLMResponse, 
  MessageContent, 
  ContentItem, 
  LLMProvider,
  ToolObject
} from './types';

export class ProviderAdapter {
  private mcpToolsGetter?: (providerId: string, settings: LLMSettings) => Promise<unknown[]>;
  private toolExecutor?: (toolName: string, args: Record<string, unknown>) => Promise<string>;
  private streamHandler?: (response: Response, onStream: (chunk: string) => void) => Promise<LLMResponse>;
  private toolShouldSendChecker?: (conversationId: string | undefined, tools: ToolObject[]) => Promise<boolean>;
  private memoryCreator?: (userMessage: string, aiResponse: string, conversationHistory: Array<{role: string, content: string}>, conversationId?: string) => Promise<void>;

  // New tool execution dependencies
  private multipleToolsExecutor?: (
    toolCalls: Array<{ id?: string; name: string; arguments: Record<string, unknown> }>,
    provider?: string
  ) => Promise<Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>>;
  private toolResultsSummarizer?: (
    results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
  ) => string;
  private toolResultsAggregator?: (
    results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
  ) => string;
  private toolResultFormatter?: (toolName: string, result: unknown) => string;

  // Inject dependencies from the main service
  setMCPToolsGetter(getter: (providerId: string, settings: LLMSettings) => Promise<unknown[]>) {
    this.mcpToolsGetter = getter;
  }

  setToolExecutor(executor: (toolName: string, args: Record<string, unknown>) => Promise<string>) {
    this.toolExecutor = executor;
  }

  setStreamHandler(handler: (response: Response, onStream: (chunk: string) => void) => Promise<LLMResponse>) {
    this.streamHandler = handler;
  }

  setToolShouldSendChecker(checker: (conversationId: string | undefined, tools: ToolObject[]) => Promise<boolean>) {
    this.toolShouldSendChecker = checker;
  }

  setMemoryCreator(creator: (userMessage: string, aiResponse: string, conversationHistory: Array<{role: string, content: string}>, conversationId?: string) => Promise<void>) {
    this.memoryCreator = creator;
  }

  setMultipleToolsExecutor(executor: (
    toolCalls: Array<{ id?: string; name: string; arguments: Record<string, unknown> }>,
    provider?: string
  ) => Promise<Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>>) {
    this.multipleToolsExecutor = executor;
  }

  setToolResultsSummarizer(summarizer: (
    results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
  ) => string) {
    this.toolResultsSummarizer = summarizer;
  }

  setToolResultsAggregator(aggregator: (
    results: Array<{ id?: string; name: string; result: string; success: boolean; executionTime: number }>
  ) => string) {
    this.toolResultsAggregator = aggregator;
  }

  setToolResultFormatter(formatter: (toolName: string, result: unknown) => string) {
    this.toolResultFormatter = formatter;
  }

  // Check if a provider is available in the new architecture
  hasProvider(providerId: string): boolean {
    return ProviderFactory.hasProvider(providerId);
  }

  // Get provider instance
  getProvider(providerId: string): ILLMProvider | null {
    return ProviderFactory.getProvider(providerId);
  }

  // Send message using new provider architecture
  async sendMessage(
    providerId: string,
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    const providerInstance = this.getProvider(providerId);
    if (!providerInstance) {
      throw new Error(`Provider ${providerId} not found in new architecture`);
    }

    // Inject dependencies into the provider instance
    await this.injectDependencies(providerInstance);

    // Use the provider's sendMessage method
    return providerInstance.sendMessage(
      message,
      settings,
      provider,
      conversationHistory,
      onStream,
      signal,
      conversationId
    );
  }

  // Fetch models using new provider architecture
  async fetchModels(providerId: string, apiKey: string, baseUrl?: string): Promise<string[]> {
    const providerInstance = this.getProvider(providerId);
    if (!providerInstance) {
      throw new Error(`Provider ${providerId} not found in new architecture`);
    }

    return providerInstance.fetchModels(apiKey, baseUrl);
  }

  // Format tools using new provider architecture
  formatTools(providerId: string, tools: ToolObject[]): unknown[] {
    const providerInstance = this.getProvider(providerId);
    if (!providerInstance) {
      throw new Error(`Provider ${providerId} not found in new architecture`);
    }

    return providerInstance.formatTools(tools);
  }

  // Get system prompt using new provider architecture
  getSystemPrompt(providerId: string): string {
    const providerInstance = this.getProvider(providerId);
    if (!providerInstance) {
      throw new Error(`Provider ${providerId} not found in new architecture`);
    }

    return providerInstance.getSystemPrompt();
  }

  // Validate tool call using new provider architecture
  validateToolCall(providerId: string, toolCall: { id?: string; name: string; arguments: Record<string, unknown> }): { valid: boolean; errors: string[] } {
    const providerInstance = this.getProvider(providerId);
    if (!providerInstance) {
      return { valid: false, errors: [`Provider ${providerId} not found`] };
    }

    return providerInstance.validateToolCall(toolCall);
  }

  // Validate tool using new provider architecture
  validateTool(providerId: string, tool: unknown): { valid: boolean; errors: string[] } {
    const providerInstance = this.getProvider(providerId);
    if (!providerInstance) {
      return { valid: false, errors: [`Provider ${providerId} not found`] };
    }

    return providerInstance.validateTool(tool);
  }

  // Get provider capabilities
  getCapabilities(providerId: string) {
    const providerInstance = this.getProvider(providerId);
    return providerInstance?.capabilities;
  }

  // Private method to inject dependencies into provider instances
  private async injectDependencies(providerInstance: ILLMProvider) {
    console.log(`üîß ProviderAdapter: Injecting dependencies into ${providerInstance.id}`);

    // Inject the MCP tools getter - always inject if available
    if (this.mcpToolsGetter) {
      (providerInstance as unknown as {getMCPToolsForProvider: unknown}).getMCPToolsForProvider = this.mcpToolsGetter;
      console.log(`‚úÖ ProviderAdapter: Injected getMCPToolsForProvider into ${providerInstance.id}`);
    } else {
      console.warn(`‚ö†Ô∏è ProviderAdapter: No mcpToolsGetter available for ${providerInstance.id}`);
    }

    // Inject the tool executor - always inject if available
    if (this.toolExecutor) {
      (providerInstance as unknown as {executeMCPTool: unknown}).executeMCPTool = this.toolExecutor;
    }

    // Inject the parallel tool executor (like Anthropic uses) - always inject if available
    if (this.multipleToolsExecutor) {
      (providerInstance as unknown as {executeMultipleToolsParallel: unknown}).executeMultipleToolsParallel = this.multipleToolsExecutor;
    }

    // Inject tool result processing methods (like Anthropic uses) - always inject if available
    if (this.toolResultsSummarizer) {
      (providerInstance as unknown as {summarizeToolResultsForModel: unknown}).summarizeToolResultsForModel = this.toolResultsSummarizer;
    }

    if (this.toolResultsAggregator) {
      (providerInstance as unknown as {aggregateToolResults: unknown}).aggregateToolResults = this.toolResultsAggregator;
    }

    if (this.toolResultFormatter) {
      (providerInstance as unknown as {formatToolResult: unknown}).formatToolResult = this.toolResultFormatter;
    }

    // Inject the stream handler - always inject if available
    if (this.streamHandler) {
      (providerInstance as unknown as {handleStreamResponse: unknown}).handleStreamResponse = this.streamHandler;
    }

    // Inject the tool should send checker
    if (this.toolShouldSendChecker && 'shouldSendTools' in providerInstance) {
      (providerInstance as unknown as {shouldSendTools: unknown}).shouldSendTools = this.toolShouldSendChecker;
    }

    // Inject the memory creator
    if (this.memoryCreator && 'createMemoryFromConversation' in providerInstance) {
      (providerInstance as unknown as {createMemoryFromConversation: unknown}).createMemoryFromConversation = this.memoryCreator;
    }

    // Inject tool names for text-based tool calling providers
    if ('setAvailableToolNames' in providerInstance && this.mcpToolsGetter) {
      try {
        // Get the actual MCP tools and extract their names
        const mcpTools = await this.mcpToolsGetter(providerInstance.id, {} as LLMSettings);
        const toolNames = (mcpTools as Array<{name?: string, function?: {name?: string}}>).map(tool => {
          // Handle different tool formats
          if (tool.name) return tool.name;
          if (tool.function?.name) return tool.function.name;
          return null;
        }).filter(Boolean) as string[];

        console.log(`üîß Injecting ${toolNames.length} tool names into ${providerInstance.id}:`, toolNames);
        (providerInstance as unknown as {setAvailableToolNames: (names: string[]) => void}).setAvailableToolNames(toolNames);
      } catch (error) {
        console.warn(`‚ö†Ô∏è Failed to inject tool names into ${providerInstance.id}:`, error);
      }
    }

    // Special injection for AnthropicProvider with new tool execution methods
    if (providerInstance.id === 'anthropic' && 'injectDependencies' in providerInstance) {
      const anthropicProvider = providerInstance as unknown as {injectDependencies?: (deps: unknown) => void};
      if (anthropicProvider.injectDependencies) {
        anthropicProvider.injectDependencies({
          executeMultipleToolsParallel: this.multipleToolsExecutor,
          summarizeToolResultsForModel: this.toolResultsSummarizer,
          aggregateToolResults: this.toolResultsAggregator,
          formatToolResult: this.toolResultFormatter,
          getMCPToolsForProvider: this.mcpToolsGetter
        });
      }
    }
  }
}

export default ProviderAdapter;

// Provider factory for managing LLM providers

import { ILLMProvider } from './BaseProvider';
import { debugLogger } from '../debugLogger';
import { OpenAIProvider } from './OpenAIProvider';
import { AnthropicProvider } from './AnthropicProvider';
import { GeminiProvider } from './GeminiProvider';
import { MistralProvider } from './MistralProvider';
import { DeepSeekProvider } from './DeepSeekProvider';
import { DeepinfraProvider } from './DeepinfraProvider';
import { LMStudioProvider } from './LMStudioProvider';
import { JanProvider } from './JanProvider';
import { OllamaProvider } from './OllamaProvider';
import { OpenRouterProvider } from './OpenRouterProvider';
import { RequestyProvider } from './RequestyProvider';
import { ReplicateProvider } from './ReplicateProvider';
import { N8NProvider } from './N8NProvider';
// import { MistralProvider } from './MistralProvider';
// import { DeepSeekProvider } from './DeepSeekProvider';
// import { LMStudioProvider } from './LMStudioProvider';
// import { OllamaProvider } from './OllamaProvider';
// import { OpenRouterProvider } from './OpenRouterProvider';
// import { RequestyProvider } from './RequestyProvider';
// import { ReplicateProvider } from './ReplicateProvider';
// import { N8NProvider } from './N8NProvider';

export class ProviderFactory {
  private static providers: Map<string, ILLMProvider> = new Map();

  static {
    // Initialize providers
    this.registerProvider(new OpenAIProvider());
    this.registerProvider(new AnthropicProvider());
    this.registerProvider(new GeminiProvider());
    this.registerProvider(new MistralProvider());
    this.registerProvider(new DeepSeekProvider());
    this.registerProvider(new DeepinfraProvider());
    this.registerProvider(new LMStudioProvider());
    this.registerProvider(new JanProvider());
    this.registerProvider(new OllamaProvider());
    this.registerProvider(new OpenRouterProvider());
    this.registerProvider(new RequestyProvider());
    this.registerProvider(new ReplicateProvider());
    this.registerProvider(new N8NProvider());
    // this.registerProvider(new MistralProvider());
    // this.registerProvider(new DeepSeekProvider());
    // this.registerProvider(new LMStudioProvider());
    // this.registerProvider(new OllamaProvider());
    // this.registerProvider(new OpenRouterProvider());
    // this.registerProvider(new RequestyProvider());
    // this.registerProvider(new ReplicateProvider());
    // this.registerProvider(new N8NProvider());
  }

  private static registerProvider(provider: ILLMProvider): void {
    this.providers.set(provider.id, provider);
    debugLogger.info('PROVIDER', `Registered provider: ${provider.id} (${provider.name})`);
  }

  static getProvider(providerId: string): ILLMProvider | null {
    const provider = this.providers.get(providerId);
    if (!provider) {
      console.warn(`‚ö†Ô∏è Provider not found: ${providerId}`);
      return null;
    }
    return provider;
  }

  static getAllProviders(): ILLMProvider[] {
    return Array.from(this.providers.values());
  }

  static getProviderIds(): string[] {
    return Array.from(this.providers.keys());
  }

  static hasProvider(providerId: string): boolean {
    return this.providers.has(providerId);
  }

  static getProviderCapabilities(providerId: string) {
    const provider = this.getProvider(providerId);
    return provider?.capabilities;
  }
}

export default ProviderFactory;

// Replicate provider implementation

import { BaseProvider } from './BaseProvider';
import { 
  LLMSettings, 
  LLMResponse, 
  MessageContent, 
  ContentItem, 
  LLMProvider,
  ToolObject,
  ProviderCapabilities
} from './types';

import { REPLICATE_SYSTEM_PROMPT, generateReplicateToolPrompt } from './prompts/replicate';

export class ReplicateProvider extends BaseProvider {
  readonly id = 'replicate';
  readonly name = 'Replicate';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: true,
    supportsTools: false, // Replicate doesn't have native tool calling
    supportsStreaming: true, // Replicate supports streaming via webhooks
    supportsSystemMessages: true,
    maxToolNameLength: undefined,
    toolFormat: 'custom'
  };

  /* eslint-disable @typescript-eslint/no-unused-vars */
  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    _conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    // Replicate has a different API structure
    let prompt = '';

    // Build prompt from conversation history and current message
    const systemPrompt = settings.systemPrompt || this.getSystemPrompt();
    if (systemPrompt) {
      prompt += `System: ${systemPrompt}\n\n`;
    }

    // Add conversation history
    for (const msg of conversationHistory) {
      const role = msg.role === 'assistant' ? 'Assistant' : 'User';
      const content = typeof msg.content === 'string' ? msg.content : JSON.stringify(msg.content);
      prompt += `${role}: ${content}\n\n`;
    }

    // Add current message
    const currentContent = typeof message === 'string' ? message : JSON.stringify(message);
    prompt += `User: ${currentContent}\n\nAssistant:`;

    const requestBody = {
      input: {
        prompt: prompt,
        max_new_tokens: settings.maxTokens,
        temperature: settings.temperature,
        top_p: 0.9,
        top_k: 50,
        stop_sequences: "<|endoftext|>,<|im_end|>"
      }
    };

    const response = await fetch(`${provider.baseUrl}/predictions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Token ${settings.apiKey}`
      },
      body: JSON.stringify({
        version: settings.model,
        input: requestBody.input
      }),
      signal
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Replicate API error: ${error}`);
    }

    const data = await response.json();
    
    // Replicate returns a prediction object, we need to poll for completion
    if (data.status === 'starting' || data.status === 'processing') {
      // For simplicity, we'll wait for completion
      const completedData = await this.pollForCompletion(data.urls.get, settings.apiKey, signal);
      return this.parseReplicateResponse(completedData, prompt);
    }

    return this.parseReplicateResponse(data, prompt);
  }

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.error('‚ùå No Replicate API key provided - cannot fetch models');
      throw new Error('Replicate API key is required to fetch available models. Please add your API key in settings.');
    }

    // Replicate doesn't have a simple models endpoint like other providers
    // Users need to specify the exact model version hash they want to use
    throw new Error('Replicate requires specific model version hashes. Please specify the exact model version in the model field (e.g., "meta/llama-2-70b-chat:02e509c789964a7ea8736978a43525956ef40397be9033abf9fd2badfe68c9e3").');
  }

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  formatTools(_tools: ToolObject[]): unknown[] {
    // Replicate doesn't support tools in the same way
    return [];
  }

  getSystemPrompt(): string {
    return REPLICATE_SYSTEM_PROMPT;
  }

  enhanceSystemPromptWithTools(basePrompt: string, tools: ToolObject[]): string {
    if (tools.length === 0) {
      return basePrompt;
    }

    const toolInstructions = generateReplicateToolPrompt(tools);
    return basePrompt + toolInstructions;
  }

  // Private helper methods
  private async pollForCompletion(getUrl: string, apiKey: string, signal?: AbortSignal): Promise<{status: string, output?: unknown}> {
    let attempts = 0;
    const maxAttempts = 60; // 5 minutes max
    
    while (attempts < maxAttempts) {
      await new Promise(resolve => setTimeout(resolve, 5000)); // Wait 5 seconds
      
      const response = await fetch(getUrl, {
        headers: {
          'Authorization': `Token ${apiKey}`
        },
        signal
      });

      if (!response.ok) {
        throw new Error(`Failed to poll Replicate prediction: ${response.statusText}`);
      }

      const data = await response.json();
      
      if (data.status === 'succeeded') {
        return data;
      } else if (data.status === 'failed') {
        throw new Error(`Replicate prediction failed: ${data.error}`);
      }
      
      attempts++;
    }
    
    throw new Error('Replicate prediction timed out');
  }

  private parseReplicateResponse(data: {output?: unknown}, prompt: string): LLMResponse {
    let content = '';
    
    if (data.output) {
      if (Array.isArray(data.output)) {
        content = data.output.join('');
      } else if (typeof data.output === 'string') {
        content = data.output;
      } else {
        content = JSON.stringify(data.output);
      }
    }

    // Estimate token usage
    const usage = this.createEstimatedUsage(prompt, content, 'Replicate estimated');

    return {
      content,
      usage
    };
  }
}

// Requesty provider implementation

import { BaseProvider } from './BaseProvider';
import {
  LLMSettings,
  LLMResponse,
  MessageContent,
  ContentItem,
  LLMProvider,
  ToolObject,
  ProviderCapabilities,
  APIResponseData
} from './types';

import { REQUESTY_SYSTEM_PROMPT } from './prompts/requesty';
import { OpenAICompatibleStreaming } from './shared/OpenAICompatibleStreaming';

export class RequestyProvider extends BaseProvider {
  readonly id = 'requesty';
  readonly name = 'Requesty';
  readonly capabilities: ProviderCapabilities = {
    supportsVision: true,
    supportsTools: true,
    supportsStreaming: true,
    supportsSystemMessages: true,
    maxToolNameLength: 64,
    toolFormat: 'openai'
  };

  async sendMessage(
    message: MessageContent,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}> = [],
    onStream?: (chunk: string) => void,
    signal?: AbortSignal,
    conversationId?: string
  ): Promise<LLMResponse> {
    // Requesty uses OpenAI-compatible API
    const messages = [];

    // Get MCP tools for Requesty
    const mcpTools = await this.getMCPToolsForProvider('requesty', settings);

    // Build system prompt with tool instructions
    let systemPrompt = settings.systemPrompt || this.getSystemPrompt();
    if (mcpTools.length > 0) {
      systemPrompt = this.enhanceSystemPromptWithTools(systemPrompt, mcpTools as ToolObject[]);
    }

    if (systemPrompt) {
      messages.push({ role: 'system', content: systemPrompt });
    }

    // Add conversation history
    messages.push(...conversationHistory);

    // Add current message
    messages.push({ role: 'user', content: message });

    const requestBody: Record<string, unknown> = {
      model: settings.model,
      messages: messages,
      temperature: settings.temperature,
      max_tokens: settings.maxTokens,
      stream: !!onStream
    };

    // Add tools if available
    if (mcpTools.length > 0) {
      requestBody.tools = mcpTools;
      requestBody.tool_choice = 'auto';
    }

    const response = await fetch(`${provider.baseUrl}/chat/completions`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${settings.apiKey}`
      },
      body: JSON.stringify(requestBody),
      signal
    });

    if (!response.ok) {
      const error = await response.text();
      throw new Error(`Requesty API error: ${error}`);
    }

    if (onStream) {
      return this.handleStreamResponse(response, onStream, settings, provider, conversationHistory, signal);
    } else {
      return this.handleNonStreamResponse(response, settings, conversationHistory, conversationId);
    }
  }

  async fetchModels(apiKey: string): Promise<string[]> {
    if (!apiKey) {
      console.log('‚ùå No Requesty API key provided - cannot fetch models');
      return [];
    }

    try {
      const response = await fetch('https://router.requesty.ai/v1/models', {
        headers: {
          'Authorization': `Bearer ${apiKey}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        console.warn(`‚ùå Requesty API error: ${response.status} - check API key`);
        return [];
      }

      const data = await response.json() as APIResponseData;
      const models = data.data?.map((model) => model.id)?.sort() || [];

      return models;
    } catch (error) {
      console.warn('‚ùå Failed to fetch Requesty models:', error);
      return [];
    }
  }

  formatTools(tools: ToolObject[]): unknown[] {
    return tools.map(tool => ({
      type: 'function',
      function: {
        name: tool.name || tool.function?.name,
        description: tool.description || tool.function?.description,
        parameters: tool.parameters || tool.function?.parameters || {
          type: 'object',
          properties: {},
          required: []
        }
      }
    }));
  }

  getSystemPrompt(): string {
    return REQUESTY_SYSTEM_PROMPT;
  }

  enhanceSystemPromptWithTools(basePrompt: string, tools: ToolObject[]): string {
    if (tools.length === 0) {
      return basePrompt;
    }

    // Requesty uses structured tool calling with tools parameter and tool_choice
    // Don't add XML tool instructions as they conflict with native function calling
    console.log(`üîß Requesty using structured tools, skipping XML tool instructions`);
    return basePrompt;
  }

  // Private helper methods
  // This method is injected by the ProviderAdapter from the LLMService
  private getMCPToolsForProvider!: (providerId: string, settings: LLMSettings) => Promise<unknown[]>;

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleStreamResponse(
    response: Response,
    onStream: (chunk: string) => void,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    _signal?: AbortSignal
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    return OpenAICompatibleStreaming.handleStreamResponse(
      response,
      onStream,
      settings,
      provider,
      conversationHistory,
      'Requesty',
      this.executeToolsAndFollowUp.bind(this)
    );
  }

  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private async executeMCPTool(_toolName: string, _args: Record<string, unknown>): Promise<string> {
    // This will be injected by the main service
    return JSON.stringify({ error: 'Tool execution not available' });
  }

  private async executeToolsAndFollowUp(
    toolCalls: Array<{ id?: string; type?: string; function?: { name?: string; arguments?: string } }>,
    initialContent: string,
    initialUsage: { prompt_tokens?: number; completion_tokens?: number; total_tokens?: number } | undefined,
    settings: LLMSettings,
    provider: LLMProvider,
    conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    onStream: (chunk: string) => void
  ): Promise<LLMResponse> {
    return OpenAICompatibleStreaming.executeToolsAndFollowUp(
      toolCalls,
      initialContent,
      initialUsage,
      settings,
      provider,
      conversationHistory,
      onStream,
      'Requesty',
      this.executeMCPTool.bind(this),
      {},
      () => this.getMCPToolsForProvider(provider.id, settings),
      (tools: unknown[]) => {
        const basePrompt = settings.systemPrompt || this.getSystemPrompt();
        return this.enhanceSystemPromptWithTools(basePrompt, tools as ToolObject[]);
      }
    );
  }

  /* eslint-disable @typescript-eslint/no-unused-vars */
  private async handleNonStreamResponse(
    response: Response,
    _settings: LLMSettings,
    _conversationHistory: Array<{role: string, content: string | Array<ContentItem>}>,
    _conversationId?: string
  ): Promise<LLMResponse> {
    /* eslint-enable @typescript-eslint/no-unused-vars */
    const data = await response.json();
    const choice = data.choices[0];
    const message = choice.message;

    if (message.tool_calls && message.tool_calls.length > 0) {
      return {
        content: message.content || '',
        usage: data.usage ? {
          promptTokens: data.usage.prompt_tokens,
          completionTokens: data.usage.completion_tokens,
          totalTokens: data.usage.total_tokens
        } : undefined,
        toolCalls: message.tool_calls.map((tc: { id: string; function: { name: string; arguments: string } }) => ({
          id: tc.id,
          name: tc.function.name,
          arguments: JSON.parse(tc.function.arguments)
        }))
      };
    }

    return {
      content: message.content,
      usage: data.usage ? {
        promptTokens: data.usage.prompt_tokens,
        completionTokens: data.usage.completion_tokens,
        totalTokens: data.usage.total_tokens
      } : undefined
    };
  }
}

